--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` int(11) NOT NULL,
  `name` varchar(500) NOT NULL,
  `description` varchar(4000) NOT NULL,
  `content` text NOT NULL,
  `hot` int(11) NOT NULL DEFAULT 0,
  `photo` varchar(500) NOT NULL,
  `price` float NOT NULL,
  `discount` int(11) NOT NULL,
  `category_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(3, 'Máy điều hòa Digital Inverter WindFree™ 9,400 BTu/h (F-AR10TYGCDW20)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y điều h&ograve;a Digital Inverter WindFree&trade; 9,400 BTu/h (F-AR10TYGCDW20)</strong></span></h2>\r\n\r\n<p>- Luồng kh&iacute; lạnh l&yacute; tưởng, tiết kiệm điện với c&ocirc;ng nghệ WindFree với 23.000 lỗ si&ecirc;u nhỏ gi&uacute;p tỏa kh&iacute; lạnh nhanh m&agrave; kh&ocirc;ng lo gi&oacute; buốt.<br />\r\n- C&ocirc;ng nghệ Kh&aacute;ng khuẩn, lọc bụi mịn v&agrave; virus 99%, chống nấm mốc hiệu quả c&ugrave;ng bộ lọc TriCare.<br />\r\n- Tiết kiệm điện l&ecirc;n đến 77%, l&agrave;m lạnh nhanh với động cơ Digital Inverter Boots v&agrave; chế độ Eco.<br />\r\n- L&agrave;m lạnh hiệu quả, độ bền m&aacute;y cao với chế độ Auto Clean.<br />\r\n- M&agrave;n h&igrave;nh LED Aurora hiển thị nhiệt độ v&agrave; mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute; tiện lợi.</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Lạnh Nhanh Kh&ocirc;ng Gi&oacute; Buốt Thổi Trực Tiếp<br />\r\nC&ocirc;ng Nghệ WindFree&trade; Đầu Ti&ecirc;n Tr&ecirc;n Thế Giới</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hơn hẳn điều h&ograve;a th&ocirc;ng thường, ngo&agrave;i hiệu quả l&agrave;m lạnh nhanh ch&oacute;ng, điều h&ograve;a Samsung WindFree&trade; c&ograve;n c&oacute; th&ecirc;m lựa chọn chế độ l&agrave;m lạnh WindFree&trade; kh&ocirc;ng gi&oacute; buốt thổi trực tiếp. Chỉ với 1 thao t&aacute;c tr&ecirc;n remote đơn giản, ph&ograve;ng sẽ nhanh ch&oacute;ng đạt nhiệt độ m&aacute;t lạnh tối ưu hoặc thổi kh&iacute; lạnh nhẹ nh&agrave;ng qua 23.000 lỗ nhỏ, hạn chế gi&oacute; thổi trực tiếp m&agrave; vẫn duy tr&igrave; nhiệt độ mong muốn của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(1069).JPG\" /><br />\r\n<br />\r\n*ASHRAE (Hội kỹ sư nghi&ecirc;n cứu về Tủ lạnh, M&aacute;y Sưởi v&agrave; Điều H&ograve;a Kh&ocirc;ng Kh&iacute; tại Mỹ) định nghĩa &ldquo;Luồng kh&ocirc;ng kh&iacute; dễ chịu&quot; l&agrave; kh&ocirc;ng kh&iacute; đạt tốc độ dưới 0,15m/s v&agrave; kh&ocirc;ng c&oacute; sự hiện diện của gi&oacute; buốt. **Kiểm định tr&ecirc;n model AR12TXCAAWKNEU. Chế độ WindFree &trade; tạo ra tiếng ồn 23dB so với 26dB của model Samsung th&ocirc;ng thường. ***Kiểm định tr&ecirc;n model AR07T9170HA3 dựa tr&ecirc;n mức ti&ecirc;u thụ năng lượng của chế độ L&agrave;m Lạnh Nhanh so với chế độ WindFree&trade;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Lọc 99% Virus, Vi Khuẩn, Bụi Si&ecirc;u Mịn V&agrave; T&aacute;c Nh&acirc;n G&acirc;y Dị Ứng<br />\r\nBộ Lọc Tri-Care</strong><br />\r\nĐược chứng nhận bởi tổ chức quốc tế ITEA v&agrave; Intertek, bộ lọc Tri-Care tr&ecirc;n điều h&ograve;a Samsung c&oacute; khả năng lọc 99% virus, vi khuẩn, bụi si&ecirc;u mịn v&agrave; t&aacute;c nh&acirc;n g&acirc;y dị ứng, giữ kh&ocirc;ng kh&iacute; lu&ocirc;n trong l&agrave;nh ho&agrave;n hảo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(930).JPG\" /><br />\r\n<br />\r\n<strong>Si&ecirc;u Tiết Kiệm Điện Đến 77%*<br />\r\nĐộng Cơ Digital Inverter Boost</strong><br />\r\nC&ocirc;ng nghệ Digital Inverter Boost ưu việt gi&uacute;p tiết kiệm điện năng hiệu quả l&ecirc;n đến 77%* v&agrave; duy tr&igrave; ổn định nhiệt độ mong muốn. Với nam ch&acirc;m neodymium v&agrave; bộ giảm &acirc;m k&eacute;p Twin Tube Muffler, m&aacute;y hoạt động y&ecirc;n tĩnh, &ecirc;m &aacute;i v&agrave; bền l&acirc;u.\\</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(772).JPG\" /><br />\r\n<br />\r\n<strong>Dễ D&agrave;ng Tự Vệ Sinh Lưới Lọc<br />\r\nBộ Lọc Easy Filter Plus</strong><br />\r\nBộ lọc Easy Filter Plus thiết kế b&ecirc;n tr&ecirc;n điều h&ograve;a c&oacute; thể dễ d&agrave;ng th&aacute;o rời để l&agrave;m sạch; gi&uacute;p điều h&ograve;a lu&ocirc;n hoạt động hiệu quả. Lớp lưới d&agrave;y giữ cho bộ trao đổi nhiệt lu&ocirc;n sạch sẽ, trong khi đ&oacute; lớp phủ chống vi khuẩn bảo vệ bạn khỏi c&aacute;c chất g&acirc;y &ocirc; nhiễm nguy hiểm c&oacute; trong kh&ocirc;ng kh&iacute;*.<br />\r\n*Đ&atilde; thử nghiệm tại ph&ograve;ng th&iacute; nghiệm H&agrave;n Quốc. Dữ liệu đ&atilde; được đo trong c&aacute;c điều kiện thử nghiệm cụ thể v&agrave; c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o c&aacute;c yếu tố m&ocirc;i trường v&agrave; việc sử dụng c&aacute; nh&acirc;n.<br />\r\n<img src=\"https://cdn.mediamart.vn/images/Upload/images/4(601).JPG\" />&nbsp;<br />\r\n<br />\r\n<strong>Tự Động L&agrave;m Sạch<br />\r\nT&iacute;nh Năng Auto Clean (3 Bước)</strong><br />\r\nTiết kiệm tối ưu thời gian v&agrave; c&ocirc;ng sức vệ sinh thiết bị. Sau khi hoạt động, t&iacute;nh năng Auto Clean sẽ tự động l&agrave;m kh&ocirc; bộ trao đổi nhiệt bằng quy tr&igrave;nh 3 bước nghi&ecirc;m ngặt. Loại bỏ ẩm ướt bằng c&aacute;ch thổi kh&iacute; từ 10 đến 30 ph&uacute;t, nhờ đ&oacute; ngăn ngừa sự t&iacute;ch tụ của vi khuẩn v&agrave; m&ugrave;i h&ocirc;i.<br />\r\n<img src=\"https://cdn.mediamart.vn/images/Upload/images/5(521).JPG\" />&nbsp;<br />\r\n<br />\r\n<strong>Bảo Vệ Tối Ưu Cho Hiệu Suất Bền Bỉ<br />\r\nC&ocirc;ng Nghệ Triple Protector Plus</strong><br />\r\nAn t&acirc;m tận hưởng hiệu suất bền bỉ trước mọi th&aacute;ch thức về m&ocirc;i trường. C&ocirc;ng nghệ Bộ Ba Bảo Vệ Triple Protector Plus ngăn chặn hư hỏng do tăng &aacute;p hoặc do dao động d&ograve;ng điện m&agrave; kh&ocirc;ng cần đến ổn &aacute;p gắn rời*. Lớp phủ chống ăn m&ograve;n bảo vệ hiệu quả b&igrave;nh ngưng tụ v&agrave; buồng m&aacute;y trong mọi điều kiện thời tiết khắc nghiệt.<br />\r\n*Được kiểm nghiệm tr&ecirc;n mẫu AR12TXCAAWKEU so với mẫu m&aacute;y AS18FCMID th&ocirc;ng thường của Samsung.<br />\r\n&nbsp;<img src=\"https://cdn.mediamart.vn/images/Upload/images/6(379).JPG\" /><br />\r\n<br />\r\n<strong>Bảo Vệ Tối Ưu Bộ Trao Đổi Nhiệt<br />\r\nC&ocirc;ng Nghệ Chống Ăn M&ograve;n DuraFin&trade;</strong><br />\r\nC&ocirc;ng nghệ Chống Ăn M&ograve;n Durafin&trade; gi&uacute;p tăng cường hiệu quả hoạt động của bộ trao đổi nhiệt với chất liệu thiết kế d&agrave;y v&agrave; đặc hơn c&ugrave;ng lớp phủ cải tiến gi&uacute;p ngăn ngừa tối đa qu&aacute; tr&igrave;nh ăn m&ograve;n. Khả năng tuyệt vời n&agrave;y đ&atilde; đạt chuẩn kiểm nghiệm sự ăn m&ograve;n trong m&ocirc;i trường nước biển axit axetic (theo chuẩn SWAAT) trong hơn 100 ng&agrave;y*.<br />\r\n*Dựa tr&ecirc;n mẫu AR13TYHYBWKNST so với mẫu m&aacute;y AR18JRFNFWK th&ocirc;ng thường của Samsung.<br />\r\n&nbsp;<img src=\"https://cdn.mediamart.vn/images/Upload/images/7(286).JPG\" /><br />\r\n<br />\r\n<strong>Th&acirc;n Thiện Với M&ocirc;i Trường<br />\r\nChất L&agrave;m Lạnh R32</strong><br />\r\nĐiều h&ograve;a sử dụng chất l&agrave;m lạnh R32 thế hệ mới gi&uacute;p hạn chế t&aacute;c động l&ecirc;n tầng ozone, từ đ&oacute; ngăn chặn t&igrave;nh trạng n&oacute;ng l&ecirc;n to&agrave;n cầu hiệu quả hơn so với chất l&agrave;m lạnh R22 hoặc R410A th&ocirc;ng thường. Bảo vệ Tr&aacute;i Đất, kiểm so&aacute;t t&igrave;nh trạng biến đổi kh&iacute; hậu với giải ph&aacute;p th&acirc;n thiện m&ocirc;i trường* từ Samsung.<br />\r\n*Kiểm định tr&ecirc;n chất l&agrave;m lạnh R32 so với c&aacute;c chất l&agrave;m lạnh R410A v&agrave; R22 th&ocirc;ng thường của Samsung.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/8(202).JPG\" /></span></p>\r\n', 0, '1657443092_9400.jpg', 13500000, 6, 3),
(5, 'Galaxy A12 SM A127F Blue (Exynos850)', '<h2><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY A12 SM A127F BLACK (EXYNOS850)</span></h2>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">4 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">Exynos 850</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.5 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">5000 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi&nbsp;bật</strong></span></h2>\r\n', '<h3><span style=\"font-size:14px\"><strong>Thiết kế rắn chắc, quen thuộc</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy A12 mang diện mạo th&acirc;n thuộc của những chiếc Samsung tiền nhiệm. Với thiết kế nguy&ecirc;n khối, lớp vỏ m&aacute;y được đ&uacute;c bao quanh th&acirc;n m&aacute;y chắc chắn, bền chặt. Phần camera trước được đặt trong notch h&igrave;nh giọt nước gi&uacute;p tăng diện t&iacute;ch cho m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/o4iFygjKn75VfwW.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cụm 4 camera sau ở g&oacute;c b&ecirc;n tr&aacute;i, nằm gọn trong khung h&igrave;nh vu&ocirc;ng viền cong c&acirc;n đối v&agrave; tinh tế. C&aacute;c cạnh v&agrave; khung viền được l&agrave;m cong nhẹ mềm mại cho cảm gi&aacute;c cầm nắm sử dụng đầm tay.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/2h92527YRft9DO3.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy A12 được trang bị&nbsp;cảm biến&nbsp;v&acirc;n tay&nbsp;cạnh viền t&iacute;ch hợp với n&uacute;t nguồn gi&uacute;p tối ưu kh&ocirc;ng gian mặt trước của m&aacute;y. Thuận tiện cho việc mở&nbsp;điện thoại&nbsp;của bạn trở n&ecirc;n nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/MrcEq3gFnnTF7tL.jpg\" /></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chất lượng m&agrave;n h&igrave;nh ổn định</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc điện thoại sử dụng m&agrave;n h&igrave;nh PLS&nbsp;TFT LCD c&oacute; độ ph&acirc;n giải HD+ (720 x 1600 pixels)&nbsp;đ&aacute;p ứng kh&aacute; tốt nhu cầu hiển thị cơ bản với chất lượng ổn định. K&iacute;ch thước m&agrave;n h&igrave;nh 6.5 inch cho những trải nghiệm tr&ecirc;n m&aacute;y th&ecirc;m rộng r&atilde;i, r&otilde; r&agrave;ng hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/AW6BPMJ8KG1KInQ.jpg\" /></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Cụm 4 camera hỗ trợ chụp ảnh đa t&iacute;nh năng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị sở hữu cụm 4 camera sau bao gồm: Cảm biến ch&iacute;nh l&ecirc;n đến 48 MP,&nbsp;camera g&oacute;c si&ecirc;u rộng&nbsp;5 MP, camera 2 MP cảm biến độ s&acirc;u v&agrave;&nbsp;camera macro&nbsp;2 MP.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/SxSUtBNZ0OPh1AY.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sự kết hợp n&agrave;y tạo th&agrave;nh chiếc m&aacute;y ảnh ho&agrave;n hảo dễ d&agrave;ng lưu lại những khoảnh khắc đ&aacute;ng nhớ của bạn đầy rực rỡ. Ngo&agrave;i ra m&aacute;y c&ograve;n hỗ trợ quay video Full HD c&ugrave;ng nhiều t&iacute;nh năng kh&aacute;c gi&uacute;p bạn tạo n&ecirc;n v&ocirc; v&agrave;n những bức ảnh đẹp nhanh ch&oacute;ng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/z1tFEi96hn79Zn5.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt trước l&agrave; m&agrave;n h&igrave;nh kiểu thiết kế giọt nước chứa camera selfie c&oacute; độ ph&acirc;n giải 8 MP dễ d&agrave;ng chụp lại những tấm ảnh đẹp m&agrave; kh&ocirc;ng cần chỉnh sửa nhiều v&agrave; tự tin hơn khi video call c&ugrave;ng bạn b&egrave; mọi nơi.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Cung cấp sức mạnh bởi Exynos 850 8 nh&acirc;n</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy A12 được trang bị con chip Exynos 850 8 nh&acirc;n gi&uacute;p cho m&aacute;y hoạt động mạnh mẽ, xử l&yacute; c&aacute;c thao t&aacute;c dễ d&agrave;ng cũng như giải tr&iacute; xem phim tốt v&agrave; đặc biệt l&agrave; khả năng chơi game tr&ecirc;n điện thoại mượt m&agrave; v&agrave; ổn định.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-10-News/913hlDF68tD59E7.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y hỗ trợ c&aacute;c t&ugrave;y chọn kết nối như: 2 Nano SIM v&agrave; mạng di động&nbsp;4G&nbsp;để truy cập trang web đọc b&aacute;o, tải ứng dung, phim ảnh giải tr&iacute; v&agrave; mức&nbsp;RAM 4 GB&nbsp;cho m&aacute;y đa nhiệm ổn đi k&egrave;m với&nbsp;bộ nhớ trong 128 GB&nbsp;phục vụ tốt nhu cầu lưu dữ liệu, th&ocirc;ng tin cần thiết của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/l3zEMj55KnS0KRO.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Pin &quot;tr&acirc;u&quot; ấn tượng</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Một điểm cộng lớn của Galaxy A12 l&agrave; dung lượng vi&ecirc;n pin 5000 mAh vừa được cải tiến của m&aacute;y xứng đ&aacute;ng được gọi l&agrave;&nbsp;điện thoại pin tr&acirc;u. Giờ đ&acirc;y, smartphone vẫn lu&ocirc;n c&oacute; khả năng đ&aacute;p ứng tốt bất k&igrave; hoạt động sử dụng n&agrave;o của bạn cả ng&agrave;y d&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-1-News/A4s4l014QNjfVD4.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, Galaxy A12 c&ograve;n được trang bị c&ocirc;ng nghệ&nbsp;sạc pin nhanh&nbsp;15 W gi&uacute;p nạp đầy năng lượng cho điện thoại nhanh ch&oacute;ng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Một hiệu năng mạnh, một vi&ecirc;n pin tốt c&ugrave;ng một chiếc m&aacute;y ảnh đẹp mắt m&agrave; Samsung Galaxy A12 sở hữu cũng như l&agrave; những ti&ecirc;u ch&iacute; người d&ugrave;ng tin chọn đầu ti&ecirc;n. Hứa hẹn l&agrave; mẫu sản phẩm đem lại cảm gi&aacute;c trải nghiệm ấn tượng đến bạn.</span></p>\r\n', 1, '1657260199_a12.jpg', 4490000, 24, 9),
(6, 'Galaxy Note20 Ultra 5G', '<h2><strong>Điện thoại Samsung Note 20 Ultra 5G - Sang trọng, hiệu năng vượt trội</strong></h2>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh bi&ecirc;n bản Galaxy Note 20 thường, Samsung c&ograve;n cho ra mắt&nbsp;<strong>Note 20 Ultra 5G</strong>&nbsp;cho khả năng kết nối dữ liệu cao c&ugrave;ng thiết kế nguy&ecirc;n khối sang trọng, bắt mắt. Đ&acirc;y sẽ l&agrave; sự lựa chọn ho&agrave;n hảo d&agrave;nh cho bạn để sử dụng m&agrave; kh&ocirc;ng bị lỗi thời sau thời gian d&agrave;i ra mắt.</span></p>\r\n', '<h2><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thiết kế sang trọng, cao cấp</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Kh&aacute;c với c&aacute;c model Galaxy s20,&nbsp;<strong>thiết kế Galaxy Note 20 Ultra 5G&nbsp;</strong>sở hữu ngoại h&igrave;nh kh&aacute; mạnh mẽ bởi thiết kế vu&ocirc;ng vắn v&agrave; cứng c&aacute;p. M&aacute;y được ho&agrave;n thiện từ khung kim loại chắc chắn tạo ra những đường viền cực k&igrave; bắt mắt, kết hợp với mắt k&iacute;nh cường lực sang trọng, b&oacute;ng bẩy.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"thiết kế Galaxy Note 20 Ultra 5G Mỹ sở hữu ngoại hình khá mạnh mẽ\" src=\"https://www.didongmy.com/vnt_upload/product/08_2020/thiet-ke-galaxy-note-20-ultra-5g-my-didongmy.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chất liệu k&iacute;nh lần n&agrave;y được Samsung sử dụng ch&iacute;nh k&iacute;nh cường lực Gorilla Glass Victus kh&ocirc;ng những đẹp m&agrave; c&ograve;n gi&uacute;p bảo vệ m&aacute;y tốt hơn. Mua Galaxy Note 20 Ultra 5G bản Mỹ trong năm nay bạn sẽ c&oacute; c&aacute;c t&ugrave;y chọn m&agrave;u sắc gồm: Galaxy Note 20 Ultra m&agrave;u đen,m&agrave;u đồng huyền b&iacute; v&agrave; m&agrave;u trắng.&nbsp;</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>M&agrave;n h&igrave;nh si&ecirc;u xịn, cấu h&igrave;nh mạnh mẽ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Điện thoại Galaxy Note 20 Ultra 5G&nbsp;</strong>l&agrave; model c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh lớn nhất hiện nay l&ecirc;n đến 6.9 inch. Chất lượng hiển thị m&agrave;n h&igrave;nh v&ocirc; c&ugrave;ng xuất sắc với độ ph&acirc;n giải 2K+. Kh&ocirc;ng nhưng vậy, m&agrave;n h&igrave;nh Galaxy Note 20 Ultra 5G c&ograve;n được t&iacute;ch hợp tốc độ l&agrave;m mớ 120Hz với độ ph&acirc;n giải Quad HD+.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mua Samsung Galaxy Note 20 Ultra 5G&nbsp;hay ch&iacute;nh h&atilde;ng bạn đều sẽ trải ghiệm chất lượng m&agrave;n h&igrave;nh tốt nhất. C&aacute;c h&igrave;nh ảnh khi chơi game ở khug h&igrave;nh cao, hay phim đều được thể hiện một c&aacute;ch mượt m&agrave;, sắc n&eacute;t với sự hiện diện của c&ocirc;ng nghệ HDR10+. B&ecirc;n cạnh đ&oacute; c&aacute;c cạnh viền bezel tr&ecirc;n điện thoại được Samsung tối ưu si&ecirc;u mỏng mang đến kh&ocirc;ng gian hiển thị lớn hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Điện thoại Galaxy Note 20 Ultra 5G Mỹ là model có kích thước màn hình lớn nhất hiện nay\" src=\"https://www.didongmy.com/vnt_upload/product/08_2020/man-hinh-galaxy-note-20-ultra-5g-my-didongmy.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&aacute;nh gi&aacute;&nbsp;<strong>cấu h&igrave;nh Galaxy Note 20 Ultra 5G</strong>&nbsp;cho thấy m&aacute;y mang đến hiệu suất vượt trội nhờ trang bị chip xử l&yacute; mới nhất của Qualcomm l&agrave; Snapdragon 865+. Trong khi đ&oacute; đối với c&aacute;c biến thể Galaxy Note 20 Ultra ch&iacute;nh h&atilde;ng, hay H&agrave;n Quốc sẽ được trang bị chip Exynos 990. Theo nhiều người d&ugrave;ng đ&aacute;nh gi&aacute; th&igrave; chipset Snapdragon 865+ c&oacute; phần mạnh hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hiệu năng cả 2 điều mang đến tốc độ si&ecirc;u nhanh. Chứa hết, đi k&egrave;m với đ&oacute; l&agrave; bộ nhớ lưu trữ cực khủng với RAM 12GB v&agrave; ROM 512GB mang đến kh&ocirc;ng gian lưu trữ rộng lớn v&agrave; đa nhiệm cực mượt. Do đ&oacute; điện thoại Samsung Note 20 Ultra Mỹ ho&agrave;n to&agrave;n c&oacute; thể dư sức c&acirc;n tất cả c&aacute;c tựa game nặng nhất hiện nay, cũng như đ&aacute;p ứng tốt mọi nhu cầu người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"cấu hình Galaxy Note 20 Ultra 5G Mỹ  mang đến hiệu suất vượt trội\" src=\"https://www.didongmy.com/vnt_upload/product/08_2020/cau-hinh-galaxy-note-20-ultra-5g-my-didongmy.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hỗ trợ kết nối 5G</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hiện tại, hầu hết c&aacute;c nước tr&ecirc;n thế giới n&oacute;i chung v&agrave; Việt Nam n&oacute;i ri&ecirc;ng đều đang bắt đầu phủ s&oacute;ng băng tần 5G. Đ&acirc;y được xem l&agrave; xu hướng mới, do đ&oacute; một flagship h&agrave;ng đầu như Samsung Note 20 Ultra Mỹ cũng kh&ocirc;ng thể thiếu. Điện thoại cũng được hỗ trợ 5G tốc độ cao mang đến c&aacute;c trải nghiệm tốt hơn cho người d&ugrave;ng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Việc trang bị c&ocirc;ng nghệ 5G sẽ gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng truy cập internet với tốc độ cao l&ecirc;n đến 10Gb/s. Thậm ch&iacute; n&oacute; được đ&aacute;nh gi&aacute; nhanh hơn nhiều so với c&aacute;c đường truyền wifi c&aacute;p quang m&agrave; bạn đang sử dụng. Do đ&oacute; mua Galaxy Note 20 Ultra 5G bản Mỹ kh&ocirc;ng chỉ mang đến cấu h&igrave;nh mạnh, m&agrave;n h&igrave;nh đẹp m&agrave; c&ograve;n mang c&ocirc;ng nghệ hiện đại 5G.</span></p>\r\n\r\n<h2><span style=\"font-size:14px\"><strong>Camera n&acirc;ng cấp chuy&ecirc;n nghiệp</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">Camera l&agrave; một trong những điểm s&aacute;ng tr&ecirc;n Galaxy Note 20 Ultra 5G Mỹ khi Samsung đ&atilde; n&acirc;ng cấp v&agrave; bổ sung th&ecirc;m nhiều t&iacute;nh năng cao cấp. Cụ thể thiết lập camera Galaxy Note 20 Ultra c&oacute; độ ph&acirc;n giải gồm: &nbsp;1 cảm biến ch&iacute;nh 108MP, 1 cảm biến si&ecirc;u rộng 12MP v&agrave; 1 cảm biến tele 12MP. Hệ thống camera hỗ trợ khả năng zoom quang học l&ecirc;n đến 50X, mang đến những h&igrave;nh ảnh chất lượng d&ugrave; ở khoảng c&aacute;ch xa, g&oacute;c rộng với độ ph&acirc;n giải l&ecirc;n đến 8K.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Camera là một trong những điểm sáng trên Galaxy Note 20 Ultra 5G Mỹ\" src=\"https://www.didongmy.com/vnt_upload/product/08_2020/camera-galaxy-note-20-ultra-5g-my-didongmy.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trong khi đ&oacute; camera trước c&oacute; độ ph&acirc;n giải cực cao l&ecirc;n đến 40MP hứa hẹn mang đến khả năng chụp ảnh selfie cực đẹp.T&oacute;m lại điện thoại&nbsp;<strong>Galaxy Note 20 Ultra 5G Mỹ gi&aacute; rẻ</strong>&nbsp;với nhiều t&iacute;nh năng h&agrave;ng đầu sẽ mang lại trải nghiệm tuyệt vời nhất c&oacute; thể cho người d&ugrave;ng.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hỗ trợ sạc nhanh 45W qua USB Type C, pin dung lượng lớn 4500mAh</strong></span></h3>\r\n\r\n<p>Samsung Note 20 Ultra 5G được h&atilde;ng trang bị khả năng sạc nhanh c&ocirc;ng suất l&ecirc;n đến 45W qua cổng USB Type C cho khả năng sạc đầy pin cực kỳ nhanh ch&oacute;ng chưa đến 90 ph&uacute;t. Kh&ocirc;ng những vậy Note 20 Ultra 5G c&ograve;n c&oacute; khả năng sạc ngược cho thiết bị kh&aacute;c với c&ocirc;ng suất 9W. M&aacute;y cũng trang bị khả năng sạc kh&ocirc;ng d&acirc;y c&ocirc;ng suất l&ecirc;n đến 15W.</p>\r\n\r\n<p><img alt=\"Galaxy Note 20 và Galaxy Note 20 Ultra sẽ đi kèm với bộ sạc nhanh 25W\" src=\"https://cdn.tgdd.vn/Files/2020/07/26/1273767/1-_2000x1125-800-resize.jpg\" /></p>\r\n\r\n<p>Với hiệu năng cực khủng k&egrave;m m&agrave;n h&igrave;nh lớn Samsung đ&atilde; trang bị cho Note 20 Ultra 5G vi&ecirc;n pin li-ion c&oacute; dung lượng l&ecirc;n đến 4500mAh. Bạn c&oacute; thể sử dụng thoải m&aacute;i chiếc điện thoại của m&igrave;nh trong cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng sợ hết pin giữa chừng. Smartphone n&agrave;y c&oacute; thể c&acirc;n mọi tựa game với độ ph&acirc;n giải cao nhất l&ecirc;n đến hơn 8 tiếng li&ecirc;n tục m&agrave; kh&ocirc;ng hết pin với chế độ tiết kiệm pin.</p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>B&uacute;t S-Pen cải tiến mạnh mẽ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">B&uacute;t S-Pen&nbsp;l&agrave; sản phẩm đặc trưng của d&ograve;ng Galaxy Note, mỗi năm ra mắt điện thoại mới Samsung cũng kh&ocirc;ng qu&ecirc;n n&acirc;ng cấp chiếc b&uacute;t n&agrave;y th&ecirc;m một tầm cao mới. Cụ thể b&uacute;t S-Pen tr&ecirc;n Galaxy Note 20 Ultra 5G Mỹ được cải tiến nhiều hơn so với c&aacute;c model cũ khi mang đến độ nhạy cực nhanh v&agrave; ch&iacute;nh x&aacute;c.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Galaxy Note 20 Ultra 5G bản Mỹ đươc trang bị pin 4500mAh, hỗ trợ sạc nhanh 45W\" src=\"https://www.didongmy.com/vnt_upload/product/08_2020/but-s-pen-tren-galaxy-note-20-ultra-5g-my-didongmy.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, chiếc b&uacute;t thần th&aacute;nh n&agrave;y c&ograve;n được hỗ trợ kết nối hiện đại nhất như Wi-Fi 802.11 ac hỗ trợ băng tần k&eacute;p. Bluetooth 5.0...Ngo&agrave;i việc kế thừa c&aacute;c t&iacute;nh năng cũ, th&igrave; giờ đ&acirc;y Samsung c&ograve;n mang đến th&ecirc;m nhiều t&iacute;nh năng mới nhằm n&acirc;ng cao trải nghiệm người d&ugrave;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, '1657436791_99e61dc20bdac977de4c3cd24cc96d45-product.jpg', 32990000, 5, 10),
(7, ' Galaxy S22 Ultra 5G S908E (8+128G) Phantom', '<h2><strong><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY S22 ULTRA 5G S908E (8+128G) &nbsp;PHANTOM</span></strong></h2>\r\n\r\n<p><span style=\"font-size:14px\">Những n&acirc;ng c&acirc;́p lớn v&ecirc;̀ hi&ecirc;̣u năng, dung lượng pin, và tr&ecirc;n h&ecirc;́t là camera sau chính là những đi&ecirc;̉m khi&ecirc;́n n&oacute; trở thành si&ecirc;u phẩm trong ph&acirc;n khúc cao c&acirc;́p. Dưới đ&acirc;y l&agrave; những th&ocirc;ng tin cần biết về d&ograve;ng điện thoại vừa ra mắt của Samsung. Dưới đ&acirc;y l&agrave; th&ocirc;ng số cấu h&igrave;nh của Samsung S22 Ultra vừa được g&atilde; khổng lồ H&agrave;n Quốc giới thiệu.</span></p>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">8 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Snapdragon 8 Gen 1</span></span></span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.8 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">5000 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n', '<h3><span style=\"font-size:14px\"><strong>Thi&ecirc;́t k&ecirc;́ nguy&ecirc;n kh&ocirc;́i - kính sang trọng với đ&ocirc;̣ b&ecirc;̀n t&ocirc;́i ưu</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy S22 Ultra đi theo ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ nguy&ecirc;n kh&ocirc;́i - kính cao c&acirc;́p và sang trọng, v&ocirc;́n đã làm n&ecirc;n sự thành c&ocirc;ng của model ti&ecirc;̀n nhi&ecirc;̣m. Chi&ecirc;́c máy có thi&ecirc;́t k&ecirc;́ mặt lưng đơn sắc t&ocirc;́i giản nhưng kh&ocirc;ng kém ph&acirc;̀n tinh t&ecirc;́, và màn hình tràn vi&ecirc;̀n bao phủ g&acirc;̀n như trọn mặt trước, tạo n&ecirc;n trải nghi&ecirc;̣m quan sát r&ocirc;̣ng rãi tr&ecirc;n m&ocirc;̣t thi&ecirc;́t bị di đ&ocirc;̣ng nhỏ gọn vừa tay c&acirc;̀m.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đ&ocirc;̣ b&ecirc;̀n của m&aacute;y được đảm bảo t&ocirc;́i ưu kh&ocirc;ng chỉ qua lớp vỏ nh&ocirc;m nguy&ecirc;n kh&ocirc;́i Amor đ&aacute;nh b&oacute;ng, mà còn qua kính cường lực Corning Gorilla Glass Victus+ bao phủ hai mặt trước và sau. Khung nh&ocirc;m v&agrave; k&iacute;nh hoạt động như bộ gi&aacute;p bảo vệ điện thoại an to&agrave;n. Chi&ecirc;́c máy còn có khả năng ch&ocirc;́ng bụi / nước đạt chu&acirc;̉n IP68 giúp người dùng lu&ocirc;n an t&acirc;m khi dùng máy trong những đi&ecirc;̀u ki&ecirc;̣n m&ocirc;i trường khác nhau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Samsung S22 Ultra 5G có thiết kế sang trọng với độ bền tối ưu\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-4.png\" style=\"width:800px\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Ống kính 108MP mang lại khả năng chụp ảnh kh&ocirc;ng giới hạn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Ống k&iacute;nh ch&iacute;nh tr&ecirc;n Samsung S22 Ultra với độ ph&acirc;n giải l&ecirc;n đến 108MP, với những khả năng chụp ảnh đ&ecirc;m, chụp ch&acirc;n dung, chụp góc si&ecirc;u r&ocirc;̣ng, và zoom quang học 100x đ&ecirc;̀u hi&ecirc;̣n di&ecirc;̣n. Chi tiết ảnh v&agrave; m&agrave;u sắc tốt nằm trong khoảng zoom 10x, c&oacute; thể sử dụng zoom 30x trong điều kiện &aacute;nh s&aacute;ng tốt, c&ograve;n zoom 100x mang t&iacute;nh chất lưu lại th&ocirc;ng tin. Camera ch&iacute;nh v&agrave; camera tele hỗ trợ c&ocirc;ng nghệ chống rung quang học OIS.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cụm camera sau cho phép người dùng sáng tạo ảnh chụp của mình kh&ocirc;ng giới hạn. Hai ống k&iacute;nh c&ograve;n lại l&agrave; ống k&iacute;nh 12MP hỗ trợ chụp g&oacute;c si&ecirc;u rộng v&agrave; ống k&iacute;nh tele 10MP. Người d&ugrave;ng c&oacute; thể sử dụng S22 chụp ảnh macro với ống k&iacute;nh si&ecirc;u rộng. M&agrave;u sắc v&agrave; chi tiết ảnh cao hơn c&aacute;c model tầm trung, cho độ ph&acirc;n giải v&agrave; cảm biến tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Camera 108MP trên S22 Ultra mang lại khả năng chụp ảnh không giới hạn\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/galaxy-s22-ultra-3.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị được trang bị khả năng chụp ảnh ch&acirc;n dung ban đ&ecirc;m, trong điều kiện thiếu s&aacute;ng ấn tượng với bộ xử l&yacute; h&igrave;nh ảnh AI kết hợp với camera g&oacute;c rộng 108MP mang lại bức ảnh ban đ&ecirc;m s&aacute;ng r&otilde;. Với kh&ocirc;ng gian kh&ocirc;ng qu&aacute; tối, chỉ với thao t&aacute;c sử dụng chế độ tự động, AI sẽ tự xử l&yacute; cho ra h&igrave;nh ảnh s&aacute;ng, đầy đủ chi tiết v&agrave; kh&ocirc;ng bị nhiễu. Trường hợp thiếu s&aacute;ng, chế độ chụp đ&ecirc;m l&agrave; sự lựa chọn ho&agrave;n hảo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tốc độ khung h&igrave;nh ph&aacute;t hiện &aacute;nh s&aacute;ng v&agrave; tự động chuyển sang tốc độ khung h&igrave;nh tối ưu. Samsung Galaxy S22 Ultra sử dụng bộ xử l&yacute; 4nm với c&ocirc;ng nghệ Super Night Solution giảm nhiễu hiệu quả.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đồng thời, người d&ugrave;ng c&oacute; thể điều chỉnh được mức độ x&oacute;a ph&ocirc;ng sau khi chụp. Khi thực hiện quay video cần hiệu chỉnh phần hậu cảnh, người d&ugrave;ng c&oacute; thể l&agrave;m nổi bật chủ thể bằng c&aacute;ch sử dụng bộ lọc đổi ph&ocirc;ng th&agrave;nh đen trắng. Phần chuyển n&eacute;t mượt m&agrave; v&agrave; kh&ocirc;ng c&oacute; cảm gi&aacute;c bị lẹm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Đánh giá camera Samsung Galaxy S22 Ultra 5G\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/galaxy-s22-ultr-1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ chụp ảnh, thiết bị c&ograve;n mang lại khả năng quay phim chất lượng với khả năng chống rung v&agrave; c&ocirc;ng nghệ AI mang lại những thước phim chuyển động nhanh m&agrave; vẫn đảm bảo r&otilde; n&eacute;t, zoom tốt hơn. Điện thoại c&ograve;ng được trang bị c&ocirc;ng nghệ Super HDR tốt với tất cả c&aacute;c ống k&iacute;nh mang lại những thước phim gi&agrave;u chi tiết. Ấn tượng nhất tr&ecirc;n camera của Samsung S22&nbsp;Ultra&nbsp;đ&oacute; ch&iacute;nh l&agrave; khả năng thu ph&oacute;ng 100 lần đầy ấn tượng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ph&iacute;a trước m&aacute;y l&agrave; camera selfie đơn với độ ph&acirc;n giải l&ecirc;n đến 40 MP, kh&acirc;̉u đ&ocirc;̣ f/2.2 với khả năng chụp góc r&ocirc;̣ng cho ra ảnh chụp selfie bắt mắt. Tính năng làm đẹp AI, gọi video call kép và HDR tự đ&ocirc;̣ng sẽ giúp tăng cường trải nghi&ecirc;̣m chụp ảnh tr&ecirc;n chiếc flagship n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm nổi bật nữa của cụm camera nằm ở thời gian phơi s&aacute;ng đến 6 gi&acirc;y nhưng bị rung nh&ograve;e kể cả kh&ocirc;ng sử dụng ch&acirc;n đế!</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Đánh giá camera Samsung Galaxy S22 Ultra 5G - Ảnh 2\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-10.png\" style=\"width:800px\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Màn hình 6.8 inch 120 Hz tương thích bút S-Pen hi&ecirc;̣n đại</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Với Galaxy S22 Ultra th&igrave; Samsung đã cho ra đời loại màn hình phủ trọn mặt trước đ&acirc;̀y &acirc;́n tượng. Chi&ecirc;́c màn hình điện thoại có kích thước 6.8 inch, đ&ocirc;̣ ph&acirc;n giải Quad HD+ (3200 x 1440 pixels) và sử dụng t&acirc;́m n&ecirc;̀n Dynamic AMOLED 2X với Vision Booster cho ra đ&ocirc;̣ bão hòa màu sắc đ&acirc;̀y ch&acirc;n thực.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Màn hình của Samsung S22 Ultra còn được tích hợp những tính năng cao c&acirc;́p đặc trưng của Samsung như Always on display, t&ocirc;́c đ&ocirc;̣ làm tươi đ&ecirc;́n 120 Hz cho trải nghi&ecirc;̣m gaming vượt tr&ocirc;̣i và chứng nh&acirc;̣n màu HDR10+. B&ecirc;̀ mặt màn hình được bảo v&ecirc;̣ bởi kính Gorilla Glass Victus ch&ocirc;́ng xước hi&ecirc;̣u quả.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Samsung S22 Ultra có màn hình 6.8 inch 120 Hz tương thích bút S-Pen hiện đại\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-9_1.jpg\" style=\"width:800px\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Vi xử lý Snapdragon 8 Gen 1 cùng pin lớn giúp giải trí hi&ecirc;̣u quả</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Với Samsung S22 Ultra 5G, nh&agrave; sản xuất đã ch&ecirc;́ tạo n&ecirc;n b&ocirc;̣ vi xử lý th&ecirc;́ h&ecirc;̣ mới mang t&ecirc;n Qualcomm Snapdragon 8 Gen 1. Hi&ecirc;̣u năng tr&ecirc;n&nbsp;Qualcomm Snapdragon 8 Gen 1 được cải ti&ecirc;́n r&acirc;́t nhi&ecirc;̀u so với thế hệ trước nhằm mang lại hi&ecirc;̣u năng mượt mà đáp ứng cả những tựa game nặng nh&acirc;́t, đ&ocirc;̀ng thời chứa khả năng ti&ecirc;́t ki&ecirc;̣m năng lượng pin giúp người dùng y&ecirc;n t&acirc;m dùng máy dài l&acirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Con chip&nbsp;Qualcomm Snapdragon 8 Gen 1 được sản xuất tr&ecirc;n tiến tr&igrave;nh 4nm kh&ocirc;ng chỉ mang lại một hiệu năng mạnh mẽ m&agrave; c&ograve;n sở hữu sức mạnh AI mang lại trải nghiệm chụp đ&ecirc;m vượt trội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nói v&ecirc;̀ pin, điện thoại được trang bị vi&ecirc;n pin dung lượng 5000mAh sẽ cung c&acirc;́p cho người dùng tới 81 giờ chơi nhạc. C&ocirc;ng ngh&ecirc;̣ sạc nhanh si&ecirc;u tốc 45W, cùng sạc kh&ocirc;ng d&acirc;y chu&acirc;̉n Qi và sạc ngược sẽ giúp Galaxy S22 Ultra trở thành chi&ecirc;́c smartphone đa chức năng n&ocirc;̉i b&acirc;̣t trong ph&acirc;n khúc. Pin hỗ trợ tiết kiệm năng lượng, cho thời lượng sử dụng trong thời gian d&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Đánh giá hiệu năng Galaxy S22 Ultra với Snapdragon 8 Gen 1\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-10.jpg\" style=\"width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đ&aacute;nh gi&aacute; hiệu năng Samsung S22 Ultra với Snapdragon 8 Gen 1</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau nhiều năm chạy chip Exynos th&igrave; S22 Ultra l&agrave; mẫu điện thoại Samsung ch&iacute;nh h&atilde;ng hiếm hoi tại Việt Nam chạy Snapdragon 8 Gen 1. Trước khi đ&aacute;nh gi&aacute; hiệu năng, c&ugrave;ng điểm lại một số th&ocirc;ng số cấu h&igrave;nh nh&eacute;:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Vi xử l&yacute;: Snapdragon 8 Gen 1</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Bộ nhớ RAM: 8GB hoặc 12GB.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Bộ nhớ trong: 128GB hoặc 256GB hoặc 512GB.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau đ&acirc;y l&agrave; điểm số benchmark được c&aacute;c trang chuy&ecirc;n c&ocirc;ng nghệ thực hiện.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm Geekbench 5 của S22 Ultra PCMag thực hiện:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Điểm đa nh&acirc;n: 3.433 điểm&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Điểm đơn nh&acirc;n: 1.232</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm số n&agrave;y c&oacute; sự kh&aacute;c biệt so với chip Snapdragon 888 được ra mắt nắm 2020. Cụ thể, điểm đơn l&otilde;i tăng 13% v&agrave; đa l&otilde;i tăng 9%. Nhưng tr&ecirc;n thang điểm chuẩn của GFXBench th&igrave; ở một số t&aacute;c vụ nhất định, con chip n&agrave;y cho hiệu năng tốt hơn khoảng 20%.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img alt=\"Đánh giá hiệu năng Galaxy S22 Ultra với Snapdragon 8 Gen 1 - Ảnh 2\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-11.png\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hỗ trợ b&uacute;t S Pen tiện lợi</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tương tự như model ti&ecirc;̀n nhi&ecirc;̣m, điện thoại th&ocirc;ng minh Samsung&nbsp;Galaxy S22 Ultra&nbsp;cũng hoàn toàn tương thích với bút cảm ứng S-Pen qua điều khiển kh&ocirc;ng day Air Actions - v&ocirc;́n là đi&ecirc;̉m đặc trưng của dòng Galaxy Note trước đ&acirc;y. Người dùng có th&ecirc;̉ dùng S-Pen cho những tác vụ thi&ecirc;́t k&ecirc;́, ghi chú nhanh, và đi&ecirc;̀u khi&ecirc;̉n từ xa với tính hi&ecirc;̣u quả vượt tr&ocirc;̣i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Độ ch&iacute;nh x&aacute;c b&uacute;t S-Pen cao, gi&uacute;p c&aacute;c ghi ch&uacute; v&agrave; tin nhắn của bạn trở n&ecirc;n sinh động, th&uacute; vị. Chữ viết tay th&agrave;nh văn bản nhanh ch&oacute;ng hoặc th&ecirc;m h&igrave;nh ảnh động v&agrave;o, n&acirc;ng cao t&iacute;nh chuy&ecirc;n nghiệp v&agrave; s&aacute;ng tạo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung đ&atilde; trang bị khe cắm&nbsp;S Pen tr&ecirc;n thiết bị gi&uacute;p người d&ugrave;ng c&oacute; thể tiện lợi khi sử dụng. Tuy nhi&ecirc;n kh&aacute;c với trước đ&oacute; l&agrave; S Pen sẽ c&oacute; c&ugrave;ng m&agrave;u với m&aacute;y th&igrave; năm nay,&nbsp;S-Pen sẽ chỉ c&oacute; m&agrave;u đen.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với b&uacute;t S Pen, giời đ&acirc;y người d&ugrave;ng&nbsp;<strong>Galaxy S22 Ultra</strong>&nbsp;c&oacute; thể thoải m&aacute;i ghi ch&uacute;, điều khiển điện thoại như tr&ecirc;n c&aacute;c d&ograve;ng Samsung Note. B&uacute;t Spen mới n&agrave;y cũng được cải thiện độ trễ mang lại trải nghiệm d&ugrave;ng ch&acirc;n thực từ viết chữ, vẽ hay ghi ch&uacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Samsung Galaxy S22 Ultra hỗ trợ bút S Pen tiện lợi\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-6.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Kết nối dễ d&agrave;ng với hệ sinh th&aacute;i&nbsp;Galaxy</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Ở phi&ecirc;n bản điện thoại Samsung S22 Ultra mới, người d&ugrave;ng c&oacute; thể kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng th&ocirc;ng qua hệ sinh th&aacute;i Galaxy như Galaxy S22+, Galaxy Tab S8 Ultra, Galaxy Buds Pro, Galaxy Watch 4,&hellip;. Nhờ đ&oacute;, người d&ugrave;ng c&oacute; thể truy cập v&agrave;o c&aacute;c ứng dụng tr&ecirc;n điện thoại th&ocirc;ng qua m&aacute;y t&iacute;nh Samsung một c&aacute;ch dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"S22 Ultra kết nối dễ dàng với hệ sinh thái Galaxy\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s22-ultra-12.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; thể kết nối với Galaxy Tab S8 Ultra, Samsung Notes được đồng bộ h&oacute;a cho ph&eacute;p chỉnh sửa. Ngo&agrave;i ra, việc di chuyển từ m&agrave;n h&igrave;nh S22 Ultra sang m&agrave;n h&igrave;nh thiết bị kh&aacute;c th&ocirc;ng qua gh&eacute;p nối gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm m&agrave;n h&igrave;nh k&eacute;p.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ecirc;m v&agrave;o đ&oacute; l&agrave; t&iacute;nh năng Smart Switch cho phpeps chuyển đổi dữ liệu qua thiết bị mới nhanh ch&oacute;ng kể cả c&aacute;c thiết bi cũ kh&aacute;c hệ điều h&agrave;nh.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Kết nối trực tuyến, bảo mật th&ocirc;ng tin c&aacute; nh&acirc;n</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Người d&ugrave;ng c&oacute; thể kết nối với nhau trực tuyến th&ocirc;ng qua Google Duo, chia sẻ m&agrave;n h&igrave;nh để xem video c&ugrave;ng nhau giống IRL. B&ecirc;n cạnh đ&oacute;, khi khởi động Samsung Knox, dữ liệu c&aacute; nh&acirc;n của bạn sẽ được bảo mật an to&agrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Knox Vault bảo vệ th&ocirc;ng tin sinh trắc học, trong khi Thư mục bảo mật kh&oacute;a dữ liệu ri&ecirc;ng tư quan trọng. Trang tổng quan Sử dụng quyền mới cho ph&eacute;p xem ứng dụng đang chia sẻ dữ liệu hoặc đang theo d&otilde;i người d&ugrave;ng. Bạn cũng c&oacute; thể cấp hoặc từ chối quyền truy cập theo điều khoản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"S22 Ultra hỗ trợ kết nối trực tuyến, bảo mật thông tin cá nhân\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/galaxy-s22-ultra-2.jpg\" /></span></p>\r\n', 0, '1657262428_s22 ultra.jpg', 31490000, 23, 11);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(8, 'Galaxy Z Flip3 5G 256GB', '<h2><strong><span style=\"font-size:16px\">Nối tiếp th&agrave;nh c&ocirc;ng của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip-5g\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Z Flip 5G chính hãng tại TGDĐ\">Samsung Galaxy Z Flip 5G</a>, trong sự kiện Galaxy Unpacked vừa qua nh&agrave; sản xuất&nbsp;tiếp tục giới thiệu đến thế giới về&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip3-5g-256gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Z Flip3 5G chính hãng tại TGDĐ\">Samsung Galaxy Z Flip3 5G 256GB</a>. Sản phẩm c&oacute; nhiều cải tiến từ độ bền cho đến hiệu năng v&agrave; thậm ch&iacute; n&oacute; c&ograve;n vượt xa hơn mong đợi của mọi người.</span></strong></h2>\r\n\r\n<p><strong><span style=\"font-size:16px\">Th&ocirc;ng tin sản phẩm</span></strong></p>\r\n', '<h3><span style=\"font-size:14px\"><strong>Thiết kế nhỏ gọn đầy l&ocirc;i cuốn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Z Flip3 5G dễ d&agrave;ng lấy l&ograve;ng ph&aacute;i nữ khi thiết kế của n&oacute; được lấy cảm hứng từ hộp đựng phấn trang điểm. Kết hợp với 7 m&agrave;u sắc kh&aacute;c nhau, gi&uacute;p bạn thoải m&aacute;i thể hiện c&aacute; t&iacute;nh, từ mạnh mẽ sang trọng đến hiện đại trẻ trung.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-2-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 256GB | Thiết kế nhỏ gọn đầy lôi cuốn\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-2-1.jpg\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 256GB | Thiết kế nhỏ gọn đầy lôi cuốn\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&acirc;n m&aacute;y si&ecirc;u mỏng với 6.9 mm, nhẹ 183 g tạo n&ecirc;n một kiểu d&aacute;ng đầy sexy. Kết hợp với c&aacute;c g&oacute;c cạnh được bo cong ho&agrave;n hảo mang đến cảm gi&aacute;c cầm nắm thoải m&aacute;i, thư th&aacute;i suốt cả ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-12.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Thân máy siêu mỏng nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-12.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Thân máy siêu mỏng nhẹ\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng những đẹp mắt, m&aacute;y c&ograve;n mang đến sự tiện lợi khi được thiết kế m&agrave;n h&igrave;nh gập độc đ&aacute;o. Khi gập lại người d&ugrave;ng c&oacute; thể bỏ v&agrave;o t&uacute;i quần hay t&uacute;i &aacute;o một c&aacute;ch một c&aacute;ch dễ d&agrave;ng m&agrave; kh&ocirc;ng g&acirc;y kh&oacute; chịu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-31.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Trạng thái khi gập lại\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-31.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Trạng thái khi gập lại\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave; l&agrave; một thiết bị c&oacute; ngoại h&igrave;nh hiện đại v&agrave; sang trọng nhưng Samsung Galaxy Z Flip3 5G vẫn kh&ocirc;ng k&eacute;m phần cứng c&aacute;p khi được chế t&aacute;c từ hợp kim nh&ocirc;m Armor Aluminum cao cấp, d&ugrave; v&ocirc; t&igrave;nh l&agrave;m rơi hay va đập mạnh m&aacute;y vẫn c&oacute; khả năng sống s&oacute;t cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-24.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Khung viền nhôm cao cấp nhất thế giới smartphone\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-24.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Khung viền nhôm cao cấp nhất thế giới smartphone\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về bảo mật, Galaxy Z Flip3 5G c&oacute; một m&aacute;y&nbsp;qu&eacute;t dấu v&acirc;n tay&nbsp;gắn cạnh b&ecirc;n v&agrave; t&iacute;nh năng&nbsp;nhận dạng khu&ocirc;n mặt&nbsp;cho người d&ugrave;ng mở kh&oacute;a nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-29.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Cảm biến vân tay ở cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-29.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Cảm biến vân tay ở cạnh viền\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Trải nghiệm thị gi&aacute;c cực đỉnh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ m&agrave;n h&igrave;nh Dynamic AMOLED 2X c&ugrave;ng độ ph&acirc;n giải Full HD+ cho m&agrave;u sắc hiển thị rực rỡ, mọi chi tiết được t&aacute;i hiện một c&aacute;ch ch&acirc;n thực.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-26.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình giải trí siêu nét\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-26.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình giải trí siêu nét\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">K&iacute;ch thước m&agrave;n h&igrave;nh ch&iacute;nh l&agrave;&nbsp;6.7&nbsp;inch&nbsp;được trang bị mặt k&iacute;nh Ultra Thin Glass (UTG), cho bạn trải nghiệm sự linh hoạt kh&ocirc;ng tưởng với độ bền hơn 80% khi so với phi&ecirc;n bản Galaxy Z Flip LTE v&agrave; c&oacute; khả năng gập mở đến 200.000 lần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình uống dẻo gập thoải mái\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-14.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình uống dẻo gập thoải mái\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tối ưu h&oacute;a kh&ocirc;ng gian sử dụng từ lướt web, xem phim hay chơi game với thiết kế m&agrave;n h&igrave;nh nốt ruồi. Kết hợp với độ s&aacute;ng tối đa l&ecirc;n đến 1200 nits, mang lại cho bạn một trải nghiệm thị gi&aacute;c cực đ&atilde;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-19.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình giải trí cực đã\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-19.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình giải trí cực đã\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, Galaxy Z Flip3 5G c&oacute; tần số qu&eacute;t l&ecirc;n đến 120 Hz, để mọi lần chạm đều c&oacute; thể nhận được phản hồi ch&iacute;nh x&aacute;c v&agrave; nhạy hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Tần số quét 120 Hz\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-3.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Tần số quét 120 Hz\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thỏa sức giải tr&iacute; v&agrave; thao t&aacute;c dễ d&agrave;ng m&agrave; kh&ocirc;ng cần mở điện thoại với m&agrave;n h&igrave;nh ngo&agrave;i 1.9 inch. Bạn c&oacute; thể l&agrave;m những việc như xem th&ocirc;ng b&aacute;o, chụp ảnh, tr&igrave;nh ph&aacute;t nhạc c&ugrave;ng c&aacute;c chức năng kh&aacute;c v&ocirc; c&ugrave;ng tiện lợi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-40.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình ngoài tiện lợi\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-40.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Màn hình ngoài tiện lợi\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đồng thời người d&ugrave;ng c&oacute; thể nhập v&agrave; tr&ograve; chuyện trong khi xem phim với t&iacute;nh năng chia đ&ocirc;i m&agrave;n h&igrave;nh khi ở chế độ mở ho&agrave;n to&agrave;n, gi&uacute;p bạn tăng gấp đ&ocirc;i hiệu quả.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-41.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng chia đôi màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-41.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng chia đôi màn hình\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Cấu h&igrave;nh cực khủng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Mang cho m&igrave;nh một th&acirc;n h&igrave;nh nhỏ b&eacute; nhưng b&ecirc;n trong lại chứa một sức mạnh phi thường khi được trang bị phần cứng h&agrave;ng đầu hiện nay với bộ vi xử l&yacute; Snapdragon 888.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Cấu hình cực khủng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Cấu hình cực khủng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với hiệu năng n&agrave;y người d&ugrave;ng c&oacute; thể thừa sức chiến mọi tựa game nặng tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">smartphone</a>&nbsp;hiện nay, c&aacute;c t&aacute;c vụ như lướt mạng x&atilde; hội, đọc b&aacute;o cũng đều trở n&ecirc;n trơn tru hơn khi kết hợp c&ugrave;ng m&agrave;n h&igrave;nh 120 Hz.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-20.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Hiệu năng mượt mà ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-20.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Hiệu năng mượt mà ấn tượng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bộ xử l&yacute; đồ họa Adreno 660 c&aacute;c chi tiết được t&aacute;i tạo ch&acirc;n thực đến từng sợi t&oacute;c của nh&acirc;n vật hay những mảnh k&iacute;nh vỡ đều hiển thị sắc n&eacute;t như ngo&agrave;i đời thật cho bạn trải nghiệm h&igrave;nh ảnh tr&ecirc;n game r&otilde; r&agrave;ng v&agrave; sống động hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-25.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Điểm hiệu năng Antutu\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-25.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Điểm hiệu năng Antutu\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Z Flip3 5G được trang bị thanh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com \">RAM 8 GB</a>&nbsp;gi&uacute;p hiệu suất trơn tru ngay cả với nhiều tiến tr&igrave;nh chạy c&ugrave;ng l&uacute;c. Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-256gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ trong 256 GB tại Thegioididong.com\">ROM 256 GB</a>&nbsp;cho bạn nhiều kh&ocirc;ng gian lưu trữ m&agrave; kh&ocirc;ng lo đầy bộ nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Cấu hình hàng đầu thế giới\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-9.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Cấu hình hàng đầu thế giới\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với 5G, chiếc smartphone n&agrave;y chắc chắn kh&ocirc;ng l&agrave;m bạn thất vọng khi đem đến trải nghiệm kết nối với thế giới cực nhanh, việc tải game hay xem phim Full HD cũng đều trở n&ecirc;n nhanh ch&oacute;ng v&agrave; ổn định.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-33.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Trải nghiệm xem video tuyệt đỉnh với công nghệ 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-33.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Trải nghiệm xem video tuyệt đỉnh với công nghệ 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Knox l&agrave; một lớp bảo mật độc quyền được t&iacute;ch hợp tr&ecirc;n c&aacute;c d&ograve;ng sản phẩm Samsung gi&uacute;p bảo vệ an to&agrave;n thiết bị của bạn tr&aacute;nh khỏi phần mềm độc hại, c&aacute;c mối đe dọa g&acirc;y nguy hiểm l&agrave;m ảnh hưởng đến th&ocirc;ng tin của người d&ugrave;ng.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chuy&ecirc;n gia nhiếp ảnh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thoải m&aacute;i s&aacute;ng tạo với bộ đ&ocirc;i camera k&eacute;p ph&iacute;a sau c&oacute; cảm biến ch&iacute;nh v&agrave;&nbsp;ống k&iacute;nh g&oacute;c si&ecirc;u rộng&nbsp;c&ugrave;ng độ ph&acirc;n giải 12 MP, kết hợp c&ugrave;ng m&aacute;y ảnh trước 10 MP để bạn c&oacute; thể chụp những bức ảnh đẹp cho d&ugrave; bằng chế độ n&agrave;o, chỉ cần bấm n&uacute;t chụp v&agrave; việc c&ograve;n lại h&atilde;y để điện thoại xử l&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Cụm camera kép chuyên gia nhíp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-8.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Cụm camera kép chuyên gia nhíp ảnh\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với camera ch&iacute;nh sẽ mang đến cho bạn một thế giới h&igrave;nh ảnh si&ecirc;u r&otilde; n&eacute;t. D&ugrave; trong điều kiện &aacute;nh s&aacute;ng n&agrave;o, khả năng xử l&yacute; h&igrave;nh ảnh vượt trội của Galaxy Z Flip3 5G vẫn sẽ gi&uacute;p người d&ugrave;ng ghi lại những khoảnh khắc tuyệt vời với độ chi tiết cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-30.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-30.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Ảnh chụp bằng camera sau\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm độc đ&aacute;o tiếp theo tr&ecirc;n chiếc smartphone n&agrave;y ch&iacute;nh l&agrave; bạn ho&agrave;n to&agrave;n c&oacute; thể d&ugrave;ng m&agrave;n h&igrave;nh phụ để cho ra c&aacute;c bức selfie với ống camera ch&iacute;nh. Tất cả đều diễn ra m&agrave; kh&ocirc;ng cần mở m&aacute;y ra.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-35.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng selfie mà không cần mở máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-35.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng selfie mà không cần mở máy\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Z Flip3 5G c&oacute; thể gập lại để tr&ecirc;n bề mặt phẳng m&agrave; kh&ocirc;ng cần phải cầm tr&ecirc;n tay với chế độ Flex Mode. Giờ đ&acirc;y, người d&ugrave;ng c&oacute; thể rảnh tay khi thực hiện cuộc gọi điện video hay giữ m&aacute;y lu&ocirc;n ổn định khi selfie trong thời gian d&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-38.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Tính năng Flex Mode\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-38.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Tính năng Flex Mode\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Camera selfie cũng đ&atilde; được trang bị chế độ&nbsp;x&oacute;a&nbsp;ph&ocirc;ng&nbsp;để gi&uacute;p người d&ugrave;ng c&oacute; những tấm ảnh ch&acirc;n dung rạng rỡ, đem đến h&igrave;nh ảnh kh&ocirc;ng thua k&eacute;m g&igrave; c&aacute;c m&aacute;y ảnh chuy&ecirc;n nghiệp.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Vi&ecirc;n pin k&eacute;p th&ocirc;ng minh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Z Flip3 5G sẽ c&oacute; vi&ecirc;n pin&nbsp;3300&nbsp;mAh, dung lượng pin n&agrave;y kh&aacute; yếu khi so với c&aacute;c smartphone tr&ecirc;n thị trường, tuy nhi&ecirc;n với trang bị CPU h&agrave;ng đầu th&igrave; khả năng ti&ecirc;u thụ điện năng của m&aacute;y cũng được tối ưu kh&aacute; tốt, vẫn cung cấp đủ c&aacute;c nhu cầu sử dụng trong một ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-36.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Thời lượng sử dụng pin ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-36.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Thời lượng sử dụng pin ấn tượng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị cũng hỗ trợ t&iacute;nh năng Samsung Pay đ&acirc;y l&agrave; dịch vụ thanh to&aacute;n bằng thiết bị di động độc quyền của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\">điện thoại Samsung</a>. Giờ đ&acirc;y, người d&ugrave;ng chỉ cần đưa điện thoại lại gần m&aacute;y thanh to&aacute;n l&agrave; c&oacute; thể t&iacute;nh tiền một c&aacute;ch rất đơn giản v&agrave; tiện lợi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Tính năng Samsung Pay\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Tính năng Samsung Pay\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&iacute;ch hợp c&ocirc;ng nghệ AI gi&uacute;p tối ưu mức pin theo th&oacute;i quen trải nghiệm cho bạn nhiều thời gian hơn để l&agrave;m điều m&igrave;nh th&iacute;ch. Thiết bị c&ograve;n hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc không dây chính hãng tại Thegioididong.com \">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc nhanh tại Thegioididong.com \">sạc nhanh</a>&nbsp;c&oacute; d&acirc;y&nbsp;15 W, bạn chỉ mất khoảng 1 giờ l&agrave; đ&atilde; c&oacute; thể nạp đầy cho vi&ecirc;n pin n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-18.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng sạc nhanh 15W\" src=\"https://cdn.tgdd.vn/Products/Images/42/248283/samsung-galaxy-z-flip-3-256gb-18.jpg\" title=\"Samsung Galaxy Z Flip3 5G 256GB | Khả năng sạc nhanh 15W\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ sở hữu hiệu năng mạnh mẽ hoạt động b&ecirc;n trong Galaxy Z Flip3 5G c&ograve;n sở hữu vẻ ngo&agrave;i nổi bật như một m&oacute;n trang sức đắt tiền. Đ&acirc;y sẽ l&agrave; một chiếc điện thoại đ&aacute;ng mua cho những người thật sự muốn được trải nghiệm m&agrave;n h&igrave;nh gập mới.</span></p>\r\n', 1, '1657436767_samsung-galaxy-z-flip-3-1-1.jpg', 26990000, 25, 21),
(9, 'Máy giặt cửa trên Digital Inverter 12kg (WA12T5360BV)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y giặt cửa tr&ecirc;n Digital Inverter 12kg (WA12T5360BV)</strong></span></h2>\r\n\r\n<p>M&aacute;y giặt lồng đứng 12&nbsp;kg</p>\r\n\r\n<p>Truyền động Digital inverter direct drive</p>\r\n\r\n<p>Tốc độ quay vắt: 700 v&ograve;ng/ph&uacute;t</p>\r\n\r\n<p>Lồng Giặt Swirl drum,&nbsp;Xuất xứ: Th&aacute;i Lan</p>\r\n', '<h3><span style=\"font-size:14px\">Điều Khiển Trực Quan</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Bảng Điều Khiển Mid-control</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-giat/samsung/may-giat-samsung-inverter-12kg-wa12t5360bvsv.htm\">M&aacute;y giặt Samsung Inverter 12Kg WA12T5360BV/SV</a>&nbsp;sở hữu bảng điều khiển LED nằm giữa cửa m&aacute;y giặt, cho ph&eacute;p bạn thuận tiện điều khiển v&agrave; lựa chọn chế độ giặt ph&ugrave; hợp một c&aacute;ch trực quan. Mang đến vẻ đẹp tinh tế, sang trọng cho căn nh&agrave; bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/7F1380D04S60FL5.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Dễ D&agrave;ng Xử L&yacute; Sơ Quần &Aacute;o</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Activ Dualwash</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Gi&uacute;p xử l&yacute; sơ vết bẩn dễ d&agrave;ng v&agrave; hiệu quả.&nbsp;<a href=\"https://mediamart.vn/may-giat\">M&aacute;y giặt</a>&nbsp;Activ Dualwash mới với khay giặt tay lớn hơn, dễ d&agrave;ng l&agrave;m sạch c&aacute;c vết bẩn cứng đầu ở c&aacute;c vị tr&iacute; kh&oacute; giặt trước khi bỏ v&agrave;o lồng giặt b&ecirc;n dưới. Đường nước t&iacute;ch hợp sẵn b&ecirc;n cạnh hoạt động chỉ với 1 n&uacute;t nhấn</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/Yz0Mkf352s3IP3K.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n thử nghiệm nội bộ, so s&aacute;nh với m&aacute;y giặt WA16J6750SP.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Xử L&yacute; Lượng &Aacute;o Quần Lớn Chỉ Trong Một Lần Giặt</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Khối Lượng Giặt Lớn</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Khối lượng m&aacute;y giặt lớn hơn cho ph&eacute;p bạn giặt nhiều &aacute;o quần hơn chỉ trong một lần giặt, tiết kiệm tối đa thời gian v&agrave; c&ocirc;ng sức để l&agrave;m những điều bạn y&ecirc;u th&iacute;ch. M&aacute;y giặt Activ Dualwash - Giải ph&aacute;p ho&agrave;n hảo cho cuộc sống bận rộn.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/Doa5Odi8p2Tw178.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Lọc Sạch Xơ Vải, Dễ D&agrave;ng Vệ Sinh</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Bộ Lọc Magic Filter</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bộ lọc Magic Filter lọc sạch xơ vải, l&ocirc;ng tơ v&agrave; bụi bẩn, giữ quần &aacute;o lu&ocirc;n sạch v&agrave; giảm nguy cơ tắc nghẽn đường ống tho&aacute;t nước. Thiết kế bộ lọc mới với k&iacute;ch thước lớn hơn, dễ d&agrave;ng th&aacute;o rời, thuận tiện cho việc vệ sinh.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/SG07wqYtm47dpK7.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">T&ugrave;y Chỉnh&nbsp;<a href=\"https://mediamart.vn/may-giat/samsung/?&amp;loc=chuc-nang-giat-bang-nuoc-nong\">Giặt Nước N&oacute;ng</a>&nbsp;Cho Mọi Vết Bẩn</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Magic Clean</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Magic Clean t&ugrave;y chỉnh linh hoạt chương tr&igrave;nh giặt giũ ở mức 40&deg;C** cho những vết bẩn th&ocirc;ng thường hằng ng&agrave;y, hay 60&deg;C** cho những mảng b&aacute;m dai dẳng v&agrave; cứng đầu nhất, gi&uacute;p quần &aacute;o sạch ho&agrave;n hảo*.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/2K569uv9rW29mF6.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">T&ugrave;y Chỉnh Giặt Nước N&oacute;ng Cho Mọi Vết Bẩn</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Magic Clean</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Magic Clean t&ugrave;y chỉnh linh hoạt chương tr&igrave;nh giặt giũ ở mức 40&deg;C** cho những vết bẩn th&ocirc;ng thường hằng ng&agrave;y, hay 60&deg;C** cho những mảng b&aacute;m dai dẳng v&agrave; cứng đầu nhất, gi&uacute;p quần &aacute;o sạch ho&agrave;n hảo*.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/Xzf8R1uXPi1eeoM.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Tiết kiệm 40% Điện Năng* V&agrave; Giảm Rung Ồn*</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Digital Inverter</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-giat/samsung/?&amp;loc=may-giat-inverter\">Động cơ Digital Inverter</a>&nbsp;trang bị nam ch&acirc;m vĩnh cửu gi&uacute;p m&aacute;y giặt vận h&agrave;nh bền bỉ, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng*. M&aacute;y vận h&agrave;nh bền bỉ với thời gian bảo h&agrave;nh l&ecirc;n đến 11 năm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/x18gUDo816hI5cw.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n dữ liệu nội bộ, so với sản phẩm th&ocirc;ng thường của Samsung.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Giảm Tiếng Ồn</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ VRT</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ VRT - C&ocirc;ng Nghệ Giảm Rung gi&uacute;p lồng giặt được giữ c&acirc;n bằng tuyệt đối ngay cả ở tốc độ quay cao v&agrave; khối lượng giặt lớn, gi&uacute;p giảm tiếng ồn v&agrave; độ rung trong suốt qu&aacute; tr&igrave;nh giặt. Y&ecirc;n t&acirc;m giặt giũ &aacute;o quần mọi l&uacute;c m&agrave; kh&ocirc;ng lo bị tiếng ồn l&agrave;m ảnh hưởng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/HQpgkvR2Yv3JVZ1.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-giat/samsung/?&amp;loc=ve-sinh-long-giat\">Tự Động Vệ Sinh Lồng Giặt</a></span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Chế Độ Eco Tub Clean</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chế độ Tự Động L&agrave;m Sạch - Eco Tub Clean gi&uacute;p loại bỏ ho&agrave;n to&agrave;n dư lượng bột giặt v&agrave; bụi bẩn t&iacute;ch tụ m&agrave; kh&ocirc;ng cần d&ugrave;ng đến h&oacute;a chất th&ocirc;ng qua qu&aacute; tr&igrave;nh ng&acirc;m nước, rung v&agrave; xoay l&agrave;m sạch. Khi bạn kh&ocirc;ng biết l&uacute;c n&agrave;o cần phải l&agrave;m sạch lồng giặt, b&aacute;o động sẽ tự bật sau mỗi 20 lần giặt để nhắc nhở bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Họa tiết lồng giặt c&oacute; thể kh&ocirc;ng giống với sản phẩm thực tế.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/K08C2Po6YIPj2d1.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/ajax/may-giat/samsung/?&amp;loc=chan-doan-thong-minh-bang-dien-thoai\">Giặt Th&ocirc;ng Minh C&ugrave;ng Tr&iacute; Tuệ Nh&acirc;n Tạo</a></span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Ứng Dụng SmartThings</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Giặt giũ dễ d&agrave;ng, tiết kiệm thời gian tối ưu với SmartThings* gi&uacute;p tự động nhận biết th&oacute;i quen sử dụng** của bạn để tối ưu hiệu năng vận h&agrave;nh. Tr&igrave;nh Tự Động Gợi &Yacute; Chế Độ Giặt (Laundry Recipe) mang đến c&aacute;c gợi &yacute; giặt ph&ugrave; hợp***. Với tr&igrave;nh L&ecirc;n Kế Hoạch Giặt (Laundry Planner), bạn dễ d&agrave;ng quản l&yacute; thời gian v&agrave; lịch tr&igrave;nh**** giặt theo &yacute; muốn. Gi&aacute;m s&aacute;t m&aacute;y giặt từ xa, kịp thời khắc phục c&aacute;c sự cố với tr&igrave;nh Gi&aacute;m S&aacute;t Th&ocirc;ng Minh (HomeCare Wizard).</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-4-News/5pbhmqfnL2Q0QH5.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '1657443805_vn-top-loader-wa12t5360bv-wa12t5360bv-sv-514995811.jpg', 14990000, 27, 2),
(10, 'Máy giặt thông minh AI EcoBubble™ 9kg (WW90T634DLE)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y giặt th&ocirc;ng minh AI EcoBubble&trade; 9kg (WW90T634DLE)</strong></span></h2>\r\n\r\n<p>Bảng điều khiển th&ocirc;ng minh AI Control&nbsp;tự động ghi nhớ, ph&acirc;n t&iacute;ch, đề xuất chế độ giặt ph&ugrave; hợp<br />\r\nTự động ph&acirc;n bổ lượng nước giặt, nước xả tối ưu với ngăn nước giặt xả th&ocirc;ng minh AI Dispenser</p>\r\n\r\n<p>Động cơ Digital Inverter tối ưu h&oacute;a hiệu năng v&agrave; lượng điện ti&ecirc;u thụ</p>\r\n\r\n<p>Diệt khuẩn 99,9% v&agrave; ngừa dị ứng bằng giặt hơi nước Hygiene Steam<br />\r\nChế độ vệ sinh Drum Clean+&nbsp;l&agrave;m sạch lồng giặt v&agrave; diệt khuẩn 99,9%</p>\r\n\r\n<p>Giặt sạch hơn với c&ocirc;ng nghệ giặt Eco Bubble &amp; lồng giặt kim cương<br />\r\nChế độ ng&acirc;m Bubble Soak&nbsp;đ&aacute;nh bay vết bẩn cứng đầu</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Bảng điều khiển th&ocirc;ng minh AI Control&nbsp;tự động ghi nhớ, ph&acirc;n t&iacute;ch, đề xuất chế độ giặt ph&ugrave; hợp</strong><br />\r\nM&aacute;y giặt Samsung&nbsp;thiết kế bảng điều khiển AI tự động ghi nhớ, ph&acirc;n t&iacute;ch, đề xuất chế độ giặt v&agrave; c&agrave;i đặt y&ecirc;u th&iacute;ch dựa tr&ecirc;n th&oacute;i quen sử dụng m&agrave; kh&ocirc;ng cần t&ugrave;y chỉnh mỗi lần giặt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-y2295f.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\">Ngo&agrave;i ra, bảng điều khiển với m&agrave;n h&igrave;nh hiển thị quy tr&igrave;nh giặt dễ hiểu, hướng dẫn th&ocirc;ng minh v&agrave; c&oacute; thể kết nối với điện thoại qua ứng dụng SmartThings để điều khiển m&aacute;y giặt mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-IXJOkN.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Tự động ph&acirc;n bổ lượng nước giặt, nước xả tối ưu với ngăn nước giặt xả th&ocirc;ng minh AI Dispenser</strong><br />\r\nNgăn nước giặt xả th&ocirc;ng minh AI Dispenser sẽ tự động ph&acirc;n bổ lượng nước giặt, nước xả ch&iacute;nh x&aacute;c theo khối lượng &aacute;o quần, g&oacute;p phần n&acirc;ng cao hiệu quả giặt sạch, tr&aacute;nh l&atilde;ng ph&iacute;. Bạn kh&ocirc;ng phải mất thời gian để đong nước giặt xả mỗi lần giặt do chỉ cần đổ đầy 1 lần, d&ugrave;ng trong 1 th&aacute;ng (Theo tần suất giặt 4 lần/tuần).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-33qZCp.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Chỉ mất 59 ph&uacute;t để l&agrave;m sạch quần &aacute;o nhờ chế độ giặt Super Speed</strong><br />\r\nChế độ Super Speed sử dụng v&ograve;i xịt nước si&ecirc;u tốc với lực nước mạnh mẽ gi&uacute;p tăng sức mạnh thẩm thấu của bong b&oacute;ng si&ecirc;u mịn v&agrave;o &aacute;o quần. Đồng thời, r&uacute;t ngắn thời gian xả v&agrave; vắt, đảm bảo quần &aacute;o sạch nhanh chỉ trong 59 ph&uacute;t.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-EPpYvI.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Diệt khuẩn 99,9% v&agrave; ngừa dị ứng bằng giặt hơi nước Hygiene Steam</strong><br />\r\nGiặt hơi nước diệt khuẩn Hygiene Steam sử dụng hơi nước nhiệt độ cao thổi v&agrave;o to&agrave;n bộ bề mặt v&agrave; s&acirc;u b&ecirc;n trong từng kẽ vải, gi&uacute;p loại bỏ cặn bẩn b&aacute;m s&acirc;u, diệt 99,9% vi khuẩn v&agrave; c&aacute;c t&aacute;c nh&acirc;n g&acirc;y dị ứng, bảo vệ sức khỏe cả gia đ&igrave;nh.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-WO6WHf.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Giặt giũ &ecirc;m &aacute;i với c&ocirc;ng nghệ VRT Plus</strong><br />\r\nVới c&ocirc;ng nghệ VRT Plus, việc giặt giũ &ecirc;m &aacute;i, giảm rung ồn đến 30% trong qu&aacute; tr&igrave;nh giặt. Thiết kế khung m&aacute;y mới mang lại sự ổn định cao hơn, kết hợp với bộ cảm biến ph&aacute;t hiện rung ồn, từ đ&oacute; tối ưu hiệu quả vận h&agrave;nh của động cơ.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-a199rZ.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Động cơ Digital Inverter tối ưu h&oacute;a hiệu năng v&agrave; lượng điện ti&ecirc;u thụ</strong><br />\r\nĐộng cơ Digital Inverter trang bị nam ch&acirc;m vĩnh cửu, giảm thiểu ma s&aacute;t khi vận h&agrave;nh, gi&uacute;p tiết kiện điện năng, vận h&agrave;nh &ecirc;m &aacute;i v&agrave; độ bền vượt trội.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-uMjK7m.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Chế độ vệ sinh Drum Clean+&nbsp;l&agrave;m sạch lồng giặt v&agrave; diệt khuẩn 99,9%</strong><br />\r\nKhi k&iacute;ch hoạt chế độ n&agrave;y,&nbsp;chiếc m&aacute;y giặt Inverter&nbsp;n&agrave;y tự động vệ sinh, loại bỏ cặn b&aacute;m từ gioăng cửa, lồng giặt v&agrave; diệt 99,9% vi khuẩn g&acirc;y m&ugrave;i trong lồng giặt m&agrave; kh&ocirc;ng cần sử dụng h&oacute;a chất. Tự động th&ocirc;ng b&aacute;o khi cần vệ sinh (sau 40 lần giặt).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-yn9GF2.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Chế độ ng&acirc;m Bubble Soak&nbsp;đ&aacute;nh bay vết bẩn cứng đầu</strong><br />\r\nĐối với những vết bẩn cứng đầu như c&agrave; ph&ecirc;, tương ớt đ&ograve;i hỏi xử l&yacute; sơ trước khi giặt m&aacute;y. Tuy nhi&ecirc;n, chế độ ng&acirc;m Bubble Soak lại c&oacute; thể loại bỏ những vết bẩn cứng đầu hiệu quả do tăng thời gian ng&acirc;m, gi&uacute;p bong b&oacute;ng thẩm thấu s&acirc;u hơn v&agrave;o quần &aacute;o.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-5Ma1YF.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Loại bỏ tối đa cặn bột giặt với hộp bột giặt StayClean</strong><br />\r\nHộp bột giặt StayClean được thiết kế đặc biệt với hệ thống xả nước th&ocirc;ng minh, gi&uacute;p h&ograve;a tan bột giặt/nước giặt hiệu quả, hạn chế tối đa lượng chất giặt tẩy c&ograve;n tồn đọng, giữ cho hộp đựng lu&ocirc;n sạch sẽ v&agrave; vệ sinh.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-6/1-eIEVXU.png\" /></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Khối lượng giặt 9 kg, ph&ugrave; hợp với gia đ&igrave;nh c&oacute; từ 3 - 5 người</strong><br />\r\nChiếc m&aacute;y giặt Samsung Inverter&nbsp;n&agrave;y được thiết kế&nbsp;cửa trước&nbsp;tiện lợi, c&ugrave;ng với khối lượng giặt 9 kg d&agrave;nh cho gia đ&igrave;nh từ 3 - 5 người</span></p>\r\n', 0, '1657443637_vn-front-loading-washer-ww10t634dles3-ww90t634dle-sv-514976699.jpg', 14290000, 37, 2),
(11, 'Máy lọc không khí 60m2 AX60R5080WD', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y lọc kh&ocirc;ng kh&iacute; 60m2 AX60R5080WD</strong></span></h2>\r\n\r\n<p>M&agrave;ng lọc HEPA lọc 99.97% c&aacute;c hạt bụi nhỏ đến 0.3㎛ v&agrave; vi khuẩn</p>\r\n\r\n<p>C&ocirc;ng nghệ Thổi Gi&oacute; 3 Chiều - 3 Way Air Flow gi&uacute;p ph&acirc;n bổ đều kh&ocirc;ng kh&iacute;</p>\r\n\r\n<p>Chế độ Tự Động/ Chế độ Ngủ gi&uacute;p n&acirc;ng cao tiện &iacute;ch trải nghiệm</p>\r\n\r\n<p>Dung t&iacute;ch: 60 ㎡</p>\r\n', '<h3><span style=\"font-size:14px\"><strong>C&ocirc;ng Nghệ Thổi Gi&oacute; 3 Chiều</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y lọc kh&ocirc;ng kh&iacute; Samsung AX60R5080WD/SV gi&uacute;p tận hưởng bầu kh&ocirc;ng kh&iacute; sạch v&agrave; trong l&agrave;nh khắp mọi nơi*. Hệ thống h&uacute;t kh&iacute; mạnh mẽ từ ph&iacute;a trước Front Air Inflow. Quạt c&ocirc;ng suất lớn Power Fan nhanh ch&oacute;ng đẩy kh&ocirc;ng kh&iacute; đ&atilde; lọc sạch đi mọi hướng nhờ 3 hướng thổi gi&oacute;. V&igrave; thế kh&ocirc;ng kh&iacute; trong l&agrave;nh sẽ được ph&acirc;n t&aacute;n nhanh* &amp; rộng hơn 3, với chỉ số CADR l&ecirc;n đến 467m&sup3;/h**</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/HaCe85K31tFhO81.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n kết quả thử nghiệm nội bộ khi so s&aacute;nh giữa m&aacute;y lọc kh&iacute; Samsung AX60K5580WFD với mẫu m&aacute;y th&ocirc;ng thường AX40K3020UWD. Hiệu suất c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o m&ocirc;i trường sử dụng thực tế. **CADR &gt; 467m&sup3;/h. Được kiểm nghiệm bởi KACA (Hiệp Hội Chất Lượng Kh&ocirc;ng Kh&iacute; H&agrave;n Quốc).</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hệ Thống M&agrave;ng Lọc Nhiều Lớp</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-loc-khong-khi/samsung/\" target=\"_blank\"><strong>M&aacute;y lọc kh&ocirc;ng kh&iacute; Samsung</strong></a>&nbsp;c&oacute; hệ thống m&agrave;ng lọc nhiều lớp, Giữ cho kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; sạch khuẩn với hệ thống M&agrave;ng Lọc Nhiều Lớp. M&agrave;ng lọc Than Hoạt T&iacute;nh gi&uacute;p khử m&ugrave;i v&agrave; loại bỏ nhanh ch&oacute;ng c&aacute;c kh&iacute; độc hại*. Lớp lọc HEPA gi&uacute;p lọc đến 99.97% hạt bụi si&ecirc;u mịn** v&agrave; khả năng diệt vi khuẩn*** trong kh&ocirc;ng kh&iacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/0czzJ298tAVnF55.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Đ&atilde; thử nghiệm tr&ecirc;n AX40J5009UW/SC về c&aacute;c loại kh&iacute; toluene, xylene, benzene, ethylbenzene, styrene v&agrave; formaldehyde tại Viện Kiểm Định H&agrave;n Quốc. **Chất liệu m&agrave;ng lọc c&oacute; khả năng thu giữ hiệu quả đến 99.97% bụi bẩn (hạt bụi c&oacute; k&iacute;ch thước 0.26㎛), theo quy định tại 42 CFR phần 84. Được kiểm nghiệm tại Viện Kiểm Định H&agrave;n Quốc. ***Đ&atilde; thử nghiệm tr&ecirc;n chất liệu lọc của m&agrave;ng gom bụi tại Viện Kiểm Định H&agrave;n Quốc.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>M&agrave;n H&igrave;nh Hiển Thị Số</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong><a href=\"https://mediamart.vn/may-loc-khong-khi/\" target=\"_blank\">M&aacute;y lọc kh&ocirc;ng kh&iacute;</a>&nbsp;</strong>c&oacute; m&agrave;n h&igrave;nh hiển thị số,<strong>&nbsp;</strong>Kiểm tra mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute;. Cảm biến Laser PM ph&aacute;t hiện ch&iacute;nh x&aacute;c bụi, c&aacute;c hạt nhỏ hơn 1.0㎛ c&oacute; trong kh&ocirc;ng kh&iacute; v&agrave; c&aacute;c chất g&acirc;y &ocirc; nhiễm kh&aacute;c. M&agrave;n h&igrave;nh hiển thị ch&iacute;nh x&aacute;c mức độ &ocirc; nhiễm dưới dạng c&aacute;c chỉ số PM1.0/2.5/10. M&aacute;y lọc cũng chỉ r&otilde; mức độ l&agrave;m sạch kh&ocirc;ng kh&iacute; th&ocirc;ng qua 4 chỉ số m&agrave;u.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/oB7h18zEFX3fvS6.jpg\" /></span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Di Chuyển Thuận Tiện Với B&aacute;nh Xe Ẩn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Di chuyển&nbsp;<strong>Samsung AX60R5080WD/SV</strong>&nbsp;bất cứ nơi n&agrave;o bạn muốn dễ d&agrave;ng hơn bằng c&aacute;ch sử dụng c&aacute;c b&aacute;nh xe ẩn. Chỉ cần đẩy hoặc k&eacute;o đến bất kỳ vị tr&iacute; n&agrave;o cần lọc sạch s&acirc;u, như ph&ograve;ng kh&aacute;ch hoặc ph&ograve;ng ngủ của bạn. B&aacute;nh xe được giấu gọn b&ecirc;n dưới gi&uacute;p cho thiết kế m&aacute;y lọc sang trọng ph&ugrave; hợp với kh&ocirc;ng gian sống .</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/32za6Z5p52wjTOD.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Điều Khiển Từ Xa Bằng Điện Thoại Th&ocirc;ng Minh</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong><a href=\"https://mediamart.vn/may-loc-khong-khi/samsung/may-loc-khong-khi-samsung-ax60r5080wdsv.htm\" target=\"_blank\">AX60R5080WD/SV</a>&nbsp;</strong>c&oacute; thể điều khiển từ xa bằng điện thoại th&ocirc;ng minh th&ocirc;ng qua ứng dụng Smartthing<strong>.&nbsp;</strong>Chăm s&oacute;c v&agrave; thường xuy&ecirc;n kiểm tra mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute; bất cứ l&uacute;c n&agrave;o v&agrave; bất cứ ở đ&acirc;u. Ứng dụng SmartThings* cho ph&eacute;p bạn điều khiển m&aacute;y lọc kh&ocirc;ng kh&iacute; từ xa. Chỉ với thao t&aacute;c đơn giản hoặc sử dụng giọng n&oacute;i**, bạn sẽ dễ d&agrave;ng bật hoặc tắt, kiểm tra chất lượng kh&ocirc;ng kh&iacute;, kiểm so&aacute;t c&aacute;c chức năng kh&aacute;c v&agrave; theo d&otilde;i t&igrave;nh trạng m&agrave;ng lọc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/T25e2Y19uhhW5X3.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*C&oacute; sẵn tr&ecirc;n thiết bị Android v&agrave; iOS c&oacute; kết nối Wi-Fi. **Điều khiển bằng giọng n&oacute;i được hỗ trợ với 8 ng&ocirc;n ngữ (tiếng Anh-Anh, tiếng Anh-Mỹ, tiếng H&agrave;n, tiếng Trung, tiếng Đức, tiếng Ph&aacute;p, tiếng T&acirc;y Ban Nha, tiếng &Yacute;).</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Tự Động Tối Ưu Chất Lượng Kh&ocirc;ng Kh&iacute;</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh theo c&aacute;ch của bạn. Chế độ Tự Động sẽ đo lường mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute; v&agrave; tự động điều chỉnh c&ocirc;ng suất v&agrave; tốc độ quạt. V&igrave; vậy, gi&uacute;p bạn tiết kiệm hiệu quả điện năng sử dụng. Khi bật chế độ Ngủ, m&aacute;y sẽ thổi gi&oacute; nhẹ nh&agrave;ng hơn v&agrave; tắt m&agrave;n h&igrave;nh hiển thị cho bạn giấc ngủ bạn s&acirc;u v&agrave; sảng kho&aacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/X4Aa13e8Q2K823W.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thiết Kế Mỏng Gọn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tối ưu h&oacute;a kh&ocirc;ng gian sống của bạn, dễ d&agrave;ng l&agrave;m sạch &amp; thay đổi bộ lọc. Thiết kế mỏng gọn đảm bảo ph&ugrave; hợp với tất cả mọi nơi v&agrave; kh&ocirc;ng chiếm nhiều diện t&iacute;ch. B&ecirc;n cạnh đ&oacute;, cửa h&uacute;t gi&oacute; ph&iacute;a trước dễ d&agrave;ng th&aacute;o rời gi&uacute;p thuận tiện hơn cho việc thay thế v&agrave; bảo tr&igrave; m&agrave;ng lọc m&agrave; kh&ocirc;ng cần dịch chuyển m&aacute;y ra khỏi tường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/GY7dR2S40YO5yB7.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>B&aacute;o Hiệu Thay Bộ Lọc</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">B&aacute;o Hiệu Thay Bộ Lọc sẽ nhấp nh&aacute;y tr&ecirc;n m&agrave;n h&igrave;nh hiển thị nhắc nhở bạn khi cần phải thay bộ lọc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/82RtP01dvA81p49.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Đồng Hồ Hẹn Giờ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ hẹn giờ hỗ trợ l&ecirc;n lịch bật/tắt m&aacute;y lọc kh&ocirc;ng kh&iacute; gi&uacute;p bạn kh&ocirc;ng l&atilde;ng ph&iacute; điện năng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://cdn.mediamart.vn/images/Upload/download/2019-10-News/996mpc42E8496a9.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Chế Độ Kh&oacute;a Trẻ Em</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">T&iacute;nh năng Kh&oacute;a Trẻ Em ngăn mọi thay đổi ngẫu nhi&ecirc;n trong c&agrave;i đặt hoặc c&aacute;c thao t&aacute;c sử dụng kh&ocirc;ng đ&uacute;ng c&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/may-loc-khong-khi-samsung-ax60r5080wd-sv-3-org.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, '1657442833_vn-air-purifier-ax60r5080wdsv-ax60r5080wd-sv-frontwhite-189196503.jpg', 12500000, 35, 3);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(12, 'Máy điều hòa Digital Inverter 21,500 BTu/h (F-AR24TYHYCW20)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y điều h&ograve;a Digital Inverter 21,500 BTu/h (F-AR24TYHYCW20)</strong></span></h2>\r\n\r\n<p>C&ocirc;ng suất: 21500BTU,&nbsp;C&ocirc;ng nghệ Inverter</p>\r\n\r\n<p>Chế độ L&agrave;m Lạnh Tức Th&igrave; Fast Cooling</p>\r\n\r\n<p>Bộ lọc Tri-Care Filter lọc sạch hiệu quả bụi si&ecirc;u mịn</p>\r\n\r\n<p>Chế độ Ngủ Ngon duy tr&igrave; nhiệt độ l&yacute; tưởng</p>\r\n', '<h3><span style=\"font-size:14px\">Vận h&agrave;nh &ecirc;m &aacute;i v&agrave; Si&ecirc;u tiết kiệm điện đến 73%*</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Động cơ Digital Inverter Boost</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Digital Inverter Boost ưu việt gi&uacute;p tiết kiệm điện năng hiệu quả l&ecirc;n đến 77%* v&agrave; duy tr&igrave; ổn định nhiệt độ mong muốn. Với nam ch&acirc;m neodymium v&agrave; bộ giảm &acirc;m k&eacute;p Twin Tube Muffler, m&aacute;y hoạt động y&ecirc;n tĩnh, &ecirc;m &aacute;i v&agrave; bền l&acirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/mmbz2Z8gvV4R8c4.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Kiểm định tr&ecirc;n model AR09TXCAAWKNEU so với model Samsung AQ09TSLXEA th&ocirc;ng thường.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Lan Tỏa Nhanh Hơn, Xa Hơn</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/tin-tuc/tu-van-dieu-hoa/tong-hop-cac-cong-nghe-tren-dieu-hoa-samsung.htm\">Chế độ L&agrave;m Lạnh Tức Th&igrave; Fast Cooling</a></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Nhanh ch&oacute;ng ph&acirc;n t&aacute;n kh&ocirc;ng kh&iacute; m&aacute;t lạnh đến mọi ng&oacute;c ng&aacute;ch trong ph&ograve;ng. C&ocirc;ng nghệ Digital&nbsp;<a href=\"https://mediamart.vn/dieu-hoa-nhiet-do/samsung?&amp;loc=cong-nghe-inverter\">Inverter&nbsp;</a>Boost đẩy nhanh tốc độ l&agrave;m lạnh kh&ocirc;ng kh&iacute; đến 43%. Thiết kế cải tiến với quạt gi&oacute; lớn hơn 15%, cửa h&uacute;t kh&iacute; rộng hơn 18% v&agrave; cửa thổi kh&iacute; rộng hơn 31%, gi&uacute;p kh&ocirc;ng kh&iacute; lạnh được ph&acirc;n t&aacute;n xa v&agrave; rộng hơn l&ecirc;n đến 15m**.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/gpqlomg5ukUwJ9N.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Kiểm định tr&ecirc;n model AR12TXCAAWKEU so với model Samsung AQ12EASER th&ocirc;ng thường.<br />\r\n**Kiểm định tr&ecirc;n model AR24TXFCAWKNEU.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Lọc 99% Virus, Vi Khuẩn, Bụi Si&ecirc;u Mịn V&agrave; T&aacute;c Nh&acirc;n G&acirc;y Dị Ứng</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Bộ Lọc Tri-Care Filter</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Được chứng nhận bởi tổ chức quốc tế ITEA v&agrave; Intertek, bộ lọc Tri-Care tr&ecirc;n điều h&ograve;a Samsung c&oacute; khả năng lọc 99% virus, vi khuẩn, bụi si&ecirc;u mịn v&agrave; t&aacute;c nh&acirc;n g&acirc;y dị ứng, giữ kh&ocirc;ng kh&iacute; lu&ocirc;n trong l&agrave;nh ho&agrave;n hảo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(930).JPG\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Dễ D&agrave;ng Tự Vệ Sinh</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/tin-tuc/tu-van-dieu-hoa/tong-hop-cac-cong-nghe-tren-dieu-hoa-samsung.htm\">Bộ Lọc Easy Filter Plus</a></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bộ lọc Easy Filter Plus thiết kế b&ecirc;n tr&ecirc;n điều h&ograve;a c&oacute; thể dễ d&agrave;ng th&aacute;o rời để l&agrave;m sạch; gi&uacute;p điều h&ograve;a lu&ocirc;n hoạt động hiệu quả. Lớp lưới d&agrave;y giữ cho bộ trao đổi nhiệt lu&ocirc;n sạch sẽ, trong khi đ&oacute; lớp phủ chống vi khuẩn bảo vệ bạn khỏi c&aacute;c chất g&acirc;y &ocirc; nhiễm nguy hiểm c&oacute; trong kh&ocirc;ng kh&iacute;*.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Đ&atilde; thử nghiệm tại ph&ograve;ng th&iacute; nghiệm H&agrave;n Quốc. Dữ liệu đ&atilde; được đo trong c&aacute;c điều kiện thử nghiệm cụ thể v&agrave; c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o c&aacute;c yếu tố m&ocirc;i trường v&agrave; việc sử dụng c&aacute; nh&acirc;n.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/2C5nWXdBH80077D.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Tối Ưu Nhiệt Độ Suốt Đ&ecirc;m D&agrave;i</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Chế Độ Ngủ Ngon</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/dieu-hoa-nhiet-do/samsung?&amp;loc=thoi-gio-de-chiu\">Chế độ Ngủ Ngon</a>&nbsp;duy tr&igrave; nhiệt độ l&yacute; tưởng, hạn chế gi&oacute; buốt gi&uacute;p giấc ngủ đ&ecirc;m th&ecirc;m dễ chịu. An t&acirc;m ngủ s&acirc;u hơn, ch&igrave;m v&agrave;o giấc ngủ nhanh hơn v&agrave; thức giấc với nguồn năng lượng tr&agrave;n đầy.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/2H0ARSvQfvo98H7.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Tự Động, L&agrave;m Sạch</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">T&iacute;nh Năng Auto Clean (3 Bước)</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tiết kiệm tối ưu thời gian v&agrave; c&ocirc;ng sức vệ sinh thiết bị. Sau khi hoạt động, t&iacute;nh năng Auto Clean sẽ tự động l&agrave;m kh&ocirc; bộ trao đổi nhiệt bằng quy tr&igrave;nh 3 bước nghi&ecirc;m ngặt. Loại bỏ ẩm ướt bằng c&aacute;ch thổi kh&iacute; từ 10 đến 30 ph&uacute;t, nhờ đ&oacute; ngăn ngừa sự t&iacute;ch tụ của vi khuẩn v&agrave; m&ugrave;i h&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/8kVKWhV9i4P752A.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Bảo Vệ Tối Ưu Cho Hiệu Suất Bền Bỉ</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Triple Protector Plus</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">An t&acirc;m tận hưởng hiệu suất bền bỉ trước mọi th&aacute;ch thức về m&ocirc;i trường. C&ocirc;ng nghệ Bộ Ba Bảo Vệ Triple Protector Plus ngăn chặn hư hỏng do tăng &aacute;p hoặc do dao động d&ograve;ng điện m&agrave; kh&ocirc;ng cần đến ổn &aacute;p gắn rời*. Lớp phủ chống ăn m&ograve;n bảo vệ hiệu quả b&igrave;nh ngưng tụ v&agrave; buồng m&aacute;y trong mọi điều kiện thời tiết khắc nghiệt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Được kiểm nghiệm tr&ecirc;n mẫu AR12TXCAAWKEU so với mẫu m&aacute;y AS18FCMID th&ocirc;ng thường của Samsung.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/4IFYr11zLib7Y5K.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Bảo Vệ Tối Ưu Bộ Trao Đổi Nhiệt</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Chống Ăn M&ograve;n DuraFin&trade;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Chống Ăn M&ograve;n Durafin&trade; gi&uacute;p tăng cường hiệu quả hoạt động của bộ trao đổi nhiệt với chất liệu thiết kế d&agrave;y v&agrave; đặc hơn c&ugrave;ng lớp phủ cải tiến gi&uacute;p ngăn ngừa tối đa qu&aacute; tr&igrave;nh ăn m&ograve;n. Khả năng tuyệt vời n&agrave;y đ&atilde; đạt chuẩn kiểm nghiệm sự ăn m&ograve;n trong m&ocirc;i trường nước biển axit axetic (theo chuẩn SWAAT) trong hơn 100 ng&agrave;y*.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n mẫu AR13TYHYBWKNST so với mẫu m&aacute;y AR18JRFNFWK th&ocirc;ng thường của Samsung.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/0aaDwPTKF8M6Vra.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Th&acirc;n Thiện Với M&ocirc;i Trường</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Chất L&agrave;m Lạnh R32</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Điều h&ograve;a sử dụng chất l&agrave;m lạnh R32 thế hệ mới gi&uacute;p hạn chế t&aacute;c động l&ecirc;n tầng ozone, từ đ&oacute; ngăn chặn t&igrave;nh trạng n&oacute;ng l&ecirc;n to&agrave;n cầu hiệu quả hơn so với chất l&agrave;m lạnh R22 hoặc R410A th&ocirc;ng thường. Bảo vệ Tr&aacute;i Đất, kiểm so&aacute;t t&igrave;nh trạng biến đổi kh&iacute; hậu với giải ph&aacute;p th&acirc;n thiện m&ocirc;i trường* từ Samsung.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Kiểm định tr&ecirc;n chất l&agrave;m lạnh R32 so với c&aacute;c chất l&agrave;m lạnh R410A v&agrave; R22 th&ocirc;ng thường của Samsung.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-3-News/7gK05QP9i8W48s1.jpg\" /></span></p>\r\n', 0, '1657442646_vn-wall-mount-outdoor-ar24tyhycwkxsv-f-ar24tyhycw20-217615165.jpg', 25500000, 17, 3),
(13, 'Máy hút bụi không dây Jet 90E multi (VS15R8544S1)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;M&aacute;y h&uacute;t bụi kh&ocirc;ng d&acirc;y Jet 90E multi (VS15R8544S1)</strong></span></h2>\r\n\r\n<p>C&ocirc;ng suất 410W mạnh mẽ với thiết kế gọn nhẹ<br />\r\nGiữ kh&ocirc;ng kh&iacute; trong nh&agrave; sạch hơn<br />\r\nDễ d&agrave;ng vệ sinh hộp chưa bụi<br />\r\nChổi h&uacute;t mềm dễ d&agrave;ng vệ sinh mặt s&agrave;n cứng</p>\r\n', '<h3><span style=\"font-size:14px\">C&ocirc;ng Suất H&uacute;t Mạnh Mẽ Với Thiết Kế Gọn Nhẹ</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Hiệu Suất L&agrave;m Sạch Vượt Trội, Gọn Nhẹ Dễ Di Chuyển</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hiệu quả tr&ecirc;n nhiều loại mặt s&agrave;n với thiết kế kh&ocirc;ng d&acirc;y gọn nhẹ.&nbsp;<a href=\"https://mediamart.vn/may-hut-bui/samsung/?\" target=\"_blank\">M&aacute;y h&uacute;t bụi SAMSUNG</a>&nbsp;Jet&trade; trang bị c&ocirc;ng nghệ Digital Inverter tạo c&ocirc;ng suất h&uacute;t mạnh tới 150W*. Thiết kế kh&iacute; động học gi&uacute;p tối ưu luồng kh&iacute; h&uacute;t v&agrave; bộ lọc đa luồng h&uacute;t xo&aacute;y gi&uacute;p lọc sạch bụi mịn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/NSka1gHR45u014p.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n th&iacute; nghiệm nội bộ tại chế độ mạnh nhất, dựa theo ti&ecirc;u chuẩn IEC62885-2CI.5.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Suất H&uacute;t Mạnh Mẽ</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">Động Cơ Digital Inverter</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-hut-bui/samsung/?&amp;loc=kieu-cam-tay\" target=\"_blank\">M&aacute;y h&uacute;t bụi cầm tay Samsung</a>&nbsp;c&oacute; hiệu quả vệ sinh vượt trội với c&ocirc;ng suất h&uacute;t l&ecirc;n tới 150W*. Động cơ Digital Inverter tối ưu luồng kh&iacute; bằng c&aacute;nh quạt si&ecirc;u &acirc;m, tải được điện năng đầu v&agrave;o l&ecirc;n đến 410W* v&agrave; duy tr&igrave; lực h&uacute;t mạnh đến 150W*.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/vr5k12i0i0PofrY.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Thử nghiệm nội bộ dựa tr&ecirc;n ti&ecirc;u chuẩn IEC62885-2 CI. 5.8 tại chế độ mạnh nhất.<br />\r\n**Thử nghiệm nội bộ dựa theo ti&ecirc;u chuẩn IEC60335-1 CI. 10.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Sức H&uacute;t Vượt Trội</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ H&uacute;t Đa Lốc Xo&aacute;y Jet Cyclone</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-hut-bui/?&amp;loc=kieu-cam-tay\" target=\"_blank\">M&aacute;y h&uacute;t bụi cầm tay</a>&nbsp;c&oacute; hệ thống h&uacute;t đa lốc xo&aacute;y Jet Cyclone tạo ra lực h&uacute;t mạnh vượt trội. Luồng kh&iacute; h&uacute;t được tối ưu với 9 trục h&uacute;t xo&aacute;y v&agrave; 27 cửa h&uacute;t gi&oacute;, gi&uacute;p giảm thiểu việc hao hụt lực h&uacute;t. Ngo&agrave;i ra, Jet Cyclone c&ograve;n gi&uacute;p lọc sạch bụi mịn, trả lại kh&ocirc;ng kh&iacute; sạch cho ng&ocirc;i nh&agrave; của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/o2ID88b2W6x50ia.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Giữ Kh&ocirc;ng Kh&iacute; Trong Nh&agrave; Sạch Hơn</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hệ thống lọc 5 lớp HEPA</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đầu ti&ecirc;n, luồng xo&aacute;y ch&iacute;nh v&agrave; bộ lọc lưới kim loại sẽ gi&uacute;p lọc v&agrave; giữ c&aacute;c lại c&aacute;c hạt bụi lớn. Sau đ&oacute;, hệ thống h&uacute;t đa lốc xo&aacute;y Jet Cyclone v&agrave; bộ vi lọc gi&uacute;p loại bỏ c&aacute;c hạt bụi nhỏ trong kh&ocirc;ng kh&iacute;. Cuối c&ugrave;ng, bộ lọc bụi si&ecirc;u mịn gi&uacute;p lọc 99.999%** bụi mịn với k&iacute;ch thước 0,3 ~ 10&micro;m*** v&agrave; c&aacute;c t&aacute;c nh&acirc;n g&acirc;y dị ứng, đảm bảo kh&ocirc;ng kh&iacute; xả ra từ m&aacute;y h&uacute;t bụi l&agrave; kh&ocirc;ng kh&iacute; sạch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/2M1ZvFG19qNCaMU.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*HEPA: High Efficiency Particulate Airfiltration system<br />\r\n**Dựa tr&ecirc;n th&iacute; nghiệm nội bộ tại chế độ mạnh nhất (Max), dựa theo ti&ecirc;u chuẩn IEC62885-2 CI.5.11<br />\r\n***K&iacute;ch thước si&ecirc;u nhỏ m&agrave; mắt thường kh&ocirc;ng nh&igrave;n thấy</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">40 Ph&uacute;t H&uacute;t Bụi Li&ecirc;n Tục</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/may-hut-bui/\" target=\"_blank\">M&aacute;y h&uacute;t bụi</a>&nbsp;c&oacute; Pin 1800mA cho thời gian sử dụng li&ecirc;n tục đến 40 ph&uacute;t. Khi hết pin, c&oacute; thể dễ d&agrave;ng th&aacute;o rời v&agrave; thay pin dự ph&ograve;ng để tiếp tục chu tr&igrave;nh h&uacute;t bụi*</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/5ptAvMyjPVrFwX2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Pin c&oacute; thể duy tr&igrave; 70% hiệu suất ban đầu sau 500 chu tr&igrave;nh h&uacute;t.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Trạm Sạc Tiện Lợi Với Thiết Kế C&ocirc;ng Th&aacute;i Học</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Trạm Sạc Chữ Z</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Việc sạc pin v&agrave; cất giữ m&aacute;y h&uacute;t bụi trở n&ecirc;n dễ d&agrave;ng hơn bao giờ hết với trạm sạc chữ Z tiện lợi. Thiết kế tự đứng nhỏ gọn với độ nghi&ecirc;ng 7 độ gi&uacute;p bạn dễ d&agrave;ng cất v&agrave; lấy m&aacute;y h&uacute;t bụi, kể cả tại kh&ocirc;ng gian nhỏ hẹp. Bộ sạc k&eacute;p cho ph&eacute;p sạc 2 pin c&ugrave;ng l&uacute;c chỉ trong 3.5 giờ*</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa theo th&iacute; nghiệm nội bộ, kết quả c&oacute; thể thay đổi t&ugrave;y theo người d&ugrave;ng người d&ugrave;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/A1LAipWEa0Nv3Zi.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Kiểm So&aacute;t Lực H&uacute;t Dễ D&agrave;ng</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n H&igrave;nh Digital</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng kiểm tra t&igrave;nh trạng hiện tại của m&aacute;y, v&iacute; dụ mức c&ocirc;ng suất hiện tại v&agrave; loại chổi h&uacute;t đang sử dụng. Ngo&agrave;i ra, m&agrave;n h&igrave;nh sẽ ngay lập tức th&ocirc;ng b&aacute;o khi c&oacute; vấn đề xảy ra với chổi h&uacute;t hoặc khi c&oacute; tắc nghẽn. V&igrave; vậy, bạn c&oacute; thể kiểm so&aacute;t v&agrave; duy tr&igrave; việc h&uacute;t bụi một c&aacute;ch thuận tiện.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/3y2JeJH4VW8yUKB.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Dễ D&agrave;ng Vệ Sinh To&agrave;n Bộ Hộp Chứa Bụi</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hộp Chứa Bụi C&oacute; Thể Rửa Được</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cho ph&eacute;p th&aacute;o rời v&agrave; rửa to&agrave;n bộ cấu tr&uacute;c h&uacute;t bụi dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/00v287920s197lv.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thiết Kế Khoa Học</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Dễ D&agrave;ng Điều Chỉnh Độ D&agrave;i Th&acirc;n M&aacute;y</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết kế gọn nhẹ gi&uacute;p đảm bảo t&iacute;nh di dộng của m&aacute;y h&uacute;t bụi, đồng thời giảm thiểu sức nặng l&ecirc;n cổ tay. Bạn c&oacute; thể dễ d&agrave;ng điều chỉnh độ d&agrave;i của th&acirc;n m&aacute;y để c&oacute; được tư thế h&uacute;t bụi thoải m&aacute;i nhất với 4 mức độ kh&aacute;c nhau.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/ASW7ojWj5s4I7M1.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\">Hiệu Quả Tr&ecirc;n Mặt S&agrave;n Cứng - Chổi H&uacute;t Mềm</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chổi h&uacute;t l&agrave;m từ vải dệt với th&agrave;nh phần bạc chống tĩnh điện, gi&uacute;p dễ d&agrave;ng loại bỏ bụi bẩn tr&ecirc;n mặt s&agrave;n cứng v&agrave; kẽ hở tr&ecirc;n mặt s&agrave;n, chống t&igrave;nh trạng t&oacute;c v&agrave; l&ocirc;ng th&uacute; cưng b&aacute;m v&agrave;o đầu chổi g&acirc;y tắc nghẽn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-8-News/S479HAt8wkD393y.jpg\" /></span></p>\r\n', 0, '1657442105_vn-stick-handy-vs15r8544s1-vs15r8544s1-sv-frontblue-216174216.jpg', 1850000, 35, 19),
(14, 'Robot hút bụi, lau nhà Powerbot-E (VR05R5050WK)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Robot h&uacute;t bụi, lau nh&agrave; Powerbot-E (VR05R5050WK)</strong></span></h2>\r\n\r\n<p>C&ocirc;ng nghệ h&uacute;t bụi v&agrave; lau s&agrave;n 2 trong 1 gi&uacute;p tiết kiệm thời gian, c&ocirc;ng sức.</p>\r\n\r\n<p>Pin hiệu suất cao với thời gian sạc khoảng 4 giờ, sử dụng khoảng 150 ph&uacute;t.</p>\r\n\r\n<p>Điều khiển v&agrave; kiểm so&aacute;t dễ d&agrave;ng bằng điện thoại qua kết nối wifi.</p>\r\n\r\n<p>Hệ thống cảm biến th&ocirc;ng minh di chuyển ch&iacute;nh x&aacute;c, đảm bảo l&agrave;m sạch mọi ng&oacute;c ng&aacute;ch.</p>\r\n\r\n<p>Linh hoạt lựa chọn c&aacute;c chế độ l&agrave;m sạch: chế độ tự động, chế độ zigzac, chế độ Edge, chế độ Spot.</p>\r\n\r\n<p>Thiết kế nhỏ gọn, hiện đại gi&uacute;p bạn dọn dẹp nh&agrave; cửa tiện lợi hơn.</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Thiết bị h&uacute;t bụi v&agrave; lau s&agrave;n 2 trong 1, l&agrave;m sạch nh&agrave; cửa tiện lợi, tiết kiệm c&ocirc;ng sức</strong><br />\r\nRobot h&uacute;t bụi&nbsp;loại bỏ bụi bẩn dễ d&agrave;ng c&ograve;n&nbsp;c&oacute; thể tự động bật chổi lau, điều chỉnh lượng nước với hộp chứa nước th&ocirc;ng minh, lau s&agrave;n thuận tiện.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-i0t48r.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng suất h&uacute;t&nbsp;4200 Pa cho lực h&uacute;t mạnh, vệ sinh nh&agrave; cửa nhanh ch&oacute;ng</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-yL8f1I.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>L&agrave;m sạch li&ecirc;n tục đến 150 ph&uacute;t chỉ trong 1 lần sạc</strong><br />\r\nPin c&oacute; dung lượng cao cho thời gian l&agrave;m sạch liền mạch, kh&ocirc;ng gi&aacute;n đoạn, rất tiện dụng trong ng&ocirc;i nh&agrave; c&oacute; diện t&iacute;ch lớn. Khi pin yếu, robot dễ d&agrave;ng sạc lại tại trạm sạc trong tầm 4 giờ.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-veQw6j.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quản l&yacute; robot dễ d&agrave;ng bằng điện thoại với ứng dụng POWERbot-E&nbsp;qua kết nối wifi</strong><br />\r\nTh&ocirc;ng qua ứng dụng POWERbot-E hoặc SmartThings, bạn c&oacute; thể&nbsp;khởi động hoặc tắt thiết bị, đặt lịch tr&igrave;nh l&agrave;m sạch, điều chỉnh lực h&uacute;t, điều khiển tốc độ lau,...&nbsp;linh hoạt mọi l&uacute;c mọi nơi. Ngo&agrave;i ra, sản phẩm c&ograve;n c&oacute; remote đi k&egrave;m cho ph&eacute;p t&ugrave;y chỉnh thiết bị từ xa dễ d&agrave;ng, kh&ocirc;ng cần phải trực tiếp thao t&aacute;c tr&ecirc;n th&acirc;n robot.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-o9D2oh.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hệ thống cảm biến th&ocirc;ng minh chuyển động mượt m&agrave;, dọn dẹp hiệu quả</strong><br />\r\nRobot h&uacute;t bụi Samsung&nbsp;c&oacute; cảm biến th&ocirc;ng minh gi&uacute;p kiểm so&aacute;t chuyển động tinh vi với cảm biến chống va chạm, cảm biến v&aacute;ch tường v&agrave; cảm biến con quay hồi chuyển cho robot l&agrave;m sạch mọi ng&oacute;c ng&aacute;ch dễ d&agrave;ng, an to&agrave;n cho thiết bị.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-oRh3CU.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hộp chứa bụi dung t&iacute;ch 0.2 l&iacute;t, hộp chứa nước 0.3 l&iacute;t, đ&aacute;p ứng nhu cầu l&agrave;m sạch hằng ng&agrave;y</strong><br />\r\nTrong đ&oacute; hộp chứa bụi chuẩn EZ&nbsp;mở ra nhanh ch&oacute;ng chỉ với 1 n&uacute;t nhấn, c&oacute; thể th&aacute;o rời v&agrave; l&agrave;m sạch bằng nước đơn giản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-afmt66.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&oacute; nhiều chế độ l&agrave;m sạch tiện &iacute;ch</strong><br />\r\nThiết bị c&oacute; 4 chế độ l&agrave;m sạch bao gồm chế độ Tự động, chế độ Zigzac, chế độ Edge, chế độ Spot cho bạn t&ugrave;y chọn theo nhu cầu, ho&agrave;n cảnh sử dụng. B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; chế độ l&ecirc;n lịch tr&igrave;nh dọn dẹp&nbsp;cho ph&eacute;p người d&ugrave;ng c&agrave;i đặt nhiều lịch tr&igrave;nh để robot tự động vệ sinh v&agrave;o những thời điểm bạn đ&atilde; chọn hằng ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-q46qV7.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kiểu d&aacute;ng gọn nhẹ, nhỏ nhắn, m&agrave;u đen lịch l&atilde;m, t&ocirc; điểm cho kh&ocirc;ng gian nội thất sang trọng, cao cấp hơn<br />\r\nTiện tiếp cận c&aacute;c khu vực kh&oacute; l&agrave;m sạch như gầm b&agrave;n, gầm giường, l&agrave;m sạch mọi ng&oacute;c ng&aacute;ch trong ng&ocirc;i nh&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"ghj\" src=\"https://mediamart.vn/images/uploads/data-2021/ghj-ED870W.jpg\" /></span></p>\r\n', 1, '1657441919_vn-robot-vr05r5050wk-vr05r5050wk-sv-519428263.jpg', 9750000, 39, 19),
(15, 'Lò vi sóng tráng men BESPOKE Dòng nướng 23L (MG23T5018CE/SV)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;L&ograve; vi s&oacute;ng tr&aacute;ng men BESPOKE D&ograve;ng nướng 23L (MG23T5018CE/SV)</strong></span></h2>\r\n\r\n<p>L&ograve; vi s&oacute;ng c&oacute; nướng 23L, c&ocirc;ng suất 1250W</p>\r\n\r\n<p>M&agrave;u sắc thời thượng, mặt k&iacute;nh sang trọng</p>\r\n\r\n<p>C&ocirc;ng Nghệ Chi&ecirc;n Nướng Grill Fry</p>\r\n\r\n<p>Khoang L&ograve; Tr&aacute;ng Men CERAMIC INSIDE&trade;</p>\r\n\r\n<p>Hệ Thống Tỏa Nhiệt 3 Chiều</p>\r\n\r\n<p>T&iacute;nh năng Quick Defrost gi&uacute;p bạn nhanh ch&oacute;ng r&atilde; đ&ocirc;ng thực phẩm</p>\r\n\r\n<p>Bảng Điều Khiển Cảm Ứng Glass Touch</p>\r\n\r\n<p>Chế Độ Nấu Tự Động Auto Cook</p>\r\n\r\n<p>Chế độ ECO gi&uacute;p giảm đ&aacute;ng kể mức ti&ecirc;u thụ điện năng bằng việc duy tr&igrave; c&ocirc;ng suất dự ph&ograve;ng thấp nhất</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Thiết Kế Ho&agrave;n Hảo Cho Mọi Căn Bếp</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>M&agrave;u sắc thời thượng, mặt k&iacute;nh sang trọng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&ograve; vi s&oacute;ng BESPOKE cho ph&eacute;p bạn tự m&igrave;nh quyết định m&agrave;u sắc y&ecirc;u th&iacute;ch theo sở th&iacute;ch v&agrave; c&aacute; t&iacute;nh ri&ecirc;ng. Với sự đột ph&aacute; từ những gam m&agrave;u trẻ trung, thời thượng kết hợp c&ugrave;ng mặt k&iacute;nh sang trọng; bạn thỏa sức chọn lựa m&agrave;u sắc ph&ugrave; hợp để đồng nhất hoặc tạo điểm nhấn th&uacute; vị trong kh&ocirc;ng gian bếp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Gi&ograve;n Tan V&agrave; Bổ Dưỡng<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/33f1f951-c9bf-4df6-b0f1-6319be521d8a.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng Nghệ Chi&ecirc;n Nướng Grill Fry</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thưởng thức đồ ăn gi&ograve;n tan, bổ dưỡng như được chế biến bởi l&ograve; nướng th&ocirc;ng thường với chế độ Grill Fry. Chức năng Grill Fry gi&uacute;p bạn chi&ecirc;n nướng thức ăn m&agrave; kh&ocirc;ng cần sử dụng dầu ăn, gi&uacute;p đồ ăn được nướng ch&iacute;n đều v&agrave; tốt cho sức khỏe.<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/78564585-7e6e-4d76-a3bc-3b9fb2f02b2f.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Khoang L&ograve; Bền Đẹp, Kh&aacute;ng Khuẩn</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Khoang L&ograve; Tr&aacute;ng Men CERAMIC INSIDE&trade;</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khoang l&ograve; tr&aacute;ng men Ceramic gi&uacute;p bạn dễ d&agrave;ng lau ch&ugrave;i sạch sẽ, m&agrave; kh&ocirc;ng sợ t&igrave;nh trạng ngả m&agrave;u theo thời gian. Đồng thời c&oacute; khả năng loại bỏ 99.9% vi khuẩn c&oacute; hại, theo chứng nhận của viện Hohenstein, Đức. Bề mặt bền đẹp c&oacute; khả năng chống trầy xước v&agrave; rỉ s&eacute;t gấp 7 lần so với c&aacute;c nội thất kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://mediamart.vn/images/uploads/2021/0df7ffa1-a1be-42ae-b9b9-7eca392882e1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ch&iacute;n Đều Trong Ngo&agrave;i</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hệ Thống Tỏa Nhiệt 3 Chiều</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&ograve; vi s&oacute;ng th&ocirc;ng minh Samsung được trang bị Hệ Thống Tỏa Nhiệt 3 Chiều TDS tăng cường khả năng tản nhiệt gi&uacute;p thức ăn ch&iacute;n đều từ trong ra ngo&agrave;i cho vị ngon ho&agrave;n hảo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">* Chức năng n&agrave;y c&oacute; thể kh&ocirc;ng được t&iacute;ch hợp trong một số mẫu.<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/b1ec3378-2244-4000-b502-e2fb6e892ad6.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>R&atilde; Đ&ocirc;ng Nhanh Ch&oacute;ng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quick Defrost</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&iacute;nh năng Quick Defrost gi&uacute;p bạn nhanh ch&oacute;ng r&atilde; đ&ocirc;ng thực phẩm với 5 lựa chọn cho 5 loại thực phẩm phổ biến: Thịt b&ograve;, thịt g&agrave;, c&aacute;, rau củ v&agrave; b&aacute;nh m&igrave;. Chỉ cần lựa chọn loại thực phẩm v&agrave; khối lượng thực phẩm, l&ograve; vi s&oacute;ng sẽ tự động điều chỉnh thời gian r&atilde; đ&ocirc;ng, gi&uacute;p giữ trọn vị tươi ngon cho thực phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">*Dựa tr&ecirc;n thử nghiệm nội bộ theo ti&ecirc;u chuẩn IEC.<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/fabf994f-584a-41d3-8945-4d5ed3eb92f9.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Sang Trọng V&agrave; Dễ Sử Dụng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bảng Điều Khiển Cảm Ứng Glass Touch</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng lựa chọn chế độ nấu ăn chỉ với một n&uacute;t bấm. Mặt bảng điều khiển được l&agrave;m ho&agrave;n to&agrave;n bằng k&iacute;nh, đem đến sự tối giản v&agrave; sang trọng cho căn bếp của bạn.<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/74d58d65-004e-475f-8457-554dedbd8ead.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đơn Giản V&agrave; Tiện Lợi</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế Độ Nấu Tự Động Auto Cook</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cho ph&eacute;p bạn nấu m&oacute;n ăn y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng cần nhớ c&ocirc;ng thức. Auto cook c&oacute; nhiều lựa chọn cho c&aacute;c m&oacute;n ăn được nấu thường xuy&ecirc;n nhất như thịt g&agrave; hay m&igrave; ống. Mỗi m&oacute;n ăn được lập tr&igrave;nh sẵn với chế độ, thời gian v&agrave; nhiệt độ tối ưu. Chỉ cần chọn loại v&agrave; khối lượng của thực phẩm, l&ograve; vi s&oacute;ng Samsung sẽ tự động nấu m&oacute;n ăn y&ecirc;u th&iacute;ch của bạn.<br />\r\n<img src=\"https://mediamart.vn/images/uploads/2021/48fc216c-fd12-427a-a1ee-179d1b4d8ca5.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Tiết Kiệm Điện Tối Ưu</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế Độ ECO Mode</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nấu nướng ngon v&agrave; tiết kiệm hơn với chế độ ECO Mode. Chế độ ECO gi&uacute;p giảm đ&aacute;ng kể mức ti&ecirc;u thụ điện năng bằng việc duy tr&igrave; c&ocirc;ng suất dự ph&ograve;ng thấp nhất. Khi bạn kh&ocirc;ng nấu ăn, năng lượng được sử dụng để duy tr&igrave; c&aacute;c t&iacute;nh năng thiết yếu của l&ograve; vi s&oacute;ng được giữ ở mức tổi thiểu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://mediamart.vn/images/uploads/2021/e2ab07e4-0194-4519-ad5b-0ab52cf3239d.PNG\" /></span></p>\r\n', 0, '1657439198_vn-microwaves-oven-grill-mg23t5018ck-324512-mg23t5018ce-sv-505450992.jpg', 5990000, 25, 20),
(16, 'Lò nướng, chiên không dầu Bespoke 7 trong 1 (MC35R8088LE/SV)', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;L&ograve; nướng, chi&ecirc;n kh&ocirc;ng dầu Bespoke 7 trong 1 (MC35R8088LE/SV)</strong></span></h2>\r\n\r\n<p>C&ocirc;ng suất 1450W, dung t&iacute;ch 35L, Tiện lợi, ho&agrave;n hảo cho gia đ&igrave;nh bận rộn</p>\r\n\r\n<p>M&agrave;u sắc thời thượng, mặt k&iacute;nh sang trọng</p>\r\n\r\n<p>C&ocirc;ng nghệ Thổi Kh&iacute; N&oacute;ng HotBlast&trade;</p>\r\n\r\n<p>C&ocirc;ng nghệ Chi&ecirc;n Kh&ocirc;ng Dầu Air Fry</p>\r\n\r\n<p>C&ocirc;ng nghệ PowerGrill Duo&trade;</p>\r\n\r\n<p>C&ocirc;ng nghệ hấp Pro Steamer</p>\r\n\r\n<p>Chế độ nấu tự động Auto Cook</p>\r\n\r\n<p>Tho&aacute;t dầu, thức ăn ch&iacute;n gi&ograve;n</p>\r\n\r\n<p>Khoang l&ograve; tr&aacute;ng men CERAMIC INSIDE&trade;</p>\r\n\r\n<p>Bảng điều khiển cảm ứng Glass Touch v&agrave; đ&egrave;n LED trắng</p>\r\n', '<p><span style=\"font-size:14px\"><strong>L&ograve; vi s&oacute;ng đa năng thế hệ mới</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Tiện lợi, ho&agrave;n hảo cho gia đ&igrave;nh bận rộn</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&ograve; vi s&oacute;ng đa năng BESPOKE d&ograve;ng đối lưu t&iacute;ch hợp nhiều t&iacute;nh năng chuy&ecirc;n biệt như Chi&ecirc;n kh&ocirc;ng dầu, Nướng, Hấp, Vi S&oacute;ng, Sấy Kh&ocirc; v&agrave; Ủ l&ecirc;n men sữa chua mang đến sự tiện lợi cho người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://mediamart.vn/images/uploads/2021/64229b2f-b5b1-4a26-96e8-dd8368ff7893.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Thiết kế ho&agrave;n hảo cho mọi căn bếp</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>M&agrave;u sắc thời thượng, mặt k&iacute;nh sang trọng</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">L&ograve; vi s&oacute;ng BESPOKE cho ph&eacute;p bạn tự m&igrave;nh quyết định m&agrave;u sắc y&ecirc;u th&iacute;ch theo sở th&iacute;ch v&agrave; c&aacute; t&iacute;nh ri&ecirc;ng. Với sự đột ph&aacute; từ những gam m&agrave;u trẻ trung, thời thượng kết hợp c&ugrave;ng mặt k&iacute;nh sang trọng; bạn thỏa sức chọn lựa m&agrave;u sắc ph&ugrave; hợp để đồng nhất hoặc tạo điểm nhấn th&uacute; vị trong kh&ocirc;ng gian bếp. Đ&acirc;y cũng l&agrave; L&ograve; vi s&oacute;ng mang trong m&igrave;nh sự linh hoạt vượt trội với khả năng nấu ăn đa năng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/02e7ab63-975a-4f52-a873-76a73ad0feb1.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Gi&ograve;n tan b&ecirc;n ngo&agrave;i, mềm mọng b&ecirc;n trong</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>C&ocirc;ng nghệ Thổi Kh&iacute; N&oacute;ng HotBlast&trade;</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Thổi Kh&iacute; N&oacute;ng HotBlast&trade; với c&ocirc;ng suất 2700W v&agrave; 72 lỗ tỏa nhiệt gi&uacute;p bạn giảm thiểu thời gian nấu nướng l&ecirc;n đến 50%, đồng thời gi&uacute;p m&oacute;n ăn gi&ograve;n tan b&ecirc;n ngo&agrave;i, mềm mọng b&ecirc;n trong.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/e93eca69-c6df-4ca6-ad62-3d65e5784afd.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>M&oacute;n ngon bổ dưỡng kh&ocirc;ng bị kh&ocirc; cứng</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>C&ocirc;ng nghệ Chi&ecirc;n Kh&ocirc;ng Dầu Air Fry</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Chi&ecirc;n Kh&ocirc;ng Dầu chỉ sử dụng một lượng dầu tối thiểu, đảm bảo m&oacute;n được chi&ecirc;n nhanh m&agrave; kh&ocirc;ng bị kh&ocirc; cứng, đồng thời vẫn ngon bổ dưỡng v&agrave; tốt cho sức khỏe.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://mediamart.vn/images/uploads/2021/1b0de6d9-6d8d-405e-bd06-55054bcfcc36.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Nướng thức ăn ch&iacute;n đều ho&agrave;n hảo</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>C&ocirc;ng nghệ PowerGrill Duo&trade;</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ PowerGrill Duo&trade; với thanh nhiệt nướng k&eacute;p rộng v&agrave; c&ocirc;ng suất mạnh mẽ l&ecirc;n tới 2300W gi&uacute;p cho nguồn nhiệt lu&ocirc;n đồng nhất, ổn định v&agrave; mang đến cho bạn những m&oacute;n nướng ch&iacute;n đều v&agrave; gi&ograve;n tan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/2815dccd-c76d-4f22-ad79-d0b6b8c99066.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Thức ăn mềm ẩm, mọng nước</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>C&ocirc;ng nghệ hấp Pro Steamer</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nắp ngăn hấp với ba lớp kh&ocirc;ng gỉ giữ cho thực phẩm lu&ocirc;n mềm ẩm v&agrave; ngăn mất nước, giữ trọn vị ngon từ trong ra ngo&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/779e0057-42a0-4f38-b6e2-be8cf39b593f.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Đơn giản v&agrave; tiện lợi</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Chế độ nấu tự động Auto Cook</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cho ph&eacute;p bạn nấu m&oacute;n ăn y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng cần nhớ c&ocirc;ng thức. Auto cook c&oacute; nhiều lựa chọn cho c&aacute;c m&oacute;n ăn được nấu thường xuy&ecirc;n nhất như thịt g&agrave; hay m&igrave; ống. Mỗi m&oacute;n ăn được lập tr&igrave;nh sẵn với chế độ, thời gian v&agrave; nhiệt độ tối ưu. Chỉ cần chọn loại v&agrave; khối lượng của thực phẩm, l&ograve; vi s&oacute;ng Samsung sẽ tự động nấu m&oacute;n ăn y&ecirc;u th&iacute;ch của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/b72c1030-4ebe-4a3d-bde5-4d6bccfa13c0.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Đĩa chống d&iacute;nh v&agrave; phản nhiệt</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Tho&aacute;t dầu, thức ăn ch&iacute;n gi&ograve;n</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trang bị đĩa phản nhiệt c&oacute; thiết kế r&atilde;nh tho&aacute;t dầu ngay cạnh gi&uacute;p thức ăn ch&iacute;n gi&ograve;n đều, kh&ocirc;ng bị ngấm dầu v&agrave; d&iacute;nh v&agrave;o đĩa sau khi chi&ecirc;n; bạn dễ d&agrave;ng lau rửa sau khi sử dụng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<img src=\"https://mediamart.vn/images/uploads/2021/12b13401-845e-44a5-be36-f5258d0d22b5.PNG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Dễ d&agrave;ng vệ sinh</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Khoang l&ograve; tr&aacute;ng men CERAMIC INSIDE&trade;</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khoang l&ograve; tr&aacute;ng men CERAMIC INSIDE&trade; chống trầy xước hiệu quả v&agrave; kh&ocirc;ng bị ngả m&agrave;u theo thời gian. Bề mặt phẳng mượt gi&uacute;p bạn dễ d&agrave;ng lau ch&ugrave;i sạch sẽ, cho l&ograve; vi s&oacute;ng lu&ocirc;n sạch đẹp v&agrave; bền bỉ như mới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/a5e3cb25-1cc6-4883-959b-9c9d7f18b104.PNG\" />&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Trực quan v&agrave; dễ sử dụng</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><strong>Bảng điều khiển cảm ứng Glass Touch v&agrave; đ&egrave;n LED trắng</strong></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng lựa chọn chế độ nấu ăn với bảng điều khiển cảm ứng mượt m&agrave;. Mặt bảng điều khiển được l&agrave;m ho&agrave;n to&agrave;n bằng k&iacute;nh c&ugrave;ng đ&egrave;n LED trắng thanh lịch hiển thị thời gian r&otilde; r&agrave;ng cho bạn trải nghiệm nấu nướng thuận tiện nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2021/9790fbd9-0c02-4632-8d87-226193537a7c.PNG\" /></span></p>\r\n', 0, '1657439027_vn-bespoke-mw8000r-mc35r8088le-sv-505437378.jpg', 15490000, 25, 20);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(17, 'Máy chiếu siêu gần công nghệ Smart TV 4K UHD 130\" The Premiere LSP9T', '<h2><span style=\"font-size:16px\"><strong>Th&ocirc;ng số kỹ thuật M&aacute;y Chiếu Si&ecirc;u Gần Laser 4K Samsung The Premiere LSP9T</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Loại m&aacute;y:</p>\r\n\r\n	<p>M&aacute;y chiếu laser 3 tia 4KM&agrave;n chiếu tối đa 130 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Tizen OS 6.0</p>\r\n	</li>\r\n	<li>\r\n	<p>Độ s&aacute;ng (Lumen ANSi):</p>\r\n\r\n	<p>2800 lumens</p>\r\n	</li>\r\n	<li>\r\n	<p>Độ tương phản:</p>\r\n\r\n	<p>2.000.000 : 1</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ h&igrave;nh ảnh:</p>\r\n\r\n	<p>C&ocirc;ng nghệ Filmmaker ModeC&ocirc;ng nghệ Game EnhancerC&ocirc;ng nghệ h&igrave;nh ảnh Pur ColorHDR 10+</p>\r\n	</li>\r\n	<li>\r\n	<p>Điều khiển bằng điện thoại:</p>\r\n\r\n	<p>Bằng ứng dụng SmartApp</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng th&ocirc;ng minh:</p>\r\n\r\n	<p>Hỗ trợ t&igrave;m kiếm bằng giọng n&oacute;i YouTube bằng tiếng ViệtTrợ l&yacute; ảo Bixby (Chưa c&oacute; tiếng Việt)</p>\r\n	</li>\r\n	<li>\r\n	<p>K&iacute;ch thước - Khối lượng:</p>\r\n\r\n	<p>Cao 14.1 cm - Ngang 55 cm - D&agrave;y 36.7 cm - Nặng 11.5 kg</p>\r\n	</li>\r\n	<li>\r\n	<p>Năm ra mắt:</p>\r\n\r\n	<p>2021</p>\r\n	</li>\r\n</ul>\r\n', '<h3><span style=\"font-size:14px\">Khoảng c&aacute;ch đặt m&aacute;y chiếu si&ecirc;u gần ph&ugrave; hợp lắp đặt mọi vị tr&iacute;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y chiếu sẽ&nbsp;dễ d&agrave;ng bố tr&iacute; v&agrave; thuận tiện sử dụng&nbsp;hơn bao giờ hết nhờ&nbsp;c&ocirc;ng nghệ chiếu si&ecirc;u gần Ultra-Short Throw gi&uacute;p chiếu h&igrave;nh ảnh thẳng l&ecirc;n tường thay v&igrave; chiếu về ph&iacute;a trước&nbsp;khi<strong>&nbsp;đặt m&aacute;y chiếu s&aacute;t tường chỉ 11.3 cm.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107113.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Khoảng cách đặt máy siêu gần\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107113.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">M&aacute;y chiếu sở hữu c&aacute;c t&iacute;nh năng như một chiếc Smart tivi</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.dienmayxanh.com/may-chieu/may-chieu-samsung-the-premiere-lsp9t\" target=\"_blank\" title=\"Mời bạn xem thêm Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T đang kinh doanh tại Điện máy XANH\" type=\"Mời bạn xem thêm Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T đang kinh doanh tại Điện máy XANH\">M&aacute;y Chiếu Si&ecirc;u Gần Laser Samsung 4K The Premiere LSP9T</a>&nbsp;sở hữu&nbsp;<strong>khả năng hiển thị h&igrave;nh ảnh ấn tượng</strong>&nbsp;<strong>c&ugrave;ng c&aacute;c</strong>&nbsp;<strong>t&iacute;nh năng th&ocirc;ng minh v&agrave; giao diện như một chiếc<a href=\"https://www.dienmayxanh.com/tivi?g=smart-tivi\" target=\"_blank\" title=\"Mời bạn xem thêm Smart tivi đang kinh doanh tại Điện máy XANH\" type=\"Mời bạn xem thêm Smart tivi đang kinh doanh tại Điện máy XANH\">&nbsp;Smart tivi</a>&nbsp;</strong>như: kho ứng dụng phong ph&uacute;, điều khiển điện thoại, tr&igrave;nh chiếu th&ocirc;ng tin từ điện thoại l&ecirc;n m&aacute;y chiếu...</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, The Premiere LSP9T c&ograve;n gi&uacute;p người d&ugrave;ng tiết kiệm thời gian nhờ&nbsp;khả năng khởi động tức th&igrave;, lu&ocirc;n sẵn s&agrave;ng để bạn sử dụng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107091.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Máy chiếu như một chiếc tivi\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107091.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thiết kế thanh m&atilde;nh, liền mạch, sang trọng với lớp vải bọc cao cấp</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">The Premiere sở hữu đường n&eacute;t mềm mại, ấm &aacute;p với lớp vỏ bọc kh&aacute;c biệt. Chất vải Kvadrat gi&uacute;p The Premiere tăng th&ecirc;m vẻ sang trọng v&agrave;<strong>&nbsp;dễ d&agrave;ng được bố tr&iacute; tại mọi vị tr&iacute;</strong>&nbsp;trong căn nh&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1118100.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1118100.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Khả năng tr&igrave;nh chiếu h&igrave;nh ảnh l&ecirc;n đến 130 inch c&ugrave;ng độ ph&acirc;n giải 4K sắc n&eacute;t</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y chiếu 4K c&oacute; khả năng tr&igrave;nh chiếu h&igrave;nh ảnh với<strong>&nbsp;k&iacute;ch thước khung h&igrave;nh l&ecirc;n đến 130 inch</strong>&nbsp;mang đến trải nghiệm thoải m&aacute;i xem nội dung sống động bất kể g&oacute;c nh&igrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, m&aacute;y chiếu với độ ph&acirc;n giải&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/tivi-do-phan-giai-full-hd-va-tivi-4k-co-thuc-su-ch-746388\" target=\"_blank\" title=\"Mời bạn xem thêm độ phân giải 4K\" type=\"Mời bạn xem thêm độ phân giải 4K\">4K</a>&nbsp;mang đến chất lượng tr&igrave;nh chiếu sắc n&eacute;t v&agrave; m&agrave;u sắc hiển thị tươi s&aacute;ng, rực rỡ trọn vẹn mọi khung h&igrave;nh. Bạn c&oacute; thể chiếu trực tiếp l&ecirc;n bức tường m&agrave; kh&ocirc;ng cần r&egrave;m chiếu như những loại m&aacute;y th&ocirc;ng thường kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107114.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Độ phân giải 4K\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107114.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Hiển thị m&agrave;u sắc chuẩn x&aacute;c mọi thời điểm với độ s&aacute;ng 2800 ANSI Lumen</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">The Premiere tối ưu khả năng hiển thị m&agrave;u sắc ở mọi ho&agrave;n cảnh nhờ v&agrave;o&nbsp;<strong>độ s&aacute;ng l&ecirc;n đến 2800 ANSI Lumen&nbsp;</strong>. B&ecirc;n cạnh đ&oacute;,&nbsp;The Premiere LSP9T với<strong>&nbsp;</strong>laser 4K&nbsp;gi&uacute;p nội dung truyền tải rực rỡ đầy ấn tượng dưới mọi điều kiện s&aacute;ng từ m&ocirc;i trường xung quanh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102060.gif\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Độ sáng 2800 ANSI Lumen\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102060.gif\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">H&igrave;nh ảnh hiển thị rực rỡ, gần với m&agrave;u tự nhi&ecirc;n với c&ocirc;ng nghệ HDR 10+, Purcolor,&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-cong-nghe-hinh-anh-tren-tivi-lg-2020-1255817#hmenuid4\" target=\"_blank\" title=\"Mời bạn xem thêm công nghệ Filmmaker Mode\" type=\"Mời bạn xem thêm công nghệ Filmmaker Mode\">Filmmaker Mode</a></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn sẽ c&oacute; những gi&acirc;y ph&uacute;t đấm ch&igrave;m trong thế giới điện ảnh nhờ chế độ Filmmaker Mode gi&uacute;p truyền tải trọn vẹn đồ nghệ thuật của đạo diễn. B&ecirc;n cạnh đ&oacute;, m&aacute;y chiếu c&ograve;n được trang bị c&ocirc;ng nghệ HDR 10+, Purcolor gi&uacute;p h&igrave;nh ảnh hiển thị rực rỡ v&agrave; gần với m&agrave;u tự nhi&ecirc;n nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107136.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - HDR10+, Purcolor,Filmmaker Mode\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107136.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Tận hưởng &acirc;m thanh v&ograve;m 3 chiều sống động với c&ocirc;ng nghệ Dolby Digital Plus v&agrave; Adapatvie Sound</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/nhung-dieu-can-biet-ve-cong-nghe-am-thanh-dolby-di-795833#hmenuid2\" target=\"_blank\" title=\"Mời bạn xem thêm công nghệ Dolby Digital Plus\" type=\"Mời bạn xem thêm công nghệ Dolby Digital Plus\">Dolby Digital Plus</a>&nbsp;của Samsung mang đến&nbsp;&acirc;m thanh v&ograve;m sống động bao phủ căn ph&ograve;ng của bạn, để bạn c&oacute; thể tận hưởng trải nghiệm&nbsp;<strong>&acirc;m thanh như thật.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, m&aacute;y chiếu c&ograve;n được trang bị c&ocirc;ng nghệ Adapative Sound loa thanh Samsung tự động ph&acirc;n t&iacute;ch t&iacute;n hiệu &acirc;m thanh v&agrave; tối ưu &acirc;m thanh ph&aacute;t ra theo từng ph&acirc;n cảnh. C&ocirc;ng nghệ Adaptive Sound gi&uacute;p bạn lắng nghe r&otilde; đoạn hội thoại ngay cả khi &acirc;m lượng nhỏ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107137.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Công nghệ âm thanh\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-1107137.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thưởng thức chất lượng &acirc;m thanh vượt trội với c&ocirc;ng nghệ Acoustic Beam</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ n&agrave;y sử dụng 22 lỗ tho&aacute;t &acirc;m trang bị tr&ecirc;n m&aacute;y chiếu để mang đến người nghe những &acirc;m thanh d&agrave;y v&agrave; s&acirc;u, c&acirc;n bằng tr&agrave;n ngập khắp căn ph&ograve;ng từ cả 2 hướng tr&aacute;i/ phải.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Do đ&oacute; bạn sẽ được tận hưởng những &acirc;m thanh ch&iacute;nh x&aacute;c với từng h&agrave;nh động diễn ra tr&ecirc;n m&agrave;n h&igrave;nh. Khiến bạn bị cuốn h&uacute;t trong từng chuyển động tr&ecirc;n phim.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/untitled-1.jpg\" onclick=\"return false;\"><img alt=\"Thưởng thức chất lượng âm thanh vượt trội với công nghệ Acoustic Beam\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/untitled-1.jpg\" title=\"Thưởng thức chất lượng âm thanh vượt trội với công nghệ Acoustic Beam\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng nghệ Game Enhancer tối ưu h&oacute;a c&agrave;i đặt c&aacute;c tr&ograve; chơi, trải nghiệm mượt m&agrave;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn sẽ được trải nghiệm game tr&ecirc;n m&agrave;n h&igrave;nh lớn r&otilde; hơn,&nbsp; h&igrave;nh ảnh sắc n&eacute;t, game chạy mượt m&agrave; với số khung h&igrave;nh tr&ecirc;n gi&acirc;y cao hơn nhờ c&ocirc;ng nghệ Game Enhancer của m&aacute;y chiếu si&ecirc;u gần sẽ mang đến những c&agrave;i đặt ph&ugrave; hợp nhằm tạo lợi thế vượt trội gi&uacute;p bạn chiến thắng dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/gameenhancer99999.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser 4K Samsung The Premiere LSP9T - Game Enhancer tối ưu cài đặt trò chơi\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/gameenhancer99999.jpg\" title=\"Máy Chiếu Siêu Gần Laser 4K Samsung The Premiere LSP9T - Game Enhancer tối ưu cài đặt trò chơi\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Tr&igrave;nh chiếu m&agrave;n h&igrave;nh điện thoại l&ecirc;n m&aacute;y chiếu dễ d&agrave;ng nhờ&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/air-play-2-tren-tivi-la-gi-nhung-tivi-nao-ho-tro-a-1172156#hmenuid1\" target=\"_blank\" title=\"Mời bạn xem thêm AirPlay2\" type=\"Mời bạn xem thêm AirPlay2\">AirPlay 2&nbsp;</a>v&agrave; Tap View</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng tr&igrave;nh chiếu nội dung từ điện thoại Samsung th&ocirc;ng qua Tap View&nbsp;<strong>1 chạm</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102071.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - TapView\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102071.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tr&igrave;nh chiếu m&agrave;n h&igrave;nh điện thoại iPhone m&aacute;y chiếu dễ d&agrave;ng nhờ AirPlay 2</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102082.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - AirPlay2\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102082.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Kho ứng dụng đa dạng, phong ph&uacute; với hệ điều h&agrave;nh Tizen OS</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y chiếu Samsung chạy<strong>&nbsp;hệ điều h&agrave;nh Tizen</strong>&nbsp;với thiết kế<strong>&nbsp;th&acirc;n thiện, dễ d&agrave;ng sử dụng</strong>. B&ecirc;n cạnh đ&oacute;, hệ điều h&agrave;nh mang đến kho ứng dụng giải tr&iacute; khủng đ&aacute;p ứng đa dạng nhu cầu giải tr&iacute; của gia đ&igrave;nh bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đặc biệt, m&aacute;y chiếu c&oacute;<strong>&nbsp;hỗ trợ t&igrave;m kiếm bằng giọng n&oacute;i tiếng Việt</strong>&nbsp;v&agrave; trợ l&yacute; ảo BIXBY.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102093.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Tizen OS\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-170421-1102093.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Đa dạng cổng kết nối tiện lợi</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-11071711.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Siêu Gần Laser Samsung 4K The Premiere LSP9T - Cổng kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/862/237113/Slider/samsung-the-premiere-lsp9t-160421-11071711.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Nh&igrave;n chung,&nbsp;M&aacute;y Chiếu Si&ecirc;u Gần Laser Samsung 4K The Premiere LSP9T với thiết kế thanh m&atilde;nh , nhỏ gọn tiết kiệm kh&ocirc;ng gian kết hợp c&ugrave;ng c&aacute;c c&ocirc;ng nghệ tiến tiến như: Laser 3 chiều 4K, HDR 10+,...&nbsp;The Premiere LSP9T sẽ l&agrave; chiếc m&aacute;y chiếu đ&aacute;ng c&acirc;n nhắc đối với những ai c&oacute; nhu cầu t&igrave;m mua m&aacute;y chiếu.</em></span></p>\r\n', 0, '1657437648_vn-the-premiere-lsp9t-sp-lsp9tkaxxv-414047979.jpg', 189900000, 5, 5),
(18, 'Máy chiếu Smart TV 100 inch bỏ túi The Freestyle SP-LSP3', '<p><strong><span style=\"font-size:16px\">Th&ocirc;ng số kỹ thuật M&aacute;y Chiếu Smart Tivi 100 inch Bỏ T&uacute;i Samsung The Freestyle SP-LSP3</span></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Loại m&aacute;y:</p>\r\n\r\n	<p>M&aacute;y chiếu bỏ t&uacute;iTối đa 100 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Tizen OS 6.0</p>\r\n	</li>\r\n	<li>\r\n	<p>Độ s&aacute;ng (Lumen ANSi):</p>\r\n\r\n	<p>550 lumens</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ h&igrave;nh ảnh:</p>\r\n\r\n	<p>Auto Motion PlusContrast 300:1Contrast EnhancerC&ocirc;ng nghệ Auto KeystoneC&ocirc;ng nghệ h&igrave;nh ảnh Pur ColorFilm ModeHDR 10HLG</p>\r\n	</li>\r\n	<li>\r\n	<p>Điều khiển bằng điện thoại:</p>\r\n\r\n	<p>Samsung TV AccesỨng dụng SmartThings</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng th&ocirc;ng minh:</p>\r\n\r\n	<p>Hỗ trợ t&igrave;m kiếm bằng giọng n&oacute;i YouTube bằng tiếng ViệtTrợ l&yacute; ảo Bixby (Chưa c&oacute; tiếng Việt)Trợ l&yacute; ảo Google Assistant</p>\r\n	</li>\r\n	<li>\r\n	<p>K&iacute;ch thước - Khối lượng:</p>\r\n\r\n	<p>Cao 15.8 cm - Ngang 9.5 cm - Nặng 0.8 kg</p>\r\n	</li>\r\n	<li>\r\n	<p>Năm ra mắt:</p>\r\n\r\n	<p>2022</p>\r\n	</li>\r\n</ul>\r\n', '<h3><span style=\"font-size:14px\">Thiết kế</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<a href=\"https://www.dienmayxanh.com/may-chieu/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3?src=osp#top-article\" target=\"_blank\" title=\"Mời bạn xem thêm Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3\" type=\"Mời bạn xem thêm Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3\">M&aacute;y Chiếu Smart Tivi 100 inch Bỏ T&uacute;i Samsung The Freestyle SP-LSP3</a>&nbsp;sở hữu<strong>&nbsp;thiết kế gọn nhẹ với trọng lượng 0.8 kg&nbsp;</strong>dễ d&agrave;ng mang theo khắp mọi nơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<strong>Điều chỉnh linh hoạt 180&deg;</strong>, đắm ch&igrave;m v&agrave;o nội dung y&ecirc;u th&iacute;ch với mọi g&oacute;c độ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<a href=\"https://www.dienmayxanh.com/may-chieu-samsung\" target=\"_blank\" title=\"Mời bạn xem thêm máy chiếu Samsung đang kinh doanh tại Điện máy XANH\" type=\"Mời bạn xem thêm máy chiếu Samsung đang kinh doanh tại Điện máy XANH\">M&aacute;y chiếu Samsung</a>&nbsp;kết nối với<strong>&nbsp;pin sạc dự ph&ograve;ng</strong>&nbsp;th&ocirc;ng qua c&aacute;p USB - Type C đầu ra 50W/20V trở l&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-021800.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3 - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-021800.jpg\" title=\"Máy chiếu công nghệ bỏ túi FHD Samsung The Freestyle SP-LSP3 - Thiết kế\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng nghệ h&igrave;nh ảnh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<strong>C&ocirc;ng nghệ Auto Keystone&nbsp;</strong>với khả năng tự động điều chỉnh h&igrave;nh ảnh bị m&eacute;o mang đến chất lượng h&igrave;nh ảnh đỉnh cao ở mọi g&oacute;c độ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- M&agrave;n h&igrave;nh ma thuật với khả năng tr&igrave;nh chiếu từ&nbsp;<strong>30 inch đến 100 inch ở khoảng c&aacute;ch 0.8m đến 3m</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thể hiện h&igrave;nh ảnh sắc n&eacute;t với&nbsp;<strong>độ ph&acirc;n giải&nbsp;Full HD</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<strong>C&ocirc;ng nghệ Pur Color, HDR 10, HLG</strong>&nbsp;mang đến m&agrave;u sắc sống động.<a href=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-023231.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3 - Công nghệ hình ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-023231.jpg\" title=\"Máy chiếu công nghệ bỏ túi FHD Samsung The Freestyle SP-LSP3 - Công nghệ hình ảnh\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng nghệ &acirc;m thanh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">- C&ocirc;ng nghệ&nbsp;<strong>Dolby Digital Plus&nbsp;</strong>mang đến&nbsp;<strong>&acirc;m thanh thanh 360 độ</strong>&nbsp;sống động c&ugrave;ng&nbsp;<strong>c&ocirc;ng suất loa 5W</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-033506.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3  - Công nghệ âm thanh\" src=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-033506.jpg\" title=\"Máy chiếu công nghệ bỏ túi FHD Samsung The Freestyle SP-LSP3  - Công nghệ âm thanh\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hệ điều h&agrave;nh</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<strong>Hệ điều h&agrave;nh Tizen OS 6.0</strong>&nbsp;kho ứng dụng phong ph&uacute; đ&aacute;p ứng đa dạng nhu cầu giải tr&iacute;: Clip TV, FPT Play, Galaxy Play, MyTV, Netflix, POPS Kids, Spotify, VieON, YouTube, Zing MP3,...</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;Hỗ trợ&nbsp;<strong>t&igrave;m kiếm bằng giọng n&oacute;i YouTube, trợ l&yacute; ảo Google Assistant bằng tiếng Việt</strong>,&nbsp;<strong>Bixby</strong>&nbsp;(chưa c&oacute; tiếng Việt).</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-023539.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3 - Hệ điều hành Tizen OS 6.0\" src=\"https://cdn.tgdd.vn/Products/Images/862/269378/cong-nghe-bo-tui-fhd-samsung-the-freestyle-sp-lsp3-170122-023539.jpg\" title=\"Máy chiếu công nghệ bỏ túi FHD Samsung The Freestyle SP-LSP3 - Hệ điều hành Tizen OS 6.0\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&aacute;c t&iacute;nh năng, tiện &iacute;ch</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;<strong>Dễ d&agrave;ng chiếu m&agrave;n h&igrave;nh l&ecirc;n điện thoại l&ecirc;n m&aacute;y chiếu</strong>&nbsp;với AirPlay 2 tr&ecirc;n thiết bị Apple.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Tap View phản chiếu m&agrave;n h&igrave;nh điện thoại th&ocirc;ng minh&nbsp;<strong>kết nối với 1 chạm</strong>. (Tap View khả dụng tr&ecirc;n c&aacute;c thiết bị Samsung Galaxy với hệ điều h&agrave;nh Android 8.1 trở l&ecirc;n)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&nbsp;Samsung TV Access&nbsp;<strong>phản chiếu, điều khiển tivi th&ocirc;ng qua&nbsp;<a href=\"https://www.dienmayxanh.com/may-chieu\" target=\"_blank\" title=\"Mời bạn xem thêm máy chiếu đang kinh doanh tại Điện máy XANH\" type=\"Mời bạn xem thêm máy chiếu đang kinh doanh tại Điện máy XANH\">m&aacute;y chiếu</a></strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- SmartThings thoải m&aacute;i điều khiển m&aacute;y chiếu qua điện thoại.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/862/269378/product-269378-170122-050711.jpg\" onclick=\"return false;\"><img alt=\"Máy Chiếu Smart Tivi 100 inch Bỏ Túi Samsung The Freestyle SP-LSP3 - Tính năng, tiện ích\" src=\"https://cdn.tgdd.vn/Products/Images/862/269378/product-269378-170122-050711.jpg\" title=\"Máy chiếu công nghệ bỏ túi FHD Samsung The Freestyle SP-LSP3 - Tính năng, tiện ích\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Nh&igrave;n chung,&nbsp;m&aacute;y chiếu c&ocirc;ng nghệ bỏ t&uacute;i FHD Samsung The Freestyle SP-LSP3 với thiết kế nhỏ gọn, linh hoạt được t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh, &acirc;m thanh hiện đại như&nbsp;Auto Keystone,&nbsp;Pur Color, HDR 10,&nbsp;Dolby Digital Plus. Đ&acirc;y sẽ chiếc m&aacute;y chiếu ph&ugrave; hợp với những g&igrave; c&oacute; nhu cầu t&igrave;m mua m&aacute;y chiếu bỏ t&uacute;i, qu&aacute;n cafe, gia đ&igrave;nh,...</em></span></p>\r\n', 0, '1657437411_vn-the-freestyle-lsp3b-sp-lsp3blaxxv-530663510.jpg', 25000000, 7, 5),
(19, 'Loa Thanh S-series S61B', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Loa Thanh S-series S61B</strong></span></h2>\r\n\r\n<p>C&ocirc;ng suất mạnh mẽ, thiết kế tinh gọn<br />\r\n&Acirc;m thanh v&ograve;m Dolby Atmos kh&ocirc;ng d&acirc;y đầu ti&ecirc;n tr&ecirc;n thế giới<br />\r\nC&ocirc;ng nghệ Q-Symphony đồng bộ tối đa với loa TV<br />\r\nPh&aacute;t nhạc từ mọi nơi nhờ kết nối Wi-Fi v&agrave; Bluetooth<br />\r\n&Acirc;m thanh đa hướng ch&acirc;n thực<br />\r\n&Acirc;m trầm mạnh mẽ, đầy uy lực<br />\r\nLắng nghe b&agrave;i h&aacute;t bạn y&ecirc;u th&iacute;ch v&agrave; điều khiển dễ d&agrave;ng bằng giọng n&oacute;i<br />\r\nĐơn giản chạm v&agrave; ph&aacute;t</p>\r\n', '<h3>TH&Ocirc;NG SỐ KỸ THUẬT</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Thương hiệu</td>\r\n			<td>Samsung</td>\r\n		</tr>\r\n		<tr>\r\n			<td>M&atilde; sản phẩm</td>\r\n			<td>HW-S61B/XV</td>\r\n		</tr>\r\n		<tr>\r\n		</tr>\r\n		<tr>\r\n			<td>Kết nối kh&ocirc;ng d&acirc;y</td>\r\n			<td>\r\n			<ul>\r\n				<li>Bluetooth</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&ocirc;ng suất loa</td>\r\n			<td>\r\n			<ul>\r\n				<li>200W</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&ocirc;ng nghệ &acirc;m thanh</td>\r\n			<td>\r\n			<ul>\r\n				<li><a href=\"https://mediamart.vn/tu-van-tivi/tim-hieu-ve-chuan-am-thanh-dolby-atmos\" target=\"_blank\">Dolby Atmos/DTS Virtual:X</a></li>\r\n				<li>Q-Symphony</li>\r\n				<li>SpaceFit Sound</li>\r\n				<li>Adaptive Sound</li>\r\n				<li>Game Mode</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kết nối</td>\r\n			<td>\r\n			<ul>\r\n				<li>HDMI</li>\r\n				<li>HDMI-ARC</li>\r\n				<li><a href=\"https://mediamart.vn/cong-nghe-tivi/one-remote-la-gi-cac-tinh-nang-noi-bat-cua-one-remote-tren-tivi-samsung\" target=\"_blank\">Điều khiển th&ocirc;ng minh One Remote Control</a></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bảo h&agrave;nh</td>\r\n			<td>12 th&aacute;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Xuất xứ</td>\r\n			<td>Việt Nam</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, '1657433062_vn-soundbar-hw-s61b-hw-s61b-xv-531573869.jpg', 8990000, 0, 16),
(20, 'Loa Thanh Q-series Q700B', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Loa Thanh Q-series Q700B</strong></span></h2>\r\n\r\n<p>C&ocirc;ng nghệ &acirc;m thanh chuẩn Dolby Atmos/DTS:X r&otilde; từng chất &acirc;m.<br />\r\nKết nối Dolby Atmos kh&ocirc;ng d&acirc;y<br />\r\nC&ocirc;ng suất loa 320W mạnh mẽ - Số loa: 09 loa<br />\r\nLoa đ&aacute;nh trần vật l&yacute; với dải tần rộng tạo &acirc;m thanh v&ograve;m tối ưu<br />\r\n&Acirc;m thanh ch&acirc;n thực 3.1.2 k&ecirc;nh<br />\r\nC&ocirc;ng nghệ Q-symphony tạo n&ecirc;n &acirc;m thanh v&ograve;m ho&agrave;n hảo đồng bộ loa với to&agrave;n bộ loa của TV Samsung<br />\r\nC&ocirc;ng nghệ hiệu chỉnh &acirc;m tự động theo kh&ocirc;ng gian: SpaceFit Sound c&oacute; thể hoạt động độc lập kh&ocirc;ng cần kết nối với TV<br />\r\nTruyền tải nội dung HDR10+ qua cổng 4K Pass-Through 6.5&rdquo; Subwoofer kh&ocirc;ng d&acirc;y<br />\r\nThiết kế kh&ocirc;ng d&acirc;y tinh giản, sang trọng v&agrave; tinh tế</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Thiết kế</strong><br />\r\n-&nbsp;Loa thanh Samsung HW-Q700B&nbsp;trong thiết kế tinh giản m&agrave; hiện đại, m&agrave;u đen sang trọng, h&agrave;i h&ograve;a c&aacute;c kh&ocirc;ng gian kiến tr&uacute;c cao cấp, ph&ugrave; hợp bố tr&iacute; cho ph&ograve;ng kh&aacute;ch, ph&ograve;ng họp, ph&ograve;ng hội nghị,&hellip;<br />\r\n- Cấu tạo&nbsp;loa&nbsp;chất liệu nhựa v&agrave; kim loại chắc chắn, chống biến dạng hay ăn m&ograve;n, bền bỉ theo năm th&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-cxCD0t.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng suất</strong><br />\r\n- Tạo kh&ocirc;ng gian &acirc;m thanh ngập tr&agrave;n với chất &acirc;m mạnh mẽ, đầy khuấy động tr&ecirc;n tổng c&ocirc;ng suất loa&nbsp;320W.<br />\r\n-&nbsp;Hệ thống 3.1.2 k&ecirc;nh&nbsp;bao gồm&nbsp;1 loa si&ecirc;u trầm&nbsp;v&agrave;&nbsp;2 loa đ&aacute;nh trần&nbsp;chất lượng tốt, kiến tạo &acirc;m thanh v&ograve;m cuốn h&uacute;t, chất &acirc;m rắn chắc, đầy uy lực, t&aacute;i hiện ho&agrave;n hảo s&acirc;n khấu &acirc;m nhạc trong kh&ocirc;ng gian ng&ocirc;i nh&agrave; bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-36sZ3R.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ &acirc;m thanh</strong><br />\r\n-&nbsp;C&ocirc;ng nghệ Dolby Atmos/DTS:X&nbsp;t&aacute;i tạo &acirc;m thanh đa chiều lan tỏa, bao phủ khắp kh&ocirc;ng gian ph&ograve;ng để bạn tận hưởng thế giới giải tr&iacute; s&ocirc;i động tựa như đối diện với s&acirc;n khấu lớn.<br />\r\n-&nbsp;C&ocirc;ng nghệ&nbsp;Q-Symphony&nbsp;đồng bộ h&oacute;a loa v&agrave; hệ thống &acirc;m thanh của smart tivi để thưởng thức &acirc;m nhạc, phim điện ảnh, c&aacute;c trận đấu thể thao hay chơi game,&hellip; th&ecirc;m h&agrave;o hứng, hấp dẫn.<br />\r\n-&nbsp;C&ocirc;ng nghệ&nbsp;SpaceFit Sound&nbsp;ph&acirc;n t&iacute;ch độ dội &acirc;m trong kh&ocirc;ng gian bố tr&iacute;, tự động tối ưu hiệu ứng &acirc;m thanh v&ograve;m v&agrave; giọng n&oacute;i r&otilde; n&eacute;t. C&ugrave;ng với&nbsp;c&ocirc;ng nghệ&nbsp;Adaptive Sound&nbsp;điều chỉnh c&agrave;i đặt &acirc;m thanh theo nội dung để trải nghiệm nghe tối ưu nhất với người d&ugrave;ng.<br />\r\n-&nbsp;Chế độ&nbsp;Game Mode Pro&nbsp;tạo &acirc;m bass mạnh mẽ, chắc khỏe hơn, kết hợp loa hướng trần tạo &acirc;m thanh v&ograve;m, gia tăng k&iacute;ch th&iacute;ch từng t&aacute;c vụ game tham chiến.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-799SRv.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Điều khiển</strong><br />\r\n-&nbsp;Đi k&egrave;m&nbsp;One Remote Control&nbsp;cho bạn bật/tắt, tăng/giảm &acirc;m lượng, th&ecirc;m hiệu ứng &acirc;m thanh tr&ecirc;n&nbsp;loa Samsung&nbsp;dễ d&agrave;ng.<br />\r\n- Ứng dụng&nbsp;Smart Things&nbsp;biến điện thoại th&ocirc;ng minh th&agrave;nh remote để điều khiển loa tiện lợi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-ux5f26.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>T&iacute;nh năng th&ocirc;ng minh</strong><br />\r\nChạm điện thoại v&agrave;o&nbsp;loa thanh (soundbar) Samsung,&nbsp;t&iacute;nh năng Tap Sound&nbsp;tức th&igrave; chia sẻ bản nhạc bạn đang nghe đến loa để c&oacute; được &acirc;m thanh mạnh mẽ v&agrave; sống động hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-5za272.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Kết nối</strong><br />\r\n- Truyền ph&aacute;t nhạc từ điện thoại đến&nbsp;loa thanh&nbsp;dễ d&agrave;ng qua t&iacute;nh năng&nbsp;AirPlay 2,&nbsp;Spotify.<br />\r\n- Kết nối loa với tivi qua đường truyền kh&ocirc;ng d&acirc;y&nbsp;Bluetooth,&nbsp;Wifi, b&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; c&aacute;c cổng kết nối&nbsp;HDMI, HDMI eARC&nbsp;th&ocirc;ng dụng&nbsp;gi&uacute;p bạn li&ecirc;n kết để khuếch đại &acirc;m thanh qua loa, tạo kh&ocirc;ng gian giải tr&iacute; mở rộng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"1\" src=\"https://mediamart.vn/images/uploads/data-2022/1-9168Hm.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&oacute;m lại, trang bị loa thanh Samsung HW-Q700B cho hệ thống &acirc;m thanh nh&agrave; bạn, c&ugrave;ng với smart tivi, mỗi khung giờ giải tr&iacute; sẽ th&ecirc;m hứng khởi, s&ocirc;i động hơn nhờ &acirc;m thanh v&ograve;m Dolby Atmos/DTS:X v&agrave; c&aacute;c tiện &iacute;ch kết nối, chia sẻ th&ocirc;ng minh kh&aacute;c t&iacute;ch hợp tr&ecirc;n loa.</span></p>\r\n', 0, '1657432829_vn-q-series-soundbar-hw-q700b-hw-q700b-xv-531574180.jpg', 13990000, 5, 16),
(21, 'Smart TV 4K The Serif 55 inch LS01T', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Smart TV 4K The Serif 55 inch LS01T</strong></span></h2>\r\n\r\n<p>Hiển thị 100% dải m&agrave;u với c&ocirc;ng nghệ Quantum Dot<br />\r\nThiết kế chữ I&nbsp;bởi nh&agrave; thiết kế nổi tiếng người Ph&aacute;p<br />\r\nKhả năng kết nối AriPlay2 v&agrave; NFC<br />\r\nCh&acirc;n đế đi k&egrave;m linh hoạt, dễ d&agrave;ng th&aacute;o lắp</p>\r\n', '<h3><span style=\"font-size:14px\">Thiết Kế 360 Độ Ho&agrave;n Mỹ</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thưởng thức một tổng thể ho&agrave;n thiện, liền mạch v&agrave; sắc sảo đến từng chi tiết với&nbsp;<a href=\"https://mediamart.vn/tivi/samsung/qled-tivi-serif-samsung-4k-55-inch-qa55ls01takxxv-lifestyle-tv.htm\">QLED Tivi Serif Samsung 4K 55 inch QA55LS01TA</a>. Tuyệt t&aacute;c m&agrave;n h&igrave;nh kết hợp với thiết kế tinh giản từ The Serif khắc hoạ n&ecirc;n n&eacute;t chấm ph&aacute; ho&agrave;n mỹ l&agrave;m trọn vẹn vẻ đẹp kh&ocirc;ng gian, hấp dẫn mọi &aacute;nh nh&igrave;n ngay từ lần đầu ti&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/tivi/samsung/qled-tivi-serif-samsung-4k-55-inch-qa55ls01takxxv-lifestyle-tv.htm\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/92HJRf2iqfc7o0a.jpg\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Ch&acirc;n Đế Dễ Th&aacute;o Rời</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Khung viền thời thượng c&ugrave;ng ch&acirc;n đế kim loại, dễ th&aacute;o rời gi&uacute;p bạn dễ d&agrave;ng di chuyển v&agrave; lắp đặt&nbsp;<a href=\"https://mediamart.vn/tivi/samsung/qled-tivi-serif-samsung-4k-55-inch-qa55ls01takxxv-lifestyle-tv.htm\">Tivi Serif Samsung</a>&nbsp;tại mọi nơi trong kh&ocirc;ng gian. Vừa vặn ho&agrave;n hảo với mọi cấu tr&uacute;c ph&ograve;ng, thoả m&atilde;n gu thẩm mỹ đậm dấu ấn ri&ecirc;ng bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/gR3VrS8u6nu4J6v.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Kết Nối Kh&ocirc;ng D&acirc;y NFC Tr&ecirc;n&nbsp;<a href=\"https://mediamart.vn/tivi/\">TV</a></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đơn giản kết nối, thưởng thức bất tận thế giới &acirc;m nhạc đỉnh cao c&ugrave;ng những khung h&igrave;nh ấn tượng tr&ecirc;n The Serif.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/Y0tr1PfTTOKuNIi.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Color Volume 100% &amp; HDR 10+</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://mediamart.vn/tivi/samsung/\">Tivi Samsung</a>&nbsp;với c&ocirc;ng nghệ Chấm Lượng Tử sử dụng c&aacute;c chấm lượng tử k&iacute;ch thước nano để t&aacute;i tạo 100% dải sắc m&agrave;u ấn tượng, mang đến bạn những chi tiết si&ecirc;u thực d&ugrave; cho khung h&igrave;nh s&aacute;ng rực rỡ hay tối thăm thẳm. C&ocirc;ng nghệ&nbsp;<a href=\"https://mediamart.vn/tin-tuc/review-dien-tu/samsung-nang-cap-cong-nghe-hinh-anh-tv-voi-chuan-hdr10.htm\">HDR10+</a>&nbsp;ti&ecirc;n tiến ph&acirc;n t&iacute;ch từng cảnh quay, điều chỉnh độ s&aacute;ng cho mọi khoảnh khắc th&ecirc;m sắc n&eacute;t, sống động.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/a7CVHG3Z4n37hHk.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">T&iacute;nh Năng Tap View</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chỉ cần chạm nhẹ điện thoại l&ecirc;n&nbsp;<a href=\"https://mediamart.vn/tivi/samsung/qled-tivi-serif-samsung-4k-55-inch-qa55ls01takxxv-lifestyle-tv.htm\">Tivi Serif Samsung QA55LS01TA</a>, mọi thao t&aacute;c tr&ecirc;n điện thoại đều c&oacute; thể được hiển thị tr&ecirc;n m&agrave;n h&igrave;nh TV, cho bạn tận hưởng nội dung liền mạch tr&ecirc;n cả 2 thiết bị, n&acirc;ng tầm trải nghiệm th&ecirc;m ho&agrave;n hảo hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/7WhT5M36M5lWy1M.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">T&iacute;nh Năng Multi View</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tiện lợi hơn với chế độ Multi View mang đến cho bạn trải nghiệm xem c&ugrave;ng l&uacute;c cả hai nội dung tr&ecirc;n TV v&agrave; tr&ecirc;n điện thoại. Chỉ cần kết nối với điện thoại của bạn v&agrave; sử dụng t&iacute;nh năng đa nhiệm với Multi View.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/2oIvE6VIonFMg5d.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Active Voice Amplifier (AVA)</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hạn chế tiếng ồn b&ecirc;n ngo&agrave;i, thoải m&aacute;i tập trung v&agrave;o nội dung quan trọng hơn. Với c&ocirc;ng nghệ Active Voice Amplifier,&nbsp;<a href=\"https://mediamart.vn/tivi/samsung/qled-tivi-serif-samsung-4k-55-inch-qa55ls01takxxv-lifestyle-tv.htm\">The Serif TV</a>&nbsp;nhanh ch&oacute;ng nhận biết c&aacute;c tạp &acirc;m dễ g&acirc;y sao nh&atilde;ng xung quanh bạn. Từ đ&oacute;, tự động điều chỉnh &acirc;m lượng, n&acirc;ng cao độ r&otilde; cho giọng n&oacute;i v&agrave; c&aacute;c cuộc hội thoại đang diễn ra tr&ecirc;n TV th&ocirc;ng qua quy tr&igrave;nh xử l&yacute; t&iacute;n hiệu &acirc;m thanh n&acirc;ng cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2020-5-News/c69hKaPv6OOBnqN.jpg\" /></span></p>\r\n', 0, '1657432344_vn-serif-tv-ls01t-qa55ls01takxxv-frontwhite-221737538.jpg', 28500000, 35, 17);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(22, 'Lifestyle TV 75 inch The Frame 4K LS03B', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Lifestyle TV 75 inch The Frame 4K LS03B</strong></span></h2>\r\n\r\n<p>Thiết kế khung tranh hiện đại v&agrave; thanh lịch<br />\r\nBiến h&oacute;a đa phong c&aacute;ch với khung viền linh hoạt<br />\r\nKh&aacute;m ph&aacute; hơn 1.600 t&aacute;c phẩm tranh từ Art Mode<br />\r\nMang nghệ thuật v&agrave;o kh&ocirc;ng gian sống</p>\r\n', '<p><span style=\"font-size:14px\"><strong>Thỏa sức s&aacute;ng tạo tuyệt t&aacute;c TV của&nbsp;bạn</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/1e5e35a8-5cb2-408f-b9fd-9b98f06b480f.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Trải nghiệm tranh ch&acirc;n thực bằng tấm nền QLED với c&ocirc;ng nghệ chống phản s&aacute;ng</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ QLED với tấm nềnchống phản s&aacute;ng</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thưởng thức trọn vẹn t&aacute;c phẩm nghệ thuật hay chương tr&igrave;nh y&ecirc;u th&iacute;ch tr&ecirc;n TV The Frame với c&ocirc;ng nghệ m&agrave;n h&igrave;nh chống phản s&aacute;ng mới. Hạn chế đ&aacute;ng kể độ ch&oacute;i, C&ocirc;ng nghệ QLED với tấm nền chống phản s&aacute;ng mang tới chất lượng hiển thị si&ecirc;u thực cho bạn trải nghiệm xem sống động tới từng chi tiết.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/29793a0e-5e2c-4082-ae26-e6c1e695dd5f.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết kế như một khung tranh nghệ thuật</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Thiết kế khung tranh hiện đại</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Được lấy cảm hứng từ vẻ đẹp của sự tinh giản, The Frame h&ograve;a quyện ho&agrave;n hảo với mọi kh&ocirc;ng gian nội thất. Thiết kế mỏng hơn, chắc chắn hơn c&ugrave;ng mặt sau được ho&agrave;n thiện tỉ mỉ với lớp m&agrave;u đơn sắc ấn tượng, gi&uacute;p nổi bật vẻ đẹp tinh tế của TV từ mọi g&oacute;c nh&igrave;n.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/2202ea31-a6e9-4d51-9284-28ef27594567.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">H&agrave;i h&ograve;a trong mọi kh&ocirc;ng gian với khung viền linh hoạt</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Khung viền linh hoạt</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đa dạng lựa chọn khung viền c&aacute; t&iacute;nh, cho bạn thỏa th&iacute;ch ngắm nh&igrave;n tuyệt t&aacute;c khung tranh. Thoải m&aacute;i chuyển đổi m&agrave;u sắc v&agrave; phong c&aacute;ch khung viền nhờ c&aacute;c khớp nối nam ch&acirc;m linh hoạt.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/2effc043-a4a1-4c56-aaf5-677a3e831db4.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Chế độ tranh Art Mode với hơn 1600+ t&aacute;c phẩm từ bảo t&agrave;ng khắp thế&nbsp;giới</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế độ Art Mode</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi tắt, The Frame mang đến cho bạn một kh&ocirc;ng gian trưng b&agrave;y nghệ thuật đ&uacute;ng nghĩa chỉ với một n&uacute;t bấm. Với hơn 1.600 t&aacute;c phẩm nghệ thuật từ chế độ Art Mode, bạn c&oacute; thể tr&igrave;nh chiếu bộ sưu tập nghệ thuật c&aacute; nh&acirc;n với nhiều phong c&aacute;ch tr&igrave;nh b&agrave;y đầy s&aacute;ng tạo v&agrave; mới lạ - dễ d&agrave;ng t&ugrave;y chỉnh v&agrave; điều khiển theo &yacute; th&iacute;ch.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/2902dfe3-5d29-4dd1-b80a-e7891479adff.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Mang những kiệt t&aacute;c nghệ thuật v&agrave;o trong cuộc sống</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Thư viện nghệ thuật Art Store</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kiến tạo bộ sưu tập nghệ thuật c&aacute; nh&acirc;n với hơn 1.600 kiệt t&aacute;c ấn tượng từ c&aacute;c tổ chức nổi tiếng tr&ecirc;n thế giới. Thỏa th&iacute;ch kh&aacute;m ph&aacute; kho t&agrave;ng nghệ thuật kh&ocirc;ng giới hạn, đa dạng phong c&aacute;ch từ những bức họa kinh điển, những tuyệt t&aacute;c đương đại cho đến c&aacute;c t&aacute;c phẩm nhiếp ảnh đặc sắc tại Art Store.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/89eba993-9f93-4916-90bf-104dab63de84.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Khung tranh ho&agrave;n hảo cho bức ảnh của&nbsp;bạn</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bộ sưu tập của t&ocirc;i</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với The Frame, bạn kh&ocirc;ng chỉ trưng b&agrave;y c&aacute;c kiệt t&aacute;c nghệ thuật m&agrave; c&ograve;n c&oacute; thể hiển thị những t&aacute;c phẩm của ri&ecirc;ng m&igrave;nh l&ecirc;n TV. Gửi ảnh của bạn đến The Frame th&ocirc;ng qua điện thoại hoặc USB để l&agrave;m sống động lại những khoảnh khắc đ&aacute;ng nhớ trong cuộc sống.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/91fda84d-f4dc-4b75-bd7e-0fc41803d544.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute; nh&acirc;n h&oacute;a t&aacute;c&nbsp;phẩm của bạn</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ph&ocirc;ng nền Mat Canvas</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nổi bật t&aacute;c phẩm nghệ thuật với ph&ocirc;ng nền mat canvas đầy ch&acirc;n thực. Thỏa th&iacute;ch thay đổi phong c&aacute;ch hiển thị với 5 lựa chọn ph&ocirc;ng nền v&agrave; 16 m&agrave;u sắc kh&aacute;c nhau.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/98c76c10-9924-4112-bc45-91ad8b560079.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">D&otilde;i theo bước&nbsp;ch&acirc;n&nbsp;bạn</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Cảm biến chuyển động Motion&nbsp;Sensor</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cảm biến chuyển động sẽ tự động nhận diện v&agrave; hiển thị c&aacute;c t&aacute;c phẩm nghệ thuật khi bạn di chuyển đến gần The Frame. Khi rời đi, m&agrave;n h&igrave;nh được tự động tắt gi&uacute;p tiết kiệm tối ưu năng lượng sử dụng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/82b0dcf3-056d-46a4-bd58-2f10148e8d7c.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Rực rỡ mọi t&aacute;c phẩm nghệ thuật</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Cảm biến &aacute;nh s&aacute;ng Brightness&nbsp;Sensor</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nếu &aacute;nh s&aacute;ng trong ph&ograve;ng thay đổi, cảm biến &aacute;nh s&aacute;ng sẽ tự động điều chỉnh t&ocirc;ng m&agrave;u v&agrave; độ s&aacute;ng m&agrave;n h&igrave;nh, mang đến bạn khả năng tr&igrave;nh chiếu ho&agrave;n hảo, mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/9301ddc6-49dd-4e72-be14-a8828058da6e.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng lắp đặt tr&ecirc;n tường với gi&aacute; treo s&aacute;t&nbsp;tường</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Gi&aacute; treo s&aacute;t tường</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chi&ecirc;m ngưỡng The Frame như một bức tranh nghệ thuật đ&iacute;ch thực với gi&aacute; treo &aacute;p s&aacute;t v&agrave;o tường. Dễ d&agrave;ng xoay gi&aacute; đỡ để c&oacute; được g&oacute;c nh&igrave;n ph&ugrave; hợp v&agrave; thoải m&aacute;i nhất.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/66b60b53-b9fd-4fce-8304-483e11345441.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Một ch&acirc;n đế gi&uacute;p điều chỉnh độ cao, một ch&acirc;n đế cho ph&eacute;p di chuyển TV khắp nơi</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ch&acirc;n đế điều chỉnh độ cao</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&acirc;n đế điều chỉnh độ cao cho ph&eacute;p bạn n&acirc;ng The Frame l&ecirc;n cao v&agrave; lắp đặt loa thanh ph&iacute;a dưới dễ d&agrave;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/fc3adad2-8712-4d93-8398-199df4b3c7c1.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Biến tấu độc đ&aacute;o với m&agrave;n h&igrave;nh xoay</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Gi&aacute; treo Tự Động Xoay</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tự động xoay TV theo nội dung bạn xem với Gi&aacute; treo Tự Động Xoay đầy s&aacute;ng tạo.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/434802f1-2cda-426a-9fb3-840d0d9030c6.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Dọc hay ngang? Xoay TV theo &yacute; bạn</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ch&acirc;n đế Tự Động Xoay</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thỏa th&iacute;ch xem nội dung theo khung h&igrave;nh bạn muốn với Ch&acirc;n Đế Tự Động Xoay linh hoạt, tiện lợi.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/e414d620-66f4-4855-a009-b0004ed72419.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Thổi hồn phong c&aacute;ch mới cho kh&ocirc;ng gian</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Ch&acirc;n đế Studio</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với ch&acirc;n đế Studio, bạn c&oacute; thể di chuyển The Frame đến khắp mọi nơi, thỏa sức s&aacute;ng tạo phong c&aacute;ch b&agrave;i tr&iacute; v&agrave; trải nghiệm TV dưới những g&oacute;c nh&igrave;n mới lạ.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/713bd2bb-1f5d-4f6e-9ece-c3e7dd6c8d1d.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Ngắm nh&igrave;n thế giới với một tỷ sắc m&agrave;u</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Ch&acirc;́m Lượng Tử hiển thị 100%&nbsp;dải&nbsp;m&agrave;u</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng nghệ Ch&acirc;́m Lượng Tử hiển thị 100% dải m&agrave;u với độ ch&acirc;n thực sống động v&agrave; r&otilde; n&eacute;t, cho bạn thưởng thức từng khung h&igrave;nh mang m&agrave;u sắc cuộc sống, tuyệt đẹp ở mọi mức độ s&aacute;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/8120b011-6378-48c7-9584-d9cb2423fab4.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">N&acirc;ng cấp h&igrave;nh ảnh l&ecirc;n chuẩn 4K bằng AI</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bộ vi xử l&yacute; Quantum Lite 4K</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tận hưởng sức mạnh vượt trội của bộ xử l&yacute; Quantum 4K th&ocirc;ng minh. Với khả năng học hỏi, th&iacute;ch ứng v&agrave; n&acirc;ng cao, tr&iacute; tuệ nh&acirc;n tạo gi&uacute;p tối ưu vượt trội cả &acirc;m thanh lẫn h&igrave;nh ảnh, đưa trải nghiệm xem TV của bạn l&ecirc;n tầm cao mới.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/639b8d6e-5b5d-458d-9a8e-653624113139.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Bảo vệ mắt với hiển thị h&igrave;nh ảnh tối ưu</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế độ Bảo Vệ Mắt</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chế độ Bảo Vệ Mắt độc đ&aacute;o tự động điều chỉnh m&agrave;n h&igrave;nh theo thời gian thực cho trải nghiệm tối ưu. M&agrave;n h&igrave;nh sẽ tự động giảm s&aacute;ng hoặc m&agrave;u sắc khi &aacute;nh s&aacute;ng m&ocirc;i trường thay đổi để dịu mắt hơn cho bạn thưởng thức nội dung với trạng th&aacute;i thư gi&atilde;n nhất.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/83c4657b-96fd-40fd-8ed0-194dcb713de6.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute; nh&acirc;n h&oacute;a kho ứng dụng giải tr&iacute;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Smart Hub</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ứng dụng Smart Hub mới từ Samsung hiển thị nội dung y&ecirc;u th&iacute;ch chuẩn x&aacute;c trong mục Kh&aacute;m ph&aacute;, gi&uacute;p bạn kh&ocirc;ng tốn nhiều thời gian để t&igrave;m kiếm v&agrave; tận hưởng nhiều hơn khoảng thời gian xem phim cũng như nhiều chương tr&igrave;nh kh&aacute;c.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/e7d1f7fa-5f78-41ca-b1f4-91381ca1d42e.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Kết nối tức th&igrave;, trải nghiệm nhanh ch&oacute;ng</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>T&iacute;nh năng Mobile Mirroring</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp tục cuộc gọi video tr&ecirc;n m&agrave;n h&igrave;nh lớn? Ứng dụng SmartThings tr&ecirc;n thiết bị Android v&agrave; Apple AirPlay tr&ecirc;n iPhone gi&uacute;p bạn dễ d&agrave;ng kết nối v&agrave; tr&igrave;nh chiếu liền mạch nội dung y&ecirc;u th&iacute;ch tr&ecirc;n m&agrave;n h&igrave;nh lớn, tuyệt đẹp của The Frame.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/5b0f0971-296a-47dd-a4c7-8058adb330ab.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Đa m&agrave;n h&igrave;nh,đa trải nghiệm</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>T&iacute;nh năng Multi View</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng c&acirc;̀n phải lo lắng bản th&acirc;n sẽ lỡ m&acirc;́t những th&ocirc;ng tin quan trọng tr&ecirc;n đi&ecirc;̣n thoại khi đang xem TV với tính năng Multi View. Bạn sẽ thỏa sức theo dõi video hướng d&acirc;̃n game trong lúc v&acirc;̃n nắm được tỷ s&ocirc;́ tr&acirc;̣n đ&acirc;́u y&ecirc;u thích chỉ bằng c&aacute;ch kết nối TV v&agrave; điện thoại để trải nghiệm đa m&agrave;n h&igrave;nh, dễ d&agrave;ng chưa từng c&oacute;!</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/db06a3fe-5c3a-46cf-8e88-1f501985bc1e.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Ch&igrave;m đắm trong &acirc;m&nbsp;thanh v&ograve;m theo chuyển động h&igrave;nh&nbsp;ảnh</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng ghệ Object Tracking Sound (Dolby Atmos&reg;)</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với khả năng nhận biết chuyển động của h&igrave;nh ảnh, The Frame mang đến trải nghiệm &acirc;m thanh v&ograve;m ho&agrave;n hảo, chuẩn điện ảnh. Đắm ch&igrave;m trong &acirc;m thanh 3D sống động, ch&acirc;n thực như đang hiện diện trong từng ph&acirc;n cảnh với nhiều sắc th&aacute;i, đa dạng đ&aacute;ng kinh ngạc.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/431fe879-ddc8-4d82-af32-8c514d47e4e3.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Bộ đ&ocirc;i ho&agrave;n hảo TV &amp; Loa thanh</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Q-Symphony</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thăng hoa c&ugrave;ng những thanh &acirc;m tuyệt vời, được đồng bộ liền mạch giữa loa thanh Samsung v&agrave; TV. C&ocirc;ng nghệ Q-Symphony tận dụng loa TV v&agrave; loa thanh, kiến tạo kh&ocirc;ng gian giải tr&iacute; đỉnh cao, bao tr&ugrave;m mọi gi&aacute;c quan.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/ec4d4b03-f8bd-44cf-a62f-89459a373638.jpg\" /></span></p>\r\n', 0, '1657419885_vn-the-frame-qa75ls03bakxxv-front-black-531139141.jpg', 54890000, 5, 17),
(23, 'Smart TV Neo QLED 4K 65 inch QN85B', '<h2><strong><span style=\"font-size:14px\">Điểm nổi bật TV&nbsp;65 inch Neo QLED 4K QN85B</span></strong></h2>\r\n\r\n<p>Tối ưu độ s&aacute;ng với Ma Trận Lượng Tử Quantum Matrix<br />\r\nBộ xử l&yacute; Lượng Tử Neo 4K trang bị khả năng học s&acirc;u<br />\r\nT&aacute;i hiện sắc m&agrave;u ch&acirc;n thực với Lượng Tử HDR 24x</p>\r\n', '<h3><span style=\"font-size:14px\">Kh&aacute;m Ph&aacute; Thế Hệ M&agrave;n H&igrave;nh Tối T&acirc;n Từ Samsung</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/Iz48g0l2k8aEtK2.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Khả Năng Kiểm So&aacute;t Độ S&aacute;ng Đỉnh Cao</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quantum Matrix Technology</strong>&nbsp;-&nbsp;Trải nghiệm Neo QLED - m&agrave;n h&igrave;nh đến từ tương lai v&agrave; đột ph&aacute; c&ocirc;ng nghệ Ma Tr&acirc;̣n Lượng Tử Quantum Matrix, cho ph&eacute;p điều khiển ch&iacute;nh x&aacute;c tới từng lượng tử của đèn n&ecirc;̀n Mini LED. Với độ s&aacute;ng được tinh chỉnh tối ưu, TV truyền tải trọn vẹn từng chi tiết r&otilde; n&eacute;t c&ugrave;ng sắc đen s&acirc;u thẳm v&agrave; sắc trắng thuần khiết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(974).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Học S&acirc;u Tr&ecirc;n Bộ Xử L&yacute; Th&ocirc;ng Minh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bộ xử l&yacute; lượng tử Samsung Neo 4K</strong>&nbsp;-&nbsp;Mang đến bạn trải nghiệm xem tối ưu chưa từng c&oacute;. Bộ xử l&yacute; mạnh mẽ với c&ocirc;ng nghệ học s&acirc;u từ Samsung tinh chỉnh từng khung h&igrave;nh dựa tr&ecirc;n điều kiện xem, truyền tải ho&agrave;n hảo từng ph&uacute;t gi&acirc;y giải tr&iacute; linh hoạt. T&iacute;nh năng n&acirc;ng cấp h&igrave;nh ảnh bằng tr&iacute; tuệ nh&acirc;n tạo (AI) đảm bảo bạn lu&ocirc;n thưởng thức được chất lượng h&igrave;nh ảnh chuẩn 4K kh&aacute;c biệt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(699).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Tương Phản Vượt Trội Cho Những Sắc M&agrave;u Thật Như Mắt Nh&igrave;n</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quantum HDR 24x -&nbsp;</strong>Thoả th&iacute;ch ngắm nh&igrave;n mọi chi tiết ch&acirc;n thực, gi&agrave;u sắc th&aacute;i với bảng m&agrave;u rực rỡ v&agrave; độ tương phản đỉnh cao. C&ocirc;ng nghệ HDR10+ tuỳ chỉnh m&agrave;u sắc v&agrave; độ tương phản trong từng cảnh quay, cho những khung h&igrave;nh ho&agrave;n mỹ, ấn tượng kh&oacute; qu&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(975).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Trải Nghiệm &Acirc;m Thanh V&ograve;m Theo Chuyển Động H&igrave;nh Ảnh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Object Tracking Sound&nbsp;-&nbsp;</strong>Với khả năng nhận biết chuyển động của h&igrave;nh ảnh, Smart TV 4K Neo QLED mang đến trải nghiệm &acirc;m thanh v&ograve;m ho&agrave;n hảo, chuẩn điện ảnh. Đắm ch&igrave;m trong &acirc;m thanh 3D sống động, ch&acirc;n thực như đang hiện diện trong từng ph&acirc;n cảnh nhờ loa up-firing (loa đ&aacute;nh trần) chuy&ecirc;n dụng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/nd-that-tracks-the-object-217214887.gif\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Mượt M&agrave; Từng Chuyển Động Với 4K 120Hz</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Motion Xcelerator Turbo +&nbsp;</strong>C&ocirc;ng ngh&ecirc;̣ Motion Xcelerator Turbo+ sẽ giúp bạn t&acirc;̣n hưởng từng khung hình game tuy&ecirc;̣t đẹp và chi&ecirc;́n thắng d&ecirc;̃ dàng mọi thử thách trong các màn thi đ&acirc;́u nhờ hình ảnh mượt mà, chuy&ecirc;̉n cảnh kh&ocirc;ng rung lắc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/4(471).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">C&ocirc;ng Nghệ Chấm Lượng Tử Hiển Thị Tỷ Sắc M&agrave;u</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Quantum Dot</strong>&nbsp;- C&ocirc;ng nghệ Ch&acirc;́m Lượng Tử hiển thị 100% dải m&agrave;u với độ ch&acirc;n thực sống động v&agrave; r&otilde; n&eacute;t, cho bạn thưởng thức từng khung h&igrave;nh mang m&agrave;u sắc cuộc sống, tuyệt đẹp ở mọi mức độ s&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/5(403).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Giảm Đ&ocirc;̣ Chói, Tăng Trải Nghi&ecirc;̣m Xem</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chống phản chiếu&nbsp;Anti-Reflection -&nbsp;</strong>C&ocirc;ng nghệ Chống Ch&oacute;i ấn tượng từ Samsung loại bỏ tối đa &aacute;nh s&aacute;ng b&ecirc;n ngo&agrave;i g&acirc;y xao nh&atilde;ng như &aacute;nh nắng hay &aacute;nh đ&egrave;n, để bạn đắm ch&igrave;m v&agrave;o c&aacute;c nội dung giải tr&iacute;, bất kể ng&agrave;y hay đ&ecirc;m.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/6(285).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">M&agrave;u sắc rực rỡ từ mọi g&oacute;c nh&igrave;n</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>G&oacute;c nh&igrave;n rộng</strong><strong>&nbsp;Wide</strong>&nbsp;<strong>Viewing Angle</strong>&nbsp;-&nbsp;C&ocirc;ng nghệ Viewing Angle giảm thiểu tối đa sự r&ograve; rỉ &aacute;nh s&aacute;ng, đảm bảo trải nghiệm xem lu&ocirc;n trọn vẹn, rực rỡ sắc m&agrave;u từ mọi g&oacute;c nh&igrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(733).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Tối Ưu Hiển Thị Bất Kể M&ocirc;i Trường Xung Quanh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>H&igrave;nh ảnh th&iacute;ch ứng với c&ocirc;ng nghệ Adaptive Picture, Brightness - Color Detection&nbsp;</strong>-&nbsp;Sẵn s&agrave;ng đắm ch&igrave;m trong trải nghiệm xem đỉnh cao, đồng nhất ở bất kỳ thời điểm n&agrave;o trong ng&agrave;y với khả năng tự động điều chỉnh độ s&aacute;ng v&agrave; độ tương phản ph&ugrave; hợp với nội dung hiển thị v&agrave; m&ocirc;i trường thực xung quanh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(613).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Bộ Đ&ocirc;i Ho&agrave;n Hảo TV &amp; Loa Thanh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Q-Symphony -&nbsp;</strong>Kh&ocirc;ng c&acirc;̀n lo lắng b&ocirc;̣ loa của bạn kh&ocirc;ng đủ c&ocirc;ng su&acirc;́t và s&ocirc;́ lượng k&ecirc;nh vòm khi xem những thước phim đẳng c&acirc;́p tại nhà. C&ocirc;ng ngh&ecirc;̣ Q-Symphony cho phép t&ocirc;́i ưu các loa của TV trở thành những k&ecirc;nh &acirc;m thanh b&ocirc;̉ sung cho dàn loa thanh của bạn, giúp bạn trải nghi&ecirc;̣m nghe và xem th&acirc;̣t mãn nhãn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/8(150).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">&Acirc;m Thanh Ho&agrave;n Hảo Cho Mọi Kh&ocirc;ng Gian</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&Acirc;m thanh SpaceFit</strong>&nbsp;- Bạn kh&ocirc;ng c&acirc;̀n lo lắng cách đặt TV hay thi&ecirc;́t k&ecirc;́ nhà sẽ ảnh hưởng đ&ecirc;́n trải nghi&ecirc;̣m &acirc;m thanh tr&ecirc;n TV. C&ocirc;ng ngh&ecirc;̣ SpaceFit Sound sẽ đem đ&ecirc;́n &acirc;m thanh nguy&ecirc;n bản trung thực nh&acirc;́t ở b&acirc;́t kỳ kh&ocirc;ng gian nào.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(838).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Nghe R&otilde; Từng C&acirc;u Thoại</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bộ khuếch đại giọng n&oacute;i hoạt động (AVA)&nbsp;</strong>- C&ocirc;ng ngh&ecirc;̣ AVA giúp khu&ecirc;́ch đại &acirc;m thanh thoại, giúp bạn lu&ocirc;n hi&ecirc;̉u rõ n&ocirc;̣i dung phim và các tình ti&ecirc;́t quan trọng dù kh&ocirc;ng gian xung quanh bạn có nhi&ecirc;̀u ti&ecirc;́ng &ocirc;̀n làm nhi&ecirc;̃u, ảnh hưởng trải nghi&ecirc;̣m xem.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(731).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thiết Kế Sang Trọng, Đường N&eacute;t Tinh Tế</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Thiết kế Samsung NeoSlim&nbsp;</strong>-&nbsp;Thi&ecirc;́t k&ecirc;́ si&ecirc;u mỏng, thanh lịch như m&ocirc;̣t ki&ecirc;̣t tác sẽ làm sáng bừng mọi kh&ocirc;ng gian nhà của bạn, tăng th&ecirc;m ni&ecirc;̀m tự hào cho chủ nh&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(845).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Bứt Ph&aacute; Trải Nghiệm Game Gay Cấn</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>GameView &amp; Game Bar Super Ultrawide -&nbsp;</strong>C&aacute;c game thủ chuy&ecirc;n nghi&ecirc;̣p sẽ d&ecirc;̃ dàng chi&ecirc;́n thắng trong các pha gay c&acirc;́n của mọi loại game nhờ góc nhìn r&ocirc;̣ng hơn với tùy chọn khung hình 21:9 và 32:9 tr&ecirc;n TV Samsung. Ngoài ra Menu Game bar thi&ecirc;́t k&ecirc;́ chuy&ecirc;n cho game thủ sẽ giúp thao tác thi&ecirc;́t l&acirc;̣p mọi th&ocirc;ng s&ocirc;́ t&ocirc;́i ưu cho game như tỉ lệ m&agrave;n h&igrave;nh, kiểm tra độ trễ đầu v&agrave;o, FPS, HDR, thiết lập của tai nghe kh&ocirc;ng d&acirc;y v&agrave; hơn thế nữa, t&acirc;́t cả chỉ trong nháy mắt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(730).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Tất Cả Nội Dung, Tr&ecirc;n Một M&agrave;n H&igrave;nh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Nhiều chế độ xem&nbsp;MultiView,&nbsp;TapView</strong>&nbsp;-&nbsp;Kh&ocirc;ng c&acirc;̀n phải lo lắng bản th&acirc;n sẽ lỡ m&acirc;́t những th&ocirc;ng tin quan trọng tr&ecirc;n đi&ecirc;̣n thoại khi đang xem TV với tính năng Multi View. Bạn sẽ thỏa sức theo dõi video hướng d&acirc;̃n game trong lúc v&acirc;̃n nắm được tỷ s&ocirc;́ tr&acirc;̣n đ&acirc;́u y&ecirc;u thích chỉ trong 1 màn hình TV k&ecirc;́t n&ocirc;́i với điện thoại d&ecirc;̃ dàng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/s3fuj9vU5CY180s.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Kết Nối Chỉ Với Một Chạm</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Nhấn v&agrave;o Xem</strong>&nbsp;- Kh&ocirc;ng c&acirc;̀n phải m&acirc;́t thời gian k&ecirc;́t n&ocirc;́i TV và điện thoại đ&ecirc;̉ xem các n&ocirc;̣i dung y&ecirc;u thích khi giờ đ&acirc;y bạn chỉ c&acirc;̀n chạm nhẹ vào cạnh TV đ&ecirc;̉ gửi tín hi&ecirc;̣u mọi n&ocirc;̣i dung và hình ảnh từ đi&ecirc;̣n thoại. Xem phim, hình ảnh hay nghe nhạc từ điện thoại sang TV cực nhanh chỉ với m&ocirc;̣t chạm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/s0iG4Crt96dwzdk.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Truyền h&igrave;nh&nbsp;miễn ph&iacute;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Samsung TV Plus -&nbsp;</strong>Thưởng thức h&agrave;ng ngh&igrave;n giờ xem TV, phim, thể thao miễn ph&iacute; với 150 k&ecirc;nh v&agrave; đang ph&aacute;t triển.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/5(404).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">D&agrave;nh thời gian xem, kh&ocirc;ng phải t&igrave;m kiếm</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng t&igrave;m thấy tất cả c&aacute;c bộ phim v&agrave; chương tr&igrave;nh truyền h&igrave;nh y&ecirc;u th&iacute;ch của bạn ở một nơi, với hướng dẫn đơn giản tr&ecirc;n m&agrave;n h&igrave;nh cung cấp cho bạn c&aacute;c đề xuất ph&ugrave; hợp để kh&aacute;m ph&aacute; nội dung mới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(610).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Chăm s&oacute;c sức khỏe tại nh&agrave;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Samsung Health</strong>&nbsp;-&nbsp;L&agrave;m chủ to&agrave;n bộ th&oacute;i quen chăm s&oacute;c sức khỏe cho t&acirc;m tr&iacute; v&agrave; cơ thể của bạn v&agrave; vượt qua c&aacute;c mục ti&ecirc;u của bạn với nội dung từ c&aacute;c đối t&aacute;c h&agrave;ng đầu trong ng&agrave;nh thể dục v&agrave; quản l&yacute; số liệu thống k&ecirc; h&agrave;ng ng&agrave;y của bạn từ ứng dụng Samsung Health.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(611).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Gallery:</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(846).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(734).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(614).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/4(473).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/5(406).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"javascript:void(0);\" onclick=\"fProductDetailContentSeeMore();\">Thu gọn</a></span></p>\r\n', 0, '1657418600_vn-qled-qn85b-qa65qn85bakxxv-530992953.jpg', 53900000, 7, 18),
(24, 'Smart TV 8K Neo QLED 65 inch QN700B 2022', '<h2><strong><span style=\"font-size:16px\">Điểm nổi bật&nbsp;Smart TV 8K Neo QLED 65 inch QN700B 2022</span></strong></h2>\r\n\r\n<p>Bật r&otilde; c&aacute;c chi tiết ẩn s&acirc;u với Quantum Matrix Pro<br />\r\nChuẩn 8K si&ecirc;u chi tiết với bộ xử l&yacute; Neo Quantum 8K<br />\r\nĐỉnh cao tuyệt t&aacute;c m&agrave;n h&igrave;nh v&ocirc; cực<br />\r\nT&aacute;i Hiện Chuẫn X&aacute;c Những Chi Tiết Ẩn Nhờ Độ Tương Phản Vượt Trội<br />\r\nN&acirc;ng Cấp H&igrave;nh Ảnh Chuẩn 8k Bằng Tr&iacute; Tuệ Nh&acirc;n Tạo Với 16 Hệ Thống Đa Nơ-Ron<br />\r\nTương Phản Sống Động Cho Sắc M&agrave;u Rực Rỡ<br />\r\nTrải Nghiệm &Acirc;m Thanh V&ograve;m Theo Chuyển Động H&igrave;nh Ảnh</p>\r\n', '<h3><span style=\"font-size:14px\">Một Thế Hệ QLED 8K &quot;HO&Agrave;N MỸ&quot;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/DysaMPWc2nE3Lx7.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Bật R&otilde; C&aacute;c &quot;Chi Tiết Ẩn S&acirc;u&quot; Với Đ&egrave;n Nền Quantum Mini Led - Đỉnh Cao Tương Phản 8K</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quantum Matrix Technology Pro</strong>&nbsp;-&nbsp;Cho Bạn Trải nghiệm trọn vẹn từng chi tiết &quot;Ẩn S&acirc;u&quot;c&ugrave;ng sắc đen s&acirc;u thẳm v&agrave; sắc trắng thuần khiết với độ s&aacute;ng được tinh chỉnh tối ưu. C&ugrave;ng Neo QLED 8K- đột ph&aacute; mạnh mẽ với c&ocirc;ng nghệ Backlight Dimming ti&ecirc;n tiến gi&uacute;p điều khiển ch&iacute;nh x&aacute;c c&aacute;c đ&egrave;n Quantum Mini LED với k&iacute;ch cở nhỏ hơn 40 lần so với loại đ&egrave;n th&ocirc;ng thường, V&agrave; v&ugrave;ng chiếu s&aacute;ng nhiều hơn 1,5 lần so với c&ocirc;ng nghệ Quantum Matrix độc quyền từ Samsung.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(971).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">N&acirc;ng Tầm Đẳng Cấp Chất Lượng 8K Bằng&quot;Tr&iacute; Tuệ Nh&acirc;n Tạo&#39;&#39; Mới</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bộ xử l&yacute; lượng tử Samsung Neo 8K -&nbsp;</strong>Kiến tạo v&agrave; Tự N&acirc;ng cấp cho bạn trải nghiệm chất lượng h&igrave;nh ảnh 8K tối ưu chưa từng c&oacute;. Thay v&igrave; chỉ sử dụng một mạng m&ocirc; phỏng thần kinh nh&acirc;n tạo để ph&acirc;n t&iacute;ch dữ liệu hình ảnh, bộ xử l&yacute; Neo Quantum 8K sử dụng dữ liệu được tạo ra từ 16 mạng m&ocirc; phỏng thần kinh nh&acirc;n tạo gi&uacute;p tăng cường đ&ecirc;́n từng chi ti&ecirc;́t theo chuẩn 8K cho mọi n&ocirc;̣i dung</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/QN900A_Feature_Neo%20Quantum%20Processor%208K.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Ho&agrave;n Thiện Xu Hướng &quot; Vẻ Đẹp Tối Giản&quot;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Thiết kế Infinity One</strong>&nbsp;- Kh&ocirc;ng thỏa hiệp trước những giới hạn, Infinity One đại diện cho tuy&ecirc;n ng&ocirc;n thiết kế mới của Samsung. Thiết kế b&oacute;ng bẩy xứng tầm tuyệt t&aacute;c với viền mỏng chưa từng c&oacute;, cho bạn đắm ch&igrave;m v&agrave;o mọi nội dung hiển thị, n&acirc;ng tầm sang trọng cho kh&ocirc;ng gian sống.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(972).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Khi &Acirc;m Thanh V&ograve;m &quot;Theo Dấu&quot; H&igrave;nh Ảnh Chuyển Động</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Object Tracking Sound+ (OTS+) -&nbsp;</strong>Với c&aacute;c loa bố tr&iacute; b&ecirc;n rong TV tạo hiệu ứng 3D, &acirc;m thanh sẽ theo dấu chuyển động h&igrave;nh ảnh của từng ph&acirc;n cảnh, khiến bạn cảm thấy như thể đang xảy ra xung quanh bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/vn-feature-dynamic-sound-that-tracks-the-object-224807538.gif\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Độ Tương Phản Vượt Trội Từ &quot;G&oacute;c Nh&igrave;n Đạo Diễn&quot;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quantum HDR 32x -&nbsp;</strong>T&aacute;i hiện chuẩn x&aacute;c từng sắc th&aacute;i, chi tiết ch&acirc;n thực với dải m&agrave;u rực rỡ v&agrave; độ tương phản ấn tượng. Thưởng thức trọn mọi chi tiết ho&agrave;n mỹ với c&ocirc;ng nghệ HDR10+ tuỳ chỉnh m&agrave;u sắc v&agrave; độ tương phản trong từng cảnh phim.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(835).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Mang Bạn Đến Thế Giới &quot;Ch&acirc;n Thật Ho&agrave;n Hảo&quot;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Độ ph&acirc;n giải 8K thực</strong>&nbsp;- Thưởng thức khung h&igrave;nh r&otilde; n&eacute;t vượt trội với 33 triệu điểm ảnh c&ugrave;ng độ ph&acirc;n giải cao gấp 4 lần so với chuẩn 4K th&ocirc;ng thường, Như bước v&agrave;o thế giới ch&acirc;n thật nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(608).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Biến H&oacute;a Mọi Nội Dung Của Bạn Th&agrave;nh Chuẩn 8K</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>N&acirc;ng cấp 8K AI</strong>&nbsp;- Bằng c&aacute;ch kết hợp thuật to&aacute;n học s&acirc;u với bộ xử l&yacute; Quantum Neo 8K, t&iacute;nh năng N&acirc;ng Cấp Nội Dung Chuẩn 8K Với Tr&iacute; Tuệ Nh&acirc;n Tạo được n&acirc;ng l&ecirc;n một chuẩn mực mới. Thuật to&aacute;n sẽ tiến h&agrave;nh ph&acirc;n t&iacute;ch h&igrave;nh ảnh v&agrave; điều chỉnh để kh&ocirc;i phục những chi tiết nhỏ nhất trong từng điểm ảnh, cho bạn chất lượng hiển thị ho&agrave;n hảo nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/4(470).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Mang &quot;Một Tỷ Sắc M&agrave;u&quot; Từ Cuộc Sống Với Quantum Dot</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ Quantum Dot</strong>&nbsp;- Hiển Thị 100% Dải m&agrave;u sắc với độ ch&acirc;n thật sống động chưa từng c&oacute;. Mang bạn đến với &quot; Bản giao hưởng về M&agrave;u sắc&quot; trong mọi điều kiện độ s&aacute;ng n&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/5(403).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Giảm Thiểu Độ Ch&oacute;i, Giảm Nhiễu Tuyệt Đối</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chống phản chiếu&nbsp;Anti-Reflection -&nbsp;</strong>C&ocirc;ng nghệ Chống Ch&oacute;i ấn tượng từ Samsung loại bỏ tối đa &aacute;nh s&aacute;ng b&ecirc;n ngo&agrave;i g&acirc;y xao nh&atilde;ng như &aacute;nh nắng hay &aacute;nh đ&egrave;n, để bạn đắm ch&igrave;m v&agrave;o c&aacute;c nội dung giải tr&iacute;, bất kể ng&agrave;y hay đ&ecirc;m.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/6(285).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Trải Nghiệm Lu&ocirc;n Trọn Vẹn Từ Mọi G&oacute;c Nh&igrave;n</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>G&oacute;c nh&igrave;n cực cao&nbsp;Ultra Viewing Angle</strong>&nbsp;-&nbsp;Ngồi ở đ&acirc;u kh&ocirc;ng c&ograve;n quan trọng nữa Thoải m&aacute;i tận hưởng mọi g&oacute;c nh&igrave;n kh&aacute;c nhau, d&ugrave; bạn ngồi trong g&oacute;c căn ph&ograve;ng. C&ocirc;ng nghệ Ultra Viewing Angle giảm thiểu tối đa sự r&ograve; rỉ &aacute;nh s&aacute;ng bằng c&aacute;ch tập trung v&agrave; ph&acirc;n bổ đều nguồn s&aacute;ng khắp m&agrave;n h&igrave;nh TV.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/vn-feature-see-the-best-picture-no-matter-where-you-sit-227093881.gif\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Bản Giao Hưởng Tuyệt Vời Từ TV &amp; Soundbar</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Q-Symphony -&nbsp;</strong>Khi cả Loa Soundbar &amp; Loa Tivi c&ugrave;ng đồng bộ thăng hoa tạo ra bản giao hưởng &acirc;m thanh tuyệt vời C&ocirc;ng nghệ Q-Symphony tận dụng loa TV v&agrave; loa thanh, kiến tạo kh&ocirc;ng gian giải tr&iacute; đỉnh cao, bao tr&ugrave;m mọi gi&aacute;c quan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/8(150).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">&Acirc;m Thanh &quot;Nhập Gia -T&ugrave;y Biến&quot; Theo Kh&ocirc;ng Gian Của Bạn</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&Acirc;m thanh SpaceFit</strong>&nbsp;- Bạn kh&ocirc;ng c&acirc;̀n lo lắng vị tr&iacute; &amp; cách đặt TV theo thi&ecirc;́t k&ecirc;́ nhà sẽ ảnh hưởng đ&ecirc;́n trải nghi&ecirc;̣m &acirc;m thanh tr&ecirc;n TV. D&ugrave; TV gắn tường hay đặt tr&ecirc;n mặt phẳng.C&ocirc;ng ngh&ecirc;̣ SpaceFitsound sẽ đem đ&ecirc;́n &acirc;m thanh nguy&ecirc;n bản trung thực nh&acirc;́t ở b&acirc;́t kỳ kh&ocirc;ng gian nào.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(838).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Giải Ph&aacute;p Cho Kh&ocirc;ng Gian Tinh Gọn</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Kết nối Slim One -&nbsp;</strong>D&ugrave; bạn c&agrave;i đặt TV ở bất cứ đ&acirc;u, thiết bị Slim One Connect đều gi&uacute;p bạn giảm thiểu đ&aacute;ng kể t&igrave;nh trạng d&acirc;y c&aacute;p bừa bộn, cho ph&eacute;p lắp đặt gọn g&agrave;ng v&agrave; thẩm mỹ hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(609).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Chiến Game Mượt M&agrave; Theo Từng Chuyển Động Chuẩn 120Hz</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Motion Xcelerator Turbo + -&nbsp;</strong>Chiến thắng mọi m&agrave;n game với chuyển động mượt m&agrave; l&ecirc;n đến 4K 120Hz nhờ c&ocirc;ng nghệ Motion Xcelerator Turbo+. Thỏa sức chơi game, sẵn s&agrave;ng l&acirc;m trận với những thao t&aacute;c mượt m&agrave;, kh&ocirc;ng lo chuyển động bị trễ hay mờ nh&ograve;e.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/4(471).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Trải Nghiệm M&agrave;n H&igrave;nh Game Thủ</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>GameView &amp; Game Bar Super Ultrawide -&nbsp;</strong>Chiến thắng nhiều trận game gay cấn hơn bằng c&aacute;ch mở rộng khung h&igrave;nh đến tầm nh&igrave;n cực đại từ m&agrave;n h&igrave;nh tỉ lệ 21:9 v&agrave; 32:9. Menu Game Bar dễ d&agrave;ng truy cập bằng một ph&iacute;m điều hướng cho ph&eacute;p bạn ngay lập tức điều chỉnh c&aacute;c th&ocirc;ng số như tỉ lệ m&agrave;n h&igrave;nh, kiểm tra độ trễ đầu v&agrave;o, FPS, HDR, thiết lập của tai nghe kh&ocirc;ng d&acirc;y v&agrave; hơn thế nữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(730).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Đa Nhiệm Nội Dung Tr&ecirc;n C&ugrave;ng M&agrave;n H&igrave;nh</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Nhiều chế độ xem&nbsp;MultiView,&nbsp;TapView</strong>&nbsp;-&nbsp;Thoả sức thưởng thức c&ugrave;ng một l&uacute;c nội dung TV v&agrave; điện thoại tr&ecirc;n một m&agrave;n h&igrave;nh ( L&ecirc;n đến 04 Khung h&igrave;nh). Theo d&otilde;i đồng thời video hướng dẫn tr&ograve; chơi v&agrave; bảng thống k&ecirc; tỉ số trận đấu thể thao chỉ bằng c&aacute;ch kết nối TV v&agrave; điện thoại để trải nghiệm đa m&agrave;n h&igrave;nh, dễ d&agrave;ng như chưa từng c&oacute;!</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/s3fuj9vU5CY180s.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Kết Nối Chỉ Với Một Chạm</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>T&iacute;nh Năng Tap View</strong>&nbsp;- Kh&ocirc;ng c&acirc;̀n phải m&acirc;́t thời gian k&ecirc;́t n&ocirc;́i TV và ĐT đ&ecirc;̉ xem các n&ocirc;̣i dung y&ecirc;u thích khi giờ đ&acirc;y bạn chỉ c&acirc;̀n chạm nhẹ vào cạnh TV đ&ecirc;̉ gửi tín hi&ecirc;̣u mọi n&ocirc;̣i dung và hình ảnh từ đi&ecirc;̣n thoại. Xem film, hình ảnh hay nghe nhạc từ ĐT sang TV cực nhanh chỉ với m&ocirc;̣t chạm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/download/2021-2-News/s0iG4Crt96dwzdk.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Truyền h&igrave;nh&nbsp;miễn ph&iacute;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Samsung TV Plus -&nbsp;</strong>Thưởng thức h&agrave;ng ngh&igrave;n giờ xem TV, phim, thể thao miễn ph&iacute; với 150 k&ecirc;nh v&agrave; đang ph&aacute;t triển.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/5(404).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">D&agrave;nh thời gian xem, kh&ocirc;ng phải t&igrave;m kiếm</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Dễ d&agrave;ng t&igrave;m thấy tất cả c&aacute;c bộ phim v&agrave; chương tr&igrave;nh truyền h&igrave;nh y&ecirc;u th&iacute;ch của bạn ở một nơi, với hướng dẫn đơn giản tr&ecirc;n m&agrave;n h&igrave;nh cung cấp cho bạn c&aacute;c đề xuất ph&ugrave; hợp để kh&aacute;m ph&aacute; nội dung mới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(610).jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Gallery:</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/1(836).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/2(727).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/3(607).jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.mediamart.vn/images/Upload/images/4(469).jpg\" /></span></p>\r\n', 0, '1657418255_vn-qled-qn700b-qa65qn700bkxxv-531504242.jpg', 69899000, 5, 18);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(25, 'Galaxy Watch Active2 - 40mm (Mặt Nhôm)', '<h2><strong><span style=\"font-size:16px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-active-2-40-mm-den\" target=\"_blank\" title=\" Đồng hồ thông minh Samsung Galaxy Watch Active 2\" type=\" Đồng hồ thông minh Samsung Galaxy Watch Active 2\">Đồng hồ th&ocirc;ng minh Samsung Galaxy Watch Active 2</a>&nbsp;</span></strong><span style=\"font-size:16px\">40 mm l&agrave; phi&ecirc;n bản nhỏ gọn của d&ograve;ng sản phẩm. Với c&aacute;c t&iacute;nh năng chăm s&oacute;c sức khỏe, m&agrave;n h&igrave;nh cải tiến với viền cảm ứng mang lại trải nghiệm ho&agrave;n hảo cho người d&ugrave;ng.</span></h2>\r\n\r\n<h2><strong>Th&ocirc;ng tin sản phẩm</strong></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế d&agrave;nh cho bạn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch Active 2 mặt tr&ograve;n truyền thống, đường k&iacute;nh mặt 30 mm ph&ugrave; hợp với người c&oacute; cổ tay nhỏ, giao diện dễ thay đổi t&ugrave;y theo sở th&iacute;ch của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&ugrave;y &yacute; thay đổi m&agrave;u d&acirc;y bạn th&iacute;ch với thiết kế th&aacute;o rời tiện lợi. Chất liệu sillicone mềm mại, hạn chế bị trầy xước v&agrave; dễ lau kh&ocirc; khi tiếp x&uacute;c với nước hay mồ h&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den2-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den2-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 thời trang\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">M&agrave;n h&igrave;nh chống trầy, hiển thị sắc n&eacute;t</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh đồng hồ sử dụng mặt k&iacute;nh Gorilla DX+ chống trầy hiệu quả. Tấm nền Super AMOLED cho mọi th&ocirc;ng tin hiển thị sắc n&eacute;t, chi tiết hơn, ngo&agrave;i ra c&ograve;n chống ch&oacute;i v&agrave; tiết kiệm pin đ&aacute;ng kể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm nổi bật nằm ở thiết kế viền cảm ứng cho cảm gi&aacute;c vuốt mượt tay hơn thiết kế xoay v&ograve;ng của anh em tiền nhiệm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den3-2.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 hiển thị rõ nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den3-2.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 hiển thị rõ nét\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Nhận th&ocirc;ng b&aacute;o, nghe v&agrave; từ chối cuộc gọi</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\" type=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\">Đồng hồ th&ocirc;ng minh</a>&nbsp;được kết nối với điện thoại bằng Bluetooth cho ph&eacute;p người d&ugrave;ng nghe gọi dễ d&agrave;ng, th&ocirc;ng b&aacute;o c&aacute;c nội dung tin nhắn từ ứng dụng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den4-1.jpg\" onclick=\"return false;\"><img alt=\"Nghe điện thoại trên đồng hồ thông minh Samsung Galaxy Watch Active 2 \" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den4-1.jpg\" title=\"Nghe điện thoại trên đồng hồ thông minh Samsung Galaxy Watch Active 2 \" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">C&oacute; c&aacute;c tiện &iacute;ch chăm s&oacute;c sức khỏe</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm các mẫu smartwatch Samsung khác\" type=\"Xem thêm các mẫu smartwatch Samsung khác\">Smartwatch Samsung</a>&nbsp;lu&ocirc;n theo d&otilde;i nhịp tim của bạn khi chơi thể thao, nghỉ ngơi hay l&uacute;c ngủ v&agrave; cảnh b&aacute;o khi nhịp tim cao thấp bất thường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den5-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đo nhịp tim liên tục\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den5-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đo nhịp tim liên tục\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ c&ograve;n cảnh b&aacute;o mức độ căng thẳng của bạn đồng thời gợi &yacute; c&aacute;c b&agrave;i tập h&iacute;t thở để cải thiện t&igrave;nh trạng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den6-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 cảnh báo stress\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den6-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 cảnh báo stress\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&iacute;ch hợp nhiều b&agrave;i tập với nhiều chế độ từ cơ bản đến nhanh dần hỗ trợ r&egrave;n luyện sức khỏe của bạn tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den7-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 có nhiều bài tập \" src=\"https://cdn.tgdd.vn/Products/Images/7077/212734/samsung-galaxy-watch-active-2-40-mm-den7-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 có nhiều bài tập \" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Nghe nhạc tr&ecirc;n đồng hồ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; thể nghe nhạc độc lập tr&ecirc;n đồng hồ m&agrave; kh&ocirc;ng cần mang theo điện thoại. Chỉ cần copy nhạc v&agrave;o bộ nhớ của đồng hồ, kết nối với tai nghe bluetooth rồi thưởng thức những b&agrave;i nhạc y&ecirc;u th&iacute;ch.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">C&aacute;c t&iacute;nh năng kh&aacute;c tr&ecirc;n đồng hồ:</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">B&aacute;o thức, nghe nhạc với tai nghe Bluetooth, đồng hồ bấm giờ, t&igrave;m điện thoại.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khả năng sạc kh&ocirc;ng d&acirc;y: Bạn chỉ cần đặt đồng hồ xuống đế sạc nam ch&acirc;m v&agrave; đồng hồ sẽ tự động sạc.</span></p>\r\n', 0, '1657271332_active2nhom.jpg', 5990000, 5, 12),
(26, ' Galaxy Watch 4 LTE 44mm', '<h2><span style=\"font-size:16px\"><strong>Cấu h&igrave;nh Samsung Galaxy Watch 4 LTE 44mm</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>M&agrave;n h&igrave;nh:</p>\r\n\r\n	<p>SUPER AMOLED1.4 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Thời lượng pin:</p>\r\n\r\n	<p>Khoảng 1.5 ng&agrave;y</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết nối với hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Android d&ugrave;ng Google Mobile Service</p>\r\n	</li>\r\n	<li>\r\n	<p>Mặt:</p>\r\n\r\n	<p>K&iacute;nh cường lực Gorrilla Glass Dx+44 mm</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng cho sức khỏe:</p>\r\n\r\n	<p>Theo d&otilde;i giấc ngủĐiện t&acirc;m đồĐo huyết &aacute;pĐo nhịp timĐo nồng độ oxy (SpO2)Đếm số bước ch&acirc;n</p>\r\n	</li>\r\n</ul>\r\n', '<h2><strong>Th&ocirc;ng tin sản phẩm</strong></h2>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thiết kế sang trọng c&ugrave;ng phong c&aacute;ch hiện đại</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Mang tr&ecirc;n m&igrave;nh &ldquo;chiếc &aacute;o&rdquo; m&agrave;u đen sang trọng, huyền b&iacute; c&ugrave;ng phong c&aacute;ch hiện đại, chiếc đồng hồ th&ocirc;ng minh Samsung Galaxy Watch 4 LTE sẽ l&agrave; phụ kiện đem đến sự thời thượng cho người đeo. Sở hữu khung viền nh&ocirc;m c&oacute; trọng lượng nhẹ, bền chắc ở mọi điều kiện thời tiết v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/day-dong-ho\" target=\"_blank\" title=\"Xem thêm dây đeo đang kinh doanh tại Thế Giới Di Động\">d&acirc;y đeo</a>&nbsp;silicone rộng 2 cm, cho khả năng kh&ocirc;ng thấm nước, đ&agrave;n hồi, mềm mại, &ocirc;m trọn cổ tay.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-2-2.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-2-2.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm - Thiết kế\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chip Exynos W920 mạnh mẽ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Exynos W920 l&agrave; chip mới nhất được Samsung cho ra mắt, ph&aacute;t triển ri&ecirc;ng cho đồng hồ th&ocirc;ng minh tr&ecirc;n quy tr&igrave;nh 5nm. Trong b&agrave;i kiểm tra CPU, Exynos W920 nhanh hơn khoảng 20% so với Exynos 9110 của Galaxy Watch 3, hiệu suất đồ họa nhanh gấp 10 lần v&agrave; giảm mức ti&ecirc;u thụ điện năng tổng thể hiệu quả.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có chip Exynos W920 cho hiệu năng xử lý mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-7.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có chip Exynos W920 cho hiệu năng xử lý mạnh mẽ\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hệ điều h&agrave;nh One UI Watch cho trải nghiệm v&agrave; hiệu suất liền mạch hơn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đ&acirc;y l&agrave; phi&ecirc;n bản đồng hồ mới nhất được trang bị hệ điều h&agrave;nh&nbsp;<strong>One UI Watch</strong>, một nền tảng mới mang t&iacute;nh hợp nhất của Samsung với Google, gi&uacute;p Galaxy Watch v&agrave; smartphone t&iacute;ch hợp với nhau một c&aacute;ch to&agrave;n diện hơn. Cụ thể, khi bạn c&agrave;i đặt một ứng dụng tr&ecirc;n điện thoại, đồng hồ th&ocirc;ng minh của bạn cũng sẽ tự động c&agrave;i đặt phi&ecirc;n bản ứng dụng tương th&iacute;ch v&agrave; nhiều t&iacute;nh năng th&uacute; vị kh&aacute;c c&oacute; tr&ecirc;n nền tảng n&agrave;y chờ bạn kh&aacute;m ph&aacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có hệ điều hành One UI Watch mới mẻ, hiệu năng nổi trội\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-13.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có hệ điều hành One UI Watch mới mẻ, hiệu năng nổi trội\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">M&agrave;n h&igrave;nh hiển thị r&otilde; n&eacute;t, chống trầy</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đường k&iacute;nh mặt tr&ograve;n của đồng hồ l&agrave; 44 mm, k&iacute;nh cường lực&nbsp;<strong>Gorilla Glass DX+</strong>&nbsp;chống trầy xước, chịu va đập tốt, bảo vệ m&agrave;n h&igrave;nh&nbsp;<strong>1.4 inch</strong>&nbsp;c&oacute; độ ph&acirc;n giải l&agrave; 450 x 450 pixels. Mang lại trải nghiệm tốt nhất c&ugrave;ng khả năng hiển thị th&ocirc;ng tin r&otilde; n&eacute;t dưới &aacute;nh mặt trời, m&agrave;u sắc sống động, rực rỡ với c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/loai-man-hinh-tft-lcd-amoled-la-gi--592346\" target=\"_blank\" title=\"Công nghệ màn hình SUPER AMOLED là gì?\">SUPER AMOLED</a>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có màn hình Super Amoled hiển thị rõ nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-8.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có màn hình Super Amoled hiển thị rõ nét\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">eSIM - nghe gọi trực tiếp tr&ecirc;n đồng hồ&nbsp;&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Watch 4 LTE được t&iacute;ch hợp&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-esim\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ có tính năng eSIM đang bán tại Thế Giới Di Động\">eSIM</a>&nbsp;hiện đại, cho ph&eacute;p bạn nghe v&agrave; nhận cuộc gọi ngay tr&ecirc;n đồng hồ m&agrave; kh&ocirc;ng cần phải c&oacute; điện thoại. Tiện lợi hơn, khi cuộc tr&ograve; chuyện của bạn sẽ tiết kiệm được kh&aacute; nhiều thời gian v&agrave; kh&ocirc;ng bị gi&aacute;n đoạn nhờ v&agrave;o t&iacute;nh năng hiển thị lịch sử tin nhắn c&ugrave;ng hồi đ&aacute;p th&ocirc;ng minh c&oacute; tr&ecirc;n đồng hồ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có hỗ trợ eSIM yên tâm nghe gọi\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-14.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có hỗ trợ eSIM yên tâm nghe gọi\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thời lượng pin sử dụng trong 1.5 ng&agrave;y</strong>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ Samsung đang bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Samsung</a>&nbsp;Galaxy Watch 4 LTE 44 mm c&oacute; dung lượng pin&nbsp;<strong>361 mAh</strong>, cho thời lượng sử dụng trong 1.5 ng&agrave;y. Sạc đầy năng lượng tiện lợi với chức năng sạc kh&ocirc;ng d&acirc;y, đem đến những trải nghiệm sử dụng hiện đại cho người đeo.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lưu &yacute;: Chế độ<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh có chế độ Always On Display đang bán tại Thế Giới Di Động\">&nbsp;Always On Display</a>&nbsp;v&agrave; GPS sẽ khiến đồng hồ mau hết pin.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-5.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có thời lượng pin sử dụng trong 1.5 ngày\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-5.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có thời lượng pin sử dụng trong 1.5 ngày\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh hỗ trợ định vị GPS đang bán tại Thế Giới Di Động\">Định vị GPS</a>&nbsp;ch&iacute;nh x&aacute;c, đ&aacute;ng tin cậy</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh đang bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y được ho&agrave;n thiện hơn với t&iacute;nh năng định vị GPS hiện đại, ch&iacute;nh x&aacute;c, c&ugrave;ng c&aacute;c bộ cảm biến GLONASS, Beidou, Galileo, thu thập t&iacute;n hiệu vị tr&iacute; từ nhiều vệ tinh kh&aacute;c nhau, gi&uacute;p giảm tối đa sai số. Chức năng n&agrave;y sẽ l&agrave; trợ l&yacute; đắc lực cho người d&ugrave;ng khi leo n&uacute;i hoặc đi phượt. B&ecirc;n cạnh đ&oacute;, đồng hồ sẽ ph&aacute;t hiện kịp thời nếu bạn gặp sự cố khi đi đường hoặc đang hoạt động thể thao, vị tr&iacute; của bạn sẽ được gửi cho số điện thoại đ&atilde; c&agrave;i đặt trước đ&oacute; để ph&aacute;t t&iacute;n hiệu cầu cứu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có định vị GPS chính xác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-3.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có định vị GPS chính xác\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Bộ sưu tập mặt đồng hồ đa dạng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Sản phẩm được trang bị bộ sưu tập mặt đồng hồ đa dạng, bạn c&oacute; thể lựa chọn giữa mặt đồng hồ kim hay đồng hồ kỹ thuật số với nhiều h&igrave;nh nền v&agrave; m&agrave;u sắc kh&aacute;c nhau để ph&ugrave; hợp với c&aacute; t&iacute;nh, sở th&iacute;ch hay trang phục mỗi ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-12.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm có bộ sưu tập mặt đồng hồ đa dạng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-12.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm có bộ sưu tập mặt đồng hồ đa dạng\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thoải m&aacute;i đi mưa, tắm với hệ số&nbsp;chống nước 5 ATM</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Watch 4 đạt chuẩn chống nước 5 ATM theo ISO 22810:2010.&nbsp;Bạn c&oacute; thể thoải m&aacute;i an t&acirc;m sử dụng chiếc smartwatch n&agrave;y khi di chuyển dưới mưa hoặc đi bơi ở v&ugrave;ng nước n&ocirc;ng. Để mang đến tuổi thọ l&acirc;u d&agrave;i cho sản phẩm th&igrave; bạn kh&ocirc;ng n&ecirc;n ấn c&aacute;c n&uacute;t điều chỉnh, kh&ocirc;ng d&ugrave;ng trong ph&ograve;ng tắm hơi, tắm nước n&oacute;ng hay đi bơi ở v&ugrave;ng nước s&acirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-02.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm - Chống nước\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-02.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm - Chống nước\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Theo d&otilde;i hoạt động v&agrave; tập luyện h&agrave;ng ng&agrave;y</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Watch 4 sẽ theo d&otilde;i tiến tr&igrave;nh tập luyện, c&aacute;c hoạt động thể chất,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\" title=\"Xem thêm mốt số mẫu đồng hồ thông minh có tính năng đếm số bước chân đang bán tại Thế Giới Di Động\">đếm số bước ch&acirc;n</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh có tính năng đo lượng Calories tiêu thụ đang bán tại Thế Giới Di Động\">kiểm tra lượng calories</a>, theo d&otilde;i sơ đồ bằng GPS. Từ đ&oacute;, đồng hồ sẽ đưa ra b&aacute;o c&aacute;o ch&iacute;nh x&aacute;c về qu&aacute; tr&igrave;nh tập luyện của bạn v&agrave; hỗ trợ hơn 90 b&agrave;i tập để gi&uacute;p bạn n&acirc;ng cao sức bền, cải thiện sức khỏe v&agrave; v&oacute;c d&aacute;ng. Ngo&agrave;i ra, đồng hồ sẽ theo d&otilde;i bước đi v&agrave; bạn c&oacute; thể cạnh tranh với người th&acirc;n hay bạn b&egrave; th&ocirc;ng qua một bản tin trực tiếp, gi&uacute;p cho việc luyện tập của bạn trở n&ecirc;n th&uacute; vị hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm hỗ trợ 90 chế độ tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-9.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm hỗ trợ 90 chế độ tập luyện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Theo d&otilde;i c&aacute;c chỉ số cơ thể&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Cảm biến<strong>&nbsp;Samsung&nbsp;BioActive</strong>&nbsp;sẽ&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=do-nong-do-oxy-trong-mau-spo2\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh có tính năng đo nồng độ oxy trong máu đang bán tại Thế Giới Di Động\">đo nồng độ oxy trong m&aacute;u</a>&nbsp;(SpO2), theo d&otilde;i huyết &aacute;p của người d&ugrave;ng một c&aacute;ch to&agrave;n diện. T&igrave;m hiểu tỷ lệ chất b&eacute;o, cơ xương, lượng nước trong cơ thể v&agrave; nhận kết quả đo ph&acirc;n t&iacute;ch điện trở sinh học (BIA) chỉ bằng hai ng&oacute;n tay sau 15 gi&acirc;y, hỗ trợ người đeo theo d&otilde;i sức khỏe mọi l&uacute;c, mọi nơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, đồng hồ c&ograve;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh có tính năng theo dõi giấc ngủ đang bán tại Thế Giới Di Động\">theo d&otilde;i giấc ngủ</a>&nbsp;của bạn, ph&acirc;n t&iacute;ch v&agrave; đ&aacute;nh gi&aacute; n&acirc;ng cao th&ocirc;ng qua việc đo nồng độ oxy trong m&aacute;u, ph&aacute;t hiện tiếng ng&aacute;y gi&uacute;p cải thiện chất lượng nghỉ ngơi của bạn. Samsung hợp t&aacute;c với Tổ chức National Sleep Foundation để cung cấp c&aacute;c th&ocirc;ng tin chuy&ecirc;n s&acirc;u về giấc ngủ, gi&uacute;p bạn hiểu r&otilde; v&agrave; cải thiện giấc ngủ của ch&iacute;nh m&igrave;nh. Lưu &yacute;, chức năng quản l&yacute; giấc ngủ chỉ khả dụng khi được gh&eacute;p nối với điện thoại th&ocirc;ng minh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-4-2.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm - Tính năng sức khoẻ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-4-2.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm - Tính năng sức khoẻ\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi c&oacute; b&aacute;o c&aacute;o về t&igrave;nh trạng sức khỏe, bạn sẽ được đưa ra những lời khuy&ecirc;n, đề xuất nghỉ ngơi, tập luyện để c&oacute; thể trạng tốt nhất. Bạn c&oacute; thể xem lại lịch sử huyết &aacute;p được đo trong ứng dụng&nbsp;<strong>Samsung Health Monitor</strong>&nbsp;tr&ecirc;n điện thoại th&ocirc;ng minh được gh&eacute;p nối.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với bản cập nhật mới v&agrave;o th&aacute;ng 4/2022, người d&ugrave;ng Việt Nam đ&atilde; c&oacute; thể sử dụng t&iacute;nh 2 t&iacute;nh năng&nbsp;<strong>đo huyết &aacute;p</strong>&nbsp;v&agrave;&nbsp;<strong>điện t&acirc;m đồ ECG</strong>. Để k&iacute;ch hoạt, bạn cần tải ứng dụng&nbsp;<a href=\"https://galaxystore.samsung.com/detail/com.samsung.android.shealthmonitor\" target=\"_blank\" title=\"Ứng dụng Samsung Health Monitor\">Samsung Health Monitor</a>&nbsp;v&agrave; cập nhật phi&ecirc;n bản phần mềm mới nhất d&agrave;nh cho Galaxy Watch 4. Giờ đ&acirc;y bạn đ&atilde; c&oacute; thể theo d&otilde;i th&ocirc;ng tin sức khỏe bản th&acirc;n mọi l&uacute;c mọi nơi chỉ với chiếc đồng hồ tr&ecirc;n cổ tay của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ocirc;ng tin chi tiết về bản cập nhật n&agrave;y bạn c&oacute; thể xem tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ra-mat-tinh-nang-do-huyet-ap-va-dien-tam-do-tren-galaxy-watch-4-1426241\" target=\"_blank\" title=\"Ra mắt tính năng Đo Huyết Áp và Điện tâm đồ trên Galaxy Watch 4 tại Việt Nam giúp người dùng chăm sóc sức khỏe toàn diện\">Ra mắt t&iacute;nh năng Đo Huyết &Aacute;p v&agrave; Điện t&acirc;m đồ tr&ecirc;n Galaxy Watch 4 tại Việt Nam</a>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-4.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm theo dõi các chỉ số sức khỏe 24/24\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-4.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm theo dõi các chỉ số sức khỏe 24/24\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Trải nghiệm nhiều hơn với c&aacute;c tiện &iacute;ch kh&aacute;c&nbsp;</strong>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc smartwatch n&agrave;y c&ograve;n được trang bị một số chức năng như t&igrave;m điện thoại, dự b&aacute;o thời tiết, bấm giờ, chơi nhạc, Always on Display,...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-2.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE 44mm hỗ trợ nhiều tiện ích\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248755/samsung-galaxy-watch-4-lte-44mm-2.jpg\" title=\"Samsung Galaxy Watch 4 LTE 44mm hỗ trợ nhiều tiện ích\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-4-lte-44mm\" target=\"_blank\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 4 LTE 44mm đang bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Samsung Galaxy Watch 4 LTE 44 mm</a>&nbsp;sở hữu diện mạo thời thượng, sang trọng c&ugrave;ng c&aacute;c t&iacute;nh năng hiện đại của một chiếc smartwatch. Bộ khung viền cho trọng lượng nhẹ, d&acirc;y đeo silicone mềm mại, hệ điều h&agrave;nh thế hệ mới, eSIM tiện lợi, bộ nhớ trong 16GB cho khả năng lưu trữ thoải m&aacute;i, gợi &yacute; tập luyện v&agrave; c&aacute;c chỉ số sức khỏe lu&ocirc;n được cập nhật,... Đ&acirc;y hứa hẹn sẽ l&agrave; sự đồng h&agrave;nh tuyệt vời c&ugrave;ng bạn tr&ecirc;n h&agrave;nh tr&igrave;nh trở th&agrave;nh phi&ecirc;n bản ho&agrave;n hảo hơn của m&igrave;nh.</span></p>\r\n', 0, '1657274325_watchlte44.jpg', 7990000, 15, 14),
(27, 'Galaxy Watch4 Bluetooth (40mm)', '<h2><span style=\"font-size:14px\"><strong>Cấu h&igrave;nh Samsung Galaxy Watch 4 40mm V&agrave;ng Hồng</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>M&agrave;n h&igrave;nh:</p>\r\n\r\n	<p>SUPER AMOLED1.2 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Thời lượng pin:</p>\r\n\r\n	<p>Khoảng 1.5 ng&agrave;y</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết nối với hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Android d&ugrave;ng Google Mobile Service</p>\r\n	</li>\r\n	<li>\r\n	<p>Mặt:</p>\r\n\r\n	<p>K&iacute;nh cường lực Gorrilla Glass Dx+40 mm</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng cho sức khỏe:</p>\r\n\r\n	<p>Theo d&otilde;i giấc ngủT&iacute;nh lượng calories ti&ecirc;u thụĐiện t&acirc;m đồĐo huyết &aacute;pĐo nhịp timĐo nồng độ oxy (SpO2)Đếm số bước ch&acirc;n</p>\r\n	</li>\r\n</ul>\r\n', '<h3><strong><span style=\"font-size:14px\">Phong c&aacute;ch năng động, thời thượng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-4-40mm\" target=\"_blank\" title=\"Đồng hồ Samsung Galaxy Watch 4 40mm được bán tại Thế Giới Di Động\">đồng hồ Samsung Galaxy Watch 4</a>&nbsp;n&agrave;y được thiết kế đơn giản với mặt đồng hồ dạng tr&ograve;n c&oacute; đường k&iacute;nh 40 mm thường thấy ở&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm đồng hồ thông minh được bán tại Thế Giới Di Động\" type=\"Xem thêm đồng hồ thông minh được bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh</a>&nbsp;tạo n&ecirc;n phong c&aacute;ch năng động, cuốn h&uacute;t. Khung viền đồng hồ được l&agrave;m từ chất liệu nh&ocirc;m bền chắc, trọng lượng nhẹ. B&ecirc;n cạnh đ&oacute;&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicone\" target=\"_blank\" title=\"Xem thêm đồng hồ có dây silicone bán tại Thế Giới Di Động\" type=\"Xem thêm đồng hồ có dây silicone bán tại Thế Giới Di Động\">d&acirc;y đeo silicone</a>&nbsp;&ecirc;m &aacute;i, kh&ocirc;ng g&acirc;y kh&oacute; chịu khi đeo suốt ng&agrave;y d&agrave;i, kể cả khi người d&ugrave;ng vận động, tập thể thao ra nhiều mồ h&ocirc;i tay.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-4-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-4-1.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Thiết kế\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">M&agrave;n h&igrave;nh sắc n&eacute;t, hiển thị r&otilde; r&agrave;ng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ được h&atilde;ng trang bị m&agrave;n h&igrave;nh Super AMOLED c&oacute; độ ph&acirc;n giải 396 x 396 pixels cho khả năng hiển thị sắc n&eacute;t, độ tương phản cao v&agrave; đặc biệt l&agrave; tiết kiệm năng lượng tốt. M&agrave;n h&igrave;nh được bảo vệ tốt hơn với tấm k&iacute;nh cường lực Gorilla Glass Dx+ cứng c&aacute;p, c&oacute; độ bền cao, kh&ocirc;ng lo t&igrave;nh trạng nứt vỡ khi bị va đập.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-2.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-2.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Màn hình\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Dễ d&agrave;ng kết nối với &quot;dế y&ecirc;u&quot; th&ocirc;ng qua Bluetooth v5.0</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; thể dễ d&agrave;ng kết nối c&aacute;c thiết bị Android với chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh Samsung đang bán tại Thế Giới Di Động\">đồng hồ Samsung</a>&nbsp;n&agrave;y. Sau khi kết nối, bạn c&oacute; thể kiểm tra th&ocirc;ng b&aacute;o tin nhắn, cuộc gọi tr&ecirc;n đồng hồ nhanh ch&oacute;ng v&agrave; tiện lợi, kh&ocirc;ng lo bỏ lỡ th&ocirc;ng b&aacute;o n&agrave;o trong l&uacute;c đang luyện tập thể thao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-3.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Bluetooth\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-3.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Bluetooth\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Cấu h&igrave;nh mạnh mẽ với con chip&nbsp;Exynos W920</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ được trang bị chip Exynos W920 c&oacute; tốc độ nhanh hơn 20% so với Exynos 9110 được trang bị ở chiếc Samsung Galaxy Watch 3. B&ecirc;n cạnh đ&oacute;, con chip Exynos W920 n&agrave;y c&ograve;n gi&uacute;p tiết kiệm pin, tăng thời lượng sử dụng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-4-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Chip\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-4-1.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Chip\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hệ điều h&agrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-deo-tay\" target=\"_blank\" title=\"Xem thêm đồng hồ bán tại Thế Giới Di Động\" type=\"Xem thêm đồng hồ bán tại Thế Giới Di Động\">đồng hồ</a>&nbsp;l&agrave; Wear Os, đặc biệt Samsung Galaxy Watch 4 l&agrave; chiếc đồng hồ th&ocirc;ng minh đầu ti&ecirc;n c&oacute; nền tảng hợp nhất với giao diện One UI Watch mang lại trải nghiệm ho&agrave;n to&agrave;n mới mẻ v&agrave; th&uacute; vị.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">C&aacute; nh&acirc;n h&oacute;a thiết bị với bộ sưu tập mặt đồng hồ đa dạng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thoải m&aacute;i thay đổi h&igrave;nh nền đồng hồ với bộ sưu tập mặt đồng hồ đa dạng, bạn c&oacute; thể lựa chọn c&aacute;c loại mặt đồng hồ kh&aacute;c nhau như mặt đồng hồ kim, mặt điện tử t&ugrave;y theo sở th&iacute;ch của bạn. Với t&iacute;nh năng n&agrave;y, chiếc đồng hồ của bạn sẽ lu&ocirc;n được đổi mới, kh&ocirc;ng lo nh&agrave;m ch&aacute;n khi sử dụng l&acirc;u ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-5.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Giao diện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-5.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Giao diện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thời lượng pin 1.5 ng&agrave;y</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Với dung lượng pin 247 mAh, bạn c&oacute; thể sử dụng đồng hồ trong khoảng 1.5 ng&agrave;y, đồng hồ được sạc th&ocirc;ng qua cổng sạc kh&ocirc;ng d&acirc;y tiện lợi. Lưu &yacute;, thời gian sử dụng n&agrave;y c&oacute; thể thay đổi t&ugrave;y theo c&aacute;ch sử dụng của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-6.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Pin\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-6.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Pin\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Khả năng định vị chuẩn x&aacute;c</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn kh&ocirc;ng cần phải qu&aacute; lo lắng về vấn đề đường đi khi du lịch hay đi phượt một m&igrave;nh nhờ t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/gps-la-gi-806129\" target=\"_blank\" title=\"Tìm hiểu thêm về GPS\">định vị GPS</a>&nbsp;chuẩn x&aacute;c được t&iacute;ch hợp tr&ecirc;n người bạn đồng h&agrave;nh n&agrave;y. Bạn c&oacute; thể dễ d&agrave;ng biết được th&ocirc;ng tin ch&iacute;nh x&aacute;c về vị tr&iacute; hiện tại, qu&atilde;ng đường đi gi&uacute;p bạn chủ động hơn trong những chuyến đi xa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-7.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - GPS\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-7.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - GPS\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chuẩn chống nước 5 ATM - ISO 22810:2010</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m đeo đồng hồ th&ocirc;ng minh khi đi mưa, đi bơi ở v&ugrave;ng nước n&ocirc;ng với chuẩn chống nước 5 ATM. Tuy nhi&ecirc;n, để đảm bảo được tuổi thọ của đồng hồ, người d&ugrave;ng kh&ocirc;ng n&ecirc;n ng&acirc;m đồng hồ qu&aacute; l&acirc;u dưới nước tr&aacute;nh t&igrave;nh trạng v&agrave;o nước g&acirc;y hỏng h&oacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-8-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Kháng nước\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-8-1.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Kháng nước\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Bộ cảm biến ti&ecirc;n tiến theo d&otilde;i sức khỏe người d&ugrave;ng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ được trang bị c&aacute;c cảm biến &aacute;nh s&aacute;ng, địa từ, gia tốc kế,... đặc biệt l&agrave; bộ cảm biến&nbsp;BioActive đột ph&aacute; của Samsung. Nhờ bộ cảm biến hiện đại n&agrave;y, người d&ugrave;ng c&oacute; thể nhận kết quả ph&acirc;n t&iacute;ch th&agrave;nh phần cơ thể (qua phương ph&aacute;p đo BIA) theo thời gian thực mọi l&uacute;c, mọi nơi chỉ với hai ng&oacute;n tay trong 15 gi&acirc;y. Cụ thể l&agrave; c&aacute;c chỉ số như&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=do-nong-do-oxy-trong-mau-spo2\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh có tính năng đo nồng độ oxy trong máu đang bán tại Thế Giới Di Động\">nồng độ oxy trong m&aacute;u (SpO2)</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-do-nhip-tim\" target=\"_blank\" title=\"Xem thêm mẫu đồng hồ có chức năng đo nhịp tim tại Thế Giới Di Động\" type=\"Xem thêm mẫu đồng hồ có chức năng đo nhịp tim tại Thế Giới Di Động\">đo nhịp tim</a>&nbsp;hay lượng calories m&agrave; bạn đ&atilde; ti&ecirc;u thụ, gi&uacute;p bạn kiểm so&aacute;t tốt c&aacute;c mục ti&ecirc;u sức khoẻ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-10.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Cảm biến\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-10.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Cảm biến\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, đồng hồ c&ograve;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh có tính năng theo dõi giấc ngủ đang bán tại Thế Giới Di Động\">theo d&otilde;i giấc ngủ</a>&nbsp;của bạn, ph&acirc;n t&iacute;ch v&agrave; đ&aacute;nh gi&aacute; n&acirc;ng cao th&ocirc;ng qua việc đo nồng độ oxy trong m&aacute;u, ph&aacute;t hiện tiếng ng&aacute;y gi&uacute;p cải thiện chất lượng nghỉ ngơi của bạn. Samsung hợp t&aacute;c với Tổ chức National Sleep Foundation để cung cấp c&aacute;c th&ocirc;ng tin chuy&ecirc;n s&acirc;u về giấc ngủ, gi&uacute;p bạn hiểu r&otilde; v&agrave; cải thiện giấc ngủ của ch&iacute;nh m&igrave;nh. Lưu &yacute;, chức năng quản l&yacute; giấc ngủ chỉ khả dụng khi được gh&eacute;p nối với điện thoại th&ocirc;ng minh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tinh-nang-do-chi-so-spo2-va-vo2-max-tren-smartwatch-la-gi-1279892\" target=\"_blank\" title=\"Tìm hiểu thêm về SpO2\" type=\"Tìm hiểu thêm về SpO2\">SpO2 l&agrave; g&igrave;?</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với bản cập nhật mới v&agrave;o th&aacute;ng 4/2022, người d&ugrave;ng Việt Nam đ&atilde; c&oacute; thể sử dụng t&iacute;nh 2 t&iacute;nh năng&nbsp;<strong>đo huyết &aacute;p</strong>&nbsp;v&agrave;&nbsp;<strong>điện t&acirc;m đồ ECG</strong>. Để k&iacute;ch hoạt, bạn cần tải ứng dụng&nbsp;<a href=\"https://galaxystore.samsung.com/detail/com.samsung.android.shealthmonitor\" target=\"_blank\" title=\"Ứng dụng Samsung Health Monitor\">Samsung Health Monitor</a>&nbsp;v&agrave; cập nhật phi&ecirc;n bản phần mềm mới nhất d&agrave;nh cho Galaxy Watch 4. Giờ đ&acirc;y bạn đ&atilde; c&oacute; thể theo d&otilde;i th&ocirc;ng tin sức khỏe bản th&acirc;n mọi l&uacute;c mọi nơi chỉ với chiếc đồng hồ tr&ecirc;n cổ tay của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ocirc;ng tin chi tiết về bản cập nhật n&agrave;y bạn c&oacute; thể xem tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ra-mat-tinh-nang-do-huyet-ap-va-dien-tam-do-tren-galaxy-watch-4-1426241\" target=\"_blank\" title=\"Ra mắt tính năng Đo Huyết Áp và Điện tâm đồ trên Galaxy Watch 4 tại Việt Nam giúp người dùng chăm sóc sức khỏe toàn diện\">Ra mắt t&iacute;nh năng Đo Huyết &Aacute;p v&agrave; Điện t&acirc;m đồ tr&ecirc;n Galaxy Watch 4 tại Việt Nam</a>.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hỗ trợ luyện tập dễ d&agrave;ng hơn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch 4 trang bị nhiều b&agrave;i tập cho bạn lựa chọn như đạp xe, đi bộ, chạy bộ, leo n&uacute;i,... B&ecirc;n cạnh đ&oacute;, thiết bị c&ograve;n theo d&otilde;i v&agrave; đưa ra b&aacute;o c&aacute;o cụ thể về qu&aacute; tr&igrave;nh tập luyện, gi&uacute;p việc r&egrave;n luyện sức khỏe của bạn đạt hiệu quả cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-11.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-11.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Tập luyện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Tiện &iacute;ch kh&aacute;c</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch 4 được h&atilde;ng trang bị nhiều tiện &iacute;ch kh&aacute;c như b&aacute;o thức, t&igrave;m điện thoại, dự b&aacute;o thời tiết,... Những tiện &iacute;ch n&agrave;y sẽ gi&uacute;p đồng hồ trở th&agrave;nh một người bạn đồng h&agrave;nh hỗ trợ tối đa trong c&ocirc;ng việc v&agrave; c&aacute;c hoạt động thường ng&agrave;y của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-12.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Tiện ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248752/samsung-galaxy-watch-4-40mm-hong-12.jpg\" title=\"Đồng hồ Galaxy Watch 4 40mm Vàng Hồng - Tiện ích khác\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mẫu đồng hồ th&ocirc;ng minh Samsung Galaxy Watch 4 n&agrave;y c&oacute; thiết kế liền mạch, độc đ&aacute;o với phong c&aacute;ch s&agrave;nh điệu ph&ugrave; hợp với nhiều lứa tuổi kh&aacute;c nhau. Đồng hồ được trang bị nhiều cảm biến hiện đại, hỗ trợ theo d&otilde;i sức khỏe người d&ugrave;ng, c&ugrave;ng nhiều b&agrave;i tập luyện hữu &iacute;ch. Chắn chắn, mẫu đồng hồ n&agrave;y sẽ được nhiều người ưa chuộng nhờ vẻ ngo&agrave;i thu h&uacute;t c&ugrave;ng nhiều t&iacute;nh năng đ&aacute;ng mong đợi.</span></p>\r\n', 0, '1657273893_watch4.jpg', 6490000, 15, 14);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(28, 'Galaxy Watch Active2 - 44mm (Mặt Thép)', '<h2><span style=\"font-size:16px\"><strong>Đặc điểm nổi bật của&nbsp;Galaxy Watch Active 2 44mm viền th&eacute;p d&acirc;y da</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-active-2-44-mm-day-da\" target=\"_blank\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đang kinh doanh tại Thegioidiong.com\" type=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đang kinh doanh tại Thegioidiong.com\">Đồng hồ th&ocirc;ng minh Samsung Galaxy Watch Active 2</a>&nbsp;44mm l&agrave; phi&ecirc;n bản c&oacute; sự cải tiến về c&ocirc;ng nghệ để mang đến những trải nghiệm tốt nhất cho người d&ugrave;ng với nhiều t&iacute;nh năng chăm s&oacute;c sức khỏe, nhận cuộc gọi trực tiếp cũng như nhiều t&iacute;nh năng tiện &iacute;ch kh&aacute;c.</span></p>\r\n', '<h3><strong><span style=\"font-size:14px\">7 chế độ luyện tập n&acirc;ng cao sức khỏe</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\" type=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\">Đồng hồ th&ocirc;ng minh</a>&nbsp;Samsung Galaxy Watch Active 2 thực sự l&agrave; huấn luyện vi&ecirc;n thể thao chuy&ecirc;n nghiệp của bạn với việc t&iacute;ch hợp những chế độ luyện tập kh&aacute;c nhau cho ph&ugrave; hợp với cơ địa v&agrave; thể trạng của từng người.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da4-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 có nhiều chế độ luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da4-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 có nhiều chế độ luyện tập\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Những th&ocirc;ng số về&nbsp;<strong>qu&atilde;ng đường chạy</strong>,&nbsp;<strong>đếm số bước ch&acirc;n</strong>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\" type=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\">lượng calories</a>&nbsp;ti&ecirc;u thụ sẽ l&agrave; tư liệu để cho bạn kiểm so&aacute;t tốt c&aacute;c chế độ luyện tập hằng ng&agrave;y.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Theo d&otilde;i giấc ngủ - đo nhịp tim - cảnh b&aacute;o stress</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc đồng hồ th&ocirc;ng minh n&agrave;y sẽ gi&uacute;p bạn&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=do-nhip-tim\" target=\"_blank\" title=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\" type=\"Xem thêm một số đồng hồ thông minh đang kinh doanh tại Thegioididong.com\">theo d&otilde;i nhịp tim</a>&nbsp;ch&iacute;nh x&aacute;c 24/24 ngay cả khi bạn đang ngủ v&agrave; cảnh b&aacute;o nếu nhịp tim thay đổi bất thường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da5-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 theo dõi nhịp tim liên tục\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da5-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 theo dõi nhịp tim liên tục\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Theo d&otilde;i mức độ căng thẳng của cơ thể bạn v&agrave; đưa ra&nbsp;<strong>cảnh b&aacute;o stress</strong>, hướng dẫn bạn những b&agrave;i tập h&iacute;t thở cơ bản để giải tỏa stress.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thiết kế với những đường n&eacute;t tinh tế</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch Active 2 mang đến bạn thiết kế trẻ trung năng động nhưng kh&ocirc;ng k&eacute;m phần tinh tế.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da2-2.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 thiết kế năng động, thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da2-2.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 thiết kế năng động, thời trang\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh&nbsp;<strong>SUPER AMOLED 1.4 inch</strong>&nbsp;sắc n&eacute;t với chất liệu mặt l&agrave; k&iacute;nh cường lực&nbsp;<strong>Gorilla Glass</strong>&nbsp;chống trầy hiệu quả.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 hiển thị rõ, sắc nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 hiển thị rõ, sắc nét\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Gọi điện trực tiếp tr&ecirc;n đồng hồ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ th&ocirc;ng minh Samsung Galaxy Watch Active 2 c&ograve;n t&iacute;ch hợp t&iacute;nh năng gọi điện trực tiếp, hiển thị th&ocirc;ng b&aacute;o về nội dung tin nhắn, Zalo, Messenger, Line,...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da1-1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 cho phép nghe gọi\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da1-1.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 cho phép nghe gọi\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bạn cũng c&oacute; thể sử dụng đồng hồ như thiết bị điều khiển từ xa, tiện lợi khi chụp h&igrave;nh v&agrave; quay video.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da-4.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 điều khiển từ xa\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da-4.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 điều khiển từ xa\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thoải m&aacute;i tắm, bơi ở v&ugrave;ng nước n&ocirc;ng nhờ chỉ số&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-chong-nuoc-5-atm\" target=\"_blank\" title=\"Xem thêm 1 số mẫu đồng hồ thông minh có hệ số chống nước 5 ATM\">chống nước 5 ATM</a></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị đạt chuẩn 5 ATM - ISO 22810:2010, nghĩa l&agrave; thiết bị c&oacute; thể kh&aacute;ng nước ở độ s&acirc;u 50m trong v&ograve;ng 10 ph&uacute;t.&nbsp;Bạn c&oacute; thể mang ch&uacute;ng đi bơi lội, tận hưởng việc tự do kh&aacute;m ph&aacute;, lu&ocirc;n kết nối v&agrave; ghi lại h&agrave;nh tr&igrave;nh phi&ecirc;u lưu của m&igrave;nh. C&aacute;c thiết bị đạt chuẩn n&agrave;y c&oacute; thể hoạt động trong c&aacute;c điều kiện nước n&ocirc;ng v&agrave; chuyển động ở vận tốc thấp.&nbsp;Bật t&iacute;nh năng kh&oacute;a chống nước trước khi mang sản phẩm đi bơi, đi mưa, kh&ocirc;ng ấn c&aacute;c n&uacute;t điều chỉnh, kh&ocirc;ng d&ugrave;ng trong ph&ograve;ng tắm hơi, tắm nước n&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Samsung&nbsp;kh&ocirc;ng bảo h&agrave;nh, đổi trả</strong>&nbsp;trong trường hợp sản phẩm bị v&agrave;o nước.&nbsp;Tuy nhi&ecirc;n khi gặp trường hợp tr&ecirc;n, n&ecirc;n tắt nguồn của thiết bị v&agrave; nhanh ch&oacute;ng mang đến Trung T&acirc;m Bảo H&agrave;nh gần nhất c&oacute; thể để được hỗ trợ sửa chữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/Slider/samsung-galaxy-watch-active-2-44-mm-day-da-hbv-fix-800x444.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đạt chuẩn chống nước 5 ATM\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/Slider/samsung-galaxy-watch-active-2-44-mm-day-da-hbv-fix-800x444.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 đạt chuẩn chống nước 5 ATM\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">C&aacute;c t&iacute;nh năng tiện &iacute;ch kh&aacute;c</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh những t&iacute;nh năng quan trọng th&igrave; chiếc đồng hồ n&agrave;y cũng hỗ trợ nhiều t&iacute;nh năng cơ bản kh&aacute;c như: T&igrave;m điện thoại, b&aacute;o thức, nghe nhạc,...&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0101.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 với nhiều tính năng khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0101.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch Active 2 với nhiều tính năng khác\" /></a></span></p>\r\n', 0, '1657270948_active2.jpg', 9990000, 5, 12),
(30, 'Galaxy Fit2', '<h2><span style=\"font-size:16px\"><strong>Đặc điểm nổi bật của&nbsp;Samsung Galaxy Fit2</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">Khỏe hơn mỗi ng&agrave;y với chiếc v&ograve;ng đeo tay th&ocirc;ng minh&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/samsung-galaxy-fit2\" title=\"Samsung Galaxy Fit2\" type=\"Samsung Galaxy Fit2\">Samsung Galaxy Fit2</a>, mang đến cho bạn khả năng hỗ trợ sức khỏe to&agrave;n diện chỉ với một chiếc v&ograve;ng tay thời trang v&agrave; nhỏ gọn.</span></p>\r\n', '<h2><span style=\"font-size:14px\"><strong>Đầy đủ th&ocirc;ng tin tr&ecirc;n m&agrave;n h&igrave;nh AMOLED sắc n&eacute;t</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">D&ugrave; l&agrave; một chiếc&nbsp;<a href=\"https://fptshop.com.vn/smartwatch\" title=\"Vòng tay thông minh\" type=\"Vòng tay thông minh\">v&ograve;ng đeo tay th&ocirc;ng minh</a>&nbsp;rất nhỏ gọn, nhưng Samsung Galaxy Fit2 vẫn sở hữu&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-amoled-la-gi-34866\" title=\"Màn hình Amoled\" type=\"Màn hình Amoled\">m&agrave;n h&igrave;nh AMOLED</a>&nbsp;Full Color 1,1 inch, hiển thị sắc n&eacute;t, độ s&aacute;ng cao v&agrave; m&agrave;u sắc rực rỡ. M&agrave;n h&igrave;nh được thiết kế cong 3D, cho vẻ đẹp nổi bật. Bạn sẽ dễ d&agrave;ng c&oacute; được đầy đủ th&ocirc;ng tin cần thiết tr&ecirc;n m&agrave;n h&igrave;nh đầy phong c&aacute;ch n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"màn hình Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-2.jpg\" title=\"màn hình Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Cảm gi&aacute;c đeo nhẹ nh&agrave;ng, thoải m&aacute;i của Galaxy Fit2</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ đẹp thời trang,&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/samsung\" title=\"Đồng hồ thông minh Samsung\" type=\"Đồng hồ thông minh Samsung\">Samsung</a>&nbsp;Galaxy Fit2 c&ograve;n l&agrave; thiết bị l&yacute; tưởng để b&ecirc;n bạn cả ng&agrave;y m&agrave; kh&ocirc;ng hề thấy kh&oacute; chịu. Với độ mỏng chỉ 1,11cm, Galaxy Fit2 mỏng hơn, cho cảm gi&aacute;c đeo thoải m&aacute;i hơn nhiều so với&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/samsung-gear-fit-e\" title=\"Samsung gear fit e\" type=\"Samsung gear fit e\">thế hệ trước</a>. D&acirc;y đeo với đường r&atilde;nh độc đ&aacute;o sẽ gi&uacute;p tho&aacute;t mồ h&ocirc;i nhanh, để bạn c&oacute; thể tập luyện trong thời gian d&agrave;i m&agrave; vẫn rất dễ chịu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"thiết kế Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-7.jpg\" title=\"thiết kế Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>L&agrave;m mới mỗi ng&agrave;y với hơn 70 mặt đồng hồ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thể hiện c&aacute; t&iacute;nh ri&ecirc;ng biệt với hơn 70 mặt đồng hồ tr&ecirc;n Galaxy Fit2. Bạn sẽ t&igrave;m thấy mặt đồng hồ y&ecirc;u th&iacute;ch v&agrave; c&oacute; thể thay đổi bất cứ l&uacute;c n&agrave;o để th&ecirc;m niềm cảm hứng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Giao diện màn hình Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-12.jpg\" title=\"Giao diện màn hình Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Galaxy Fit2 Chống nước bền bỉ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Luyện tập dưới nước kh&ocirc;ng phải l&agrave; vấn đề với Galaxy Fit2. Với chuẩn&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/dong-ho/chi-so-chong-nuoc-tren-dong-ho-co-y-nghia-gi-118037\" title=\"Chỉ số chống nước trên đồng hồ\" type=\"Chỉ số chống nước trên đồng hồ\">kh&aacute;ng nước 5ATM</a>, bạn c&oacute; thể ng&acirc;m nước ở độ s&acirc;u l&ecirc;n tới 50m.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"chống nước Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-4.jpg\" title=\"chống nước Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thời lượng pin l&ecirc;n tới 15 ng&agrave;y</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng cần phải lo lắng về thời lượng pin Samsung Galaxy Fit2. Thỏa sức l&agrave;m những g&igrave; bạn muốn trong suốt ng&agrave;y d&agrave;i năng động. Tập luyện, nhận th&ocirc;ng b&aacute;o, theo d&otilde;i giấc ngủ, &hellip; Galaxy Fit2 c&oacute; thể hoạt động li&ecirc;n tục trong suốt 2 tuần chỉ sau một lần sạc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"pin Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-5.jpg\" title=\"pin Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hỗ trợ tập luyện hiệu quả</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Fit2 c&oacute; thể tự động nhận diện 5 b&agrave;i tập luyện phổ biến để qu&aacute; tr&igrave;nh tập luyện hiệu quả hơn. D&ugrave; l&agrave; chạy bộ, đi bộ, tập với tạ, tập với d&acirc;y hay chạy tr&ecirc;n m&aacute;y chạy, Galaxy Fit2 cũng sẽ theo d&otilde;i những th&ocirc;ng tin cần thiết như thời gian, nhịp tim, lượng calo đốt để bạn lu&ocirc;n chinh phục những thử th&aacute;ch mới.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Hỗ trợ tập luyện Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-6.jpg\" title=\"Hỗ trợ tập luyện Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>T&iacute;nh năng nhắc nhở rửa tay th&uacute; vị của Galaxy Fit2</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Một t&iacute;nh năng rất thời thượng trong thời Covid, đ&oacute; l&agrave; nhắc nhở rửa tay đ&atilde; c&oacute; mặt tr&ecirc;n Galaxy Fit2. V&ograve;ng tay sẽ nhắc để bạn ch&uacute; &yacute; giữ g&igrave;n vệ sinh c&aacute; nh&acirc;n trong m&ugrave;a dịch bệnh, đồng thời bộ đếm ngược tiện dụng tr&ecirc;n thiết bị sẽ gi&uacute;p bạn rửa tay đ&uacute;ng c&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Tính năng nhắc rửa tay Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-8.jpg\" title=\"Tính năng nhắc rửa tay Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Cho bạn giấc ngủ ngon</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Một giấc ngủ ngon lu&ocirc;n mang đến cho bạn th&ecirc;m rất nhiều năng lượng v&agrave;o ng&agrave;y l&agrave;m việc tiếp theo. Samsung Galaxy Fit2 với t&iacute;nh năng theo d&otilde;i giấc ngủ sẽ đo lường c&aacute;c nh&acirc;n tố li&ecirc;n quan đến giấc ngủ, đ&aacute;nh gi&aacute; chất lượng giấc ngủ v&agrave; gợi &yacute; cho bạn những lời khuy&ecirc;n để c&oacute; được giấc ngủ ngon hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trải nghiệm giấc ngủ ngon Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-9.jpg\" title=\"Trải nghiệm giấc ngủ ngon Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Xua tan t&igrave;nh trạng Stress</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Trong cuộc sống hiện đại, t&igrave;nh trạng căng thẳng stress l&agrave; điều thường xuy&ecirc;n bạn gặp phải. Galaxy Fit2 với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/giai-tri/samsung-health-cap-nhat-tinh-nang-theo-doi-chu-ky-kinh-nguyet-113236\" title=\"Ứng dụng Sansung Health\" type=\"Ứng dụng Sansung Health\">ứng dụng Samsung Health</a>&nbsp;c&oacute; thể theo d&otilde;i mức độ căng thẳng v&agrave; đưa ra những b&agrave;i h&iacute;t thở hữu &iacute;ch để bạn sớm đưa bản th&acirc;n trở lại t&igrave;nh trạng c&acirc;n bằng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Xua tan stress Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-10.jpg\" title=\"Xua tan stress Samsung Galaxy Fit2\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Samsung Galaxy Fit2 với loạt t&iacute;nh năng tiện dụng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi kết nối với smartphone, Galaxy Fit2 c&oacute; thể nhận th&ocirc;ng b&aacute;o v&agrave; trả lời nhanh ngay tr&ecirc;n v&ograve;ng tay m&agrave; kh&ocirc;ng cần phải d&ugrave;ng đến điện thoại. Ngo&agrave;i ra Fit2 cũng c&oacute; thể điều khiển &acirc;m nhạc một c&aacute;ch dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"thông báo Samsung Galaxy Fit2\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-fit2-11.jpg\" title=\"thông báo Samsung Galaxy Fit2\" /></span></p>\r\n', 0, '1657270308_fit2.jpg', 1100000, 30, 15),
(32, 'Galaxy Z Fold3 5G 512GB', '<h2><span style=\"font-size:16px\"><strong><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold3-5g-512gb\" target=\"_blank\" title=\"Tham khảo giá điện thoại Samsung Galaxy Z Fold3 5G 512GB chính hãng\">Samsung Galaxy Z Fold3 5G 512GB</a>&nbsp;đ&aacute;nh dấu bước tiến mới của&nbsp;h&atilde;ng&nbsp;trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chính hãng\">điện thoại</a>&nbsp;gập cao cấp khi được cải tiến về độ bền c&ugrave;ng những n&acirc;ng cấp đ&aacute;ng gi&aacute; về cấu h&igrave;nh hiệu năng, hứa hẹn sẽ mang đến trải nghiệm sử dụng đột ph&aacute; cho người d&ugrave;ng.</strong></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3><strong><span style=\"font-size:14px\">Đột ph&aacute; thiết kế m&agrave;n h&igrave;nh gập</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đầu ti&ecirc;n, khung viền Galaxy Z Fold3 được ho&agrave;n thiện bằng chất liệu Armor Aluminum cao cấp nhất từ trước đến giờ nhằm tăng cường được độ bền, m&agrave; vẫn đảm bảo được trọng lượng c&acirc;n đối đem tới cảm gi&aacute;c v&ocirc; c&ugrave;ng chắc chắn v&agrave; cao cấp.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-23.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Thiết kế khung viền cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-23.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Thiết kế khung viền cao cấp\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhờ đ&oacute;, tổng thể m&aacute;y cũng đ&atilde; mỏng nhẹ hơn khi trọng lượng giảm xuống c&ograve;n 271 g, độ d&agrave;y phần bản lề khi gập lại c&ograve;n 16 mm cho cảm gi&aacute;c cầm nắm, sử dụng điện thoại Z Fold3 giờ đ&acirc;y đ&atilde; thoải m&aacute;i v&agrave; dễ chịu hơn rất nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-2.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Trọng lượng giảm so với tiền nhiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-2.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Trọng lượng giảm so với tiền nhiệm\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi kh&ocirc;ng d&ugrave;ng c&oacute; thể gấp gọn như một cuốn sổ ghi ch&uacute; nhỏ dễ d&agrave;ng bỏ t&uacute;i mang theo b&ecirc;n m&igrave;nh mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102514.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Trạng thái khi gập\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102514.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Trạng thái khi gập\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, với&nbsp;cảm biến v&acirc;n tay&nbsp;ở cạnh b&ecirc;n, người d&ugrave;ng c&oacute; thể nhanh ch&oacute;ng mở kh&oacute;a v&agrave; sử dụng chỉ bằng thao t&aacute;c chạm nhẹ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102533.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Cảm biến vân tay ở cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102533.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Cảm biến vân tay ở cạnh viền\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hệ thống loa k&eacute;p stereo k&iacute;ch thước lớn ở 2 cạnh tr&ecirc;n dưới t&iacute;ch hợp c&ocirc;ng nghệ Dolby Atmos cho trải nghiệm chơi game, xem phim đ&atilde; tai với &acirc;m lượng lớn, r&otilde; r&agrave;ng v&agrave; sống động.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102545.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Hệ thống loa kép, giải trí ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102545.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Hệ thống loa kép, giải trí ấn tượng\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Cụm camera đỉnh cao c&ocirc;ng nghệ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Kế đ&oacute;, cụm camera tr&ecirc;n Galaxy Z Fold3 bao gồm 3 ống k&iacute;nh: Camera g&oacute;c rộng,&nbsp;camera g&oacute;c si&ecirc;u rộng, camera tele&nbsp;t&iacute;ch hợp chống rung OIS v&agrave; đều sở hữu độ ph&acirc;n giải 12 MP đặt trong cụm m&ocirc;-đun h&igrave;nh chữ nhật được bo tr&ograve;n 2 đầu tr&ocirc;ng rất thon gọn v&agrave; đầy tinh tế.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102526.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Cụm camera đỉnh cao công nghệ\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102526.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Cụm camera đỉnh cao công nghệ\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chất lượng h&igrave;nh ảnh theo đ&aacute;nh gi&aacute; nhanh l&agrave; kh&aacute; ổn, m&agrave;u sắc tươi tắn, đặc biệt khả năng xử l&yacute;&nbsp;x&oacute;a ph&ocirc;ng&nbsp;cũng đ&atilde; cải thiện đ&aacute;ng kể, &iacute;t t&igrave;nh trạng bị lem như trước đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Giao diện camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-3.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Giao diện camera\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, m&aacute;y c&ograve;n sở hữu camera trước ở m&agrave;n h&igrave;nh ngo&agrave;i c&oacute; độ ph&acirc;n giải 10 MP v&agrave; camera trước ở m&agrave;n h&igrave;nh trong sẽ được đặt ẩn dưới m&agrave;n h&igrave;nh c&oacute; độ ph&acirc;n giải 4 MP tất cả đều cho chất lượng ảnh tốt ngo&agrave;i mong đợi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-15.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Khả năng chụp ảnh selfie \" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-15.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Khả năng chụp ảnh selfie \" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đặc biệt, c&oacute; chế độ Flex Mode khi gập m&agrave;n h&igrave;nh theo nhiều g&oacute;c độ, c&oacute; thể chia ra 2 m&agrave;n h&igrave;nh khi chụp ảnh, cho ph&eacute;p người d&ugrave;ng vừa chụp vừa xem lại c&aacute;c ảnh đ&atilde; chụp, tr&aacute;nh sự tr&ugrave;ng lặp, cũng dễ hơn trong việc tương t&aacute;c với người hay vật mẫu để điều khiển c&aacute;c tư thế tạo d&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Ảnh chụp trên camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-13.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Ảnh chụp trên camera\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">N&acirc;ng tầm giải tr&iacute; m&agrave;n h&igrave;nh gập với camera ẩn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh ch&iacute;nh tr&ecirc;n Z Fold3 c&oacute; k&iacute;ch thước 7.6 inches, sử dụng tấm nền Dynamic AMOLED 2X cho độ ph&acirc;n giải 1768 x 2208 Pixels hỗ trợ khả năng hiển thị HDR10+ c&ugrave;ng độ s&aacute;ng cao l&ecirc;n tới 1200 nits cho chất lượng hiển thị sắc n&eacute;t, m&agrave;u sắc sống động ch&acirc;n thực trong mọi điều kiện.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102520.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Nâng tầm giải trí màn hình tràn viền với camera ẩn\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102520.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Nâng tầm giải trí màn hình tràn viền với camera ẩn\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Z Fold3 ch&iacute;nh l&agrave; chiếc&nbsp;<a href=\"https://thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone Samsung chính hãng\">điện thoại Samsung</a>&nbsp;đầu ti&ecirc;n sử dụng c&ocirc;ng nghệ camera selfie ẩn dưới m&agrave;n h&igrave;nh gập, đem tới khả năng hiển thị si&ecirc;u tr&agrave;n viền gần như kh&ocirc;ng c&oacute; khiếm khuyết, những t&aacute;c vụ tr&ecirc;n Samsung Galaxy Z Fold3 đều hiển thị một c&aacute;ch trọn vẹn sắc n&eacute;t, từ v&agrave;o mạng x&atilde; hội, lướt web cho đến xem phim, chơi game.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-25.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Màn hình giải trí tràn viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-25.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Màn hình giải trí tràn viền\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, m&agrave;n h&igrave;nh ngo&agrave;i tr&ecirc;n Z Fold3 5G c&oacute; k&iacute;ch thước 6.2 inch cũng sử dụng tấm nền Dynamic AMOLED 2X, độ ph&acirc;n giải 832 x 2268 Pixels v&agrave; thiết kế Infinity-O tr&agrave;n viền, được n&acirc;ng cấp l&ecirc;n tần số qu&eacute;t 120 Hz cho khả năng vuốt chạm mượt m&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102508.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Màn hình trước cũng hỗ trợ tần số quét 120 Hz\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102508.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Màn hình trước cũng hỗ trợ tần số quét 120 Hz\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về độ bền, Samsung tự h&agrave;o tuy&ecirc;n bố Z Fold3 m&agrave;n h&igrave;nh gập của m&igrave;nh được tối ưu h&oacute;a với độ bền hơn 80% so với trước đ&acirc;y c&ugrave;ng khả năng hạn chế c&aacute;c vết trầy xước v&agrave; rơi vỡ ấn tượng nhờ được trang bị k&iacute;nh Ultra Thin Glass (UTG).</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-10.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Màn hình gập trang bị kính Ultra Thin Glass\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-10.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Màn hình gập trang bị kính Ultra Thin Glass\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Z Fold3 cũng hỗ trợ b&uacute;t S Pen lần đầu được &aacute;p dụng tr&ecirc;n điện thoại m&agrave;n h&igrave;nh gập. Mọi thao ghi ch&uacute;, ph&aacute;c thảo hoặc đ&aacute;nh dấu v&agrave;o t&agrave;i liệu đều được thực hiện một c&aacute;ch dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-27.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Hỗ trợ bút S Pen thông minh\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-27.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Hỗ trợ bút S Pen thông minh\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hiệu năng h&agrave;ng đầu với Snapdragon 888</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Z Fold3 được cung cấp sức mạnh bởi chip set Snapdragon 888 cho hiệu năng mạnh mẽ v&agrave; khả năng xử l&yacute; mọi t&aacute;c vụ đều trở n&ecirc;n nhanh ch&oacute;ng mượt m&agrave; tr&ecirc;n cả hai m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Hiệu năng mạnh mẽ hàng đầu\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---8.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Hiệu năng mạnh mẽ hàng đầu\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dung lượng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-12gb\" target=\"_blank\" title=\"Tham khảo điện thoại có dung lượng RAM 12 GB đang bán tại TGDĐ\">RAM 12 GB</a>&nbsp;cho khả năng đa nhiệm tuyệt vời nay lại c&ograve;n t&iacute;ch hợp th&ecirc;m t&iacute;nh năng Multi-Active Window, gi&uacute;p người d&ugrave;ng c&oacute; thể chạy v&agrave; tương t&aacute;c song song giữa hai ứng dụng hoặc chạy tối đa 3 ứng dụng c&ugrave;ng l&uacute;c tr&ecirc;n m&agrave;n h&igrave;nh vừa xem phim, vừa nhắn tin hay đọc b&aacute;o.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---4.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Khả năng đa nhiệm nhiều ứng dụng cùng lúc\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---4.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Khả năng đa nhiệm nhiều ứng dụng cùng lúc\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đi k&egrave;m với đ&oacute; l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-512gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ trong từ 512 GB đang bán tại TGDĐ\">bộ nhớ trong 512 GB</a>&nbsp;cho bạn thoải m&aacute;i lưu trữ dữ liệu để l&agrave;m việc, giải tr&iacute; hay c&agrave;i đặt c&aacute;c ứng dụng nặng m&agrave; kh&ocirc;ng cần lo đến việc đầy bộ nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-18.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Hỗ trợ bộ nhớ trong 512 GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-18.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Hỗ trợ bộ nhớ trong 512 GB\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm hiệu năng đo nhanh bằng AnTuTu đạt hơn 750 ngh&igrave;n điểm, chơi mượt m&agrave; c&aacute;c tựa game nặng k&yacute; như Genshin Impact, Call of Duty Mobile ở thiết lập đồ họa cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Điểm Antutu ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb---7.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Điểm Antutu ấn tượng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, Galaxy Z Fold3 cũng được cải thiện khả năng tản nhiệt khi chơi game, ho&agrave;n to&agrave;n khắc phục được t&igrave;nh trạng n&oacute;ng m&aacute;y như tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y sử dụng chip Snapdragon 888 kh&aacute;c.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Khả năng giải trí chơi game mượt mà\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-8.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Khả năng giải trí chơi game mượt mà\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Z Fold3 cũng được trang bị chuẩn kết nối internet 5G cho khả năng kết nối internet tốc độ si&ecirc;u cao gi&uacute;p bạn c&oacute; thể truy cập sử dụng mạng internet mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thời lượng pin thoải m&aacute;i sử dụng giải tr&iacute;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Về pin, Samsung cho biết Z Fold3 sở hữu pin dung lượng 4.400 mAh, thấp hơn một ch&uacute;t so với người tiền nhiệm nhưng nhu cầu sử dụng vẫn đ&aacute;p ứng tốt trong một ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102552.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Thời lượng pin sử dụng cả ngày\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102552.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Thời lượng pin sử dụng cả ngày\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n canh đ&oacute;, điện thoại cũng hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone sạc pin nhanh\">sạc pin nhanh</a>&nbsp;25 W, sạc kh&ocirc;ng d&acirc;y hay chia sẻ pin kh&ocirc;ng d&acirc;y gi&uacute;p nạp đầy năng lượng cho c&aacute;c thiết bị ngoại vi kh&aacute;c một c&aacute;ch dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102539.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Z Fold3 5G | Khả năng sạc nhanh 25 W\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102539.jpg\" title=\"Samsung Galaxy Z Fold3 5G | Khả năng sạc nhanh 25 W\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nh&igrave;n chung, Galaxy Z Fold 3 mang trong m&igrave;nh một cấu h&igrave;nh &quot;đụng n&oacute;c&quot; trong thế giới&nbsp;Android&nbsp;hiện nay, kết hợp với m&agrave;n h&igrave;nh 120 Hz v&agrave; b&uacute;t S-Pen thần th&aacute;nh c&oacute; thể n&oacute;i đ&acirc;y sẽ l&agrave; thiết bị c&ocirc;ng nghệ phục vụ cho c&ocirc;ng việc hay giải tr&iacute; rất đ&aacute;ng để sở hữu.</span></p>\r\n', 1, '1657436740_samsung-galaxy-z-fold3-5g-xanh-reu-2.jpg', 44990000, 20, 21),
(33, 'Galaxy A13 (4G+128G) A135F ', '<h2><strong><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY A13 (4G+128G) A135F&nbsp;</span></strong></h2>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">4 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">Exynos 850</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.6 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">5000 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật</strong></span></h2>\r\n\r\n<h2><span style=\"font-size:14px\"><strong>Thiết kế hiện đại</strong><br />\r\nMặt lưng m&aacute;y được cấu tạo từ nhựa b&oacute;ng mang đến c&aacute;i nh&igrave;n mới mẻ, &oacute;ng &aacute;nh khi cầm tr&ecirc;n tay, tuy nhi&ecirc;n trường hợp b&aacute;m dấu v&acirc;n tay v&agrave; mồ h&ocirc;i vẫn sẽ xuất hiện tr&ecirc;n thiết kế n&agrave;y, người d&ugrave;ng c&oacute; thể trang bị ốp lưng cho m&aacute;y nhằm khắc phục t&igrave;nh trạng tr&ecirc;n cũng như hạn chế trầy xước trong l&uacute;c sử dụng.<br />\r\nTrang bị 4 camera với camera ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n đến 50 MP, camera g&oacute;c si&ecirc;u rộng 5 MP v&agrave; 2 cảm biến phụ 2 MP gi&uacute;p bạn bắt lại những khoảnh khắc xung quanh một c&aacute;ch r&otilde; n&eacute;t c&ugrave;ng nhiều t&iacute;nh năng chụp ảnh được trang bị.</span></h2>\r\n\r\n<p><img alt=\"Hướng dẫn sử dụng Galaxy A13 5G xuất hiện, xác nhận nhiều thông tin...\" src=\"https://cdn.tgdd.vn/Files/2021/11/20/1399191/galaxy-a13-3_1280x720-800-resize.jpg\" /></p>\r\n\r\n<h3><span style=\"font-size:14px\">Ở phần mặt trước m&aacute;y trang bị camera selfie 8 MP mang đến cho bạn những bức ảnh với độ chi tiết tốt, t&aacute;i tạo v&ugrave;ng da tr&ocirc;ng kh&aacute; nịnh mắt.</span></h3>\r\n\r\n<h3><br />\r\n<span style=\"font-size:14px\"><strong>M&agrave;n h&igrave;nh k&iacute;ch thước lớn</strong><br />\r\nM&aacute;y sử dụng tấm nền&nbsp;PLS TFT LCD c&oacute; k&iacute;ch thước đến 6.6 inch c&ugrave;ng độ ph&acirc;n giải Full HD+, mang lại h&igrave;nh ảnh c&oacute; độ chi tiết cao, t&aacute;i tạo m&agrave;u sắc ch&acirc;n thực c&ugrave;ng kh&ocirc;ng gian hiển thị lớn gi&uacute;p bạn trải nghiệm c&aacute;c t&aacute;c vụ giải tr&iacute; hay thao t&aacute;c c&ocirc;ng việc tốt hơn.<br />\r\nSamsung trang bị cho m&aacute;y k&iacute;nh cường lực Gorilla Glass 5 nhằm hạn chế c&aacute;c vết trầy xước tr&ecirc;n m&agrave;n h&igrave;nh cũng như gia tăng độ bền cho thiết bị.</span></h3>\r\n\r\n<p><img alt=\"Đánh giá Samsung Galaxy A13: Pin tốt, camera ổn nhưng hiệu suất chưa tốt\" src=\"https://reviewed.vn/wp-content/uploads/2022/04/Danh-gia-Samsung-Galaxy-A13-2.5.jpg\" /></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hiệu năng ổn định c&ugrave;ng thời lượng sử dụng l&acirc;u d&agrave;i</strong></span><br />\r\n<span style=\"font-size:14px\">Với hiệu năng ổn định mang đến trải nghiệm mượt m&agrave; xuy&ecirc;n suốt qu&aacute; tr&igrave;nh sử dụng tr&ecirc;n c&aacute;c t&aacute;c vụ phổ th&ocirc;ng h&agrave;ng ng&agrave;y như nhắn tin, đ&agrave;m thoại, xem phim, k&egrave;m với đ&oacute; l&agrave; RAM 4 GB v&agrave; bộ nhớ trong 128 GB gi&uacute;p thiết bị đa nhiệm kh&aacute; tốt c&ugrave;ng kh&ocirc;ng gian lưu trữ đ&aacute;p ứng vừa đủ nhu cầu tải xuống dữ liệu cho người d&ugrave;ng cơ bản.</span></h3>\r\n\r\n<p><img alt=\"Samsung Galaxy A13 For Business | Vodafone UK\" src=\"https://cdn.vodafone.co.uk/en/assets/images/desktop/samsung-galaxy-a13-battery.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Sở hữu vi&ecirc;n pin 5000 mAh mang đến thời gian sử dụng cực kỳ l&acirc;u d&agrave;i m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề hết pin giữa chừng trong l&uacute;c sử dụng. Nhằm r&uacute;t ngắn thời gian sạc đầy vi&ecirc;n pin khủng, h&atilde;ng trang bị cho m&aacute;y c&ocirc;ng nghệ sạc nhanh 15 W.<br />\r\nQua những đ&aacute;nh gi&aacute; tr&ecirc;n cho thấy Samsung Galaxy A13 4G l&agrave; một sản phẩm đ&aacute;p ứng tốt c&aacute;c nhu cầu cơ bản c&ugrave;ng mức gi&aacute; sở hữu lại cực kỳ phải chăng.</span></p>\r\n', 0, '1657258744_a13.jpg', 5190000, 26, 9);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(34, ' Galaxy S22+ S906E 5G (8+128G) Phantom', '<h2><strong><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY S22+ S906E 5G (8+128G) PHANTOM</span></strong></h2>\r\n\r\n<p>Những n&acirc;ng c&acirc;́p lớn v&ecirc;̀ hi&ecirc;̣u năng, dung lượng pin, và tr&ecirc;n h&ecirc;́t là camera sau chính là những đi&ecirc;̉m khi&ecirc;́n n&oacute; trở thành si&ecirc;u phẩm trong ph&acirc;n khúc cao c&acirc;́p. Dưới đ&acirc;y l&agrave; những th&ocirc;ng tin cần biết về d&ograve;ng điện thoại vừa ra mắt của Samsung. Dưới đ&acirc;y l&agrave; th&ocirc;ng số cấu h&igrave;nh của Samsung S22+ vừa được g&atilde; khổng lồ H&agrave;n Quốc giới thiệu.</p>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">8 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Snapdragon 8 Gen 1</span></span></span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.6 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">4500 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n', '<h3 style=\"text-align:start\"><span style=\"font-size:14px\"><strong><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><span style=\"color:#646464\">Thiết kế sang trọng, nhiều m&agrave;u sắc trẻ trung</span></span></span></strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Thiết kế của Galaxy S22 Plus kh&ocirc;ng thay đổi qu&aacute; nhiều so với Galaxy S21 Plus. Khung viền m&aacute;y vẫn được bo tr&ograve;n bốn g&oacute;c đ&uacute;ng theo phong c&aacute;ch d&ograve;ng S. M&agrave;n h&igrave;nh Infinity-O ph&iacute;a trước c&oacute; một lỗ đục nhỏ d&agrave;nh cho camera selfie. Mặt lưng của m&aacute;y đ&atilde; được chuyển sang chất liệu k&iacute;nh thay thay v&igrave; nhựa như thế hệ trước. Đặc biệt, phần mặt lưng n&agrave;y cũng được v&aacute;t phẳng mang lại cảm gi&aacute;c cầm chắc chắn hơn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Cụm camera vẫn l&agrave; điểm nhấn nổi bật nhất ở ph&iacute;a sau. Mặc d&ugrave; thiết kế camera kh&ocirc;ng đổi nhưng c&aacute;ch phối m&agrave;u lại tương đồng với m&agrave;u m&aacute;y hơn. Với sự thay đổi n&agrave;y sẽ thật kh&oacute; để t&igrave;m được điểm ch&ecirc; tr&ecirc;n thiết kế n&agrave;y. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; bốn phi&ecirc;n bản m&agrave;u sắc trẻ trung bao gồm Trắng Phantom, Xanh Zeta, Hồng Blossom v&agrave; Đen Phantom.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><img src=\"https://admin.hoanghamobile.com/Uploads/2022/02/17/galaxy-s22-plus.png\" style=\"max-width:100%\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:start\"><strong><span style=\"font-size:14px\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><span style=\"color:#646464\">M&agrave;n h&igrave;nh Dynamic AMOLED 2X 120Hz mượt m&agrave;</span></span></span></span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Galaxy S22 Plus được trang bị tấm nền Dynamic AMOLED 2X, độ ph&acirc;n giải Full HD+. Tần số qu&eacute;t 120Hz tiếp tục được Samsung đưa l&ecirc;n sản phẩm n&agrave;y nhằm mang đến trải nghiệm mượt m&agrave; nhất cho người d&ugrave;ng. Tần số qu&eacute;t cao cũng mang tới khả năng hiển thị sắc n&eacute;t v&agrave; độ s&aacute;ng tuyệt vời khi xem video, chơi game.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">M&agrave;n h&igrave;nh được bảo vệ bởi k&iacute;nh cường lực Gorilla Glass Victus+. Đ&acirc;y l&agrave; d&ograve;ng k&iacute;nh cường lực cao cấp nhất gi&uacute;p Galaxy S22 Plus chống va đập hiệu quả. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; khả năng kh&aacute;ng bụi v&agrave; kh&aacute;ng khuẩn chuẩn IP68.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><img src=\"https://admin.hoanghamobile.com/Uploads/2022/02/17/galaxy-s22-plus-2.png\" style=\"max-width:100%\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:start\"><strong><span style=\"font-size:14px\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><span style=\"color:#646464\">Hệ thống camera n&acirc;ng cấp, nhiếp ảnh ấn tượng</span></span></span></span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Galaxy S22 Plus sở hữu bộ ba camera với camera ch&iacute;nh 50MP mở rộng tầm nh&igrave;n tối đa, camera g&oacute;c si&ecirc;u rộng 12MP c&ugrave;ng cảm biến si&ecirc;u nhạy s&aacute;ng v&agrave; camera tele 10MP thu ph&oacute;ng quang học 3x. Những n&acirc;ng cấp về cảm biến cũng như thuật to&aacute;n xử l&yacute; ảnh m&agrave; Samsung trang bị cho chiếc smartphone n&agrave;y chắc chắn sẽ mang lại những bức ảnh c&oacute; chất lượng cao.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Galaxy S22 Plus kh&ocirc;ng chỉ sở hữu cảm biến điểm ảnh lớn m&agrave; c&ograve;n c&oacute; lớp k&iacute;nh si&ecirc;u trong giảm l&oacute;e gi&uacute;p chụp ảnh đ&ecirc;m ấn tượng.T&iacute;nh năng Tốc độ khung h&igrave;nh tự động c&ugrave;ng Giải ph&aacute;p Si&ecirc;u Chụp đ&ecirc;m hỗ trợ giảm nhiễu tr&ecirc;n từng khung h&igrave;nh mang đến những thước phim m&atilde;n nh&atilde;n.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><img src=\"https://admin.hoanghamobile.com/Uploads/2022/02/17/galaxy-s22-plus-3.png\" style=\"max-width:100%\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:start\"><strong><span style=\"font-size:14px\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><span style=\"color:#646464\">Vi xử l&yacute; Snapdragon 8 Gen 1 hiệu năng mạnh mẽ h&agrave;ng đầu</span></span></span></span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Kh&aacute;c với mọi năm, Samsung thường sử dụng d&ograve;ng chip Exynos cho d&ograve;ng flagship Galaxy S, nhưng năm nay đ&atilde; thay bằng Snapdragon 8 Gen 1. V&igrave; được sản xuất tr&ecirc;n tiến tr&igrave;nh 4nm ti&ecirc;n tiến n&ecirc;n con chip n&agrave;y sẽ mang đến hiệu năng vượt trội. Đi k&egrave;m với đ&oacute; l&agrave; dung lượng RAM 8GB v&agrave; hai lựa chọn bộ nhớ trong 128GB v&agrave; 256GB. Vậy n&ecirc;n người d&ugrave;ng c&oacute; thể thoải m&aacute;i lưu trữ dữ liệu, h&igrave;nh ảnh, video.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><img src=\"https://admin.hoanghamobile.com/Uploads/2022/02/17/galaxy-s22-plus-4.png\" style=\"max-width:100%\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:start\"><strong><span style=\"font-size:14px\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\"><span style=\"color:#646464\">Dung lượng pin 4500mAh, hỗ trợ sạc nhanh 45W</span></span></span></span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Samsung đ&atilde; trang bị cho Galaxy S22 Plus vi&ecirc;n pin 4500mAh v&agrave; t&iacute;ch hợp sạc nhanh l&ecirc;n tới 45W. Nhờ đ&oacute;, người d&ugrave;ng c&oacute; thể sử dụng trong cả ng&agrave;y d&agrave;i v&agrave; sạc đầy pin nhanh ch&oacute;ng. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; c&ocirc;ng nghệ pin th&ocirc;ng minh th&iacute;ch ứng với từng hoạt động để tối ưu thời gian sử dụng tới 24 giờ. Đặc biệt, chiếc smartphone n&agrave;y c&ograve;n c&oacute; thể sạc kh&ocirc;ng d&acirc;y cho thiết bị kh&aacute;c.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Smart Switch l&agrave; một ứng dụng của Samsung c&oacute; thể được sử dụng để c&agrave;i đặt c&aacute;c bản cập nhật phần mềm, hoặc sao lưu v&agrave; kh&ocirc;i phục nội dung giữa c&aacute;c điện thoại. Ứng dụng di động Smart Switch cũng c&oacute; thể chuyển danh bạ, ảnh v&agrave; tin nhắn&nbsp;từ thiết bị iOS&nbsp;sang&nbsp;<a href=\"https://hoanghamobile.com/dien-thoai-di-dong/samsung/samsung-galaxy-s22-Series\" style=\"text-decoration:none; color:#00483d\">điện thoại Galaxy mới</a>&nbsp;của bạn.<img alt=\"Hoàng Hà Mobile\" class=\"alignnone size-full wp-image-158798\" src=\"https://cdn1.hoanghamobile.com/tin-tuc/wp-content/uploads/2022/03/Hoang-Ha-Mobile-5.png\" style=\"display:inline-block; height:628px; margin-left:5px; margin-right:5px; max-width:100%; width:1200px\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">C&aacute;c dịch vụ của Google c&oacute; thể hỗ trợ sao lưu một phần dữ liệu, tuy nhi&ecirc;n việc sao lưu dữ liệu ứng dụng hay tr&ograve; chơi sẽ kh&oacute; c&oacute; thể thực hiện. V&igrave; thế, Samsung Smart Switch l&agrave; một&nbsp;phương &aacute;n v&ocirc; c&ugrave;ng tối ưu.&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">Galaxy S22 Plus kh&ocirc;ng chỉ sang trọng m&agrave; c&ograve;n mang lại những ph&uacute;t gi&acirc;y l&agrave;m việc, giải tr&iacute; cực đ&atilde; cho người d&ugrave;ng. Nếu bạn cần t&igrave;m một flagship Android cao cấp th&igrave; đ&acirc;y l&agrave; một sự lựa chọn kh&ocirc;ng thể hợp l&yacute; hơn.&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#333333\"><span style=\"font-family:-apple-system,BlinkMacSystemFont,\"><span style=\"background-color:#ffffff\">&nbsp;</span></span></span></span></p>\r\n', 0, '1657261899_s22.jpg', 26490000, 28, 11),
(35, 'Galaxy Note 20 Ultra ', '<h2><span style=\"font-size:16px\"><strong>Mua Samsung Galaxy Note 20 Ultra&nbsp;gi&aacute; rẻ ch&iacute;nh h&atilde;ng tại MediaMart</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">Với những th&agrave;nh c&ocirc;ng m&agrave; Samsung đ&atilde; gặt h&aacute;i được từ c&aacute;c thế hệ Galaxy Note đi trước, &ocirc;ng lớn tới từ H&agrave;n Quốc lại tiếp tục l&agrave;m cho người h&acirc;m mộ một lần nữa phải ph&aacute;t cuồng v&igrave; thế hệ Galaxy Note 20 cao cấp của năm 2020. Người d&ugrave;ng giờ đ&acirc;y sẽ được trải nghiệm tất cả tinh hoa của nh&agrave; sản xuất điện thoại tới từ H&agrave;n Quốc, c&ugrave;ng tập hợp lại tr&ecirc;n si&ecirc;u phẩm Galaxy Note 20 Ultra cuối năm nay. H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; những điểm cải tiến cực đ&aacute;ng gi&aacute; tr&ecirc;n si&ecirc;u phẩm d&ograve;ng Note n&agrave;y nh&eacute;!</span></p>\r\n', '<h2><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>M&agrave;n h&igrave;nh 2K+ Dynamic AMOLED 2X&nbsp;tr&agrave;n viền ho&agrave;n hảo, thiết kế thời thượng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh tr&agrave;n viền g&oacute;c cạnh tối đa, kế thừa những đặc t&iacute;nh từ thế hệ trước, c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;Dynamic AMOLED 2X cho gam m&agrave;u ch&iacute;nh x&aacute;c chuẩn điện ảnh cho bạn những trải nghiệm những thước phim ch&acirc;n thật tr&ecirc;n ch&iacute;nh smarphone&nbsp;của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, bằng c&aacute;ch giảm thiểu tối đa &aacute;nh s&aacute;ng xanh g&acirc;y hại, Dynamic AMOLED 2X c&ograve;n gi&uacute;p hạn chế t&igrave;nh trạng mỏi mắt gi&uacute;p tối ưu trải nghiệm của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/Slider/-samsung-galaxy-note-20-ultra-5g-slider.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Giới hạn khuyết điểm m&agrave;n h&igrave;nh ở mức tối đa cũng mang đến tỷ lệ hiển thị cao nhất, cao hơn so với những smartphone&nbsp;thiết kế tai thỏ hay giọt nước đ&atilde; qu&aacute; phổ biến tr&ecirc;n thị trường, m&aacute;y cũng sở hữu mặt k&iacute;nh cảm ứng Corning Gorilla Glass Victus&nbsp;bền bỉ, chắc chắn với khả năng giảm thiểu trầy xước cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt trước v&acirc;̃n l&agrave; camera selfie&nbsp;c&oacute; độ ph&acirc;n giải l&ecirc;n đến 10 MP đặt ở vị trí trung t&acirc;m, tuy nhi&ecirc;n n&ecirc;́u đ&ecirc;̉ ý kỹ bạn sẽ th&acirc;́y kích thước n&ocirc;́t ru&ocirc;̀i đã được t&ocirc;́i giản và tr&ocirc;ng nhỏ hơn so với phi&ecirc;n bản ti&ecirc;̀n nhi&ecirc;̣m.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Camera nốt ruồi, tối giản màn hình cực rộng - Samsung Galaxy Note 20 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra7.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hiệu năng mạnh mẽ với phần cứng được n&acirc;ng cấp</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tất nhi&ecirc;n một smartphone với thiết kế đỉnh cao th&igrave; sẽ kh&ocirc;ng thiếu đi sức mạnh phần cứng, Galaxy Note 20 Ultra sử dụng con chip Exynos 990 8 nh&acirc;n mạnh mẽ, đảm nhiệm gần như ho&agrave;n hảo mọi t&aacute;c vụ từ lướt web, xem phim, ph&aacute;t họa h&igrave;nh ảnh cho đến chơi game hay edit video độ ph&acirc;n giải cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Sử dụng phát họa trên máy - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra4.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với bộ nhớ RAM 8 GB, 256 GB ROM th&igrave; m&aacute;y c&oacute; thể thừa sức đa nhiệm tốt mọi t&aacute;c vụ v&agrave; lưu trữ số lượng lớn h&igrave;nh ảnh hay video, t&agrave;i liệu nặng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cảm biến v&acirc;n tay si&ecirc;u &acirc;m&nbsp;dưới m&agrave;n h&igrave;nh được Samsung&nbsp;trang bị tr&ecirc;n&nbsp;Galaxy Note 20 Ultra l&agrave; c&ocirc;ng nghệ cảm biến v&acirc;n tay hiện đại c&oacute; thể thao t&aacute;c mở kh&oacute;a nhanh ch&oacute;ng chỉ với 1 lần chạm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Thiết lập mật khẩu vân tay trên máy - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra5.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>B&uacute;t S Pen mới, được n&acirc;ng cấp nhiều t&iacute;nh năng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">B&uacute;t S Pen tr&ecirc;n Galaxy Note 20 Ultra c&oacute; sự thay đổi sang vị tr&iacute; b&ecirc;n tr&aacute;i thay v&igrave; b&ecirc;n phải như trước đ&acirc;y, độ trễ được giảm đi nhiều lần so với phi&ecirc;n bản tiền nhiệm, mang lại trải nghiệm mượt m&agrave; hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng những c&oacute; t&iacute;nh năng hoạt động độc lập như tr&ecirc;n Galaxy Note 10, S Pen tr&ecirc;n Note 20 Ultra c&ograve;n được n&acirc;ng cấp để c&oacute; thể tương t&aacute;c thực tế ảo trực tiếp, điều khiển cử chỉ th&ocirc;ng minh hơn nhờ v&agrave;o loạt cảm biến ho&agrave;n to&agrave;n mới được Samsung &aacute;p dụng l&ecirc;n trong năm nay.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Cận cảnh bút S-Pen - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra6.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Camera độc đ&aacute;o, từ thiết kế đến t&iacute;nh năng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Note 20 Ultra với những n&acirc;ng cấp về cảm biến ảnh, camera ch&iacute;nh&nbsp;c&oacute; độ ph&acirc;n giải l&ecirc;n đến 108 MP bổ sung lấy n&eacute;t Laser AF, khắc phục những hạn chế trong lấy n&eacute;t ở những d&ograve;ng Note trước.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Camera chụp ảnh siêu sắc nét - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Camera tele c&oacute; độ ph&acirc;n giải l&agrave; 12 MP với khả năng zoom xa&nbsp;tối đa l&ecirc;n đến 50x, camera g&oacute;c si&ecirc;u rộng&nbsp;c&oacute; độ ph&acirc;n giải l&agrave; 12 MP, đ&acirc;y l&agrave; một cảm biến cho khả năng chụp những những bức ảnh phong cảnh nghệ thuật với g&oacute;c rộng hơn bao giờ hết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Hình ảnh chụp bằng camera thường - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-062020-022016.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Pin dung lượng lớn, cho trải nghiệm cả ng&agrave;y d&agrave;i</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Sở hữu vi&ecirc;n pin lớn 4500 mAh, Note 20 Ultra mang lại cho người d&ugrave;ng thời gian d&agrave;i sử dụng, b&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n được hỗ trợ c&ocirc;ng nghệ sạc nhanh&nbsp;gi&uacute;p cho thời gian sạc pin được r&uacute;t ngắn đến mức tối thiểu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra-214720-114735.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '1657436716_glass-samsung-galaxy-note-20-1.jpg', 29990000, 5, 10),
(36, 'Galaxy Note 20', '<h2><span style=\"font-size:16px\"><strong>Samsung Note 20 &ndash; M&agrave;n h&igrave;nh 2K tuyệt hảo v&agrave; b&uacute;t S-Pen cho trải nghiệm tiện &iacute;ch</strong></span></h2>\r\n\r\n<p>Sau h&agrave;ng loạt th&agrave;nh c&ocirc;ng từ những thế hệ&nbsp;Galaxy Note trước, Samsung lại tiếp tục khiến cộng đồng c&ocirc;ng nghệ phải ph&aacute;t cuồng với si&ecirc;u phẩm tiếp theo của m&igrave;nh &ndash;&nbsp;<strong>Samsung Galaxy Note 20</strong>. Si&ecirc;u phẩm n&agrave;y hứa hẹn sẽ mang đến cho người d&ugrave;ng những trải nghiệm vượt trội hơn, mạnh mẽ hơn với một thiết kế tinh tế, ho&agrave;n hảo.</p>\r\n', '<h3><span style=\"font-size:16px\"><strong>T&iacute;nh năng nối bật</strong></span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thiết kế kim loại nguy&ecirc;n khối v&agrave; mặt lưng k&iacute;nh cường lực tinh tế, sang trọng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">C&oacute; thể n&oacute;i, những thiết kế của Samsung lu&ocirc;n khiến cộng đồng đam m&ecirc; c&ocirc;ng nghệ phải say m&ecirc; khi v&ocirc; c&ugrave;ng tinh tế c&ugrave;ng với những gam m&agrave;u sang trọng. Thế hệ Samsung Galaxy Note năm 2020 sở hữu thiết kế kim loại nguy&ecirc;n khối, mặt lưng được l&agrave;m từ k&iacute;nh cường lực gi&uacute;p tăng độ b&oacute;ng bẩy cho smartphone, mang đến vẻ ngo&agrave;i đẳng cấp. Note 20 c&oacute; kiểu d&aacute;ng mạnh mẽ v&agrave; c&oacute; phần nam t&iacute;nh hơn với những g&oacute;c cạnh vu&ocirc;ng vức, mang đến cảm gi&aacute;c cầm tay chắc chắn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-235720-125702.jpg\"><img alt=\"Thiết kế mặt lưng nhám sang trọng - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-235720-125702.jpg\" title=\"Thiết kế mặt lưng nhám sang trọng - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Camera ch&iacute;nh với cảm biến cực khủng 64MP hỗ trợ nhiều t&iacute;nh năng chụp ảnh</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Camera của Samsung Galaxy Note 20 được n&acirc;ng cấp khi độ cảm biến của camera ch&iacute;nh được n&acirc;ng l&ecirc;n đến th&ocirc;ng số cực khủng 64MP. Với độ cảm biến 64MP chắc chắn bạn sẽ c&oacute; được những tấm ảnh v&ocirc; c&ugrave;ng sắc n&eacute;t v&agrave; xinh đẹp. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n hỗ trợ cho người d&ugrave;ng những t&iacute;nh năng chụp ảnh, quay phim th&ocirc;ng minh v&agrave; th&uacute; vị như quay si&ecirc;u chậm, Time Lapse, quay chậm, x&oacute;a ph&ocirc;ng, chụp ảnh g&oacute;c rộng v&agrave; g&oacute;c si&ecirc;u rộng, chống rung quang học,&hellip; để bạn c&oacute; thể ghi lại mọi khoảnh khắc một c&aacute;ch chuy&ecirc;n nghiệp, tạo ra những tấm ảnh v&ocirc; c&ugrave;ng ấn tượng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-235520-015514.jpg\"><img alt=\"Cụm 3 camera sau - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-235520-015514.jpg\" title=\"Cụm 3 camera sau - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, camera selfie với độ cảm biến cao hỗ trợ quay video Full HD, l&agrave;m đẹp Beautify, Flash m&agrave;n h&igrave;nh,&hellip; sẽ gi&uacute;p bạn c&oacute; được những tấm ảnh selfie xinh đẹp, sắc n&eacute;t v&agrave; bạn c&oacute; thể sử dụng ch&iacute;nh chiếc điện thoại của m&igrave;nh quay những vlog hằng ng&agrave;y để chia sẻ với mọi người.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-230020-020036.jpg\"><img alt=\"Camera trước 10 MP - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-230020-020036.jpg\" title=\"Camera trước 10 MP - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>B&uacute;t S Pen nhiều tiện &iacute;ch, được n&acirc;ng cấp nhiều t&iacute;nh năng hơn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">S Pen được xem l&agrave; linh hồn của d&ograve;ng&nbsp;Galaxy Note,&nbsp;si&ecirc;u tiện lợi hỗ trợ ghi ch&uacute; nhanh v&agrave; đa t&aacute;c vụ th&ocirc;ng minh tr&ecirc;n điện thoại như chụp ảnh&nbsp;m&agrave;n h&igrave;nh, chụp cắt kh&ocirc;ng gian bất kỳ tr&ecirc;n m&agrave;n h&igrave;nh, tạo ghi ch&uacute;, ph&oacute;ng to / thu nhỏ c&ugrave;ng nhiều t&aacute;c vụ tiện lợi kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điểm nổi bật của d&ograve;ng Samsung Galaxy Note ch&iacute;nh l&agrave; b&uacute;t S-Pen si&ecirc;u tiện &iacute;ch với những hỗ trợ tuyệt vời cho người d&ugrave;ng trong qu&aacute; tr&igrave;nh sử dụng v&agrave;&nbsp;<strong>Note 20</strong>&nbsp;cũng kh&ocirc;ng ngoại lệ. B&uacute;t S-Pen được xem l&agrave; linh hồn của những chiếc Galaxy Note, gi&uacute;p bạn c&oacute; thể ghi ch&eacute;p th&ocirc;ng tin một c&aacute;ch nhanh ch&oacute;ng, đơn giản. B&ecirc;n cạnh đ&oacute;, b&uacute;t S-Pen c&ograve;n hoạt động như một chiếc điều khiển từ xa để bạn c&oacute; thể điều khiển smartphone của m&igrave;nh, chụp ảnh từ xa v&ocirc; c&ugrave;ng tiện lợi.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-125720-105754.jpg\"><img alt=\"Bút S Pen kèm theo máy - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-125720-105754.jpg\" title=\"Bút S Pen kèm theo máy - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>M&agrave;n h&igrave;nh lớn 6.7 inch sử dụng c&ocirc;ng nghệ Dynamic AMOLED v&agrave; độ ph&acirc;n giải FullHD</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh 6.7 inch mở ra g&oacute;c nh&igrave;n lớn hơn, rộng hơn để bạn c&oacute; thể tận hưởng mọi thước phim, &ldquo;chiến&rdquo; game, xử l&yacute; h&igrave;nh ảnh, video,&hellip; ho&agrave;n hảo. Samsung lu&ocirc;n sử dụng những c&ocirc;ng nghệ ti&ecirc;n tiến nhất cho c&aacute;c sản phẩm smartphone của m&igrave;nh khi trang bị cho Note 20 m&agrave;n h&igrave;nh&nbsp;Dynamic AMOLED&nbsp;&ndash; m&agrave;n h&igrave;nh tốt nhất của Samsung. M&agrave;n h&igrave;nh Dynamic AMOLED với khả năng tự động điều chỉnh m&agrave;u sắc, 16 triệu m&agrave;u v&agrave; 100% dải m&agrave;u gi&uacute;p t&aacute;i tạo m&agrave;u sắc ch&acirc;n thực, ch&iacute;nh x&aacute;c, gần với thực tế để mang đến khung h&igrave;nh sống động, tuyệt vời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-125820-105803.jpg\"><img alt=\"Màn hình hiển thị siêu rõ nét - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-125820-105803.jpg\" title=\"Màn hình hiển thị siêu rõ nét - Samsung Galaxy Note 20\" /></a>B&ecirc;n cạnh đ&oacute;, m&agrave;n h&igrave;nh&nbsp;<strong>Samsung Note 20</strong>&nbsp;với độ ph&acirc;n giải FullHD v&agrave; tỉ lệ m&agrave;n h&igrave;nh 19:9 cho mật độ điểm ảnh lớn, mang đến kh&ocirc;ng gian hiển thị sắc n&eacute;t. Ngo&agrave;i ra, với thiết kế m&agrave;n h&igrave;nh đục lỗ sẽ gi&uacute;p mở rộng viền m&agrave;n h&igrave;nh đến tối đa để bạn c&oacute; những trải nghiệm h&igrave;nh ảnh rộng hơn, tuyệt vời hơn.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Bảo mật v&acirc;n tay tr&ecirc;n m&agrave;n h&igrave;nh nhanh ch&oacute;ng v&agrave; tiện lợi</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Note 20 được trang bị cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh v&agrave; nhận diện gương mặt gi&uacute;p mở kh&oacute;a smartphone nhanh hơn, kh&ocirc;ng cần phải nhập m&atilde; mỗi khi sử dụng điện thoại. B&ecirc;n cạnh đ&oacute;, cảm biến v&acirc;n tay v&agrave; gương mặt c&ograve;n c&oacute; t&iacute;nh bảo mật cao, gi&uacute;p bảo vệ c&aacute;c th&ocirc;ng tin ri&ecirc;ng tư của bạn an to&agrave;n hơn, kh&ocirc;ng sợ bị lộ mật khẩu điện thoại.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-125820-105811.jpg\"><img alt=\"Cài đặt bảo mật vân tay - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-125820-105811.jpg\" title=\"Cài đặt bảo mật vân tay - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Hiệu năng mạnh mẽ chuẩn flagship</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Note 20 c&ograve;n c&oacute; hiệu năng vượt trội với tốc độ xử l&yacute; mạnh mẽ, xử l&yacute; mọi thao t&aacute;c t&aacute;c vụ một c&aacute;ch nhanh ch&oacute;ng nhờ chip xử l&yacute; Snapdragon 865/Exynos 990 v&agrave; RAM tới 8GB v&ocirc; c&ugrave;ng ấn tượng. Ở phi&ecirc;n bản US, điện thoại sẽ sở hữu con chip Snapdragon 865 v&agrave; ở c&aacute;c phi&ecirc;n bản c&ograve;n lại, chiếc si&ecirc;u phẩm n&agrave;y sẽ được trang bị con chip&nbsp;Exynos 990. Cả hai con chip n&agrave;y đều được sản xuất theo quy tr&igrave;nh 7nm v&agrave; c&oacute; khả năng hoạt động tương đương nhau để mang đến hiệu suất hoạt động như nhau ở cả 2 phi&ecirc;n bản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-230420-020400.jpg\"><img alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-230420-020400.jpg\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Pin ở mức 43000mAh hỗ trợ sạc nhanh 25W v&agrave; sạc kh&ocirc;ng d&acirc;y</span>&nbsp;</strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Với một chiếc si&ecirc;u phẩm như Samsung Note 20, việc trang bị một vi&ecirc;n pin c&oacute; dung lượng khủng để gi&uacute;p người d&ugrave;ng trải nghiệm smartphone l&acirc;u hơn l&agrave; điều v&ocirc; c&ugrave;ng cần thiết. Ch&iacute;nh v&igrave; thế m&agrave; smartphone sở hữu vi&ecirc;n pin dung lượng ở mức 4300mAh để người d&ugrave;ng c&oacute; thể sử dụng li&ecirc;n tục suốt ng&agrave;y d&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/images/42/218355/samsung-galaxy-note-20-230520-020535.jpg\"><img alt=\"Pin lớn thoả sức sử dụng cả ngày dài - Samsung Galaxy Note 20\" src=\"https://cdn.tgdd.vn/Products/Images/42/218355/samsung-galaxy-note-20-230520-020535.jpg\" title=\"Pin lớn thoả sức sử dụng cả ngày dài - Samsung Galaxy Note 20\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hỗ trợ sạc nhanh 25W cung cấp năng lượng nhanh ch&oacute;ng, gi&uacute;p nạp nhanh pin cho smartphone để smartphone lu&ocirc;n sẵn s&agrave;ng mỗi khi sử dụng. Ngo&agrave;i ra, Samsung Galaxy Note 20 c&ograve;n hỗ trợ sạc kh&ocirc;ng d&acirc;y để bạn c&oacute; thể sạc pin tiện lợi hơn khi kh&ocirc;ng c&oacute; củ sạc.</span></p>\r\n', 0, '1657436862_ce593674025f851b260a3de7facac8cb.jpg', 23900000, 35, 10),
(37, 'Galaxy A23 (4G+128G) A235F ', '<h2><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY A23 (4G+128G) A235F&nbsp;</span></h2>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">4 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">Snapdragon 680</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.6 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">5000 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n', '<h3><span style=\"font-size:14px\"><strong>Thiết kế đặc trưng Galaxy A</strong><br />\r\nVới hai mặt được v&aacute;t tương đối phẳng mang đến thiết kế hiện đại bắt kịp xu hướng, c&ugrave;ng tone m&agrave;u trẻ trung v&agrave; nhiều phi&ecirc;n bản m&agrave;u sắc nhằm đa dạng sự lựa chọn đến kh&aacute;ch h&agrave;ng.</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Samsung Galaxy A23 giá bao nhiêu? Có nên mua không? | websosanh.vn\" src=\"https://img.websosanh.vn/v2/users/review/images/0ot2ibz9li4di.jpg?compress=85\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trang bị m&agrave;n h&igrave;nh Full HD+ c&oacute; k&iacute;ch thước đến 6.6 inch gi&uacute;p thiết bị hiển thị h&igrave;nh ảnh với độ chi tiết cao, kh&ocirc;ng gian hiển thị rộng lớn mang đến trải nghiệm ch&acirc;n thực tr&ecirc;n c&aacute;c t&aacute;c vụ giải tr&iacute; hay thao t&aacute;c c&ocirc;ng việc một c&aacute;ch nhanh ch&oacute;ng tr&ecirc;n m&agrave;n h&igrave;nh rộng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Harga dan Spesifikasi Samsung Galaxy A23, Hadirkan Quad Camera 50MP untuk  Hasil Foto Jernih - Tribunnews.com Mobile\" src=\"https://cdn-2.tstatic.net/tribunnews/foto/bank/images/samsung-galaxy-a23.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Chụp ảnh sắc n&eacute;t</strong><br />\r\nM&aacute;y sở hữu ở phần mặt lưng 4 camera với camera ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n đến 50 MP mang đến cho bạn c&aacute;c bức h&igrave;nh sắc n&eacute;t, giảm thiểu t&igrave;nh trạng vỡ n&eacute;t khi chỉnh sửa ảnh. Ngo&agrave;i ra m&aacute;y c&ograve;n được trang bị nhiều t&iacute;nh năng chụp ảnh hiện đại, gi&uacute;p bạn c&oacute; được những bức ảnh đầy nghệ thuật.</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Samsung Galaxy A23 LTE Awesome Black Specs 128GB | Samsung Philippines\" src=\"https://images.samsung.com/is/image/samsung/p6pim/ph/feature/163975450/ph-feature-ois--finally-in-the-galaxy-a23-531675088?$FB_TYPE_A_MO_JPG$\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Trải nghiệm mượt m&agrave;</strong><br />\r\nVới hiệu suất tối đa đạt được v&agrave;o khoảng 2.4 GHz nhờ trang bị vi xử l&yacute;&nbsp;Snapdragon 680 8 nh&acirc;n gi&uacute;p bạn thao t&aacute;c mượt m&agrave; tr&ecirc;n mọi t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y hay hỗ trợ chơi được c&aacute;c tựa game hiện h&agrave;nh.<br />\r\nM&aacute;y trang bị 4 GB RAM c&ugrave;ng 128 GB bộ nhớ trong mang đến khả năng đa nhiệm một c&aacute;ch mượt m&agrave; c&ugrave;ng kh&ocirc;ng gian lưu trữ tương đối lớn, đ&aacute;p ứng vừa đủ cho người d&ugrave;ng cơ bản.<br />\r\nĐ&aacute;p ứng nhu cầu sử dụng cả ng&agrave;y<br />\r\nB&ecirc;n trong thiết bị l&agrave; vi&ecirc;n pin c&oacute; dung lượng 5000 mAh thừa sức đ&aacute;p ứng nhu cầu sử dụng li&ecirc;n tục trong nhiều giờ của bạn.<br />\r\nSamsung&nbsp;trang bị cho m&aacute;y c&ocirc;ng nghệ sạc pin nhanh 25 W nhằm tối ưu thời gian sạc cho bạn, người d&ugrave;ng n&ecirc;n sạc thiết bị v&agrave;o những l&uacute;c nghỉ ngơi nhằm tr&aacute;nh mất thời gian chờ đợi, tuy nhi&ecirc;n trong hộp chỉ được trang bị sạc 15 W.</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Review of Samsung Galaxy A23, 6.6-inch large screen, 90Hz, new chip,  Snapdragon 680, more powerful than before, 4 rear cameras, 50MP (OIS), 5000  mAh battery - newsdirectory3.com - News Directory 3\" src=\"https://i0.wp.com/www.flashfly.net/wp/wp-content/uploads/2022/04/22-Samsung-Galaxy-A23-Review.jpg?resize=800%2C450&amp;ssl=1\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Qua những đ&aacute;nh gi&aacute; tr&ecirc;n cho thấy Samsung Galaxy A23 đ&aacute;p ứng tốt khả năng xử l&yacute; tốt c&aacute;c nhu cầu sử dụng cơ bản h&agrave;ng ng&agrave;y, thiết kế bắt mắt c&ugrave;ng thời lượng sử dụng l&acirc;u d&agrave;i, thực sự l&agrave; một sự lựa chọn đ&aacute;ng quan t&acirc;m trong ph&acirc;n kh&uacute;c điện thoại tầm trung</span></p>\r\n', 0, '1657261034_a23.jpg', 6190000, 21, 9),
(38, 'Galaxy A33 A336E 5G ', '<h2><span style=\"font-size:16px\">GIỚI THIỆU:&nbsp;ĐIỆN THOẠI SAMSUNG GALAXY A33 A336E 5G&nbsp;</span></h2>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">RAM</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">CPU</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Bộ Nhớ</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">M&agrave;n H&igrave;nh</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-size:10.5pt\">Dung lượng pin</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">Exynos 1280</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">128 GB</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">6.4 inch</span></strong></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><strong><span style=\"font-size:10.5pt\">5000 mAh</span></strong></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style=\"font-size:16px\">T&iacute;nh năng nổi bật</span></h2>\r\n', '<h3><span style=\"font-size:14px\"><strong>Ngoại h&igrave;nh trẻ trung, nhiều m&agrave;u sắc</strong><br />\r\nSamsung Galaxy A33 5G sở hữu thiết kế trẻ trung, khung viền nh&aacute;m c&ugrave;ng t&ocirc;ng m&agrave;u với mặt lưng tạo cảm gi&aacute;c liền mạch. Thiết bị c&oacute; 4 t&ugrave;y chọn m&agrave;u sắc c&aacute; t&iacute;nh gồm: trắng, hồng, xanh dương v&agrave; đen. Mặt trước của m&aacute;y l&agrave; m&agrave;n h&igrave;nh&nbsp;Super AMOLED 6.4 inch, hỗ trợ tần số qu&eacute;t 90 Hz v&agrave; được bảo vệ bởi&nbsp;k&iacute;nh Gorilla Glass 5.</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/a239c348-7549-4859-9c6d-74093a6f9d36.jpg\" /></span><br />\r\n&nbsp;</h3>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>4 camera hỗ trợ chụp ảnh chuy&ecirc;n nghiệp</strong><br />\r\nVề camera th&igrave; Galaxy A33 5G sở hữu đến 4 ống k&iacute;nh ph&iacute;a sau bao gồm: Camera ch&iacute;nh 48 MP c&oacute; chống rung quang học OIS, camera g&oacute;c si&ecirc;u rộng 8 MP, camera macro 5 MP v&agrave; camera đo độ s&acirc;u 2 MP. M&aacute;y cũng c&oacute; camera selfie 13 MP ở mặt trước.</span></h3>\r\n\r\n<h3><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/c9d53fcd-360d-41dd-a856-5336acbb20f5.jpg\" /></span><br />\r\n&nbsp;</h3>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Cấu h&igrave;nh kh&ocirc;ng l&agrave;m bạn thất vọng</strong><br />\r\nCung cấp sức mạnh cho m&aacute;y l&agrave; con chip Exynos 1280, đảm bảo &quot;c&acirc;n&quot; được c&aacute;c tựa game mobile phổ biến hiện nay (03/2022), cũng như gi&uacute;p người d&ugrave;ng lướt web, xem YouTube thoải m&aacute;i.&nbsp;Chiếc điện thoại cũng sẽ c&oacute; vi&ecirc;n pin 5000 mAh cho thời lượng sử dụng l&ecirc;n đến 2 ng&agrave;y, sạc nhanh 25 W v&agrave; đặc biệt l&agrave; hỗ trợ 5G.</span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/48c5578f-3580-4b5c-8458-35be9795faf0.jpg\" /><br />\r\nNh&igrave;n chung th&igrave; Samsung Galaxy A33 l&agrave; chiếc smartphone tầm trung ph&ugrave; hợp với nhiều người, hiệu năng v&agrave; c&aacute;c t&iacute;nh năng của m&aacute;y đảm bảo sẽ kh&ocirc;ng l&agrave;m bạn thất vọng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img src=\"https://mediamart.vn/images/uploads/2022/87add809-380f-445c-b60b-727e4a5d3084.jpg\" /></span></p>\r\n', 0, '1657261350_a33.jpg', 8990000, 27, 9);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(39, 'Galaxy S21 Ultra 5G ', '<h2><span style=\"font-size:16px\"><strong>Giới thiệu chi tiết Samsung Galaxy S21 Ultra 5G</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc điện thoại đang nhận được rất nhiều sự quan t&acirc;m từ c&aacute;c t&iacute;n đồ c&ocirc;ng nghệ bởi hội tụ h&agrave;ng loạt t&iacute;nh năng hiện đại. Trong số c&aacute;c model thuộc d&ograve;ng&nbsp;Galaxy S21&nbsp;th&igrave; Galaxy S21 Ultra l&agrave; phi&ecirc;n bản cao cấp nhất</span></p>\r\n\r\n<table class=\"Table\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">RAM</span></span></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">CPU</span></span></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">Bộ Nhớ</span></span></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">M&agrave;n H&igrave;nh</span></span></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">Dung lượng pin</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">12 GB</span></span></strong></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">Exynos 2100 8 nh&acirc;n</span></span></strong></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">256 GB</span></span></strong></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">6.8 inch</span></span></strong></span></span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:left\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">5000 mAh</span></span></strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>T&iacute;nh năng nổi bật</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">.</span></p>\r\n', '<h3><span style=\"font-size:14px\"><strong>Thiết kế t&aacute;o bạo, dẫn đầu xu hướng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy S21 Ultra 5G sở hữu thiết kế mang t&iacute;nh đột ph&aacute;, t&aacute;o bạo với d&agrave;n m&aacute;y ảnh mặt sau được bố tr&iacute; tinh tế trong khung viền h&igrave;nh chữ nhật &ocirc;m trọn cạnh tr&aacute;i của th&acirc;n m&aacute;y. &Yacute; tưởng thiết kế n&agrave;y gi&uacute;p S21 Ultra 5G tự tin sở hữu d&aacute;ng vẻ độc nhất v&ocirc; nhị giữa h&agrave;ng ng&agrave;n smartphone tr&ecirc;n thị trường hiện nay.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Thiết kế táo bạo, dẫn đầu xu hướng\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533.jpg\" title=\"Galaxy S21 Ultra 5G | Thiết kế táo bạo, dẫn đầu xu hướng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc điện thoại l&agrave; mẫu flagship S đầu ti&ecirc;n của Samsung đi k&egrave;m chiếc b&uacute;t S-Pen c&ocirc;ng nghệ hiện đại, hỗ trợ người d&ugrave;ng sử dụng c&aacute;c t&aacute;c vụ tr&ecirc;n m&aacute;y theo c&aacute;ch mới, thuận tiện v&agrave; dễ d&agrave;ng hơn cho c&ocirc;ng việc cũng như trong học tập của bạn.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Trang bị chipset mạnh mẽ vượt trội</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung ưu &aacute;i d&agrave;nh bộ vi xử l&yacute; quy tr&igrave;nh 5 nm ph&aacute;t triển h&agrave;ng đầu của m&igrave;nh cho Galaxy S21 Ultra 5G, chip Exynos 2100 8 nh&acirc;n c&oacute; tốc độ xử l&yacute; l&ecirc;n đến 2.9 GHz. Kết hợp với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-12gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 12 GB tại Thegioididong.com\">12 GB RAM</a>&nbsp;cung cấp sức mạnh cho những trải nghiệm giải tr&iacute; nặng nhất của người d&ugrave;ng được bất tận, kh&ocirc;ng độ trễ, lag.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Trang bị chipset mạnh mẽ vượt trội\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-1.jpg\" title=\"Galaxy S21 Ultra 5G | Trang bị chipset mạnh mẽ vượt trội\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Con chip n&agrave;y cũng được trang bị bộ xử l&yacute; c&aacute;c t&aacute;c vụ AI mới với 3 l&otilde;i c&oacute; thể thực thi tới 26 ngh&igrave;n tỷ hoạt động mỗi gi&acirc;y (TOPS) so với Exynos 990 chỉ l&agrave; 15 ngh&igrave;n tỷ, đem lại hiệu suất gần như gấp đ&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, S21 Ultra 5G c&ograve;n hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/mang-5g-la-gi-co-nhung-uu-diem-gi-so-voi-4g-1312277\" target=\"_blank\" title=\"Mạng 5G là gì? Có những ưu điểm gì so với 4G?\">5G</a>&nbsp;internet si&ecirc;u tốc độ cho bạn khả năng đăng tải th&ocirc;ng tin, dữ liệu giờ đ&acirc;y chỉ được t&iacute;nh bằng gi&acirc;y. Điện thoại mang lại nguồn cảm hứng tr&agrave;n đầy trong những khung giờ giải tr&iacute; của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Hỗ trợ kết nối 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-2.jpg\" title=\"Galaxy S21 Ultra 5G | Hỗ trợ kết nối 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đi k&egrave;m ứng dụng tăng hiệu suất Game Booster gi&uacute;p tối ưu h&oacute;a phần mềm n&acirc;ng cao trải nghiệm v&agrave; bộ nhớ trong l&ecirc;n đến&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-256gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 256 GB tại Thegioididong.com\">256 GB ROM</a>, rộng mở kh&ocirc;ng gian lưu trữ để bạn lưu giữ mọi h&igrave;nh ảnh, dữ liệu đ&aacute;ng nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-9.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Lưu trữ nhiều hơn với phiên bản 256GB ROM\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-9.jpg\" title=\"Galaxy S21 Ultra 5G | Lưu trữ nhiều hơn với phiên bản 256GB ROM\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>C&ocirc;ng nghệ m&agrave;n h&igrave;nh chuẩn cao nhất</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy S21 Ultra 5G c&oacute; m&agrave;n h&igrave;nh lớn với k&iacute;ch thước 6.8 inch cho bạn kh&ocirc;ng gian giải tr&iacute; thoải m&aacute;i hơn. Hơn thế nữa, việc trang bị tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\" title=\"Tìm hiểu màn hình Dynamic AMOLED 2X trên smartphone\">Dynamic AMOLED 2X</a>&nbsp;độ ph&acirc;n giải 2K+ đưa S21 Ultra 5G trở th&agrave;nh smartphone sở hữu m&agrave;n h&igrave;nh sống động, sắc n&eacute;t bậc nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-3.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Công nghệ màn hình chuẩn cao nhất\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-3.jpg\" title=\"Galaxy S21 Ultra 5G | Công nghệ màn hình chuẩn cao nhất\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh n&acirc;ng cấp tần số qu&eacute;t&nbsp;120 Hz khiến mọi thao t&aacute;c vuốt chạm, chuyển động h&igrave;nh ảnh cực kỳ mượt, bạn chỉ cần cử động nhẹ ng&oacute;n tay v&agrave; S21 Ultra 5G di chuyển mọi thứ theo &yacute; bạn ngay lập tức, mang đến bạn cảm gi&aacute;c đắm ch&igrave;m trong giải tr&iacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dynamic AMOLED 2X c&oacute; thể đạt độ s&aacute;ng cao nhất l&ecirc;n đến 1500 nits, hỗ trợ HDR10+ độ tương phản cao gi&uacute;p người d&ugrave;ng c&oacute; thể sử dụng điện thoại dưới mọi điều kiện s&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-4.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Độ sáng cao nhất lên đến 1500 nits\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-4.jpg\" title=\"Galaxy S21 Ultra 5G | Độ sáng cao nhất lên đến 1500 nits\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc điện thoại Galaxy S21 Ultra 5G lu&ocirc;n được bảo vệ tốt khi trang bị k&iacute;nh cường lực ở cả 2 mặt, đặc biệt l&agrave; mặt trước với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-kinh-cuong-luc-corning-gorilla-glass-victus-1284586\" target=\"_blank\" title=\"Tìm hiểu kính cường lực Corning Gorilla Glass Victus smartphone\">k&iacute;nh cường lực Gorilla Glass Victus</a>&nbsp;thế hệ mới rắn chắc, hạn chế tối đa c&aacute;c t&aacute;c nh&acirc;n g&acirc;y va đập, trầy xước.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Camera như một nhiếp ảnh gia chuy&ecirc;n nghiệp</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Hệ thống m&aacute;y ảnh của S21 Ultra 5G sẽ lu&ocirc;n l&agrave; điểm nhấn ấn tượng với những th&ocirc;ng số độ ph&acirc;n giải cao vượt trội. Camera ch&iacute;nh l&ecirc;n tới 108 MP, camera g&oacute;c si&ecirc;u rộng 12 MP v&agrave; 2 camera tele 10 MP hỗ trợ thu ph&oacute;ng tầm xa tuyệt vời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-03.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Ảnh chụp trên camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-03.jpg\" title=\"Galaxy S21 Ultra 5G | Ảnh chụp trên camera\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Độ ph&acirc;n giải cao c&ugrave;ng những t&iacute;nh năng chỉnh sửa ảnh như khả năng lấy n&eacute;t, g&oacute;c rộng, tự động cấp &aacute;nh s&aacute;ng trong điều kiện thiếu s&aacute;ng như buổi đ&ecirc;m,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-anh-hdr-tren-smartphone-la-gi-906400\" target=\"_blank\" title=\"Chế độ chụp ảnh HDR trên smartphone là gì?\">HDR</a>&nbsp;v&agrave; c&aacute;c t&iacute;nh năng zoom cự ly cực kỳ xa sẽ mang đến cho bạn những bức ảnh đẹp lung linh v&agrave; từng chi tiết chất lượng cao thực sự.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-01.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Ảnh chụp đêm\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-01.jpg\" title=\"Galaxy S21 Ultra 5G | Ảnh chụp đêm\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy S21 Ultra 5G c&ograve;n sở hữu t&iacute;nh năng quay phim mang chất lượng dẫn đầu thời đại quay video 8K si&ecirc;u sắc n&eacute;t. Hỗ trợ c&aacute;c chuẩn chống rung chuy&ecirc;n nghiệp cho bạn sự đắm ch&igrave;m trong những thước phim ch&iacute;nh tay m&igrave;nh s&aacute;ng tạo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-8.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Giao diện chụp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-8.jpg\" title=\"Galaxy S21 Ultra 5G | Giao diện chụp ảnh\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">A.I camera trước độ ph&acirc;n giải l&ecirc;n đến 40 MP trang bị mọi t&iacute;nh năng gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-lam-dep-beautify-tren-smartphone-tablet-1172231\" target=\"_blank\" title=\"Chế độ làm đẹp (Beautify) trên smartphone, tablet là gì?\">l&agrave;m đẹp</a>&nbsp;ảnh của bạn, S21 Ultra 5G tự tin sở hữu m&aacute;y ảnh selfie xuất sắc kh&ocirc;ng thua k&eacute;m g&igrave; ống k&iacute;nh mặt sau. Ngo&agrave;i ra, ống k&iacute;nh trước cũng sở hữu quay phim độ ph&acirc;n giải&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cac-chuan-quay-phim-tren-dien-thoai-tablet-pho-bi-1174134#4k\" target=\"_blank\" title=\"Các chuẩn quay phim trên điện thoại, tablet phổ biến hiện nay\">4K</a>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-02.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Ảnh chụp camera selfie\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-02.jpg\" title=\"Galaxy S21 Ultra 5G | Ảnh chụp camera selfie\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Cả ng&agrave;y d&agrave;i tr&agrave;n đầy năng lượng&nbsp;</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy S21 Ultra 5G sẵn s&agrave;ng đ&aacute;p ứng mọi hoạt động l&agrave;m việc, học tập v&agrave; giải tr&iacute; của người d&ugrave;ng nhiều giờ liền với sự hỗ trợ của vi&ecirc;n pin dung lượng lớn 5000 mAh. Trang bị củ sạc nhanh si&ecirc;u tốc c&ocirc;ng suất 25 W gi&uacute;p m&aacute;y nạp đầy trở lại năng lượng một c&aacute;ch nhanh ch&oacute;ng nhất c&oacute; thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-10.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S21 Ultra 5G | Củ sạc không kèm khi mua máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-10.jpg\" title=\"Galaxy S21 Ultra 5G | Củ sạc không kèm khi mua máy\" /></a></span></p>\r\n', 0, '1657263859_s21 ultra 5g.jpg', 30990000, 5, 11),
(40, 'Galaxy Watch 4 Classic 42mm Trắng', '<h2><span style=\"font-size:16px\"><strong>Cấu h&igrave;nh Samsung Galaxy Watch 4 Classic 42mm Trắng</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>M&agrave;n h&igrave;nh:</p>\r\n\r\n	<p>SUPER AMOLED1.2 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Thời lượng pin:</p>\r\n\r\n	<p>Khoảng 1.5 ng&agrave;y</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết nối với hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Android d&ugrave;ng Google Mobile Service</p>\r\n	</li>\r\n	<li>\r\n	<p>Mặt:</p>\r\n\r\n	<p>K&iacute;nh cường lực Gorrilla Glass Dx+42 mm</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng cho sức khỏe:</p>\r\n\r\n	<p>Chế độ luyện tậpTheo d&otilde;i giấc ngủĐiện t&acirc;m đồĐo huyết &aacute;pĐo nhịp timĐo nồng độ oxy (SpO2)Đếm số bước ch&acirc;n</p>\r\n	</li>\r\n</ul>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế cổ điển, sang trọng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-4-classic-42mm\" target=\"_blank\" title=\"Đồng hồ Samsung Galaxy Watch 4 Classic 42mm\" type=\"Đồng hồ Samsung Galaxy Watch 4 Classic 42mm\">Đồng hồ Samsung Galaxy Watch 4 Classic 42mm</a>&nbsp;sở hữu khung viền l&agrave;m từ th&eacute;p kh&ocirc;ng gỉ bền chắc v&agrave; được&nbsp;<strong>chứng nhận độ bền MIL-STD-810G</strong>, cho khả năng chống ăn m&ograve;n tốt hơn trong những điều kiện thời tiết kh&aacute;c nhau. Chiếc đồng hồ điều hướng linh hoạt với v&ograve;ng xoay bezel độc đ&aacute;o tạo n&ecirc;n vẻ đẹp sang trọng vượt thời gian. Samsung đ&atilde; sử dụng phần d&acirc;y đeo silicone mang lại cho bạn cảm gi&aacute;c &ecirc;m &aacute;i, thỏa sức tham gia c&aacute;c hoạt động thể thao như chạy bộ, đạp xe,...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032557.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032557.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Thiết kế\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">M&agrave;n h&igrave;nh c&oacute; độ hiển thị sắc n&eacute;t cao</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chiếc đồng hồ được trang bị mặt k&iacute;nh cường lực Gorilla Glass Dx+ c&oacute; độ cứng cao n&ecirc;n bạn c&oacute; thể an t&acirc;m sử dụng m&agrave; kh&ocirc;ng lo trầy xước. Samsung Galaxy Watch 4 Classic sử dụng tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cac-loai-man-hinh-thong-dung-tren-smartwatch-1169187#subhmenuid4\" target=\"_blank\" title=\"Tìm hiểu thêm về màn hình Super AMOLED\" type=\"Tìm hiểu thêm về màn hình Super AMOLED\">Super AMOLED</a>&nbsp;đặc trưng của những mẫu đồng hồ Samsung, đem lại những trải nghiệm tuyệt vời về hiển thị. K&iacute;ch thước m&agrave;n h&igrave;nh l&agrave; 1.2 inch với độ ph&acirc;n giải 396 x 396 pixels sắc n&eacute;t, c&aacute;c chi tiết nhỏ được t&aacute;i tạo rất tốt c&ugrave;ng d&atilde;y m&agrave;u rộng, n&ecirc;n c&aacute;c m&agrave;u sắc hiển thị rất tươi v&agrave; nịnh mắt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032559.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032559.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Màn hình\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hệ điều h&agrave;nh WearOS (One UI Watch 3.5) lần đầu ti&ecirc;n được ra mắt</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung đ&atilde; hợp t&aacute;c với Google để cho ra mắt hệ điều h&agrave;nh&nbsp;<strong>WearOS (One UI Watch 3.5)</strong>&nbsp;được tinh chỉnh để th&iacute;ch hợp với hệ sinh th&aacute;i của của h&atilde;ng. Hệ điều h&agrave;nh cho ph&eacute;p bạn tối ưu h&oacute;a sự đồng bộ giữa c&aacute;c thiết bị Samsung với nhau v&agrave; c&agrave;i th&ecirc;m được c&aacute;c ứng dụng b&ecirc;n thứ 3, gi&uacute;p n&acirc;ng cao trải nghiệm người d&ugrave;ng.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Bộ vi xử l&yacute; với con chip Exynos W920 mạnh mẽ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Con chip&nbsp;<strong>Exynos W920</strong>&nbsp;được t&iacute;ch hợp tr&ecirc;n Samsung Galaxy Watch 4 Classic được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm mới nhất của Samsung Foundry, mang lại hiệu suất cao, mạnh mẽ khi sử dụng tr&ecirc;n nhiều t&aacute;c vụ kh&aacute;c nhau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032601.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Chip\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032601.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Chip\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Khả năng kết nối ổn định với Bluetooth 5.0</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Mẫu đồng hồ mang lại cho bạn những trải nghiệm tuyệt với khả năng kết nối Bluetooth 5.0 ổn định. Bạn c&oacute; thể nghe nhận cuộc gọi v&agrave; trả lời c&aacute;c tin nhắn trực tiếp tr&ecirc;n Samsung Galaxy Watch 4 Classic. Chiếc đồng hồ chỉ tương th&iacute;ch với những mẫu điện thoại chạy hệ điều h&agrave;nh Android.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032602.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Bluetooth\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032602.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Bluetooth\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">T&ugrave;y biến mặt đồng hồ theo c&aacute; t&iacute;nh người d&ugrave;ng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; thể thoải m&aacute;i t&ugrave;y &yacute; thể hiện c&aacute; t&iacute;nh với sự t&ugrave;y biến mặt đồng hồ đa dạng, từ mặt đồng hồ kim cổ điển đến kỹ thuật số hiện đại hay AR Emojis sinh động với nhiều h&igrave;nh nền, ph&ocirc;ng chữ v&agrave; m&agrave;u sắc phong ph&uacute;, c&ugrave;ng c&aacute;c h&igrave;nh ảnh đồ họa sống động hiển thị hoạt động như chạy bộ hay nghe nhạc,&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032605.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Giao diện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032605.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Giao diện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thời lượng sử dụng pin khoảng 1.5 ng&agrave;y</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Với phi&ecirc;n bản 42 mm, Samsung đ&atilde; trang bị vi&ecirc;n pin 247 mAh cho bạn c&oacute; thể thoải m&aacute;i sử dụng chiếc đồng hồ trong khoảng 1.5 ng&agrave;y ở chế độ cơ bản. T&ugrave;y theo c&aacute;c t&iacute;nh năng sử dụng sẽ cho thời lượng pin kh&aacute;c nhau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032606.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Pin\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032606.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Pin\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Đi k&egrave;m c&aacute;c t&iacute;nh năng hỗ trợ người d&ugrave;ng luyện tập</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh Samsung đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh Samsung đang kinh doanh tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Samsung</a>&nbsp;hỗ trợ hơn 90 chế độ luyện tập kh&aacute;c nhau, c&aacute;c th&ocirc;ng tin như nhịp tim hay số bước ch&acirc;n trong qu&aacute; tr&igrave;nh luyện tập đều được ghi nhận ch&iacute;nh x&aacute;c. C&aacute;c số liệu được lưu lại tr&ecirc;n đồng hồ gi&uacute;p bạn dễ d&agrave;ng nắm bắt v&agrave; từ đ&oacute; đưa ra c&aacute;c điều chỉnh ph&ugrave; hợp v&agrave; tối ưu với thể trạng của bản th&acirc;n.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032608.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032608.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Tập luyện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Theo d&otilde;i sức khỏe người d&ugrave;ng li&ecirc;n tục</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch 4 Classic gi&uacute;p bạn&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh có chức năng theo dõi giấc ngủ đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh có chức năng theo dõi giấc ngủ đang kinh doanh tại Thế Giới Di Động\">theo d&otilde;i giấc ngủ</a>&nbsp;v&agrave; ghi nhận lại c&aacute;c chỉ số sức khỏe th&ocirc;ng qua&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tinh-nang-do-chi-so-spo2-va-vo2-max-tren-smartwatch-la-gi-1279892#hmenuid1\" target=\"_blank\" title=\"Tìm hiểu thêm về SpO2 \" type=\"Tìm hiểu thêm về SpO2 \">đo nồng độ oxy trong m&aacute;u (SpO2)</a>, ph&aacute;t hiện tiếng ng&aacute;y gi&uacute;p cải thiện chất lượng nghỉ ngơi của bạn.&nbsp;Samsung hợp t&aacute;c với Tổ chức National Sleep Foundation để cung cấp c&aacute;c th&ocirc;ng tin chuy&ecirc;n s&acirc;u về giấc ngủ, gi&uacute;p bạn hiểu r&otilde; v&agrave; cải thiện giấc ngủ của ch&iacute;nh m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lưu &yacute;, chức năng quản l&yacute; giấc ngủ chỉ khả dụng khi được gh&eacute;p nối với điện thoại th&ocirc;ng minh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cảm biến đột ph&aacute;&nbsp;<strong>Samsung BioActive</strong>&nbsp;gi&uacute;p người d&ugrave;ng c&oacute; thể nhận kết quả ph&acirc;n t&iacute;ch th&agrave;nh phần cơ thể (qua phương ph&aacute;p đo BIA) theo thời gian thực mọi l&uacute;c, mọi nơi chỉ với hai ng&oacute;n tay trong 15 gi&acirc;y. Cụ thể l&agrave; c&aacute;c chỉ số như nồng độ oxy trong m&aacute;u (SpO2),&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-do-nhip-tim\" target=\"_blank\" title=\"Xem thêm đồng hồ đo nhịp tim đang bán tại thegioididong.com\">đo nhịp tim</a>&nbsp;hay lượng calories m&agrave; bạn đ&atilde; ti&ecirc;u thụ, gi&uacute;p bạn kiểm so&aacute;t tốt c&aacute;c mục ti&ecirc;u sức khoẻ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032610.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Cảm biến\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032610.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Cảm biến\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với bản cập nhật mới v&agrave;o th&aacute;ng 4/2022, người d&ugrave;ng Việt Nam đ&atilde; c&oacute; thể sử dụng t&iacute;nh 2 t&iacute;nh năng&nbsp;<strong>đo huyết &aacute;p</strong>&nbsp;v&agrave;&nbsp;<strong>điện t&acirc;m đồ ECG</strong>. Để k&iacute;ch hoạt, bạn cần tải ứng dụng&nbsp;<a href=\"https://galaxystore.samsung.com/detail/com.samsung.android.shealthmonitor\" target=\"_blank\" title=\"Ứng dụng Samsung Health Monitor\">Samsung Health Monitor</a>&nbsp;v&agrave; cập nhật phi&ecirc;n bản phần mềm mới nhất d&agrave;nh cho Galaxy Watch 4. Giờ đ&acirc;y bạn đ&atilde; c&oacute; thể theo d&otilde;i th&ocirc;ng tin sức khỏe bản th&acirc;n mọi l&uacute;c mọi nơi chỉ với chiếc đồng hồ tr&ecirc;n cổ tay của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ocirc;ng tin chi tiết về bản cập nhật n&agrave;y bạn c&oacute; thể xem tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ra-mat-tinh-nang-do-huyet-ap-va-dien-tam-do-tren-galaxy-watch-4-1426241\" target=\"_blank\" title=\"Ra mắt tính năng Đo Huyết Áp và Điện tâm đồ trên Galaxy Watch 4 tại Việt Nam giúp người dùng chăm sóc sức khỏe toàn diện\">Ra mắt t&iacute;nh năng Đo Huyết &Aacute;p v&agrave; Điện t&acirc;m đồ tr&ecirc;n Galaxy Watch 4 tại Việt Nam</a>.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thoải m&aacute;i sử dụng với chỉ số chống nước 5 ATM</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Watch 4 Classic đặt ti&ecirc;u chuẩn chống nước 5 ATM dựa tr&ecirc;n ISO 22810:20210. Với chỉ số chống nước n&agrave;y sẽ gi&uacute;p bạn an t&acirc;m khi mang đi tắm hay đi bơi ở v&ugrave;ng nước n&ocirc;ng. Tuy nhi&ecirc;n, bạn kh&ocirc;ng n&ecirc;n sử dụng c&aacute;c n&uacute;t bấm trong l&uacute;c bơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032612.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Kháng nước\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032612.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Kháng nước\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Những tiện &iacute;ch kh&aacute;c hỗ trợ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh đang kinh doanh tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh</a>&nbsp;c&ograve;n được trang bị th&ecirc;m nhiều tiện &iacute;ch như điều khiển chơi nhạc, theo d&otilde;i h&agrave;nh tr&igrave;nh di chuyển qua&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh hỗ trợ GPS bán tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh hỗ trợ GPS bán tại Thế Giới Di Động\">GPS</a>, nhận diện hoạt động luyện tập,...</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032614.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Watch 4 Classic 42mm Trắng - Tiện ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248758/samsung-galaxy-watch-4-classic-42mm-200422-032614.jpg\" title=\"Galaxy Watch 4 Classic 42mm Trắng - Tiện ích khác\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đồng hồ Samsung Galaxy Watch 4 Classic 42mm sở hữu thiết kế tinh tế v&agrave; sang trọng, thể hiện t&iacute;nh chuy&ecirc;n nghiệp cho người d&ugrave;ng với nhiều t&iacute;nh năng kh&aacute;c nhau. Samsung Galaxy Watch 4 Classic sẽ đem lại cho bạn những trải nghiệm tuyệt vời cần c&oacute; của một chiếc đồng hồ th&ocirc;ng minh. Đ&acirc;y sẽ l&agrave; sự lựa chọn kh&ocirc;ng thể bỏ qua d&agrave;nh cho c&aacute;c t&iacute;n đồ y&ecirc;u th&iacute;ch c&ocirc;ng nghệ v&agrave; luyện tập thể thao.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '1657436652_vn-galaxy-watch4-classic-sm-r880nzsaxxv-481128047.jpg', 8490000, 23, 14);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(41, ' Galaxy Watch 4 LTE Classic 46mm', '<h2><span style=\"font-size:16px\"><strong>Cấu h&igrave;nh Samsung Galaxy Watch 4 LTE Classic 46mm</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>M&agrave;n h&igrave;nh:</p>\r\n\r\n	<p>SUPER AMOLED1.4 inch</p>\r\n	</li>\r\n	<li>\r\n	<p>Thời lượng pin:</p>\r\n\r\n	<p>Khoảng 1.5 ng&agrave;y</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết nối với hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Android d&ugrave;ng Google Mobile Service</p>\r\n	</li>\r\n	<li>\r\n	<p>Mặt:</p>\r\n\r\n	<p>K&iacute;nh cường lực Gorrilla Glass Dx+46 mm</p>\r\n	</li>\r\n	<li>\r\n	<p>T&iacute;nh năng cho sức khỏe:</p>\r\n\r\n	<p>Chế độ luyện tậpTheo d&otilde;i giấc ngủĐiện t&acirc;m đồĐo huyết &aacute;pĐo nhịp timĐo nồng độ oxy (SpO2)Đếm số bước ch&acirc;n</p>\r\n	</li>\r\n</ul>\r\n', '<h3><strong><span style=\"font-size:16px\">Thiết kế trẻ trung, hiện đại</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-4-lte-classic-46mm\" target=\"_blank\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm đang được kinh doanh tại Thế Giới Di Động\">Samsung Galaxy Watch 4 LTE Classic 46mm</a>&nbsp;mang n&eacute;t sang trọng, hiện đại c&ugrave;ng với bộ khung viền th&eacute;p kh&ocirc;ng gỉ cứng c&aacute;p, m&agrave;n h&igrave;nh SUPER AMOLED được bao bọc bởi lớp k&iacute;nh cường lực Gorrilla Glass Dx+ bền bỉ, chịu lực tốt.&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicone\" target=\"_blank\" title=\"   Xem thêm một số mẫu đồng hồ thông minh có dây đeo silicone đang kinh doanh tại Thế Giới Di Động\">D&acirc;y đeo silicone</a>&nbsp;&ecirc;m nhẹ, độ đ&agrave;n hồi cao, kh&ocirc;ng thấm nước, thiết kế đục lỗ cho người d&ugrave;ng sự th&ocirc;ng tho&aacute;ng khi mang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có thiết kế trẻ trung\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-1.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có thiết kế trẻ trung\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Bộ vi xử l&yacute; Exynos W920 với tốc độ nhanh hơn 20% so với d&ograve;ng cũ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Galaxy Watch4 Series l&agrave; d&ograve;ng Galaxy Watch đầu ti&ecirc;n sử dụng vi xử l&yacute; Exynos W920 c&oacute; tiến tr&igrave;nh 5 nm với CPU nhanh hơn 20%, RAM nhiều hơn 50% v&agrave; GPU nhanh hơn 10 lần so với chip Exynos 9110 của d&ograve;ng Galaxy Watch 3. Điều n&agrave;y đem đến cho người d&ugrave;ng thao t&aacute;c cuộn, lướt mượt m&agrave;, cho bạn những trải nghiệm th&uacute; vị.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-2-fix.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có bộ chip xử lý cho hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-2-fix.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có bộ chip xử lý cho hiệu năng mạnh mẽ\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Hệ điều h&agrave;nh One UI Watch</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Tự h&agrave;o l&agrave; chiếc Galaxy Watch đầu ti&ecirc;n d&ugrave;ng hệ điều h&agrave;nh One UI Watch được t&ugrave;y biến ri&ecirc;ng cho thiết bị Samsung, đồng hồ th&ocirc;ng minh Galaxy Watch 4 LTE Classic được cải tiến về hiệu suất, cho người d&ugrave;ng trải nghiệm liền mạch hơn giữa c&aacute;c thiết bị v&agrave; c&oacute; thể truy cập v&agrave;o nhiều ứng dụng hơn nữa.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">B&ecirc;n cạnh đ&oacute;, nền tảng mới n&agrave;y sẽ gi&uacute;p Galaxy Watch v&agrave; smartphone kết nối với nhau một c&aacute;ch to&agrave;n diện hơn, việc đồng bộ dữ liệu giữa điện thoại v&agrave; đồng hồ cũng diễn ra nhanh ch&oacute;ng, đem đến cho bạn những trải nghiệm tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có hệ điều hành mới One UI Watch\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-3.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có hệ điều hành mới One UI Watch\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">M&agrave;n h&igrave;nh SUPER AMOLED hiển thị h&igrave;nh ảnh sắc n&eacute;t</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh đang kinh doanh tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y c&oacute; m&agrave;n h&igrave;nh k&iacute;ch cỡ 1.4 inch c&ugrave;ng độ ph&acirc;n giải 450 x 450 pixels cho khả năng hiển thị h&igrave;nh ảnh, th&ocirc;ng b&aacute;o tốt hơn. Đồng hồ được trang bị m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cac-loai-man-hinh-thong-dung-tren-smartwatch-1169187#subhmenuid4\" target=\"_blank\" title=\"Tìm hiểu thêm về màn hình Super AMOLED\" type=\"Tìm hiểu thêm về màn hình Super AMOLED\">SUPER AMOLED</a>&nbsp;cho h&igrave;nh ảnh c&oacute; độ tương phản cao, m&agrave;u sắc rực rỡ v&agrave; tiết kiệm pin đ&aacute;ng kể. B&ecirc;n ngo&agrave;i m&agrave;n h&igrave;nh c&oacute; lớp k&iacute;nh cường lực Gorilla Glass Dx+ cứng c&aacute;p, bền chắc, bảo vệ an to&agrave;n cho đồng hồ trước những va chạm kh&ocirc;ng mong muốn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-4.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có màn hình hiển thị tốt\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-4.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có màn hình hiển thị tốt\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">eSIM hiện đại - nghe gọi độc lập với điện thoại</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Samsung đ&atilde; trang bị cho mẫu đồng hồ n&agrave;y t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-esim\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh có eSIM đang được kinh doanh tại Thế Giới Di Động\">eSIM</a>&nbsp;gi&uacute;p bạn c&oacute; thể thoải m&aacute;i nghe gọi ngay tr&ecirc;n đồng hồ m&agrave; kh&ocirc;ng cần kết nối với điện thoại, đem đến sự tiện dụng khi bạn tập luyện hoặc đi ra ngo&agrave;i m&agrave; kh&ocirc;ng cần mang theo điện thoại.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-5.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có eSim hiện đại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-5.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có eSim hiện đại\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Dễ d&agrave;ng kết nối th&ocirc;ng qua cổng bluetooth</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Bạn c&oacute; thể thoải m&aacute;i gh&eacute;p nối đồng hồ với c&aacute;c thiết bị th&ocirc;ng minh kh&aacute;c sử dụng nền tảng Android th&ocirc;ng qua bluetooth. Sau khi kết nối th&agrave;nh c&ocirc;ng, người d&ugrave;ng c&oacute; thể thoải m&aacute;i xem th&ocirc;ng b&aacute;o cuộc gọi, tin nhắn, th&ocirc;ng b&aacute;o ứng dụng (Messenger, Zalo,...), xem c&aacute;c dữ liệu về c&aacute;c buổi luyện tập hay c&aacute;c ph&acirc;n t&iacute;ch về sức khỏe,...</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-6.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm cho khả năng kết nối bluetooth nhanh chóng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-6.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm cho khả năng kết nối bluetooth nhanh chóng\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Pin d&ugrave;ng được trong khoảng 1.5 ng&agrave;y</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Mẫu&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh Samsung đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh Samsung đang kinh doanh tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Samsung</a>&nbsp;n&agrave;y sở hữu vi&ecirc;n pin dung lượng 361 mAh cho bạn thời gian sử dụng khoảng 1.5 ng&agrave;y. Thời lượng pin kh&ocirc;ng qu&aacute; l&acirc;u nhưng bạn c&oacute; thể y&ecirc;n t&acirc;m sử dụng cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng lo hết pin giữa chừng, khi bạn muốn sử dụng gấp, chỉ cần 30 ph&uacute;t sạc cũng đủ cho bạn 10 giờ sử dụng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có viên pin dùng được trong khoảng 1.5 ngày\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-7.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có viên pin dùng được trong khoảng 1.5 ngày\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Kho t&agrave;ng mặt đồng hồ th&uacute; vị</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">H&atilde;ng đ&atilde; trang bị cho mẫu Galaxy Watch 4 n&agrave;y một bộ sưu tập mặt đồng hồ đẹp mắt, bạn c&oacute; thể thoải m&aacute;i thay đổi, lựa chọn cho m&igrave;nh một mặt đồng hồ ưng &yacute; nhất, ph&ugrave; hợp với phong c&aacute;ch, t&acirc;m trạng của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có kho tàng giao diện đẹp mắt\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-8.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có kho tàng giao diện đẹp mắt\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Cảm biến hiện đại gi&uacute;p đo c&aacute;c chỉ số sức khỏe, thể chất</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://www.thegioididong.com/dong-ho-deo-tay\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm các mẫu đồng hồ đang kinh doanh tại Thế Giới Di Động\">Đồng hồ</a>&nbsp;được trang bị c&aacute;c cảm biến &aacute;nh s&aacute;ng, địa từ, gia tốc kế,... đặc biệt l&agrave; bộ cảm biến BioActive đột ph&aacute; của Samsung. Nhờ bộ cảm biến hiện đại n&agrave;y, người d&ugrave;ng c&oacute; thể nhận kết quả ph&acirc;n t&iacute;ch th&agrave;nh phần cơ thể (qua phương ph&aacute;p đo BIA) theo thời gian thực mọi l&uacute;c, mọi nơi chỉ với hai ng&oacute;n tay trong 15 gi&acirc;y. Cụ thể l&agrave; c&aacute;c chỉ số như nồng độ oxy trong m&aacute;u (<a href=\"https://www.thegioididong.com/hoi-dap/tinh-nang-do-chi-so-spo2-va-vo2-max-tren-smartwatch-la-gi-1279892#hmenuid1\" target=\"_blank\" title=\"Tìm hiểu thêm về SpO2 \" type=\"Tìm hiểu thêm về SpO2 \">SpO2</a>), đo nhịp tim hay lượng calories m&agrave; bạn đ&atilde; ti&ecirc;u thụ, gi&uacute;p bạn kiểm so&aacute;t tốt c&aacute;c mục ti&ecirc;u sức khoẻ của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-190122-121455.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm - Tính năng sức khoẻ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-190122-121455.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm - Tính năng sức khoẻ\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, đồng hồ c&ograve;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\" title=\"Xem thêm một số mẫu đồng hồ thông minh có chức năng theo dõi giấc ngủ đang kinh doanh tại Thế Giới Di Động\" type=\"Xem thêm một số mẫu đồng hồ thông minh có chức năng theo dõi giấc ngủ đang kinh doanh tại Thế Giới Di Động\">theo d&otilde;i giấc ngủ</a>&nbsp;của bạn, ph&acirc;n t&iacute;ch v&agrave; đ&aacute;nh gi&aacute; n&acirc;ng cao th&ocirc;ng qua việc đo nồng độ oxy trong m&aacute;u, ph&aacute;t hiện tiếng ng&aacute;y gi&uacute;p cải thiện chất lượng nghỉ ngơi của bạn. Samsung hợp t&aacute;c với Tổ chức National Sleep Foundation để cung cấp c&aacute;c th&ocirc;ng tin chuy&ecirc;n s&acirc;u về giấc ngủ, gi&uacute;p bạn hiểu r&otilde; v&agrave; cải thiện giấc ngủ của ch&iacute;nh m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Lưu &yacute;: Chức năng quản l&yacute; giấc ngủ chỉ khả dụng khi được gh&eacute;p nối với điện thoại th&ocirc;ng minh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Với bản cập nhật mới v&agrave;o th&aacute;ng 4/2022, người d&ugrave;ng Việt Nam đ&atilde; c&oacute; thể sử dụng t&iacute;nh 2 t&iacute;nh năng&nbsp;<strong>đo huyết &aacute;p</strong>&nbsp;v&agrave;&nbsp;<strong>điện t&acirc;m đồ ECG</strong>. Để k&iacute;ch hoạt, bạn cần tải ứng dụng&nbsp;<a href=\"https://galaxystore.samsung.com/detail/com.samsung.android.shealthmonitor\" target=\"_blank\" title=\"Ứng dụng Samsung Health Monitor\">Samsung Health Monitor</a>&nbsp;v&agrave; cập nhật phi&ecirc;n bản phần mềm mới nhất d&agrave;nh cho Galaxy Watch 4. Giờ đ&acirc;y bạn đ&atilde; c&oacute; thể theo d&otilde;i th&ocirc;ng tin sức khỏe bản th&acirc;n mọi l&uacute;c mọi nơi chỉ với chiếc đồng hồ tr&ecirc;n cổ tay của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Th&ocirc;ng tin chi tiết về bản cập nhật n&agrave;y bạn c&oacute; thể xem tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ra-mat-tinh-nang-do-huyet-ap-va-dien-tam-do-tren-galaxy-watch-4-1426241\" target=\"_blank\" title=\"Ra mắt tính năng Đo Huyết Áp và Điện tâm đồ trên Galaxy Watch 4 tại Việt Nam giúp người dùng chăm sóc sức khỏe toàn diện\">Ra mắt t&iacute;nh năng Đo Huyết &Aacute;p v&agrave; Điện t&acirc;m đồ tr&ecirc;n Galaxy Watch 4 tại Việt Nam</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm theo dõi các chỉ số sức khỏe của bạn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-9.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm theo dõi các chỉ số sức khỏe của bạn\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Đa dạng c&aacute;c chế độ tập luyện - r&egrave;n luyện sức khỏe tốt hơn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Galaxy Watch 4 Classic sẽ theo d&otilde;i tiến tr&igrave;nh tập thể dục của bạn, song song đ&oacute;, n&oacute; c&ograve;n l&agrave; chiếc đồng hồ Galaxy Watch đầu ti&ecirc;n sở hữu t&iacute;nh năng đo th&agrave;nh phần cơ thể: Tỷ lệ phần trăm chất b&eacute;o trong cơ thể, cơ xương, lượng nước trong cơ thể,... Ngo&agrave;i ra, đồng hồ hỗ trợ hơn 90 b&agrave;i tập luyện thể thao như bơi lội, đạp xe, chạy bộ,... v&agrave; chấm điểm thể dục cho bạn. Đặc biệt hơn, chiếc Galaxy Watch n&agrave;y c&ograve;n c&oacute; t&iacute;nh năng &ldquo;thử th&aacute;ch nh&oacute;m&rdquo; với c&aacute;c thử th&aacute;ch đi k&egrave;m huy hiệu kỷ niệm v&agrave; hệ thống điểm số gi&uacute;p việc tập thể dục trở n&ecirc;n th&uacute; vị hơn, tạo động lực v&agrave; gi&uacute;p bạn r&egrave;n luyện sức khỏe c&ugrave;ng với gia đ&igrave;nh, bạn b&egrave;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-hbv-fix-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có nhiều chế độ tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-hbv-fix-1.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có nhiều chế độ tập luyện\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Chống nước 5 ATM - an to&agrave;n khi đi mưa, bơi ở v&ugrave;ng nước n&ocirc;ng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Mẫu đồng hồ nh&agrave; Samsung n&agrave;y sở hữu hệ số chống nước 5 ATM theo chuẩn ISO 22810:2010, người d&ugrave;ng c&oacute; thể thoải m&aacute;i đeo khi đi mưa, đi bơi ở v&ugrave;ng nước n&ocirc;ng m&agrave; kh&ocirc;ng lo bị v&agrave;o nước.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-07.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm - Chống nước\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-07.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm - Chống nước\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, đồng hồ đạt chứng nhận độ bền MIL-STD-810G tức l&agrave; n&oacute; trải qua 28 b&agrave;i kiểm tra li&ecirc;n tục như &aacute;p suất thấp, nhiệt độ cao/thấp, nhiệt độ chấn động, nhiễm độc chất lỏng, mưa, rung động, tiếng ồn &acirc;m thanh,&hellip; v&agrave; c&oacute; thể hoạt động tốt trong c&aacute;c điều kiện khắc nghiệt như m&ocirc;i trường ẩm ướt, nhiều bụi, nắng mưa thất thường,...</span></p>\r\n\r\n<h3><strong><span style=\"font-size:16px\">Nhiều tiện &iacute;ch kh&aacute;c</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Đồng hồ c&oacute; nhiều tiện &iacute;ch tuyệt vời kh&aacute;c chờ bạn kh&aacute;m ph&aacute; như: B&aacute;o thức, đếm số bước ch&acirc;n, rung th&ocirc;ng b&aacute;o, dự b&aacute;o thời tiết,...</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-12.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch 4 LTE Classic 46mm có nhiều tiện ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/248765/samsung-galaxy-watch-4-lte-classic-46mm-12.jpg\" title=\"Samsung Galaxy Watch 4 LTE Classic 46mm có nhiều tiện ích khác\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đồng hồ th&ocirc;ng minh Galaxy Watch 4 LTE Classic sở hữu vẻ bề ngo&agrave;i c&aacute; t&iacute;nh, hiện đại c&ugrave;ng lớp vỏ th&eacute;p kh&ocirc;ng gỉ cứng c&aacute;p, mặt k&iacute;nh cường lực Gorrilla Glass Dx+ bền bỉ, hệ điều h&agrave;nh One UI Watch v&agrave; con chip Exynos W920 cho hiệu năng vượt trội. N&oacute; c&ograve;n được trang bị eSIM hiện đại c&ugrave;ng nhiều t&iacute;nh năng chăm s&oacute;c sức khỏe v&agrave; luyện tập thể thao tuyệt vời, đ&acirc;y sẽ l&agrave; một mẫu đồng hồ th&ocirc;ng minh m&agrave; c&aacute;c t&iacute;n đồ c&ocirc;ng nghệ mong chờ nhất trong th&aacute;ng 8 n&agrave;y.</span></p>\r\n', 0, '1657436681_vn-galaxy-watch4-classic-399187-sm-r895fzkaxxv-481180686.jpg', 9990000, 20, 14),
(42, 'Galaxy Buds2', '<h2><strong><span style=\"font-size:16px\">ĐẶC ĐIỂM NỔI BẬT</span></strong></h2>\r\n\r\n<ul>\r\n	<li>Loa 2 chiều : chất lượng &acirc;m thanh ch&acirc;n thật</li>\r\n	<li>Chế độ chống ồn chủ động chặn tiếng ồn m&ocirc;i trường</li>\r\n	<li>Chất lượng đ&agrave;m thoại r&otilde; r&agrave;ng với 3 micro</li>\r\n	<li>T&iacute;ch hợp chế độ chơi game giảm độ trễ xuống mức thấp nhất</li>\r\n	<li>D&ugrave;ng trong 5 giờ với 1 (bật chống ồn) , 7.5 giờ (tắt chống ồn)</li>\r\n</ul>\r\n', '<h2><span style=\"font-size:14px\"><strong>Tai nghe Samsung Galaxy Buds2</strong><strong>&nbsp;&ndash;&nbsp; M&agrave;u đầy c&aacute; t&iacute;nh v&agrave; t&iacute;nh năng chống tiếng ồn vượt trội</strong></span></h2>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp nối sự th&agrave;nh c&ocirc;ng đến từ chiếc tai nghe Samsung Galaxy Buds th&igrave; c&oacute; lẽ việc Samsung bắt tay v&agrave;o thực hiện sản phẩm tiếp theo l&agrave; điều gần như được chắc chắn. Tr&ecirc;n thị trường hiện nay th&igrave; c&aacute;c loại tai nghe kh&ocirc;ng d&acirc;y kh&ocirc;ng hề thiếu, tuy nhi&ecirc;n để đảm bảo về mặt chất lượng của sản phẩm cũng như thương hiệu đ&aacute;ng tin cậy th&igrave; tai nghe bluetooth kh&ocirc;ng d&acirc;y<strong>&nbsp;Samsung Galaxy Buds2</strong>&nbsp;sẽ l&agrave; một sự lựa chọn ho&agrave;n hảo.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Galaxy Buds2 sở hữu k&iacute;ch thước nhỏ gọn c&ugrave;ng thiết kế 4 m&agrave;u nổi bật</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thiết kế nhỏ gọn lu&ocirc;n l&agrave; một trong những lợi thế của d&ograve;ng tai nghe kh&ocirc;ng d&acirc;y. Sự tiện lợi của Buds2&nbsp;gi&uacute;p người d&ugrave;ng dễ d&agrave;ng mang đi khắp mọi nơi. Sản phẩm được t&iacute;ch hợp c&ocirc;ng nghệ hiện đại gi&uacute;p người ti&ecirc;u d&ugrave;ng nhanh ch&oacute;ng đ&agrave;m thoại hoặc đắm ch&igrave;m trong kh&ocirc;ng gian &acirc;m nhạc bất cứ đ&acirc;u v&agrave; bất k&igrave; l&uacute;c n&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Kích thước nhỏ gọn cùng thiết kế 4 màu nổi bật\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/earphone/samsung-galaxy-buds-2-2_1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sản phẩm Samsung&nbsp;Galaxy Buds2 sở hữu thiết kế v&ocirc; c&ugrave;ng đa dạng với c&aacute;c phi&ecirc;n bản m&agrave;u sắc c&aacute; t&iacute;nh để người d&ugrave;ng c&oacute; thể lựa chọn ph&ugrave; hợp dấu ấn ri&ecirc;ng của từng c&aacute; nh&acirc;n. Sản phẩm sẽ 4 m&agrave;u cho người d&ugrave;ng t&ugrave;y &yacute; lựa chọn. Ngo&agrave;i ra th&igrave; chiếc tai nghe kh&ocirc;ng d&acirc;y n&agrave;y sẽ cho ph&eacute;p bạn c&oacute; thể nắm bắt được mọi thứ xung quanh m&agrave; kh&ocirc;ng cần bỏ tai nghe xuống.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>3 micro c&ugrave;ng c&ocirc;ng nghệ nhận diện giọng n&oacute;i, đ&agrave;m thoại đỉnh cao</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Điều m&agrave; người d&ugrave;ng quan t&acirc;m nhất khi mua một sản phải tai nghe ch&iacute;nh l&agrave; chất lượng &acirc;m thanh m&agrave; n&oacute; sở hữu. Đi k&egrave;m với thương hiệu của m&igrave;nh v&agrave; những c&ocirc;ng nghệ &acirc;m thanh đ&atilde; đạt được th&agrave;nh c&ocirc;ng từ mẫu Galaxy Buds trước đ&oacute; th&igrave; d&ograve;ng sản phẩm mới n&agrave;y sẽ cho người d&ugrave;ng trải nghiệm &acirc;m thanh ho&agrave;n thiện hơn rất nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"3 Micro\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/earphone/samsung-galaxy-buds-2-3.JPG\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe&nbsp;Samsung Buds2 với hệ thống ba micro v&agrave; bộ thu &acirc;m t&iacute;ch hợp nhờ đ&oacute; c&aacute;c cuộc gọi được diễn ra r&otilde; r&agrave;ng hơn. B&ecirc;n cạnh đ&oacute; l&agrave; c&ocirc;ng nghệ nhận diện giọng n&oacute;i VPU gi&uacute;p loại bỏ những &acirc;m thanh kh&ocirc;ng mong muốn gi&uacute;p giảm thiểu sự gi&aacute;n đoạn trong những cuộc tr&ograve; chuyện. C&ugrave;ng với thiết kế tai nghe nh&ocirc; ra thấp gi&uacute;p giảm thiểu t&igrave;nh trạng tiếp x&uacute;c với gi&oacute; mang lại khả năng đ&agrave;m thoại hiệu quả hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&gt;&gt;&gt; Xem th&ecirc;m th&ocirc;ng tin&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-buds-2-pro.html\" target=\"_blank\">Galaxy Buds2 Pro</a>&nbsp;sắp được ra mắt trong 2022.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>L&agrave;m chủ &acirc;m thanh với c&ocirc;ng nghệ chống ồn chủ động</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Buds2 được trang bị hai&nbsp;micro với khả năng kiểm so&aacute;t tiếng ồn một c&aacute;ch hiệu quả.&nbsp;Hai micro n&agrave;y gi&uacute;p ph&aacute;t hiện v&agrave; chủ động chặn những tiếng ồn kh&ocirc;ng mong muốn xung quanh gi&uacute;p người d&ugrave;ng thỏa sức đắm ch&igrave;m trong bữa tiệc &acirc;m nhạc.&nbsp;C&ocirc;ng nghệ chống ồn chủ động ANC n&agrave;y gi&uacute;p loại bỏ tiếng ồn hiệu quả l&ecirc;n đến 98%. Nhờ đ&oacute; mang lại trải nghiệm sử dụng từ nghe nhạc, đọc s&aacute;ch, chơi game,.. vượt trội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Công nghệ chống ồn chủ động ANC\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/earphone/samsung-galaxy-buds-2-4.JPG\" /></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>T&ugrave;y chỉnh &acirc;m thanh theo phong c&aacute;ch ri&ecirc;ng bạn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe&nbsp;Samsung Buds2 với khả năng t&ugrave;y chỉnh &acirc;m thanh mang lại cho người d&ugrave;ng những trải nghiệm theo sở th&iacute;ch. Cụ thể, với 6 bộ t&ugrave;y chỉnh, người d&ugrave;ng c&oacute; thể thoải m&aacute;i điều chỉnh v&agrave; c&agrave;i đặt chế độ &acirc;m thanh theo phong c&aacute;ch, sở th&iacute;ch của bản th&acirc;n.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Kết nối bluetooth chuẩn v5.2</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">N&oacute;i về cấu h&igrave;nh của<strong>&nbsp;Galaxy Buds2</strong>&nbsp;th&igrave; sản phẩm được Samsung trang bị vi xử l&yacute; BES2500ZP. Một điều kh&aacute; bất ngờ đối với những t&iacute;n đồ của thương hiệu đến từ H&agrave;n Quốc. Kh&ocirc;ng những thế, khi trang bị được một bộ nhớ đủ lớn trong chiếc tai nghe kh&ocirc;ng d&acirc;y th&igrave; sản phẩm ho&agrave;n to&agrave;n c&oacute; thể hoạt động độc lập b&ecirc;n cạnh c&aacute;c sản phẩm như điện thoại Samsung hoặc Samsung Galaxy Tab.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trang bị bộ nhớ trong lên đến 8 GB và kết nối bluetooth chuẩn v5.0\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/earphone/samsung-galaxy-buds-2-1_1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sản phẩm Samsung Buds2 sẽ sử dụng chuẩn kết nối&nbsp;bluetooth 5.2. T&iacute;nh năng bluetooth th&ocirc;ng minh cho ph&eacute;p sản phẩm&nbsp;<strong>tai nghe Galaxy Buds2</strong>&nbsp;tự động kết nối với thiết bị Galaxy của bạn khi vừa mới sử dụng. Tuy nhi&ecirc;n, một điều kh&aacute;c với Samsung Galaxy Buds th&igrave; Samsung Galaxy Buds2 chỉ kết nối với c&aacute;c sản phẩm thuộc d&ograve;ng Galaxy hoặc c&aacute;c đồng hồ th&ocirc;ng minh do ch&iacute;nh h&atilde;ng Samsung sản xuất. Điều n&agrave;y sẽ hạn chế khả năng kết nối của sản phẩm nhưng sẽ x&acirc;y dựng một hệ sinh th&aacute;i kh&eacute;p k&iacute;n cũng như mang đến trải nghiệm tốt hơn.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Pin sử dụng nhiều giờ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Buds2 với vi&ecirc;n pin dung lượng lớn mang lại thời gian sử dụng li&ecirc;n tục trong nhiều giờ. Cụ thể với mỗi lần sạc đ&acirc;y, tai nghe sẽ cho 5 giờ chơi nhạc v&agrave; nếu t&iacute;nh cả case sạc th&igrave; tổng thời gian c&oacute; thể l&ecirc;n đến 20 giờ.</span></p>\r\n', 0, '1657291868_buds2tim.jpg', 2990000, 45, 37),
(43, 'Galaxy Buds Pro', '<p>Đặc điểm nổi bật</p>\r\n\r\n<ul>\r\n	<li>N&acirc;ng tầm trải nghiệm &acirc;m v&agrave; chất lượng cuộc gọi với&nbsp;<strong>chống ồn chủ động (ANC)</strong>.</li>\r\n	<li>Kết nối kh&ocirc;ng d&acirc;y&nbsp;<strong>Bluetooth 5.0&nbsp;</strong>dễ d&agrave;ng với c&aacute;c thiết bị ngo&agrave;i, đường truyền ổn định.</li>\r\n	<li>Chuẩn &acirc;m thanh studio với&nbsp;<strong>loa 2 chiều AKG</strong>&nbsp;mạnh mẽ.</li>\r\n	<li>Tận hưởng&nbsp;<strong>&acirc;m thanh v&ograve;m</strong>&nbsp;l&ocirc;i cuốn, chuẩn điện ảnh từ&nbsp;<strong>360 Audio</strong>.</li>\r\n	<li>Đ&agrave;m thoại r&otilde; r&agrave;ng với hệ thống&nbsp;<strong>3 mic</strong>&nbsp;v&agrave; bộ phận thu nhận giọng n&oacute;i (Voice Pickup Unit).</li>\r\n	<li>Loa&nbsp;<strong>2 chiều</strong>&nbsp;(loa trầm 11mm, loa bổng 6.5mm).</li>\r\n	<li>Khả năng kh&aacute;ng nước hiệu quả c&ugrave;ng xếp hạng kh&aacute;ng nước&nbsp;<strong>IPX7</strong>.</li>\r\n	<li>Thời gian sử dụng&nbsp;<strong>5 giờ</strong>&nbsp;v&agrave;&nbsp;<strong>13 giờ</strong>&nbsp;c&ugrave;ng hộp sạc (bật chống ồn), sử dụng&nbsp;<strong>8 giờ</strong>&nbsp;v&agrave;&nbsp;<strong>20 giờ</strong>&nbsp;c&ugrave;ng hộp sạc (tắt chống ồn).</li>\r\n</ul>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/Kit/bluetooth-true-wireless-galaxy-buds-pro-bac-note-2.jpg\" /></p>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế sang trọng, thời thượng c&ugrave;ng hộp sạc đồng nhất m&agrave;u sắc đi k&egrave;m</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-true-wireless-galaxy-buds-pro-bac\" target=\"_blank\" title=\"Tai nghe Bluetooth True Wireless Samsung Buds Pro\">Tai nghe Bluetooth True Wireless Samsung Buds Pro</a>&nbsp;sở hữu vẻ ngo&agrave;i đẹp mắt thời thượng với hai m&agrave;u đen v&agrave; trắng. Thiết kế&nbsp;mới tr&ecirc;n h&igrave;nh dạng tai nghe cổ điển, c&oacute; khả năng l&agrave;m giảm bớt sự kh&oacute; chịu khi sử dụng tai nghe trong nhiều giờ v&agrave; tai nghe cũng nằm chắc chắn ph&iacute;a trong tai khi bạn tập luyện hay vận động. Đồng thời, c&aacute;c lỗ tho&aacute;t kh&iacute; gi&uacute;p c&acirc;n bằng &aacute;p suất trong tai v&agrave; tăng lưu lượng kh&ocirc;ng kh&iacute;, tạo cảm gi&aacute;c mềm mại dễ chịu khi sử dụng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe c&oacute; thiết kế nhỏ gọn n&ecirc;n bạn dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch, balo v&agrave; mang theo b&ecirc;n m&igrave;nh di chuyển mọi nơi.&nbsp;Ngo&agrave;i ra, c&oacute; k&egrave;m theo n&uacute;m tai nghe với ba k&iacute;ch thước cho người d&ugrave;ng dễ thay đổi sao cho ph&ugrave; hợp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-1.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hộp sạc gọn đẹp, ph&iacute;a b&ecirc;n trong t&iacute;ch hợp nam ch&acirc;m giữ củ tai an to&agrave;n, hạn chế thất lạc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Hộp sạc\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-2.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Nghe r&otilde; bất kỳ đ&acirc;u c&ugrave;ng c&ocirc;ng nghệ chống ồn chủ động (ANC)</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/tai-nghe-samsung\" target=\"_blank\" title=\"Tham khảo các mẫu tai nghe Samsung tại Thế Giới Di Động\">Tai nghe Samsung</a>&nbsp;cho bạn thoải m&aacute;i&nbsp;gọi điện v&agrave; tr&ograve; chuyện với đối t&aacute;c, bạn b&egrave; m&agrave; kh&ocirc;ng lo ngại tiếng ồn với c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cac-cong-nghe-am-thanh-thuong-thay-tren-tai-nghe-h-1222527#Active\" target=\"_blank\" title=\"Active Noise Cancelling\">Active Noise Canceling</a>&nbsp;lọc ấn tượng đến 99% tiếng ồn xung quanh. B&ecirc;n cạnh đ&oacute;,&nbsp;bạn c&oacute; thể chọn c&aacute;c mức ANC để tăng hoặc giảm &acirc;m thanh ph&ugrave; hợp theo từng kh&ocirc;ng gian t&ugrave;y theo mức độ kh&aacute;c nhau.&nbsp;Nhờ đ&oacute;, mang đến&nbsp;chất lượng cuộc gọi ho&agrave;n hảo nhất tr&ecirc;n tai nghe kh&ocirc;ng d&acirc;y v&agrave; truyền tải r&otilde; r&agrave;ng giọng n&oacute;i, ngay cả khi bạn gọi điện ở nh&agrave; hay ở b&ecirc;n ngo&agrave;i.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Chống ồn Active Noise Cancelling\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-3.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hơn nữa, tai nghe c&ograve;n c&oacute;&nbsp;hệ thống 3 mic t&iacute;ch hợp v&agrave; bộ phận thu nhận giọng n&oacute;i (Voice Pickup Unit), gi&uacute;p mọi từ ngữ đều được truyền tải một c&aacute;ch trọn vẹn nhất. Hơn nữa, tr&ecirc;n tai nghe c&oacute; giải ph&aacute;p chắn gi&oacute; hiện đại v&agrave; ti&ecirc;n tiến gi&uacute;p ngăn chặn &acirc;m thanh của gi&oacute; khi đang gọi điện, cho bạn tận hưởng chất &acirc;m ho&agrave;n hảo hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Tích hợp 3 mic\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-4.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.0 dễ d&agrave;ng với c&aacute;c thiết bị ngo&agrave;i, đường truyền mượt m&agrave; l&ecirc;n đến 10 m</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe trang bị kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.0 cho đường truyền li&ecirc;n kết kh&ocirc;ng d&acirc;y giữa tai nghe v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo một số điện thoại tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm một số laptop tại Thế Giới Di Động\">laptop</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo một số máy tính bảng tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>&nbsp;được duy tr&igrave; tốt trong phạm vi rộng l&ecirc;n đến 10 m, nhờ đ&oacute; bạn thoải m&aacute;i duy tr&igrave; c&aacute;c cuộc gọi hoặc những bản nhạc một c&aacute;ch liền mạch v&agrave; tự do.&nbsp;Kết nối được 2 m&aacute;y đồng thời, linh hoạt d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Xem thêm một số tai nghe tại Thế Giới Di Động\">tai nghe</a>&nbsp;c&ograve;n t&iacute;ch hợp&nbsp;t&iacute;nh năng chuyển đổi hiện đại c&oacute; khả năng ph&aacute;t hiện những g&igrave; cấp b&aacute;ch nhất v&agrave; lập tức chuyển kết nối sang thiết bị đ&oacute;, chẳng hạn như khi bạn đang xem phim tr&ecirc;n m&aacute;y t&iacute;nh bảng v&agrave; c&oacute; cuộc gọi đến th&igrave; &acirc;m thanh sẽ được chuyển sang điện thoại ngay lập tức, sau đ&oacute; khi bạn g&aacute;c m&aacute;y th&igrave; &acirc;m thanh sẽ trở lại m&aacute;y t&iacute;nh bảng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-5.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Kết nối Bluetooth 5.0\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-5.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">D&ugrave;ng li&ecirc;n tục trong 8 giờ, sạc pin đơn giản qua cổng USB-C</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe sẽ cho bạn trải nghiệm li&ecirc;n tục với những cuộc gọi hay những giờ nghe nhạc kh&ocirc;ng gi&aacute;n đoạn với thời lượng d&agrave;i đến hơn 8 giờ v&agrave; c&ograve;n l&acirc;u d&agrave;i hơn khi c&oacute; hộp sạc. Bạn sẽ c&oacute; tổng cộng 18 giờ trải nghiệm ấn tượng khi bật chống ồn với 5 giờ nghe nhạc v&agrave; th&ecirc;m 13 giờ khi sử dụng hộp sạc. Đồng thời, khi tắt chống ồn th&igrave;&nbsp;bạn c&oacute; tổng cộng 28 giờ trải nghiệm, nghe trong 8 giờ v&agrave; th&ecirc;m 20 giờ nữa khi sử dụng với hộp sạc.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Thời lượng pin\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-6.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Khả năng kh&aacute;ng nước hiệu quả c&ugrave;ng xếp hạng chống nước IPX7 mạnh nhất</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Thế Giới Di Động\">Tai nghe bluetooth</a>&nbsp;sở hữu đạt chuẩn chống nước IPX7 c&oacute; khả năng&nbsp;bảo vệ thiết bị khỏi t&aacute;c động của việc ng&acirc;m trong nước l&ecirc;n tới 1 m, trong thời gian 30 ph&uacute;t, nhờ đ&oacute;&nbsp;gi&uacute;p chống nước, mồ h&ocirc;i hiệu quả đảm bảo khả năng truy cập cuộc gọi, nghe nhạc vẫn ổn định khi lỡ bị d&iacute;nh nước.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-7.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Chống nước IPX7\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-7.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">&Acirc;m thanh được c&acirc;n bằng, dải &acirc;m rộng hơn với loa 2 chiều AKG mạnh mẽ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Từng &acirc;m thanh qua tai nghe được chọn lọc cho mọi nốt nhạc đều được thể hiện một c&aacute;ch trọn vẹn, từ &acirc;m treble cao nhất đến &acirc;m bass trầm ho&agrave;n hảo&nbsp;với loa 2 chiều được tinh chỉnh bởi AKG. Loa 2 chiều được t&iacute;ch hợp cho &acirc;m thanh được c&acirc;n bằng v&agrave; dải &acirc;m được rộng hơn với loa trầm 11 mm v&agrave; loa tweeter 6.5 mm cho chất &acirc;m trong trẻo, mượt m&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-8.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Loa 2 chiều\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-8.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Tận hưởng &acirc;m thanh v&ograve;m sống động, đầy kịch t&iacute;nh từ 360 Audio</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe sở hữu c&ocirc;ng nghệ&nbsp;Dolby Head Tracking&trade; sẽ cho bạn như h&ograve;a m&igrave;nh v&agrave;o thế giới sống động của những bộ phim, b&agrave;i nhạc hoặc chương tr&igrave;nh truyền h&igrave;nh bạn y&ecirc;u th&iacute;ch. Đồng thời, c&ocirc;ng nghệ c&oacute; khả năng cảm biến theo d&otilde;i th&ocirc;ng minh v&agrave; x&aacute;c định ch&iacute;nh x&aacute;c hướng ph&aacute;t ra của &acirc;m thanh khi bạn di chuyển đầu, mang lại trải nghiệm nghe &acirc;m thanh ch&acirc;n thực, sống động từng chi tiết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-9.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Âm thanh vòm sống động\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-9.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Xua tan nỗi lo thất lạc, dễ d&agrave;ng định vị tai nghe với ứng dụng SmartThings</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đối với chiếc tai nghe&nbsp;Galaxy Buds Pro n&agrave;y th&igrave; bạn sẽ kh&ocirc;ng phải lo&nbsp;việc t&igrave;m kiếm tai nghe bị thất lạc. Bởi v&igrave; nhờ c&ocirc;ng nghệ&nbsp;SmartThings hiện đại, bạn dễ d&agrave;ng t&igrave;m kiếm v&agrave; định vị, khi đ&oacute; tai&nbsp;nghe của bạn sẽ ph&aacute;t ra tiếng b&iacute;p để bạn c&oacute; thể dễ d&agrave;ng t&igrave;m thấy ch&uacute;ng ở bất kỳ đ&acirc;u trong nh&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem chi tiết hướng dẫn sử dụng v&agrave; tải ứng dụng tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/game-app/smartthings-ung-dung-quan-ly-thiet-bi-thong-minh-236414\" target=\"_blank\" title=\"SmartThings - Ứng dụng quản lý thiết bị thông minh Samsung\">SmartThings - Ứng dụng quản l&yacute; thiết bị th&ocirc;ng minh Samsung</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-10.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - Định vị tai nghe\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-10.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thao t&aacute;c dễ d&agrave;ng, đơn giản chỉ cần chạm nhẹ l&agrave; c&oacute; thể sử dụng&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chỉ với thao t&aacute;c chạm hoặc chạm giữ l&agrave; bạn đ&atilde; c&oacute; thể ph&aacute;t hoặc dừng nhạc, trả lời hoặc từ chối cuộc gọi. Bạn cũng c&oacute; thể đặt tai nghe để thực hiện c&aacute;c t&aacute;c vụ kh&aacute;c bằng c&aacute;ch chạm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Ph&aacute;t hoặc tạm dừng b&agrave;i nhạc chỉ với một lần chạm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Ph&aacute;t b&agrave;i nhạc tiếp theo, trả lời hoặc kết th&uacute;c cuộc gọi chỉ cần chạm hai lần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Ph&aacute;t b&agrave;i nhạc trước chỉ cần chạm ba lần.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- K&iacute;ch hoạt một t&iacute;nh năng đặt trước (mặc định: Khởi chạy t&iacute;nh năng kiểm so&aacute;t tiếng ồn). Từ chối cuộc gọi hoặc kết nối Bluetooth chỉ với thao t&aacute;c chạm v&agrave; giữ tr&ecirc;n tai nghe.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-11.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc - HDSD\" src=\"https://cdn.tgdd.vn/Products/Images/54/242034/bluetooth-true-wireless-galaxy-buds-pro-bac-11.jpg\" title=\"Tai nghe Bluetooth True Wireless Galaxy Buds Pro Bạc\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tai nghe Bluetooth True Wireless Samsung Buds Pro sẽ l&agrave; sự lựa chọn ph&ugrave; hợp v&agrave; tuyệt vời d&agrave;nh cho bạn nếu bạn đang muốn t&igrave;m một chiếc tai nghe c&oacute; thiết kế thời trang, sang trọng c&ugrave;ng c&aacute;c tiện &iacute;ch th&ocirc;ng minh đ&aacute;p ứng c&aacute;c nhu cầu nghe nhạc chất lượng cao th&igrave; kh&ocirc;ng n&ecirc;n bỏ qua em tai nghe n&agrave;y nh&eacute;!</span></p>\r\n', 0, '1657292267_vn-galaxy-buds-pro-r190-sm-r190nzkaxxv-509286836.jpg', 4990000, 45, 37);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(44, 'Galaxy Buds Live', '<h2><strong><span style=\"font-size:16px\">Đặc điểm nổi bật</span></strong></h2>\r\n\r\n<ul>\r\n	<li>Ngoại h&igrave;nh độc đ&aacute;o, mới lạ, chất &acirc;m tốt.</li>\r\n	<li>Đ&agrave;m thoại được cải thiện r&otilde; rệt.</li>\r\n	<li>Kết nối tiện lợi với Bluetooth&nbsp;<strong>5.0</strong>.</li>\r\n	<li>Tương th&iacute;ch với hệ sinh th&aacute;i Galaxy.</li>\r\n	<li>Hiệu quả chống ồn đạt&nbsp;<strong>97%</strong>.</li>\r\n	<li>Tận hưởng &acirc;m nhạc c&ugrave;ng bạn b&egrave; qua&nbsp;<strong>Buds Together.</strong></li>\r\n	<li>Trợ l&yacute; ảo Bixby, kh&aacute;ng nước&nbsp;<strong>IPX2.</strong></li>\r\n	<li>Nghe nhạc&nbsp;<strong>6 giờ</strong>&nbsp;li&ecirc;n tục (<strong>21 giờ</strong>&nbsp;nếu kết hợp hộp sạc).</li>\r\n	<li><strong>5 ph&uacute;t</strong>&nbsp;sạc sử dụng đuợc&nbsp;<strong>1 giờ</strong>&nbsp;nghe nhạc.</li>\r\n	<li>Điều khiển cảm ứng dừng/ph&aacute;t, trả lời cuộc gọi.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth True Wireless Samsung Galaxy Buds Live R180 Gold\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/Kit/true-wireless-samsung-galaxy-buds-live-r180-note.jpg\" /></p>\r\n', '<h3><strong><span style=\"font-size:14px\">Ngoại h&igrave;nh ho&agrave;n to&agrave;n mới, độc đ&aacute;o ri&ecirc;ng biệt</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Buds Live R180 Gold c&oacute;&nbsp;<strong>một tone m&agrave;u đồng</strong>&nbsp;kh&aacute; sang trọng, quyến rũ ở&nbsp;<strong>cả trong v&agrave; ngo&agrave;i</strong>, rất ph&ugrave; hợp cho ph&aacute;i nữ.&nbsp;Hộp sạc của&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-samsung\" title=\"tai nghe Samsung\">tai nghe Samsung</a>&nbsp;Galaxy Buds Live dạng mở nắp vỏ s&ograve;, trọng lượng&nbsp;<strong>42.2 gram</strong>&nbsp;nhỏ gọn, người d&ugrave;ng c&oacute; thể đ&oacute;ng mở bằng một tay kh&aacute; dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040133.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế độc đáo 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040133.jpg\" title=\"Thiết kế độc đáo 252124\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần tai nghe c&oacute; trọng lượng&nbsp;<strong>5,6 gram</strong>, thiết kế c&ocirc;ng th&aacute;i học kh&aacute; vừa vặn gi&uacute;p người sử dụng thoải m&aacute;i khi đeo trong thời gian d&agrave;i m&agrave; kh&ocirc;ng bị đau tai.&nbsp;Bạn c&oacute; thể vừa mang Galaxy Buds Live vừa tham gia c&aacute;c hoạt động như chạy bộ, tập gym m&agrave; kh&ocirc;ng lo tai nghe bị rơi&nbsp;do phần housing của Galaxy Buds Live được Samsung chăm ch&uacute;t để mang lại sự ổn định khi đeo..</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Trải nghiệm &acirc;m thanh mới mẻ với &acirc;m thanh sống động v&agrave; &acirc;m bass s&acirc;u lắng.&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chất &acirc;m của Galaxy Buds Live được đ&aacute;nh gi&aacute; kh&aacute; tốt. Nhờ sự kết hợp giữa loa 12mm được tinh chỉnh bởi&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-am-thanh-akg-985993\" target=\"_blank\" title=\"AKG\">AKG</a>&nbsp;gi&uacute;p tối ưu trải nghiệm nghe tốt hơn với &acirc;m thanh sống động v&agrave; c&oacute; độ vang. B&ecirc;n cạnh đ&oacute;, ống bass truyền tải xuy&ecirc;n suốt c&aacute;c &acirc;m trầm s&acirc;u lắng, mang lại trải nghiệm &acirc;m thanh mới mẻ cho người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040131.jpg\" onclick=\"return false;\"><img alt=\"Âm thanh tốt 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040131.jpg\" title=\"Âm thanh tốt 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chất lượng đ&agrave;m thoại được cải tiến ngay khi ở m&ocirc;i trường ồn &agrave;o</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chất lượng cuộc gọi l&agrave; một trong những ưu điểm nổi bật của Samsung Galaxy Buds Live.&nbsp;Với hệ thống 3 mic cải tiến v&agrave; Bộ thu nhận giọng n&oacute;i (Voice Pickup Unit), Galaxy Buds Live mang đến cho bạn chất lượng cuộc gọi tốt nhất d&ugrave; đang ở ngo&agrave;i đường hay trong một m&ocirc;i trường ồn &agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040129.jpg\" onclick=\"return false;\"><img alt=\"Đàm thoại tốt 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040129.jpg\" title=\"Đàm thoại tốt 252124\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bộ thu nhận giọng n&oacute;i cảm nhận từng chuyển động xương h&agrave;m của bạn. Khi xương h&agrave;m rung, những dữ liệu n&agrave;y sẽ biến đổi th&agrave;nh t&iacute;n hiệu thoại v&agrave; mang đến chất lượng &acirc;m thanh cải tiến - ngay cả trong m&ocirc;i trường c&oacute; tiếng ồn.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Loại bỏ 97% tiếng ồn với c&ocirc;ng nghệ chống ồn tự động</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Một trong những điểm đ&aacute;ng gi&aacute; của Galaxy Buds Live l&agrave; t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-anc-la-gi-co-nen-mua-tai-nghe-chong-on-1301270\" target=\"_blank\" title=\"chống ồn tự động (ANC)\">chống ồn tự động (ANC)</a>. Khi bật t&iacute;nh năng n&agrave;y, 97% &acirc;m thanh của tiếng ồn m&ocirc;i trường b&ecirc;n ngo&agrave;i như xe bus, t&agrave;u hoả bị loại bỏ. Hơn nữa, n&oacute; kh&ocirc;ng lấn &aacute;t c&aacute;c &acirc;m thanh quan trọng như giọng n&oacute;i hay th&ocirc;ng b&aacute;o n&ecirc;n bạn kh&ocirc;ng lo bị lỡ mất th&ocirc;ng tin quan trọng của m&igrave;nh. T&iacute;nh năng n&agrave;y được bật, tắt tuỳ theo &yacute; muốn của người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040119.jpg\" onclick=\"return false;\"><img alt=\"ANC 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040119.jpg\" title=\"ANC 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Tận hưởng &acirc;m nhạc c&ugrave;ng bạn b&egrave;, gh&eacute;p nối dễ d&agrave;ng với m&aacute;y t&iacute;nh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Chia sẻ &acirc;m nhạc với mọi người với t&iacute;nh năng&nbsp;<strong>Buds Together</strong>, cho ph&eacute;p người d&ugrave;ng chia sẻ b&agrave;i h&aacute;t với bạn b&egrave; bằng c&aacute;ch đồng bộ h&oacute;a hai tai nghe Buds với nhau (bao gồm Galaxy Buds Live,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/samsung-galaxy-buds-2-r177n\" title=\"Galaxy Buds\">Galaxy Buds</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-tws-samsung-galaxy-bud-r175\" title=\"Galaxy Buds+\">Galaxy Buds+</a>).</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040125.jpg\" onclick=\"return false;\"><img alt=\"Buds Together 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040125.jpg\" title=\"Buds Together 252124\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Buds Live cũng c&oacute; thể gh&eacute;p nối với m&aacute;y t&iacute;nh của bạn qua t&iacute;nh năng&nbsp;<strong>Swift Pair</strong>&nbsp;tr&ecirc;n m&aacute;y t&iacute;nh&nbsp;<strong>Windows 10</strong>&nbsp;để kết nối cả hai thiết bị.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Trợ l&yacute; ảo th&ocirc;ng minh Bixby</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Sử dụng Galaxy Buds Live để điều lệnh Bixby bằng giọng n&oacute;i, Bixby sẽ thay bạn thực hiện c&aacute;c t&aacute;c vụ như kiểm so&aacute;t điện thoại, quản l&yacute; cuộc gọi, điều chỉnh &acirc;m lượng hoặc kiểm tra thời tiết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040121.jpg\" onclick=\"return false;\"><img alt=\"Trợ lý ảo Bixby 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040121.jpg\" title=\"Trợ lý ảo Bixby 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Kết nối kh&ocirc;ng d&acirc;y mượt m&agrave;, ổn định với Bluetooth 5.0</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Buds Live sử dụng kết nối Bluetooth 5.0, cho ph&eacute;p kết nối với điện thoại Android chạy phi&ecirc;n bản&nbsp;<strong>Android 5</strong>&nbsp;trở l&ecirc;n v&agrave; iPhone chạy phi&ecirc;n bản&nbsp;<strong>iOS 10</strong>&nbsp;hoặc đời cao hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chỉ cần bật Bluetooth tr&ecirc;n điện thoại th&ocirc;ng minh, mở hộp đựng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Xem thêm các loại tai nghe đang bán tại Thế Giới Di Động\">tai nghe</a>, chờ đợi th&ocirc;ng b&aacute;o kết nối hiển thị v&agrave; bạn chỉ cần nhấn v&agrave;o &ldquo;Kết nối&rdquo;, Galaxy Buds Live v&agrave; c&aacute;c thiết bị của bạn đ&atilde; kết nối với nhau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040123.jpg\" onclick=\"return false;\"><img alt=\"Bluetooth 5.0 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040123.jpg\" title=\"Bluetooth 5.0 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Tương th&iacute;ch hệ sinh th&aacute;i Galaxy</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Buds Live c&oacute; thể kết nối với c&aacute;c thiết bị trong hệ sinh th&aacute;i Galaxy của Samsung dễ d&agrave;ng th&ocirc;ng qua&nbsp;<strong>t&agrave;i khoản Samsung</strong>. Khi đ&atilde; gh&eacute;p nối th&agrave;nh c&ocirc;ng, t&ecirc;n thiết bị sẽ được ghi nhớ v&agrave;o danh s&aacute;ch thiết bị kết nối. Sau đ&oacute; nhờ Bluetooth Info Sync, bạn c&oacute; thể tự do chuyển đổi c&aacute;c thiết bị qua lại với nhau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040136.jpg\" onclick=\"return false;\"><img alt=\"Đồng bộ thiết bị Galaxy 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040136.jpg\" title=\"Đồng bộ thiết bị Galaxy 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Tuỳ chỉnh tai nghe qua ứng dụng Galaxy Wearable</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Ứng dụng Galaxy Wearable cho ph&eacute;p người d&ugrave;ng cập nhật phần mềm cho tai nghe cũng như chỉnh sửa điều khiển cảm ứng, c&agrave;i đặt EQ theo &yacute; th&iacute;ch v&agrave; cấp quyền truy cập Bixby.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Link tải ứng dụng:&nbsp;<a href=\"https://play.google.com/store/apps/details?id=com.samsung.android.app.watchmanager2&amp;hl=en&amp;gl=US\" target=\"_blank\" title=\"Android\">Android</a>&nbsp;|&nbsp;<a href=\"https://apps.apple.com/us/app/samsung-gear-s/id1117310635\" target=\"_blank\" title=\"iOS\">iOS</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040154.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Wearable 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040154.jpg\" title=\"Galaxy Wearable 252124\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, ứng dụng Galaxy Wearable c&oacute;<strong>&nbsp;6 bộ chỉnh &acirc;m</strong>&nbsp;kh&aacute;c nhau cho ph&eacute;p bạn tuỳ &yacute; chọn lựa chế độ theo &yacute; th&iacute;ch của m&igrave;nh.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hỗ trợ kh&aacute;ng nước chuẩn IPX2</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040150.jpg\" onclick=\"return false;\"><img alt=\"Kháng nước IPX2 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040150.jpg\" title=\"Kháng nước IPX2 252124\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thời lượng pin sử dụng l&ecirc;n đến 21 giờ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Buds Live c&oacute; thời lượng pin đủ để bạn nghe nhạc trong thời gian&nbsp;<strong>6 giờ li&ecirc;n tục</strong>&nbsp;sử dụng v&agrave;&nbsp;<strong>21 giờ</strong>&nbsp;nếu c&oacute; hộp sạc đi k&egrave;m. Bạn c&oacute; thể sử dụng Galaxy Buds Live cả ng&agrave;y m&agrave; kh&ocirc;ng cần lo vấn đề hết pin, phải mang theo dock sạc hay sạc dự ph&ograve;ng nữa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040152.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin tốt 252124\" src=\"https://cdn.tgdd.vn/Products/Images/54/252124/true-wireless-samsung-galaxy-buds-live-r180-121021-040152.jpg\" title=\"Thời lượng pin tốt 252124\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Buds Live c&oacute; trang bị c&ocirc;ng nghệ sạc nhanh, bạn c&oacute; thể sử dụng&nbsp;<strong>1 giờ</strong>&nbsp;nghe nhạc chỉ với&nbsp;<strong>5 ph&uacute;t</strong>&nbsp;sạc. Ngo&agrave;i ra, dưới nắp pin l&agrave; điểm tiếp x&uacute;c để sạc kh&ocirc;ng d&acirc;y n&ecirc;n bạn c&oacute; thể sử dụng trong trường hợp kh&ocirc;ng c&oacute; nguồn điện.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">C&aacute;c thao t&aacute;c cảm ứng chạm để điều khiển</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">- Chạm một lần: Nhấn v&agrave;o tấm cảm ứng để ph&aacute;t hoặc tạm dừng bản nhạc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Chạm hai lần: Nhấn hai lần v&agrave;o tấm cảm ứng để ph&aacute;t bản nhạc tiếp theo, trả lời hoặc kết th&uacute;c cuộc gọi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Chạm ba lần: Nhấn ba lần v&agrave;o tấm cảm ứng để ph&aacute;t bản nhạc trước đ&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Chạm v&agrave; giữ: K&iacute;ch hoạt t&iacute;nh năng đặt sẵn hoặc từ chối cuộc gọi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với ngoại h&igrave;nh thiết kế ri&ecirc;ng biệt, lạ mắt c&ugrave;ng c&aacute;c t&iacute;nh năng chống ồn chủ động, Buds Together c&ugrave;ng khả năng tương th&iacute;ch tốt với c&aacute;c thiết bị thuộc hệ sinh th&aacute;i Galaxy cũng như c&aacute;c thiết bị kh&aacute;c tốt,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/true-wireless-samsung-galaxy-buds-live-r180\" target=\"_blank\" title=\"Tai nghe Bluetooth True Wireless Samsung Galaxy Buds Live R180 Gold\">Tai nghe Bluetooth True Wireless Samsung Galaxy Buds Live R180 Gold</a>&nbsp;l&agrave; sản phẩm ph&ugrave; hợp cho những ai muốn đang muốn một chiếc tai nghe độc đ&aacute;o c&ugrave;ng với đ&oacute; l&agrave; trải nghiệm &acirc;m thanh kh&aacute;c biệt.</span></p>\r\n', 1, '1657384188_buds live.jpg', 4490000, 50, 37),
(45, ' Galaxy M53', '<h2><span style=\"font-size:14px\"><strong>C&oacute; lẽ 2022 l&agrave; một năm b&ugrave;ng nổ của nh&agrave; Samsung, khi h&atilde;ng li&ecirc;n tục tr&igrave;nh l&agrave;ng nhiều sản phẩm c&oacute; cấu h&igrave;nh mạnh mẽ c&ugrave;ng một mức gi&aacute; b&aacute;n hợp l&yacute; tr&ecirc;n thị trường smartphone tầm trung v&agrave; gi&aacute; rẻ, ti&ecirc;u biểu trong số đ&oacute; c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-m53\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy M53 đang kinh doanh tại Thế Giới Di Động \">Samsung Galaxy M53</a>&nbsp;được xem l&agrave; c&aacute;i t&ecirc;n được cộng đồng người d&ugrave;ng t&iacute;ch cực quan t&acirc;m v&agrave; săn đ&oacute;n kể cả khi chưa ra mắt.</strong></span></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Nổi bật với ngoại h&igrave;nh đầy ấn tượng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Samsung đ&atilde; tr&igrave;nh l&agrave;ng Galaxy M53 với ba phi&ecirc;n bản m&agrave;u v&ocirc; c&ugrave;ng đẹp mắt như m&igrave;nh đang cầm tr&ecirc;n tay, với tone m&agrave;u c&oacute; hơi sẫm đi một t&iacute; l&agrave;m to&aacute;t l&ecirc;n một vẻ huyền b&iacute; đầy cuốn h&uacute;t v&agrave; thanh lịch.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052736.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc thanh lịch - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052736.jpg\" title=\"Màu sắc thanh lịch - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ấn tượng đầu ti&ecirc;n khi nh&igrave;n thấy Galaxy M53 l&agrave; một vẻ ngo&agrave;i tr&ocirc;ng cực kỳ cao cấp, l&agrave;m m&igrave;nh li&ecirc;n tưởng đến một thiết bị ho&agrave;n thiện từ nh&ocirc;m nguy&ecirc;n khối, nhưng thực ra mặt lưng của m&aacute;y được l&agrave;m từ nhựa c&ugrave;ng với hiệu ứng giả kim loại nh&igrave;n rất l&agrave; bắt mắt, cầm nắm tr&ecirc;n tay cũng gi&uacute;p m&igrave;nh trở n&ecirc;n nổi bật hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052738.jpg\" onclick=\"return false;\"><img alt=\"Vẻ ngoài cao cấp - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052738.jpg\" title=\"Vẻ ngoài cao cấp - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt lưng của m&aacute;y cũng c&oacute; một độ nh&aacute;m nhẹ, cho ra cảm gi&aacute;c sờ rất l&agrave; th&iacute;ch trong qu&aacute; tr&igrave;nh sử dụng. Tuy nhi&ecirc;n sau khi sử dụng Galaxy M53 một thời gian, m&igrave;nh cảm thấy mặt lưng của m&aacute;y vẫn xuất hiện t&igrave;nh trạng b&aacute;m dấu v&acirc;n tay v&agrave; mồ h&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052740.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng óng ánh - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052740.jpg\" title=\"Mặt lưng óng ánh - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy M53 được bao bọc bởi một bộ khung kim loại chắc chắn, gi&uacute;p m&aacute;y trở n&ecirc;n bền bỉ hơn trong qu&aacute; tr&igrave;nh sử dụng cũng như to&aacute;t l&ecirc;n một vẻ nh&igrave;n cao cấp. B&ecirc;n cạnh đ&oacute; Samsung cũng đ&atilde; gia c&ocirc;ng phần khung n&agrave;y c&oacute; hơi cong đi một ch&uacute;t, gi&uacute;p qu&aacute; tr&igrave;nh cầm nắm sử dụng trở n&ecirc;n dễ chịu hơn hẳn, được xem l&agrave; một điểm m&agrave; m&igrave;nh rất l&agrave; ưng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052742.jpg\" onclick=\"return false;\"><img alt=\"Khung viền chắc chắn - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052742.jpg\" title=\"Khung viền chắc chắn - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bốn g&oacute;c của m&aacute;y được bo cong nhẹ, nh&igrave;n tổng quan c&oacute; hơi vu&ocirc;ng vức một t&iacute; tr&ocirc;ng kh&aacute; l&agrave; nam t&iacute;nh, kh&ocirc;ng những thế n&oacute; cũng to&aacute;t l&ecirc;n một diện mạo rất hiện đại v&agrave; hợp &ldquo;trend&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052744.jpg\" onclick=\"return false;\"><img alt=\"Bốn góc bo cong - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052744.jpg\" title=\"Bốn góc bo cong - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ở ph&iacute;a b&ecirc;n tay phải sẽ l&agrave; ph&iacute;m nguồn t&iacute;ch hợp cảm biến v&acirc;n tay được đặt b&ecirc;n dưới cụm tăng/giảm &acirc;m lượng, điều n&agrave;y gi&uacute;p cho m&igrave;nh thao t&aacute;c mở kh&oacute;a dễ d&agrave;ng hơn m&agrave; kh&ocirc;ng cảm thấy qu&aacute; với tay.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052746.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay tích hợp cạnh viền - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052746.jpg\" title=\"Cảm biến vân tay tích hợp cạnh viền - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Một điểm m&agrave; m&igrave;nh đ&aacute;nh gi&aacute; cao tr&ecirc;n chiếc m&aacute;y n&agrave;y l&agrave; về phần tối ưu viền m&agrave;n h&igrave;nh, tuy kh&ocirc;ng biết k&iacute;ch thước ch&iacute;nh x&aacute;c l&agrave; bao nhi&ecirc;u nhưng theo m&igrave;nh cảm nhận giữa Galaxy M53 v&agrave; những sản phẩm kh&aacute;c trong c&ugrave;ng ph&acirc;n kh&uacute;c, th&igrave; đ&acirc;y l&agrave; một trong số &iacute;t sản phẩm l&agrave;m m&igrave;nh ấn tượng khi cho ra cảm gi&aacute;c nh&igrave;n cực kỳ &ldquo;đ&atilde; mắt&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052748.jpg\" onclick=\"return false;\"><img alt=\"Viền màn hình mỏng - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052748.jpg\" title=\"Viền màn hình mỏng - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Trải nghiệm tốt hơn tr&ecirc;n m&agrave;n h&igrave;nh lớn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy M53 sử dụng kiểu thiết kế m&agrave;n h&igrave;nh đục lỗ c&oacute; k&iacute;ch thước l&ecirc;n đến 6.7 inch rất ấn tượng, điều n&agrave;y gi&uacute;p cho m&igrave;nh đọc văn bản, xem tin tức hay giải tr&iacute; tốt hơn khi nhiều nội dung được hiển thị tr&ecirc;n c&ugrave;ng một khung h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052749.jpg\" onclick=\"return false;\"><img alt=\"Màn hình đục lỗ - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052749.jpg\" title=\"Màn hình đục lỗ - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y được sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Super AMOLED Plus, cho ra h&igrave;nh ảnh c&oacute; độ ch&iacute;nh x&aacute;c về m&agrave;u sắc cao, hạn chế t&igrave;nh trạng &aacute;m xanh - &aacute;m v&agrave;ng thường xuất hiện tr&ecirc;n một v&agrave;i sản phẩm c&oacute; m&agrave;n AMOLED th&ocirc;ng thường, gi&uacute;p m&igrave;nh an t&acirc;m hơn tr&ecirc;n c&aacute;c c&ocirc;ng việc chỉnh sửa h&igrave;nh ảnh m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề sai lệch m&agrave;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-plus-la-gi-905774\" target=\"_blank\" title=\"Màn hình Super AMOLED Plus là gì? Thiết bị nào có Super AMOLED Plus?\">M&agrave;n h&igrave;nh Super AMOLED Plus l&agrave; g&igrave;? Thiết bị n&agrave;o c&oacute; Super AMOLED Plus?</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052751.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc hiển thị rực rỡ- Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052751.jpg\" title=\"Màu sắc hiển thị rực rỡ- Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về phần thao t&aacute;c vuốt chạm h&agrave;ng ng&agrave;y tr&ecirc;n Galaxy M53 theo m&igrave;nh cảm nhận l&agrave; rất trơn tru mượt m&agrave; nhờ m&agrave;n h&igrave;nh 120 Hz, bạn c&oacute; thể chuyển sang tần số 60 Hz để tiết kiệm pin hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052753.jpg\" onclick=\"return false;\"><img alt=\"Thao tác mượt mà - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052753.jpg\" title=\"Thao tác mượt mà - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với độ ph&acirc;n giải Full HD+ (1080 x 2400 Pixels), gi&uacute;p m&igrave;nh xem phim v&agrave; chơi game được r&otilde; r&agrave;ng hơn, ngắm nh&igrave;n nh&acirc;n vật hay quang cảnh một c&aacute;ch sắc n&eacute;t v&agrave; cực kỳ ch&acirc;n thực như đang h&ograve;a m&igrave;nh nội dung b&ecirc;n trong m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052755.jpg\" onclick=\"return false;\"><img alt=\"Hiển thị nội dung sắc nét - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052755.jpg\" title=\"Hiển thị nội dung sắc nét - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chụp ảnh sắc n&eacute;t th&ocirc;ng qua ống k&iacute;nh 108 MP</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy M53 sở hữu 4 camera ở phần mặt lưng với th&ocirc;ng số như sau: Camera ch&iacute;nh 108 MP,<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại camera góc rộng đang kinh doanh tại Thế Giới Di Động \">&nbsp;camera g&oacute;c si&ecirc;u rộng</a>&nbsp;8 MP c&ugrave;ng hai cảm biến x&oacute;a ph&ocirc;ng v&agrave; macro c&oacute; chung độ ph&acirc;n giải 2 MP, mang đến cho m&igrave;nh một khả năng chụp ảnh v&ocirc; c&ugrave;ng sắc n&eacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052757.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 4 camera - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052757.jpg\" title=\"Trang bị 4 camera - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&igrave;nh c&oacute; chụp qua một v&agrave;i tấm ảnh ở ngo&agrave;i trời bằng chế độ chụp ảnh thường, kết quả thu được l&agrave;m m&igrave;nh ấn tượng về độ chi tiết, những v&ugrave;ng như: L&aacute; c&acirc;y, b&oacute;ng v&agrave; hoa đều được lấy n&eacute;t r&otilde; r&agrave;ng, l&uacute;c zoom l&ecirc;n th&igrave; t&igrave;nh trạng vỡ ảnh cũng kh&ocirc;ng đ&aacute;ng kể gi&uacute;p m&igrave;nh thỏa sức chỉnh sửa - hậu kỳ ảnh m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến việc chất lượng bị giảm đi nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052759.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp chế độ thường - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052759.jpg\" title=\"Ảnh chụp chế độ thường - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đối với t&iacute;nh năng chụp ảnh macro, bức h&igrave;nh m&agrave; m&igrave;nh chụp được cũng c&oacute; độ sắc n&eacute;t cao, tone m&agrave;u c&oacute; hơi trầm đi một t&iacute; tr&ocirc;ng rất l&agrave; nghệ thuật.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052801.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp macro - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052801.jpg\" title=\"Ảnh chụp macro - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần mặt trước của m&aacute;y được trang bị camera selfie c&oacute; độ ph&acirc;n giải l&ecirc;n đến 32 MP gi&uacute;p m&igrave;nh tự tin hơn tr&ecirc;n những bức ảnh tự chụp nhờ chất lượng ảnh cực cao, ngo&agrave;i ra c&aacute;c t&aacute;c vụ video call cũng trở n&ecirc;n r&otilde; n&eacute;t gi&uacute;p m&igrave;nh học tập online hay li&ecirc;n lạc c&ocirc;ng việc được tốt hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052803.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie 32 MP - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052803.jpg\" title=\"Camera selfie 32 MP - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thỏa sức chiến game một c&aacute;ch mượt m&agrave;&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Điểm &ldquo;đ&aacute;ng tiền&rdquo; tiếp theo tr&ecirc;n Samsung Galaxy M53 l&agrave; về phần hiệu năng khi được trang bị b&ecirc;n trong con chip MediaTek Dimensity 900 5G, với số điểm hiệu năng cực khủng trong ph&acirc;n kh&uacute;c gi&aacute; như sau: 683 (đơn nh&acirc;n), 2146 (đa nh&acirc;n) tr&ecirc;n BenchMark v&agrave; 8654 điểm tr&ecirc;n PCMark.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-dong-chip-xu-ly-cpu-mediatek-dimensity-1376387\" target=\"_blank\" title=\"Tìm hiểu về dòng chip xử lý (CPU) MediaTek Dimensity\">T&igrave;m hiểu về d&ograve;ng chip xử l&yacute; (CPU) MediaTek Dimensity</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052805.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052805.jpg\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về khả năng chơi game tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile m&agrave; m&igrave;nh c&oacute; chơi qua ở mức cấu h&igrave;nh cao nhất, tốc độ khung h&igrave;nh đo được dao động ở mức trung b&igrave;nh 61 FPS, gi&uacute;p m&igrave;nh y&ecirc;n t&acirc;m &ldquo;xả skill&rdquo; trong giao tranh tổng m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề giật lag.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052807.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình Liên Quân Mobile - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052807.jpg\" title=\"Cấu hình Liên Quân Mobile - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*C&agrave;i đặt cấu h&igrave;nh Li&ecirc;n Qu&acirc;n Mobile</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-250522-054616.jpg\" onclick=\"return false;\"><img alt=\"Mức đo FPS - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-250522-054616.jpg\" title=\"Mức đo FPS - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*Biểu đồ đo FPS tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ở PUBG Mobile cho mức đồ họa HDR c&ugrave;ng tốc độ khung h&igrave;nh cực cao, h&igrave;nh ảnh cho ra rất l&agrave; đẹp mắt, hiệu ứng đổ b&oacute;ng tr&ocirc;ng cực kỳ ch&acirc;n thực, qu&aacute; tr&igrave;nh chơi diễn ra kh&aacute; l&agrave; su&ocirc;n sẻ, nh&acirc;n vật di chuyển linh hoạt v&agrave; &iacute;t khi gặp phải hiện tượng khựng m&agrave;n h&igrave;nh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052809.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình PUBG Mobile - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052809.jpg\" title=\"Cấu hình PUBG Mobile - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*C&agrave;i đặt cấu h&igrave;nh PUBG Mobile</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-250522-054614.jpg\" onclick=\"return false;\"><img alt=\"Mức đo FPS - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-250522-054614.jpg\" title=\"Mức đo FPS - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*Mức đo FPS tr&ecirc;n PUBG Mobile</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mức FPS m&igrave;nh đo được ở cấu h&igrave;nh n&agrave;y l&agrave; loanh quanh 40, con số n&agrave;y c&oacute; thể n&acirc;ng l&ecirc;n gần 57 - 58 khi m&igrave;nh chuyển sang mức đồ họa mượt v&agrave; tốc độ khung h&igrave;nh cực độ, tuy chất lượng h&igrave;nh ảnh c&oacute; giảm đi kh&aacute; nhiều nhưng đổi lại sự ổn định cho xuy&ecirc;n suốt qu&aacute; tr&igrave;nh chơi, thiết bị cũng được hoạt động nhẹ nh&agrave;ng hơn hẳn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052811.jpg\" onclick=\"return false;\"><img alt=\"Chiến game PUBG Mobile - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052811.jpg\" title=\"Chiến game PUBG Mobile - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, RAM M53 hỗ trợ l&ecirc;n đến 16 GB ( 8 GB mặc định + 8 GB mở rộng) đối với phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb-tro-len\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại RAM 8 GB đang kinh doanh tại Thế Giới Di Động \">điện thoại RAM 8 GB</a>&nbsp;m&agrave; m&igrave;nh đang cầm tr&ecirc;n tay, gi&uacute;p cho mọi t&aacute;c vụ đa nhiệm hay đ&oacute;ng/mở ứng dụng được vận h&agrave;nh hết sức l&agrave; trơn tru mượt m&agrave;, t&igrave;nh trạng tải lại ứng dụng cũng hiếm khi xảy ra trong l&uacute;c m&igrave;nh mở nhiều ứng dụng c&ugrave;ng một l&uacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052813.jpg\" onclick=\"return false;\"><img alt=\"Đa nhiệm mượt mà - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052813.jpg\" title=\"Đa nhiệm mượt mà - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Sử dụng d&agrave;i l&acirc;u với pin tr&acirc;u cực lớn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Cung cấp năng lượng cho m&aacute;y l&agrave; vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n đến 5000 mAh mang lại cho m&igrave;nh thời lượng sử dụng hơn 9 tiếng* cho c&aacute;c t&aacute;c vụ như: Nhắn tin, đ&agrave;m thoại, nghe nhạc, xem phim v&agrave; chơi game.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052815.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng sử dụng lâu dài - Samsung Galaxy M53\" src=\"https://cdn.tgdd.vn/Products/Images/42/247364/samsung-galaxy-m53-160522-052815.jpg\" title=\"Thời lượng sử dụng lâu dài - Samsung Galaxy M53\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhằm tối ưu thời gian sạc đầy vi&ecirc;n pin lớn th&igrave; nh&agrave; sản xuất&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại Samsung  đang kinh doanh tại Thế Giới Di Động \">điện thoại Samsung</a>&nbsp;cũng đ&atilde; trang bị th&ecirc;m cho người d&ugrave;ng c&ocirc;ng nghệ sạc pin nhanh c&oacute; c&ocirc;ng suất 25 W, giờ đ&acirc;y người d&ugrave;ng chỉ mất khoảng 1 giờ 50 ph&uacute;t* l&agrave; c&oacute; thể lấp đầy từ 0 - 100%, điều n&agrave;y gi&uacute;p bạn quay lại c&ocirc;ng việc hay giải tr&iacute; một c&aacute;ch nhanh ch&oacute;ng.</span></p>\r\n', 0, '1657384485_samsung-galaxy-m53-xanhduong-1.jpg', 12490000, 20, 22);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(46, 'Galaxy M33 5G', '<h2><span style=\"font-size:14px\"><strong>Kh&ocirc;ng chỉ g&acirc;y ấn tượng với cộng đồng người d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung\" target=\"_blank\">smartphone</a>&nbsp;bởi mức gi&aacute; b&aacute;n,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-m33-5g\" target=\"_blank\">Samsung Galaxy M33 5G</a>&nbsp;c&ograve;n mang đến cho người d&ugrave;ng một bộ th&ocirc;ng số kỹ thuật ấn tượng, camera độ ph&acirc;n giải cao c&ugrave;ng một thiết kế v&ocirc; c&ugrave;ng bắt mắt, hứa hẹn mang đến những trải nghiệm sử dụng tốt nhất d&agrave;nh cho bạn.</strong></span></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Kiểu d&aacute;ng thiết kế đặc trưng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Ấn tượng đầu ti&ecirc;n khi nh&igrave;n thấy Galaxy M33 l&agrave; chiếc m&aacute;y n&agrave;y c&oacute; một mặt lưng tuy l&agrave; ho&agrave;n thiện từ nhựa nhưng lại c&oacute; hiệu ứng giả kim loại l&agrave;m m&igrave;nh li&ecirc;n tưởng đến thiết bị được l&agrave;m từ nh&ocirc;m nguy&ecirc;n khối tr&ocirc;ng rất bắt mắt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024635.jpg\" onclick=\"return false;\"><img alt=\"Diện mạo nổi bật - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024635.jpg\" title=\"Diện mạo nổi bật - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhờ sử dụng thiết kế mặt lưng nh&aacute;m nhẹ, Galaxy M33 mang đến ưu điểm hạn chế lưu lại vết b&aacute;m mồ h&ocirc;i v&agrave; dấu v&acirc;n tay kh&aacute; tốt, kh&ocirc;ng những thế n&oacute; c&ograve;n cho ra cảm gi&aacute;c sờ rất th&iacute;ch hay tăng độ ma s&aacute;t l&agrave;m cho m&igrave;nh cầm nắm chắc chắn hơn m&agrave; kh&ocirc;ng cảm thấy trơn tuột.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024637.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng hoàn thiện nhám - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024637.jpg\" title=\"Mặt lưng hoàn thiện nhám - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cạnh viền b&ecirc;n phải bố tr&iacute; ph&iacute;m tăng/giảm &acirc;m lượng c&ugrave;ng n&uacute;t nguồn t&iacute;ch hợp cảm biến v&acirc;n tay, gi&uacute;p m&igrave;nh dễ d&agrave;ng mở kh&oacute;a thiết bị chỉ bằng một tay cực kỳ tiện lợi v&agrave; nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024639.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay cạnh viền - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024639.jpg\" title=\"Cảm biến vân tay cạnh viền - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt trước m&aacute;y được trang bị tấm nền TFT LCD, được mọi người đ&aacute;nh gi&aacute; kh&ocirc;ng cao về phần khả năng hiển thị, nhưng sau khi trải nghiệm thực tế th&igrave; m&igrave;nh thấy chất lượng ảnh tr&ecirc;n m&agrave;n h&igrave;nh vẫn rất ổn, độ tương phản cao, g&oacute;c nh&igrave;n rộng, với mức gi&aacute; phải chăng th&igrave; chất lượng cho ra như vậy l&agrave; ho&agrave;n to&agrave;n hợp l&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-tft-lcd-la-gi-co-uu-diem-gi-thiet-bi-nao-905743\" target=\"_blank\">M&agrave;n h&igrave;nh TFT LCD l&agrave; g&igrave;? C&oacute; ưu điểm g&igrave;? Thiết bị n&agrave;o d&ugrave;ng m&agrave;n h&igrave;nh TFT?</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024641.jpg\" onclick=\"return false;\"><img alt=\"Màn hình hiển thị đẹp mắt - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024641.jpg\" title=\"Màn hình hiển thị đẹp mắt - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy M33 sử dụng thiết kế m&agrave;n h&igrave;nh giọt nước c&oacute; k&iacute;ch thước 6.6 inch v&agrave; độ ph&acirc;n giải Full HD+ (1080 x 2408 Pixels), mang đến cho m&igrave;nh những khung h&igrave;nh c&oacute; độ chi tiết cao, t&aacute;i hiện h&igrave;nh ảnh sống động c&ugrave;ng một kh&ocirc;ng gian hiển thị rộng lớn hỗ trợ rất tốt cho c&aacute;c c&ocirc;ng việc đọc tin tức, lướt mạng x&atilde; hội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024643.jpg\" onclick=\"return false;\"><img alt=\"Màn hình giọt nước - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024643.jpg\" title=\"Màn hình giọt nước - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điều m&agrave; rất &iacute;t sản phẩm c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute; c&oacute; thể l&agrave;m được như Galaxy M33 đ&oacute; ch&iacute;nh l&agrave; hỗ trợ tần số qu&eacute;t 120 Hz, những thao t&aacute;c vuốt chạm được diễn ra một c&aacute;ch mượt m&agrave;, chơi game FPS cao đ&atilde; mắt hơn rất nhiều so với m&agrave;n h&igrave;nh th&ocirc;ng thường (60 Hz).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tan-so-quet-tren-dien-thoai-la-gi-co-phai-la-xu-hu-1343818\" target=\"_blank\">Tần số qu&eacute;t tr&ecirc;n điện thoại l&agrave; g&igrave;? C&oacute; phải l&agrave; xu hướng tương lai?</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024645.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ tần số quét cao - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024645.jpg\" title=\"Hỗ trợ tần số quét cao - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Sử dụng mượt m&agrave; nhờ chipset đến từ Samsung</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Điểm nổi bật nhất tr&ecirc;n Galaxy M33 l&agrave; về khả năng xử l&yacute; ổn định tr&ecirc;n c&aacute;c t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y như l&agrave; lướt web, lướt facebook, tiktok, nghe gọi, nhắn tin, đọc b&aacute;o nhờ trang bị chipset được ch&iacute;nh&nbsp;<a href=\"https://www.thegioididong.com/samsung\">Samsung</a>&nbsp;nghi&ecirc;n cứu v&agrave; ph&aacute;t triển mang t&ecirc;n Exynos 1280 8 nh&acirc;n c&oacute; hiệu suất tối đa 2.4 GHz.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024647.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm mượt mà - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024647.jpg\" title=\"Trải nghiệm mượt mà - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&igrave;nh c&oacute; d&ugrave;ng qua ứng dụng đ&aacute;nh gi&aacute; hiệu năng để kiểm tra xem Galaxy M33 c&oacute; thực sự khai th&aacute;c tối đa sức mạnh tr&ecirc;n chip hay kh&ocirc;ng v&agrave; kết quả c&oacute; được như sau: 739 (đơn nh&acirc;n), 1876 (đa nh&acirc;n) tr&ecirc;n BenchMark v&agrave; 10390 điểm tr&ecirc;n PCMark.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024648.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024648.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*BenchMark b&ecirc;n tr&aacute;i v&agrave; PCMark b&ecirc;n phải</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Để mang đến cho người d&ugrave;ng một c&aacute;i nh&igrave;n trực quan nhất về khả năng chơi game tr&ecirc;n Galaxy M33 5G, th&igrave; m&igrave;nh đ&atilde; chơi qua hai tựa game hiện đang rất thịnh h&agrave;nh l&agrave; Li&ecirc;n Qu&acirc;n Mobile v&agrave; PUBG Mobile.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024650.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024650.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đầu li&ecirc;n l&agrave; Li&ecirc;n Qu&acirc;n Mobile ở mức c&agrave;i đặt cấu h&igrave;nh cao nhất m&agrave; m&aacute;y đạt được, m&aacute;y vận h&agrave;nh kh&aacute; l&agrave; trơn tru từ giai đoạn chọn tướng cho đến đi đường, nh&acirc;n vật di chuyển mượt m&agrave; v&agrave; kh&ocirc;ng thấy t&igrave;nh trạng giật lag khi tung skill, những pha giao tranh tổng cũng được xử l&yacute; một c&aacute;ch ổn định với tốc độ khung h&igrave;nh dao động loanh quanh 55 - 60 PFS.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024652.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình Liên Quân Mobile - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024652.jpg\" title=\"Cấu hình Liên Quân Mobile - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*Cấu h&igrave;nh (b&ecirc;n tr&ecirc;n) v&agrave; mức FPS đo được (b&ecirc;n dưới)</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tiếp đến l&agrave; PUBG Mobile ở mức đồ họa HD v&agrave; tốc độ khung h&igrave;nh cao, h&igrave;nh ảnh game cho ra tr&ocirc;ng kh&aacute; ch&acirc;n thực, quang cảnh được t&aacute;i hiện r&otilde; r&agrave;ng gi&uacute;p m&igrave;nh nhận diện kẻ địch ch&iacute;nh x&aacute;c hơn, t&igrave;nh trạng khựng nhẹ đ&ocirc;i l&uacute;c vẫn c&ograve;n diễn ra nhưng kh&ocirc;ng đến mức kh&oacute; chịu, FPS dao động loanh quanh mức 28 - 30.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024654.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình PUBG Mobile - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024654.jpg\" title=\"Cấu hình PUBG Mobile - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*Cấu h&igrave;nh (b&ecirc;n tr&ecirc;n) v&agrave; mức FPS đo được (b&ecirc;n dưới)</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về phần đ&oacute;ng/mở ứng dụng hay thao t&aacute;c đa nhiệm đều được Galaxy M33 5G xử l&yacute; su&ocirc;n sẻ v&agrave; cực kỳ nhanh ch&oacute;ng m&agrave; kh&ocirc;ng gặp phải t&igrave;nh trạng tải lại từ đầu, bởi được hỗ trợ t&iacute;nh năng &ldquo;RAM Plus&rdquo; gi&uacute;p n&acirc;ng tổng số RAM l&ecirc;n đến 12 GB (6 GB mặc định + 6 GB mở rộng).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/virtual-ram-tren-smartphone-la-gi-day-la-xu-huong-tuong-lai-1337924\" target=\"_blank\">RAM ảo (Virtual RAM) tr&ecirc;n smartphone l&agrave; g&igrave;?</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024656.jpg\" onclick=\"return false;\"><img alt=\"Đa nhiệm mượt mà - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024656.jpg\" title=\"Đa nhiệm mượt mà - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Kh&aacute;m ph&aacute; thế giới xung quanh một c&aacute;ch r&otilde; n&eacute;t</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy M33 5G sở hữu 4 camera được đặt đối xứng trong một cụm ở phần mặt lưng với th&ocirc;ng số kỹ thuật lần lượt: Camera ch&iacute;nh 50 MP, camera g&oacute;c si&ecirc;u rộng 5 MP, v&agrave; hai cảm biến x&oacute;a ph&ocirc;ng, macro c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024658.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 4 ống kính - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024658.jpg\" title=\"Trang bị 4 ống kính - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với c&aacute;c bức ảnh chụp đủ s&aacute;ng m&aacute;y mang lại bức ảnh c&oacute; độ chi tiết cao, m&agrave;u sắc được t&aacute;i hiện kh&aacute; ch&iacute;nh x&aacute;c v&agrave; gần như kh&ocirc;ng bị lệch m&agrave;u, hỗ trợ cho c&aacute;c c&ocirc;ng việc chỉnh sửa - in ấn kh&aacute; tốt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024700.jpg\" onclick=\"return false;\"><img alt=\"Chính xác về màu sắc - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024700.jpg\" title=\"Chính xác về màu sắc - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chất lượng chụp đ&ecirc;m th&igrave; Galaxy M33 cho ra kết quả kh&aacute; tốt tuy nhi&ecirc;n vẫn c&ograve;n một số chi tiết được t&aacute;i hiện chưa tốt, c&ograve;n nh&ograve;e ở những v&ugrave;ng c&oacute; độ s&aacute;ng cao, khi m&igrave;nh c&oacute; bật t&iacute;nh năng &ldquo;Ban đ&ecirc;m (Night Mode)&rdquo; th&igrave; thấy t&igrave;nh trạng n&agrave;y được cải thiện đi đ&ocirc;i ch&uacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024702.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp đêm - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024702.jpg\" title=\"Ảnh chụp đêm - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ở chế độ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\">x&oacute;a ph&ocirc;ng</a>&nbsp;th&igrave; bức ảnh mang lại tr&ocirc;ng c&oacute; chiều s&acirc;u, phần l&agrave;m mờ kh&ocirc;ng bị lẹm v&agrave;o chủ thể qu&aacute; nhiều, độ chi tiết ở mức ổn trong ph&acirc;n kh&uacute;c gi&aacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024703.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh xóa phông - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024703.jpg\" title=\"Chụp ảnh xóa phông - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về khả năng chụp ảnh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-macro\" target=\"_blank\">Macro</a>&nbsp;th&igrave; m&agrave;u sắc mang lại c&oacute; độ tương phản tương đối cao v&agrave; tốc độ bắt n&eacute;t ổn, gi&uacute;p m&igrave;nh c&oacute; được những bức ảnh nghệ thuật một c&aacute;ch nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-025947.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp macro - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-025947.jpg\" title=\"Ảnh chụp macro - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Sử dụng thoải m&aacute;i với vi&ecirc;n pin tr&acirc;u</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Cung cấp năng lượng cho Galaxy M33 l&agrave; vi&ecirc;n pin 5000 mAh mang đến thời lượng sử dụng hơn 8 tiếng* khi m&agrave; m&igrave;nh d&ugrave;ng thiết bị để l&agrave;m việc, giải tr&iacute;. Với thời gian sử dụng l&acirc;u như vậy l&agrave;m m&igrave;nh kh&aacute; l&agrave; an t&acirc;m, kh&ocirc;ng qu&aacute; lo lắng đến việc hết pin giữa chừng l&agrave;m gi&aacute;n đoạn trải nghiệm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024708.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng sử dụng - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024708.jpg\" title=\"Thời lượng sử dụng - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thời gian sạc l&agrave;m m&igrave;nh hơi bất ngờ khi chỉ mất 1 giờ 36 ph&uacute;t* để lấp đầy vi&ecirc;n pin từ 0 - 100%, được biết đ&acirc;y l&agrave; th&agrave;nh quả của việc t&iacute;ch hợp c&ocirc;ng nghệ sạc nhanh 25 W do Samsung hỗ trợ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024710.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sạc - Samsung Galaxy M33 5G\" src=\"https://cdn.tgdd.vn/Products/Images/42/256197/samsung-galaxy-m33-5g-200422-024710.jpg\" title=\"Thời gian sạc - Samsung Galaxy M33 5G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>*Thời gian sử dụng/sạc c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ v&agrave; nhiệt độ của m&aacute;y</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy M33 5G với khả năng xử l&yacute; c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y hay chơi game ổn định, chụp ảnh sắc n&eacute;t đến từng chi tiết nhỏ nhờ camera 50 MP c&ugrave;ng một diện mạo v&ocirc; c&ugrave;ng bắt mắt v&agrave; thời thượng, xứng đ&aacute;ng l&agrave; một sự lựa chọn ưu ti&ecirc;n h&agrave;ng đầu trong ph&acirc;n kh&uacute;c điện thoại tầm trung - gi&aacute; rẻ.</span></p>\r\n', 0, '1657384686_samsung-galaxy-m33-xanh-1-min.jpg', 7690000, 10, 22),
(47, ' Galaxy Tab S6 Lite', '<h2><span style=\"font-size:16px\"><strong>Sau th&agrave;nh c&ocirc;ng của Galaxy Tab S6,&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo một số mẫu sản phẩm Samsung đang kinh doanh tại Thế Giới Di Động \">Samsung</a>&nbsp;tiếp tục chinh phục người d&ugrave;ng với&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s6-lite\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bnagr Samsung Galaxy Tab S6 Lite đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng Samsung Galaxy Tab S6 Lite</a>&nbsp;c&oacute; ph&acirc;n kh&uacute;c gi&aacute; rẻ hơn. Thiết bị vẫn hỗ trợ b&uacute;t S Pen thần th&aacute;nh, thiết kế kim loại cao cấp v&agrave; m&agrave;n h&igrave;nh, &acirc;m thanh giải tr&iacute; đỉnh cao.</strong></span></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế thời thượng v&agrave; cao cấp</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y t&iacute;nh bảng Galaxy Tab S6 Lite sở hữu thiết ấn tượng với độ d&agrave;y chỉ 7mm v&agrave; trọng lượng si&ecirc;u nhẹ 467g, gi&uacute;p người d&ugrave;ng dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch hay mang theo bất k&igrave; đ&acirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite1-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S6 Lite | Thiết kế đẹp mắt\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite1-1.jpg\" title=\"Galaxy Tab S6 Lite | Thiết kế đẹp mắt\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">To&agrave;n th&acirc;n của m&aacute;y được chế t&aacute;c từ kim loại nguy&ecirc;n khối cao cấp, đảm bảo độ bền v&agrave; cho vẻ đẹp sang trọng, thanh tho&aacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt sau của Tab S6 Lite được thiết kế tổng thể được l&agrave;m tối giản gi&uacute;p chiếc m&aacute;y tr&ocirc;ng hiện đại hơn. Dải anten lớn ph&acirc;n t&aacute;ch c&aacute;c th&agrave;nh phần kim loại gi&uacute;p thiết bị bắt s&oacute;ng tốt v&agrave; ổn định hơn.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">B&uacute;t S Pen mới tối ưu hiệu suất sử dụng</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tương tự như tr&ecirc;n Tab S6, phi&ecirc;n bản S6 Lite cũng được trang bị b&uacute;t S Pen l&agrave;m trợ thủ đắc lực trong c&aacute;c c&ocirc;ng việc h&agrave;ng ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&uacute;t S Pen mới tr&ecirc;n Tab S6 Lite được thiết kế lớn hơn với k&iacute;ch thước tương tự như c&acirc;y b&uacute;t th&ocirc;ng thường cho cảm gi&aacute;c cầm tự nhi&ecirc;n v&agrave; thoải m&aacute;i. Tối ưu năng suất viết, vẽ tr&ecirc;n m&aacute;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite4-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S6 Lite | Bút S Pen tiện lợi\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite4-1.jpg\" title=\"Galaxy Tab S6 Lite | Bút S Pen tiện lợi\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trải nghiệm viết tay tiếng Việt tr&ecirc;n Tab S6 Lite cho thấy m&aacute;y nhận diện chữ viết rất tốt v&agrave; ch&iacute;nh x&aacute;c, tốc độ viết khoảng 30 từ/ph&uacute;t, tuy kh&ocirc;ng nhanh như khi g&otilde; ph&iacute;m nhưng l&agrave; rất ấn tượng so với thao t&aacute;c nhập liệu tr&ecirc;n m&agrave;n h&igrave;nh cảm ứng. B&uacute;t cũng &iacute;t g&acirc;y mỏi khi sử dụng l&acirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Một ưu điểm kh&aacute;c S Pen mới l&agrave; kh&ocirc;ng sử dụng pin v&agrave; kh&ocirc;ng cần sạc, n&ecirc;n người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể thao t&aacute;c v&agrave; sử dụng trong một thời gian d&agrave;i.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c t&iacute;nh năng th&ocirc;ng minh kh&aacute;c với S Pen vẫn xuất hiện đầy đủ tr&ecirc;n tab S6 Lite như: Đ&aacute;nh dấu v&agrave; ph&oacute;ng to nội dung l&ecirc;n tới 300% để tương t&aacute;c thuận tiện hơn, dịch th&ocirc;ng minh, ghi ch&uacute; l&ecirc;n m&agrave;n h&igrave;nh, camera AR&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi kh&ocirc;ng c&ograve;n sử dụng, b&uacute;t S Pen tr&ecirc;n Tab S6 Lite cũng c&oacute; thể được cất gọn g&agrave;ng bằng c&aacute;ch đặt l&ecirc;n cạnh b&ecirc;n m&aacute;y, b&uacute;t sẽ d&iacute;nh chặt v&agrave;o m&aacute;y nhờ lực h&uacute;t nam ch&acirc;m. Hoặc kh&aacute;ch h&agrave;ng cũng c&oacute; thể sử dụng bao da để giữ b&uacute;t gọn g&agrave;ng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab S6 Lite | Hỗ trợ bút SPen tiện lợi\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite3.jpg\" title=\"Samsung Galaxy Tab S6 Lite | Hỗ trợ bút SPen tiện lợi\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Trải nghiệm giải tr&iacute; đỉnh cao</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Trải nghiệm h&igrave;nh ảnh sống động vượt trội tr&ecirc;n m&agrave;n h&igrave;nh rộng tới 10.4&rdquo;. Phần viền si&ecirc;u mảnh v&agrave; tỷ lệ m&agrave;n h&igrave;nh rộng 16:10 gi&uacute;p cho kh&ocirc;ng gian giải tr&iacute; v&agrave; l&agrave;m việc cực thoải m&aacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tấm nền PLS LCD với độ ph&acirc;n giải 1200 x 2000 cho h&igrave;nh ảnh r&otilde; n&eacute;t v&agrave; m&agrave;u sắc ch&acirc;n thực.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite5-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S6 Lite | Viền máy mỏng tăng tỉ lệ màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite5-1.jpg\" title=\"Galaxy Tab S6 Lite | Viền máy mỏng tăng tỉ lệ màn hình\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh phần h&igrave;nh ảnh chất lượng cao, phần &acirc;m thanh tr&ecirc;n Galaxy Tab S6 Lite cũng được chăm ch&uacute;t với bộ loa k&eacute;p đến từ AKG v&agrave; c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos sống động. Khi so s&aacute;nh thực nghiệm với flagship Galaxy Note 10 th&igrave; chất &acirc;m tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng kinh doanh tại Thegioididong.com\">tablet</a>&nbsp;n&agrave;y thậm ch&iacute; chắc v&agrave; ấm hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung cũng tuy&ecirc;n bố thiết bị c&oacute; thể xem video li&ecirc;n tục đến 13 giờ nhờ vi&ecirc;n pin lớn 7040 mAh. Ngo&agrave;i ra c&ocirc;ng nghệ sạc nhanh 15 W gi&uacute;p r&uacute;t ngắn tối đa thời gian sạc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Về cấu h&igrave;nh, Tab S6 Lite được trang bị chip xử l&yacute; Exynos 9611 8 nh&acirc;n,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-4gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 4 GB đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute; RAM 4 GB</a>&nbsp;v&agrave; dung lượng bộ nhớ trong 64 GB kh&aacute; thoải m&aacute;i.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite3-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S6 Lite | Có hỗ trợ thẻ nhớ ngoài\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite3-1.jpg\" title=\"Galaxy Tab S6 Lite | Có hỗ trợ thẻ nhớ ngoài\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị dễ d&agrave;ng đ&aacute;p ứng tốt nhu cầu sử dụng xem phim, lướt web hay chơi c&aacute;c tựa game phổ biến tr&ecirc;n di động như Call of Duty hay Gunny...</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra Tab S6 Lite cũng hỗ trợ SIM nghe gọi như một chiếc smartphone thực thụ v&agrave; khả năng kết nối 4G nhanh ch&oacute;ng.</span></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite6.jpg\" onclick=\"return false;\"><span style=\"font-size:14px\"><img alt=\"Samsung Galaxy Tab S6 Lite | Giao diện OneUI ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/522/219912/samsung-galaxy-tab-s6-lite6.jpg\" title=\"Samsung Galaxy Tab S6 Lite | Giao diện OneUI ấn tượng\" /></span></a></p>\r\n', 0, '1657388480_samsung-galaxy-z-fold3-5g-xanh-reu-2.jpg', 9990000, 10, 30),
(48, 'Samsung Galaxy Tab S7', '<p><img alt=\"Samsung Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/Slider/-samsung-galaxy-tab-s7-1z.jpg\" /></p>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế tr&agrave;n viền cao cấp, thời thượng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 vẫn giữ nguy&ecirc;n thiết kế kim loại nguy&ecirc;n khối, g&oacute;c cạnh như người tiền nhiệm Samsung Galaxy Tab S6&nbsp;trước đ&oacute;, cho cảm gi&aacute;c sang trọng v&agrave; cao cấp, cầm v&agrave;o tay rất vừa vặn v&agrave; thoải m&aacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế cao cấp tràn viền thời thượng | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533.jpg\" title=\"Thiết kế cao cấp tràn viền thời thượng | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c cạnh b&ecirc;n được v&aacute;t thẳng, bo tr&ograve;n ở c&aacute;c g&oacute;c, ho&agrave;n thiện bằng lớp vỏ kim loại cứng c&aacute;p &iacute;t b&aacute;m mồ h&ocirc;i v&agrave; dấu v&acirc;n tay, khả năng ho&agrave;n thiện cực kỳ cao v&agrave; chắc chắn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế mặt lưng | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-9.jpg\" title=\"Thiết kế mặt lưng | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">N&uacute;t nguồn v&agrave; n&uacute;t tăng giảm &acirc;m lượng được bố tr&iacute; ở cạnh tr&aacute;i, trong khi hệ thống 4 loa ngo&agrave;i được tinh chỉnh bởi AKG của thiết bị được đặt ở cạnh tr&ecirc;n v&agrave; dưới, cho chất lượng &acirc;m thanh tốt, phục vụ nhu cầu giải tr&iacute; di động.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-9.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S7 | Hệ thống 4 loa AKG chất lượng cao\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-9.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Hệ thống 4 loa AKG chất lượng cao\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng giống như người anh em của m&igrave;nh Galaxy Tab S7 chỉ được trang bị cảm biến v&acirc;n tay một chạm nằm ở cạnh b&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225420-015446.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225420-015446.jpg\" title=\"Cảm biến vân tay được đặt ở cạnh bên | Galaxy Tab S7\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-2.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay được đặt ở cạnh bên | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-2.jpg\" title=\"Cảm biến vân tay được đặt ở cạnh bên | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tab S7 c&oacute; k&iacute;ch thước tổng thể si&ecirc;u mỏng v&agrave; nhẹ với k&iacute;ch thước c&aacute;c cạnh lần lượt l&agrave; d&agrave;i 253.8 mm, ngang 165.3 mm, d&agrave;y 6.3 mm c&ugrave;ng trọng lượng 500 gram, dễ d&agrave;ng bỏ trong balo v&agrave; mang theo b&ecirc;n người.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\">M&agrave;n h&igrave;nh sắc n&eacute;t, tốc độ l&agrave;m mới 120 Hz</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 được trang bị m&agrave;n h&igrave;nh LCD 11 inch độ ph&acirc;n giải WQXGA (2.560 x 1.600 pixels) c&oacute; độ phủ gam m&agrave;u 100% DCI-P3 v&agrave; hỗ trợ HDR.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225420-015439.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225420-015439.jpg\" title=\"Màn hình sắc nét, tốc độ làm mới 120 Hz | Galaxy Tab S7\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét, tốc độ làm mới 120 Hz | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-3.jpg\" title=\"Màn hình sắc nét, tốc độ làm mới 120 Hz | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần viền bezels xung quanh m&agrave;n h&igrave;nh cũng đ&atilde; được mỏng dễ nh&igrave;n hơn cho kh&ocirc;ng gian sử dụng rộng r&atilde;i, hiển thị được nhiều th&ocirc;ng tin nhưng vẫn đảm bảo độ sắc n&eacute;t cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đặc biệt,&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng Samsung chính hãng\">Samsung</a>&nbsp;đ&atilde; n&acirc;ng tầm chiếc m&aacute;y t&iacute;nh bảng của m&igrave;nh l&ecirc;n một tầm cao mới khi trang bị cho Galaxy Tab S7 m&agrave;n h&igrave;nh c&oacute; tốc độ l&agrave;m tươi 120 Hz mang đến một trải nghiệm đ&atilde; mắt nhất, ph&ugrave; hợp với việc chơi game, xem Netflix hoặc ngay cả l&agrave;m việc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-4.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S7 | So sánh màn hình giải trí 60 và 120 Hz \" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-4.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | So sánh màn hình giải trí 60 và 120 Hz \" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i ra, m&agrave;n h&igrave;nh tr&ecirc;n Galaxy Tab S7 c&ograve;n hỗ trợ b&uacute;t S-Pen với độ trễ thấp như ở si&ecirc;u phẩm Galaxy Note 20 Ultra c&ugrave;ng thời gian phản hồi l&agrave; 9 ms để cho người d&ugrave;ng trải nghiệm viết tự nhi&ecirc;n nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225520-015515.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225520-015515.jpg\" title=\"Hỗ trợ bút S Pen thông minh | Galaxy Tab S7\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-5.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ bút S Pen thông minh | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-5.jpg\" title=\"Hỗ trợ bút S Pen thông minh | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hỗ trợ c&aacute;c thiết bị th&ocirc;ng minh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đầu ti&ecirc;n, B&uacute;t S Pen tr&ecirc;n Tab S7 cũng đ&atilde; được cải tiến rất nhiều, bạn c&oacute; thể tạo c&aacute;c thao t&aacute;c nhanh như quay lại, trang chủ, gần đ&acirc;y, chọn th&ocirc;ng minh v&agrave; quay m&agrave;n h&igrave;nh, hỗ trợ tốt hơn cho nhiều t&aacute;c vụ như tr&igrave;nh chiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dải nam ch&acirc;m gắn b&uacute;t S Pen nối liền với camera v&agrave; phẳng chứ kh&ocirc;ng l&otilde;m như thế hệ trước cũng như trang bị b&agrave;n ph&iacute;m kết nối với Tab S7 qua nam ch&acirc;m, để sử dụng như laptop.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-1-1.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-1-1.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Hỗ trợ bàn phím rời\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-6.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S7 | Dải nam châm gắn bút S Pen nối liền với camera\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-6.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Dải nam châm gắn bút S Pen nối liền với camera\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chế độ DeX cung cấp giao diện giống như m&aacute;y t&iacute;nh để b&agrave;n, c&ugrave;ng hỗ trợ h&agrave;ng loạt c&aacute;c phần mềm quan trọng h&agrave;ng đầu của Microsoft bao gồm Word, Excel, Powerpoint,... Đ&acirc;y thực sự l&agrave; chiếc m&aacute;y t&iacute;nh bảng Android to&agrave;n diện nhất từ trước đến nay.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Camera k&eacute;p với chất lượng&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Mặt sau của Galaxy Tab S7 được trang bị thiết lập camera k&eacute;p bao gồm cảm biến ch&iacute;nh 13 MP v&agrave; ống k&iacute;nh si&ecirc;u rộng 5 MP.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-11.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 | Hỗ trợ Camera kép\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-11.jpg\" title=\"Galaxy Tab S7 | Hỗ trợ Camera kép\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chất lượng ảnh chụp tr&ecirc;n thiết bị được đ&aacute;nh gi&aacute; kh&aacute; tốt, c&oacute; thể đ&aacute;p ứng tốt nhu cầu th&iacute;ch chụp ảnh của bạn. Ngo&agrave;i ra, Galaxy Tab S7 c&ograve;n c&oacute; khả năng quay video chất lượng cao 4K sắc n&eacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-12.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 | Hỗ trợ quay video 4K\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-12.jpg\" title=\"Galaxy Tab S7 | Hỗ trợ quay video 4K\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mặt trước của thiết bị được trang bị camera selfie 8 MP phục vụ việc chụp ảnh tự sướng v&agrave; c&aacute;c cuộc gọi video call với chất lượng cao.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hiệu năng mạnh mẽ, dẫn đầu ph&acirc;n kh&uacute;c m&aacute;y t&iacute;nh bảng Android</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ g&acirc;y ấn tượng thiết kế v&agrave; m&agrave;n h&igrave;nh, Galaxy Tab S7 c&ograve;n thể hiện m&igrave;nh l&agrave; một thiết bị tốt to&agrave;n diện hơn nữa khi được trang bị vi xử l&yacute; Snapdragon 865+ mạnh mẽ, đ&acirc;y l&agrave; chip mới nhất v&agrave; tốt nhất của Qualcomm ở thời điểm hiện tại.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-1.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-1.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Hiệu năng dẫn đầu thị trường máy tính bảng Android\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-7.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S7 | Hiệu năng dẫn đầu thị trường máy tính bảng Android\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-7.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Hiệu năng dẫn đầu thị trường máy tính bảng Android\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với số điểm m&agrave; Galaxy Tab S7 đo được tr&ecirc;n Antutu l&agrave; 581.720 điểm dễ d&agrave;ng gi&uacute;p m&aacute;y đ&aacute;p ứng tốt c&aacute;c t&aacute;c vụ từ cơ bản đến n&acirc;ng cao, kể cả những hoạt động thực tế ảo VR hay c&aacute;c tựa game 3D y&ecirc;u cầu mức đồ họa cao.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-6gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 6 GB đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute; RAM 6GB</a>, bộ nhớ lưu trữ 128 GB v&agrave; được t&iacute;ch hợp khay sim c&oacute; thể gọi điện, nhắn tin giống như&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chính hãng\">smartphone</a>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-10.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 | Hỗ trợ khe cắm thẻ nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-10.jpg\" title=\"Galaxy Tab S7 | Hỗ trợ khe cắm thẻ nhớ\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Giao diện One UI được sắp xếp hợp l&yacute;, nhiều t&iacute;nh năng đa nhiệm th&ocirc;ng minh cho ph&eacute;p bạn mở v&agrave; sử dụng đồng thời ba ứng dụng c&ugrave;ng l&uacute;c tr&ecirc;n m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Dung lượng pin lớn, hỗ trợ c&ocirc;ng nghệ sạc si&ecirc;u nhanh</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 được trang bị vi&ecirc;n pin dung lượng khổng lồ 8000 mAh, gi&uacute;p bạn sử dụng cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng cần lo lắng về việc hết pin khi đang d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab S7 | Thời lượng pin lớn\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab S7 | Thời lượng pin lớn\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết bị cung cấp thời lượng pin khoảng 7 giờ 30 ph&uacute;t khi duyệt web, 6 giờ 45 ph&uacute;t khi xem video v&agrave; gần 10 giờ chơi game.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Điều ấn tượng hơn nữa l&agrave; chiếc m&aacute;y t&iacute;nh bảng n&agrave;y c&ograve;n hỗ trợ sạc nhanh 45 W gi&uacute;p người d&ugrave;ng c&oacute; thể nhanh ch&oacute;ng sử dụng lại m&aacute;y chỉ trong một thời gian ngắn sạc.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225520-015508.jpg\" onclick=\"return false;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-225520-015508.jpg\" title=\"Hỗ trợ sạc nhanh 45W | Galaxy Tab S7\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-8.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc nhanh 45W | Galaxy Tab S7\" src=\"https://cdn.tgdd.vn/Products/Images/522/225031/samsung-galaxy-tab-s7-800x533-8.jpg\" title=\"Hỗ trợ sạc nhanh 45W | Galaxy Tab S7\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy nhi&ecirc;n c&oacute; thể bạn sẽ phải chi th&ecirc;m tiền th&igrave; mới c&oacute; thể sử dụng được t&iacute;nh năng n&agrave;y, bởi Samsung chỉ tặng k&egrave;m bộ sạc 15 W trong hộp đựng của Galaxy Tab S7 Plus.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Từ những đ&aacute;nh gi&aacute; tr&ecirc;n, c&oacute; thể thấy Galaxy Tab S7 thực sự l&agrave; một đối thủ đ&aacute;ng gờm tr&ecirc;n thị trường m&aacute;y t&iacute;nh bảng hiện tại. Chiếc m&aacute;y t&iacute;nh bảng n&agrave;y sở hữu m&agrave;n h&igrave;nh tuyệt đẹp với tốc độ l&agrave;m mới 120Hz si&ecirc;u mượt, thời lượng pin tốt c&ugrave;ng những cải tiến b&uacute;t S Pen v&agrave; hiệu năng si&ecirc;u khủng, mang lại năng suất l&agrave;m việc v&agrave; giải tr&iacute; cao hơn. Nếu bạn đ&atilde; quen thuộc sử dụng c&aacute;c m&aacute;y t&iacute;nh bảng Android th&igrave; Galaxy Tab S7 trong năm 2020 l&agrave; chiếc m&aacute;y kh&ocirc;ng thể bỏ qua.</span></p>\r\n', 0, '1657385177_samsung-galaxy-tab-s7-xanh-duong-1-org.jpg', 18990000, 15, 30);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(49, ' Galaxy Tab S7 FE 4G', '<h2><span style=\"font-size:16px\"><strong><a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung chính hãng tại Thegioididong.com\" type=\"Tham khảo sản phẩm Samsung chính hãng tại Thegioididong.com\">Samsung</a>&nbsp;ch&iacute;nh thức tr&igrave;nh l&agrave;ng mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng, tablet chính hãng, nhiều khuyến mãi hấp dẫn tại Thegioididong.com\" type=\"Tham khảo giá máy tính bảng, tablet chính hãng, nhiều khuyến mãi hấp dẫn tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;c&oacute; t&ecirc;n&nbsp;Galaxy Tab S7 FE, m&aacute;y trang bị cấu h&igrave;nh mạnh mẽ, m&agrave;n h&igrave;nh giải tr&iacute; si&ecirc;u lớn v&agrave; điểm ấn tượng nhất l&agrave; vi&ecirc;n pin si&ecirc;u khủng được t&iacute;ch hợp b&ecirc;n trong, gi&uacute;p tăng hiệu suất l&agrave;m việc nhưng vẫn c&oacute; t&iacute;nh di động cực cao.</strong></span></h2>\r\n', '<h3><span style=\"font-size:14px\"><strong>Vi&ecirc;n pin &ldquo;khủng long&rdquo; vượt mốc 10000 mAh</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 FE sẽ khiến bạn cho&aacute;ng ngợp với dụng lượng&nbsp;pin cực khủng&nbsp;10090 mAh đảm bảo cho cường độ l&agrave;m việc, giải tr&iacute; li&ecirc;n tục trong nhiều giờ liền.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-05.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Dung lượng pin lớn 10.090 mAh\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-05.jpg\" title=\"Galaxy Tab S7 FE | Dung lượng pin lớn 10.090 mAh\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Theo c&ocirc;ng bố từ Samsung, với dung lượng lớn n&agrave;y gi&uacute;p cho Galaxy Tab S7 FE c&oacute; thể d&ugrave;ng l&ecirc;n tới 12 giờ lướt web với mạng LTE v&agrave; 13 giờ xem video trực tuyến qua wifi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&ecirc;n cạnh đ&oacute;, thời gian sạc cũng được r&uacute;t ngắn đ&aacute;ng kể khi&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có sạc nhanh đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute;&nbsp;sạc nhanh</a>&nbsp;c&ocirc;ng suất tối đa l&ecirc;n đến 45 W, tuy nhi&ecirc;n để sạc với tốc độ n&agrave;y bạn cần mua củ sạc ph&ugrave; hợp v&igrave; sạc k&egrave;m theo m&aacute;y chỉ 15 W.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Chơi hết sức, l&agrave;m việc hết m&igrave;nh với m&agrave;n h&igrave;nh si&ecirc;u lớn</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s7-fan-editon\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng Samsung Galaxy Tab S7 FE đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng Samsung Galaxy Tab S7 FE</a>&nbsp;c&oacute; một phi&ecirc;n bản m&agrave;n h&igrave;nh duy nhất với&nbsp;k&iacute;ch thước lớn 12.4 inch, phần viền bao quanh được thu nhỏ, tạo n&ecirc;n kh&ocirc;ng gian rộng r&atilde;i, thoải m&aacute;i cho bạn l&agrave;m việc hay giải tr&iacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-03.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Màn hình kích thước lớn 12.4 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-03.jpg\" title=\"Galaxy Tab S7 FE | Màn hình kích thước lớn 12.4 inch\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh của m&aacute;y c&oacute; độ ph&acirc;n giải&nbsp;1600 x 2560 pixel đảm bảo cho chất lượng h&igrave;nh ảnh sắc n&eacute;t tr&ecirc;n m&agrave;n h&igrave;nh lớn. M&agrave;n h&igrave;nh lớn gi&uacute;p c&aacute;c nội dung giải tr&iacute; được hiển thị tối ưu khi cầm m&aacute;y ở cả chiều ngang hay dọc, cho bạn trải nghiệm chơi game v&agrave; xem phim tuyệt vời nhất.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Thiết kế cao cấp, t&iacute;nh di động cao</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 FE vẫn sử dụng thiết kế v&aacute;t thẳng mạnh mẽ ở phần cạnh viền tương tự&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s7\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng Samsung Galaxy Tab S7 chính hãng tại Thegioididong.com\" type=\"Tham khảo giá máy tính bảng Samsung Galaxy Tab S7 chính hãng tại Thegioididong.com\">Galaxy Tab S7</a>.&nbsp;Tuy vậy, cảm gi&aacute;c cầm S7 FE vẫn rất tốt, c&aacute;c g&oacute;c cạnh đều đ&atilde; được xử l&yacute; mềm mại hơn, để kh&ocirc;ng g&acirc;y bất kỳ sự kh&oacute; chịu hay cấn tay khi cầm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-01.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Thiết kế tổng thể\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-01.jpg\" title=\"Galaxy Tab S7 FE | Thiết kế tổng thể\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&acirc;n m&aacute;y được ho&agrave;n thiện nguy&ecirc;n khối, được gia c&ocirc;ng bề mặt v&ocirc; c&ugrave;ng ho&agrave;n hảo, dải anten được bố tr&iacute; v&agrave; l&agrave;m đồng m&agrave;u với th&acirc;n m&aacute;y tạo n&ecirc;n thiết kế liền mạch v&agrave; đẳng cấp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/tab-s7-fe-1.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Xử lý tốt công việc trên màn hình lớn\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/tab-s7-fe-1.jpg\" title=\"Galaxy Tab S7 FE | Xử lý tốt công việc trên màn hình lớn\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 FE cũng l&agrave; chiếc m&aacute;y t&iacute;nh bảng c&oacute; độ mỏng ấn tượng 6.3 mm, d&ugrave; được trang bị vi&ecirc;n pin cực khủng. Trọng lượng 608 g nhẹ hơn nhiều so với&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo giá Laptop, máy tính xách tay chính hãng, nhiều khuyến mãi tại thegioididong.com\" type=\"Tham khảo giá Laptop, máy tính xách tay chính hãng, nhiều khuyến mãi tại thegioididong.com\">laptop</a>, thoải m&aacute;i để bạn mang theo b&ecirc;n m&igrave;nh v&agrave; xử l&yacute; c&ocirc;ng việc, kiểm tra email ở bất cứ đ&acirc;u.</span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Cấu h&igrave;nh của Galaxy Tab S7 FE phần n&agrave;o đ&atilde; được cắt giảm so với phi&ecirc;n bản ti&ecirc;u chuẩn, nhưng c&aacute;c tựa game đồ họa cao v&agrave; ứng dụng văn ph&ograve;ng vẫn kh&ocirc;ng l&agrave;m kh&oacute; được mẫu m&aacute;y n&agrave;y, nhờ trang bị&nbsp;vi xử l&yacute; Snapdragon 750G gi&uacute;p cho m&aacute;y c&oacute; tốc độ mở ứng dụng kh&aacute; nhanh v&agrave; chơi game 3D mượt m&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/tab-s7-fe-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Trang bị con chip Snapdragon 750G\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/tab-s7-fe-2.jpg\" title=\"Galaxy Tab S7 FE | Trang bị con chip Snapdragon 750G\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang-ram-4gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 4 GB đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 4 GB</a>&nbsp;đ&aacute;p ứng tốt nhu cầu đa nhiệm của m&aacute;y v&agrave;&nbsp;bộ nhớ trong 64 GB&nbsp;hỗ trợ mở rộng th&ecirc;m dung lượng qua thẻ nhớ cho bạn thoải m&aacute;i lưu trữ dữ liệu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-08.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Nhu cầu đa nhiệm tốt\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-08.jpg\" title=\"Galaxy Tab S7 FE | Nhu cầu đa nhiệm tốt\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung tiếp tục mang&nbsp;l&ecirc;n d&ograve;ng Tab S7 FE&nbsp;hệ thống loa AKG chuẩn Dolby Atmos gi&uacute;p n&acirc;ng tầm trải nghiệm như nghe nhạc, xem phim với chất &acirc;m cao cấp được tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia &acirc;m thanh h&agrave;ng đầu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-02.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Trang bị hệ thống loa AKG\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-02.jpg\" title=\"Galaxy Tab S7 FE | Trang bị hệ thống loa AKG\" /></a></span></p>\r\n\r\n<h3><span style=\"font-size:14px\">Thỏa m&atilde;n s&aacute;ng tạo với b&uacute;t S-Pen</span></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S7 FE tương th&iacute;ch với b&uacute;t S-Pen thế hệ mới, cải tiến với độ trễ cực thấp mang đến những n&eacute;t vẽ tự nhi&ecirc;n v&agrave; ch&acirc;n thật cho ph&eacute;p bạn thỏa sức s&aacute;ng tạo, thực hiện c&aacute;c &yacute; tưởng của m&igrave;nh mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-11.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Hỗ trợ với bút S-Pen\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-11.jpg\" title=\"Galaxy Tab S7 FE | Hỗ trợ với bút S-Pen\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hỗ trợ chế độ Samsung DeX c&ugrave;ng b&agrave;n ph&iacute;m rời, gi&uacute;p cho Galaxy Tab S7 FE trở th&agrave;nh một chiếc m&aacute;y t&iacute;nh c&aacute; nh&acirc;n với giao diện đa cửa sổ tương tự Window, gi&uacute;p bạn xử l&yacute; nhiều c&ocirc;ng việc c&ugrave;ng l&uacute;c tr&ecirc;n 1 m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-07.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab S7 FE | Hỗ trợ kết nối với bàn phím ngoài\" src=\"https://cdn.tgdd.vn/Products/Images/522/240254/samsung-galaxy-tab-s7-fan-editon-07.jpg\" title=\"Galaxy Tab S7 FE | Hỗ trợ kết nối với bàn phím ngoài\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Lưu &yacute;: C&aacute;c phụ kiện hỗ trợ như b&agrave;n ph&iacute;m sẽ kh&ocirc;ng đi k&egrave;m trong hộp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">T&oacute;m lại, Galaxy Tab S7 FE được xem l&agrave; phi&ecirc;n bản r&uacute;t gọn Galaxy Tab S7 mang đến một thiết bị c&oacute; mức gi&aacute; hợp l&yacute; hơn những vẫn đầy đủ những t&iacute;nh năng của d&ograve;ng Galaxy Tab. Để t&igrave;m một chiếc tablet vừa mạnh mẽ vừa c&oacute; thể linh động sẵn s&agrave;ng mang đi đến bất kỳ đ&acirc;u, th&igrave; Galaxy Tab S7 FE sẽ l&agrave; c&aacute;i t&ecirc;n s&aacute;ng gi&aacute; m&agrave; bạn n&ecirc;n c&acirc;n nhắc.</span></p>\r\n', 0, '1657385351_samsung-galaxy-tab-s7-fe-wifi-1.jpg', 15990000, 15, 30),
(50, 'Galaxy Tab S8', '<h2><strong><span style=\"font-size:16px\">Samsung Galaxy Tab S8 ra mắt v&agrave; vẫn giữ được đặc trưng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung-galaxy-tab-s\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng Galaxy Tab S đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng&nbsp;Galaxy Tab S</a>&nbsp;với cấu h&igrave;nh mạnh mẽ, m&agrave;n h&igrave;nh hiển thị ổn c&ugrave;ng khả năng hỗ trợ b&uacute;t S Pen hỗ trợ học tập l&agrave;m việc tốt hơn.</span></strong></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế đặc trưng Galaxy Tab S</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">X&eacute;t về mặt ngoại h&igrave;nh th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s8\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng Samsung Galaxy Tab S8 đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng&nbsp;Samsung Galaxy Tab S8</a>&nbsp;thừa hưởng những thiết kế nổi bật của&nbsp;Tab S7, về tổng thể th&igrave; Tab S8 l&agrave; phi&ecirc;n bản n&acirc;ng cấp phần cứng b&ecirc;n trong so với Tab S7.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113835.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế đặc trưng - Samsung Galaxy Tab S8\" src=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113835.jpg\" title=\"Thiết kế đặc trưng - Samsung Galaxy Tab S8\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">To&agrave;n bộ phần th&acirc;n m&aacute;y vẫn được l&agrave;m từ kim loại nguy&ecirc;n khối sang trọng, đi c&ugrave;ng với viền m&agrave;n h&igrave;nh mỏng, đem lại cảm gi&aacute;c hiện đại cũng như rộng r&atilde;i trong qu&aacute; tr&igrave;nh sử dụng.<br />\r\nGalaxy Tab S8 cũng được trang bị b&uacute;t S Pen cải tiến mới, c&oacute; độ nhạy 6.2 ms cho cảm gi&aacute;c giống như đang viết tr&ecirc;n giấy.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113841.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ bút S Pen - Samsung Galaxy Tab S8\" src=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113841.jpg\" title=\"Hỗ trợ bút S Pen - Samsung Galaxy Tab S8\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Mạnh sẽ với chip Snapdragon</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Tab S8 sử dụng CPU Snapdragon 8 Gen1 mới, mang lại hiệu năng cực kỳ ổn định v&agrave; vấn đề nhiệt độ đ&atilde; được giải quyết v&agrave; được kiểm so&aacute;t hơn v&igrave; k&iacute;ch thước m&aacute;y t&iacute;nh bảng kh&aacute; lớn khi so với điện thoại, gi&uacute;p khả năng tản nhiệt được tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 8 GB đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 8 GB</a>&nbsp;n&ecirc;n mọi t&aacute;c vụ của bạn sẽ được khởi chạy mượt m&agrave;, đa nhiệm tốt hơn. Bộ nhớ trong 128 GB c&oacute; hỗ trợ mở rộng qua thẻ MicroSD.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-1-1.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8\" src=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-1-1.jpg\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Snapdragon 8 Gen 1 c&ograve;n hỗ trợ kết nối 5G ti&ecirc;n tiến bật nhất hiện nay, cho mọi trải nghiệm tải l&ecirc;n, xuống của bạn tr&ecirc;n kh&ocirc;ng gian mạng được trơn tru nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với hệ điều h&agrave;nh Android 12 bạn c&oacute; thể c&oacute; chọn k&iacute;ch thước, vị tr&iacute; v&agrave; số lượng cửa sổ bật l&ecirc;n để bạn thực hiện nhiều t&aacute;c vụ c&ugrave;ng 1 l&uacute;c, đa nhiệm tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&ugrave;ng với đ&oacute; l&agrave; giao diện Samsung Dex cải tiến cho ph&eacute;p bạn l&agrave;m việc với Galaxy Tab S8 như với một chiếc m&aacute;y t&iacute;nh thực thụ.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Khả năng quay, chụp tốt</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng</a>&nbsp;c&oacute; cụm 2 camera với camera ch&iacute;nh 13 MP v&agrave; camera phụ 6 MP, hỗ trợ quay video độ ph&acirc;n giải 4K. Camera trước g&oacute;c si&ecirc;u rộng 12 MP với t&iacute;nh năng Auto Framing lu&ocirc;n đặt bạn v&agrave;o trung t&acirc;m khung h&igrave;nh trong mọi t&igrave;nh huống.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-2.jpg\" onclick=\"return false;\"><img alt=\"Camera Samsung Galaxy Tab S8\" src=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-2.jpg\" title=\"Camera Samsung Galaxy Tab S8\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Th&ecirc;m kh&ocirc;ng gian hiển thị</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab S8 c&oacute; m&agrave;n h&igrave;nh 11 inch, độ ph&acirc;n giải đến 2K cho bạn trải nghiệm cực m&atilde;n nh&atilde;n, c&ugrave;ng với tần số qu&eacute;t 120 Hz mọi chuyển động tr&ecirc;n m&agrave;n h&igrave;nh đều trở n&ecirc;n mượt m&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;u sắc được hiển thị tr&ecirc;n m&agrave;n h&igrave;nh một c&aacute;ch tươi s&aacute;ng, h&igrave;nh ảnh r&otilde; n&eacute;t mang lại trải nghiệm tuyệt vời về phần nh&igrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113839.jpg\" onclick=\"return false;\"><img alt=\"Màn hình hiển thị tốt - Samsung Galaxy Tab S8\" src=\"https://cdn.tgdd.vn/Products/Images/522/247510/samsung-galaxy-tab-s8-040322-113839.jpg\" title=\"Màn hình hiển thị tốt - Samsung Galaxy Tab S8\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Pin dung lượng lớn, c&ocirc;ng suất sạc được n&acirc;ng cấp</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Ngo&agrave;i việc được trang bị một con chip khủng, m&agrave;n h&igrave;nh c&oacute; tần số qu&eacute;t cao&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo một số sản phẩm Samsung  đang kinh doanh tại Thế Giới Di Động \">Samsung</a>&nbsp;đ&atilde; trang bị tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng thế hệ mới vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n đến 8000 mAh, c&ugrave;ng với đ&oacute; l&agrave; c&ocirc;ng nghệ sạc nhanh 45 W.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bạn c&oacute; thể y&ecirc;n t&acirc;m sử dụng Tab S8 cho c&aacute;c nhu cầu học h&agrave;nh, l&agrave;m việc, giải tr&iacute; suốt cả một ng&agrave;y d&agrave;i v&agrave; sạc đầy pin một c&aacute;ch nhanh ch&oacute;ng với c&ocirc;ng nghệ sạc nhanh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nếu bạn đang cần một chiếc tablet c&oacute; một thiết kế tinh tế, nhỏ gọn, thường xuy&ecirc;n di chuyển v&agrave; mang m&aacute;y t&iacute;nh bảng b&ecirc;n m&igrave;nh để học tập v&agrave; l&agrave;m việc th&igrave; chắc chắn Galaxy Tab S8 sẽ l&agrave; một lựa chọn ph&ugrave; hợp.</span></p>\r\n', 0, '1657385508_samsung-galaxy-tab-s8-white-1.jpg', 20990000, 7, 30),
(51, 'Galaxy Tab A7 Lite', '<h2><span style=\"font-size:16px\"><strong><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a7-lite\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng Samsung Galaxy Tab A7 Lite đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng Samsung Galaxy Tab A7 Lite</a>&nbsp;l&agrave; phi&ecirc;n bản r&uacute;t gọn của&nbsp;d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng đang kinh doanh tại Thế Giới Di Động \">tablet</a>&nbsp;&quot;ăn kh&aacute;ch&quot;&nbsp;Galaxy Tab A7&nbsp;thuộc thương hiệu&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung chính hãng mới nhất tại Thegioididong.com\" type=\"Tham khảo sản phẩm Samsung chính hãng mới nhất tại Thegioididong.com\">Samsung</a>, đ&aacute;p ứng nhu cầu giải tr&iacute; của&nbsp;kh&aacute;ch h&agrave;ng thuộc ph&acirc;n kh&uacute;c b&igrave;nh d&acirc;n với m&agrave;n h&igrave;nh lớn nhưng vẫn gọn nhẹ hợp t&uacute;i tiền.</strong></span></h2>\r\n', '<h3><strong><span style=\"font-size:14px\">Thiết kế si&ecirc;u mỏng nhẹ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab A7 Lite sở hữu kiểu thiết kế đặc trưng của d&ograve;ng Galaxy Tab A7. Thiết bị sử dụng chất liệu nh&ocirc;m với thiết kế nguy&ecirc;n khối gi&uacute;p cho m&aacute;y trở n&ecirc;n cao cấp hơn d&ugrave; chỉ thuộc ph&acirc;n kh&uacute;c tầm trung gi&aacute; rẻ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-002.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-002.jpg\" title=\"Galaxy Tab A7 Lite | Thiết kế mặt lưng\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy được ho&agrave;n thiện từ nh&ocirc;m nguy&ecirc;n khối, nhưng Galaxy Tab A7 Lite vẫn c&oacute; độ mỏng nhẹ ấn tượng khi chỉ nặng 371 g v&agrave; d&agrave;y chỉ 8 mm, mang đến sự thoải m&aacute;i khi cầm sử dụng trong thời gian d&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-004.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Thiết kế cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-004.jpg\" title=\"Galaxy Tab A7 Lite | Thiết kế cạnh viền\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với k&iacute;ch thước chỉ tương đương với một quyển sổ n&ecirc;n&nbsp;Galaxy Tab A7 Lite sẽ&nbsp;v&ocirc; c&ugrave;ng gọn g&agrave;ng để bạn c&oacute; thể mang theo khi cần thiết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-009.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Thiết kế gọn ràng, cầm nắm thoải mái\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-009.jpg\" title=\"Galaxy Tab A7 Lite | Thiết kế gọn ràng, cầm nắm thoải mái\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">M&agrave;n h&igrave;nh lớn hiển thị nhiều hơn, r&otilde; hơn</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y t&iacute;nh bảng Galaxy Tab A7 Lite c&oacute; m&agrave;n h&igrave;nh k&iacute;ch thước 8.7 inch, độ ph&acirc;n giải cao 800 x 1340 pixel, cho chất lượng hiển thị kh&aacute;, m&agrave;u sắc tươi tắn, m&agrave;n h&igrave;nh lớn sẽ gi&uacute;p bạn xem được nhiều nội dung hơn, thoải m&aacute;i k&eacute;o thả, chỉnh sửa h&igrave;nh ảnh, văn bản dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-001.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Trang bị màn hình kích thước lớn 8.7 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-001.jpg\" title=\"Galaxy Tab A7 Lite | Trang bị màn hình kích thước lớn 8.7 inch\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phần viền 2 b&ecirc;n cũng được Samsung l&agrave;m mỏng tối đa gi&uacute;p cho khung h&igrave;nh được trải rộng hơn, tăng trải nghiệm khi xem phim hay thưởng thức c&aacute;c chương tr&igrave;nh giải tr&iacute;.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hiệu năng ổn định cho nhu cầu giải tr&iacute; cơ bản</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Trang bị vi xử l&yacute; MediaTek MT8768T 8 nh&acirc;n xung nhịp tối đa 2.3 GHz, Galaxy Tab A7 Lite c&oacute; hiệu năng ổn định, đ&aacute;p ứng cho nhu cầu giải tr&iacute; kh&aacute;c nhau như chơi game nhẹ nh&agrave;ng, kiểm tra email, tin nhắn hay đọc b&aacute;o cập nhật tin tức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-09.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Trang bị con chip Helio P22T đến từ MediaTek\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-09.jpg\" title=\"Galaxy Tab A7 Lite | Trang bị con chip Helio P22T đến từ MediaTek\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang-ram-3gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 3 GB  đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 3 GB</a>&nbsp;v&agrave;&nbsp;bộ nhớ trong 32 GB&nbsp;l&agrave; những th&ocirc;ng số kh&aacute; vừa vặn trong tầm gi&aacute;, vẫn đủ cho đa nhiệm v&agrave; nhu cầu lưu trữ ở mức độ cơ bản. B&ecirc;n cạnh đ&oacute;, m&aacute;y hỗ trợ khe cắm thẻ nhớ l&ecirc;n đến 1 TB gi&uacute;p tăng th&ecirc;m kh&ocirc;ng gian để lưu trữ những b&agrave;i h&aacute;t hay bộ phim y&ecirc;u th&iacute;ch của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-008.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | RAM 3 GB, ROM 32 GB hỗ trợ thẻ nhớ mở rộng lên đến 1 TB\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-008.jpg\" title=\"Galaxy Tab A7 Lite | RAM 3 GB, ROM 32 GB hỗ trợ thẻ nhớ mở rộng lên đến 1 TB\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab A7 Lite trang bị vi&ecirc;n pin 5100 mAh gi&uacute;p m&aacute;y c&oacute; thể hoạt động li&ecirc;n tục trong nhiều giờ liền với c&aacute;c t&aacute;c vụ th&ocirc;ng thường.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Kh&ocirc;ng chỉ vậy, m&aacute;y cũng hỗ trợ&nbsp;sạc nhanh&nbsp;l&ecirc;n đến 15 W gi&uacute;p pin được nạp đầy nhanh ch&oacute;ng. Tuy nhi&ecirc;n, sạc đi k&egrave;m m&aacute;y chỉ 7.75 W n&ecirc;n bạn cần mua ri&ecirc;ng phụ kiện tương th&iacute;ch nếu muốn sạc nhanh hơn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-007.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Hỗ trợ sạc nhanh 15 W\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-007.jpg\" title=\"Galaxy Tab A7 Lite | Hỗ trợ sạc nhanh 15 W\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab A7 Lite hỗ trợ đầy đủ c&aacute;c kết nối mạng th&ocirc;ng dụng như Wi-Fi, mạng 4G với tốc độ truy cập nhanh v&agrave; ổn định. Bạn cũng c&oacute; thể lắp được&nbsp;sim nghe gọi, cho ph&eacute;p Galaxy Tab A7 Lite c&oacute; thể nhận v&agrave; thực hiện được cuộc gọi như&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại, smartphone chính hãng, nhiều khuyến mãi HOT nhất tại Thegioididong.com\" type=\"Tham khảo giá điện thoại, smartphone chính hãng, nhiều khuyến mãi HOT nhất tại Thegioididong.com\">điện thoại</a>&nbsp;th&ocirc;ng thường.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Thưởng thức chất &acirc;m sống động từ loa stereo</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Đắm ch&igrave;m trong thế giới giải tr&iacute; với loa stereo c&ocirc;ng nghệ Dolby Atmos mang đến chất &acirc;m lớn v&agrave; sống động, cho bạn trải nghiệm nghe nhạc hay xem phim sẽ c&agrave;ng ho&agrave;n hảo v&agrave; ch&acirc;n thật hơn bao giờ hết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-005.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Đắm chìm trong thế giới giải trí với loa stereo công nghệ Dolby Atmos\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-005.jpg\" title=\"Galaxy Tab A7 Lite | Đắm chìm trong thế giới giải trí với loa stereo công nghệ Dolby Atmos\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&aacute;y cũng trang bị camera sau 8 MP&nbsp;quay được video Full HD v&agrave; camera trước 2 MP hỗ trợ gọi video call cũng như ghi lại những tấm ảnh chụp thường ng&agrave;y của gia đ&igrave;nh v&agrave; cuộc sống xung quanh bạn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-003.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A7 Lite | Camera sau 8 MP\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-003.jpg\" title=\"Galaxy Tab A7 Lite | Camera sau 8 MP\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">OneUI 3.1 th&ocirc;ng minh, mượt m&agrave; dễ sử dụng</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Galaxy Tab A7 Lite sử dụng giao diện OneUI 3.1 ho&agrave;n to&agrave;n mới được tối ưu, với c&aacute;c thao t&aacute;c sử dụng v&ocirc; c&ugrave;ng th&acirc;n thiện đơn giản v&agrave; kh&ocirc;ng chứa ứng dụng r&aacute;c g&acirc;y nặng m&aacute;y, đảm bảo cho bạn c&oacute; một trải nghiệm tốt nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Với mức gi&aacute; phải chăng, cấu h&igrave;nh ổn, m&agrave;n h&igrave;nh lớn nhưng vẫn nhỏ gọn, Galaxy Tab A7 Lite sẽ l&agrave; một trong những sản phẩm ph&ugrave; hợp với mọi người đ&aacute;p ứng tốt nhu cầu học tập cũng như&nbsp;cho nhu cầu giải tr&iacute; hằng ng&agrave;y.</span></p>\r\n', 0, '1657385777_samsung-galaxy-tab-a7-lite-1-3-org.jpg', 4490000, 11, 29),
(52, ' Galaxy Tab A8', '<h2><span style=\"font-size:16px\"><strong>Cấu h&igrave;nh M&aacute;y t&iacute;nh bảng Samsung Galaxy Tab A8 (2022)</strong></span></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>M&agrave;n h&igrave;nh:</p>\r\n\r\n	<p>10.5&quot;TFT LCD</p>\r\n	</li>\r\n	<li>\r\n	<p>Hệ điều h&agrave;nh:</p>\r\n\r\n	<p>Android 11</p>\r\n	</li>\r\n	<li>\r\n	<p>Chip:</p>\r\n\r\n	<p>UniSOC T618</p>\r\n	</li>\r\n	<li>\r\n	<p>RAM:</p>\r\n\r\n	<p>4 GB</p>\r\n	</li>\r\n	<li>\r\n	<p>Bộ nhớ trong:</p>\r\n\r\n	<p>64 GB</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết nối:</p>\r\n\r\n	<p>Hỗ trợ 4GC&oacute; nghe gọi</p>\r\n	</li>\r\n	<li>\r\n	<p>SIM:</p>\r\n\r\n	<p>1 Nano SIM</p>\r\n	</li>\r\n	<li>\r\n	<p>Camera sau:</p>\r\n\r\n	<p>8 MP</p>\r\n	</li>\r\n	<li>\r\n	<p>Camera trước:</p>\r\n\r\n	<p>5 MP</p>\r\n	</li>\r\n	<li>\r\n	<p>Pin, Sạc:</p>\r\n\r\n	<p>7040 mAh15 W</p>\r\n	</li>\r\n</ul>\r\n', '<h3><strong><span style=\"font-size:14px\">Nghe nh&igrave;n m&atilde;n nh&atilde;n với m&agrave;n h&igrave;nh lớn v&agrave; &acirc;m thanh ch&acirc;n thật&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a8\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng Samsung Galaxy Tab A8 (2022) đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng Samsung Galaxy Tab A8 (2022)</a>&nbsp;c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh<strong>&nbsp;10.5 inch</strong>, tỉ lệ&nbsp;16:10 cho kh&ocirc;ng gian hiển thị rộng hơn, rất l&yacute; tưởng cho người d&ugrave;ng trải nghiệm xem phim, livestream, chơi game.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&agrave;n h&igrave;nh&nbsp;<strong>TFT LCD</strong>&nbsp;c&oacute; độ ph&acirc;n giải&nbsp;1200 x 1920 Pixels t&aacute;i hiện h&igrave;nh ảnh kh&aacute; chi tiết, m&agrave;u sắc trung thực, phong ph&uacute;. Tận hưởng &acirc;m thanh sống động đến từ hệ thống&nbsp;4 loa hỗ trợ c&ocirc;ng nghệ&nbsp;<strong>Dolby Atmos</strong>&nbsp;cung cấp &acirc;m thanh v&ograve;m b&ugrave;ng nổ, nghe nhạc rất đ&atilde; tai.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114017.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Nghe nhìn mãn nhãn với màn hình lớn và âm thanh chân thật\" src=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114017.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Nghe nhìn mãn nhãn với màn hình lớn và âm thanh chân thật\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thiết kế mỏng gọn với độ d&agrave;y chỉ 6.9 mm, khung v&agrave; mặt lưng bằng kim loại tạo cho&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" title=\"Tham khảo giá máy tính bảng, tablet chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;vẻ ngo&agrave;i đẳng cấp, sang trọng, cầm đầm tay nhưng thoải m&aacute;i.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Hiệu năng mạnh mẽ</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Sử dụng vi xử l&yacute;<strong>&nbsp;UniSOC T618</strong>&nbsp;cho hiệu suất CPU v&agrave; GPU nhanh hơn 10% so với d&ograve;ng Tab A7,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-4gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có RAM 4 GB đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 4 GB</a>&nbsp;gi&uacute;p trải nghiệm c&aacute;c t&aacute;c vụ văn ph&ograve;ng, giải tr&iacute; mượt m&agrave;. Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" title=\"Tham khảo giá máy tính bảng, tablet Samsung chính hãng\">m&aacute;y t&iacute;nh bảng Samsung</a>&nbsp;cho khả năng lưu trữ cực ấn tượng với bộ nhớ trong&nbsp;<strong>64 GB</strong>, d&ugrave;ng được với thẻ nhớ ngo&agrave;i hỗ trợ dung lượng l&ecirc;n đến&nbsp;<strong>1 TB</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114020.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114020.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Hiệu năng mạnh mẽ\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Chụp h&igrave;nh đẹp với camera trước v&agrave; sau</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Trong khi camera sau c&oacute; độ ph&acirc;n giải&nbsp;<strong>8 MP</strong>&nbsp;c&oacute; khả năng tự động lấy n&eacute;t, quay được video chuẩn Full HD với tốc độ khung h&igrave;nh&nbsp;30 fps&nbsp;th&igrave; camera trước cảm biến&nbsp;<strong>5 MP</strong>&nbsp;cho bạn gọi video, chụp h&igrave;nh ch&acirc;n dung với chất lượng tốt, ảnh chụp n&eacute;t đẹp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114019.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Học tập và giảng dạy trực tuyến tiện lợi với camera trước và sau\" src=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114019.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Học tập và giảng dạy trực tuyến tiện lợi với camera trước và sau\" /></a></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14px\">Giải tr&iacute; thoải m&aacute;i cả ng&agrave;y&nbsp;</span></strong></h3>\r\n\r\n<p><span style=\"font-size:14px\">Thỏi pin Li-Po cho dung lượng lớn&nbsp;<strong>7040 mAh</strong>, cung cấp năng lượng đủ để bạn lướt web, chơi game, xem phim h&agrave;ng giờ liền m&agrave; kh&ocirc;ng sợ hết pin, dễ d&agrave;ng tận hưởng bất kỳ sở th&iacute;ch n&agrave;o của bạn. Hỗ trợ c&ocirc;ng nghệ&nbsp;<strong><a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo thêm các dòng máy tính bảng hỗ trợ sạc nhanh tại Thegioididong.com\" type=\"Tham khảo thêm các dòng máy tính bảng hỗ trợ sạc nhanh tại Thegioididong.com\">sạc nhanh</a></strong>&nbsp;<strong>15 W</strong>&nbsp;gi&uacute;p m&aacute;y t&iacute;nh bảng nạp pin nhanh ch&oacute;ng, giảm thời gian chờ đợi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114022.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Giải trí thoải mái cả ngày\" src=\"https://cdn.tgdd.vn/Products/Images/522/251704/samsung-galaxy-tab-a8-170122-114022.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A8 (2022) - Giải trí thoải mái cả ngày\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Samsung Galaxy Tab A8 (2022) l&agrave; 1 phi&ecirc;n bản tuyệt vời trong Galaxy Tab A Series, sản phẩm n&agrave;y chắc chắn sẽ trở th&agrave;nh c&ocirc;ng cụ li&ecirc;n lạc online trong thời hiện đại m&agrave; bạn chắc chắn sẽ rất h&agrave;i l&ograve;ng khi sở hữu.</span></p>\r\n', 0, '1657385959_samsung-galaxy-tab-a8-1-1.jpg', 8490000, 12, 29);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(53, 'Smart TV Crystal UHD 4K 65 inch AU8000 2021', '<h2><span style=\"font-size:16px\"><strong>Điểm nổi bật&nbsp;Smart TV Crystal UHD 4K 65 inch AU8000 2021</strong></span></h2>\r\n\r\n<p>Tận hưởng m&agrave;u sắc ch&acirc;n thực nhờ c&ocirc;ng nghệ Dynamic Crystal 4K<br />\r\nDynamic Crystal Color đắm m&igrave;nh v&agrave;o khung h&igrave;nh<br />\r\nDễ d&agrave;ng t&igrave;m kiếm nội dung nhờ Trợ l&yacute; ảo Google<br />\r\nMulti View xem nhiều nội dung tr&ecirc;n một khung h&igrave;nh</p>\r\n', '<p style=\"text-align:left\"><span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Helvetica&quot;,sans-serif\"><span style=\"color:black\">Sắc M&agrave;u Ấn Tượng Tr&ecirc;n Thiết Kế Mỏng Chưa Từng C&oacute;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\"><img alt=\"https://cdn.mediamart.vn/images/Upload/images/1(963).jpg\" src=\"https://cdn.mediamart.vn/images/Upload/images/1(963).jpg\" style=\"height:493px; width:800px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Helvetica&quot;,sans-serif\"><span style=\"color:black\">Ch&acirc;n Thực Sắc M&agrave;u Với Trạng Th&aacute;i Tinh Khiết Của Tinh Thể Mịn</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">C&ocirc;ng Nghệ Dynamic Crystal Color